<script>
const sketch = (p) => {
    // State Variables
    let spiralType = 'double';
    let itemType = 'point';
    let spiralGrowth = 0.15;

    // Locators (using p5.Vector for x,y coordinates which represent complex numbers)
    let z0_loc;
    let z_plus_loc;
    let z_minus_loc;

    // Dragging state
    let draggingZ0 = false;
    let draggingZPlus = false;
    let draggingZMinus = false;
    const locatorRadius = 10;

    // Fox shape vertices (relative to origin, facing right)
    const fox_vertices = [
        {x:-15, y:0}, {x:-10, y:-5}, {x:-5, y:-4}, {x:0, y:-5}, {x:5, y:-5}, 
        {x:8, y:-2}, {x:10, y:-6}, {x:12, y:-6}, {x:15, y:0}, {x:12, y:6}, 
        {x:10, y:6}, {x:8, y:2}, {x:5, y:5}, {x:0, y:5}, {x:-5, y:4}, {x:-10, y:5}
    ];

    p.setup = () => {
        const canvasContainer = document.getElementById('canvas-container');
        const canvas = p.createCanvas(canvasContainer.offsetWidth, 600);
        canvas.id('p5-canvas');
        canvas.parent(canvasContainer);

        // Initialize locators
        z0_loc = p.createVector(0, 0);
        z_plus_loc = p.createVector(-100, 0);
        z_minus_loc = p.createVector(100, 0);

        setupControls();
        p.noLoop();
        p.redraw();
    };

    const setupControls = () => {
        // Type controls
        const btnTypeSingle = document.getElementById('btn-type-single');
        const btnTypeDouble = document.getElementById('btn-type-double');

        btnTypeSingle.addEventListener('click', () => {
            spiralType = 'single';
            updateActiveButton('type', btnTypeSingle);
            p.redraw();
        });
        btnTypeDouble.addEventListener('click', () => {
            spiralType = 'double';
            updateActiveButton('type', btnTypeDouble);
            p.redraw();
        });

        // Item controls
        const btnItemNone = document.getElementById('btn-item-none');
        const btnItemPoint = document.getElementById('btn-item-point');
        const btnItemFox = document.getElementById('btn-item-fox');
        const btnItemFilledFox = document.getElementById('btn-item-filled-fox');

        btnItemNone.addEventListener('click', () => { itemType = 'none'; updateActiveButton('item', btnItemNone); p.redraw(); });
        btnItemPoint.addEventListener('click', () => { itemType = 'point'; updateActiveButton('item', btnItemPoint); p.redraw(); });
        btnItemFox.addEventListener('click', () => { itemType = 'fox'; updateActiveButton('item', btnItemFox); p.redraw(); });
        btnItemFilledFox.addEventListener('click', () => { itemType = 'filled-fox'; updateActiveButton('item', btnItemFilledFox); p.redraw(); });

        // Growth slider
        const sliderGrowth = document.getElementById('slider-growth');
        const displayGrowth = document.getElementById('display-growth');

        sliderGrowth.value = spiralGrowth;
        displayGrowth.textContent = Number(spiralGrowth).toFixed(4);

        sliderGrowth.addEventListener('input', (e) => {
            spiralGrowth = parseFloat(e.target.value);
            displayGrowth.textContent = spiralGrowth.toFixed(4);
            p.redraw();
        });

        // Reset button
        const btnResetGrowth = document.getElementById('btn-reset-growth');
        btnResetGrowth.addEventListener('click', () => {
            spiralGrowth = 0.25;
            sliderGrowth.value = spiralGrowth;
            displayGrowth.textContent = spiralGrowth.toFixed(4);
            p.redraw();
        });

        // Set initial active buttons
        updateActiveButton('type', btnTypeDouble);
        updateActiveButton('item', btnItemPoint);
    };

    const updateActiveButton = (group, activeButton) => {
        const groupContainer = document.getElementById(`${group}-controls`);
        groupContainer.querySelectorAll('button').forEach(btn => btn.classList.remove('active'));
        activeButton.classList.add('active');
    };

    p.draw = () => {
        p.background(255);
        p.translate(p.width / 2, p.height / 2);

        if (spiralType === 'single') {
            drawSingleSpiral();
            drawLocators();
        } else if (spiralType === 'double') {
            drawDoubleSpiral();
            drawLocators();
        }
    };

    // --- Drawing Logic ---

    function drawSingleSpiral() {
        const k = spiralGrowth;
        const z0 = z0_loc;
        const t_max = p.log(p.width * 1.5 / 2) / k; // Calculate t to fill screen

        // Draw curve
        p.stroke('#4A6DA8');
        p.strokeWeight(2);
        p.noFill();
        p.beginShape();
        for (let t = -t_max; t < t_max; t += 0.05) {
            const r = p.exp(k * t);
            const x = z0.x + r * p.cos(t);
            const y = z0.y - r * p.sin(t); // Invert y for standard math coords
            p.curveVertex(x, y);
        }
        p.endShape();

        // Draw items
        if (itemType !== 'none') {
            const itemStep = p.PI / 3;
            for (let t = -t_max; t < t_max; t += itemStep) {
                const r = p.exp(k * t);
                const pos = p.createVector(
                    z0.x + r * p.cos(t),
                    z0.y - r * p.sin(t)
                );

                if (itemType === 'point') {
                    drawPoint(pos);
                } else {
                    // Tangent angle is t + PI/2 for this simple spiral
                    const angle = t + p.PI / 2;
                    drawFox(pos, angle, 0.5);
                }
            }
        }
    }

    function drawDoubleSpiral() {
        const k = spiralGrowth;
        const p_vec = z_plus_loc;
        const q_vec = z_minus_loc;
        const t_max = 8 * p.PI;
        const epsilon = 0.01;

        const drawBranch = (tStart, tEnd) => {
            p.beginShape();
            for (let t = tStart; t < tEnd; t += 0.05) {
                const z = mobiusTransform(t, k, p_vec, q_vec);
                if (z && p.abs(z.x) < p.width * 2 && p.abs(z.y) < p.height * 2) {
                     p.vertex(z.x, -z.y); // Invert y
                }
            }
            p.endShape();
        };

        p.stroke('#4A6DA8');
        p.strokeWeight(2);
        p.noFill();

        drawBranch(epsilon, t_max);
        drawBranch(-t_max, -epsilon);

        if (itemType !== 'none') {
            const itemStep = p.PI / 2;
            const drawItemsOnBranch = (tStart, tEnd) => {
                for (let t = tStart; t < tEnd; t += itemStep) {
                    const pos = mobiusTransform(t, k, p_vec, q_vec);
                    if (pos) {
                        const plotPos = p.createVector(pos.x, -pos.y);
                         if (itemType === 'point') {
                            drawPoint(plotPos);
                        } else {
                            const derivative = mobiusDerivative(t, k, p_vec, q_vec);
                            const angle = p.atan2(derivative.y, derivative.x);
                            drawFox(plotPos, angle, 0.4);
                        }
                    }
                }
            };
            drawItemsOnBranch(epsilon, t_max);
            drawItemsOnBranch(-t_max, -epsilon);
        }

        // Draw "z0Loc" label
        const midPoint = p5.Vector.add(p_vec, q_vec).div(2);
        p.fill(0);
        p.noStroke();
        p.textSize(12);
        p.textAlign(p.CENTER, p.BOTTOM);
        p.text("z0Loc", midPoint.x, midPoint.y - 15);
    }

    function drawLocators() {
        if (spiralType === 'single') {
            drawLocator(z0_loc, [255, 255, 0]);
        } else {
            drawLocator(z_plus_loc, [255, 255, 0]);
            drawLocator(z_minus_loc, [0, 255, 0]);
        }
    }

    function drawLocator(pos, color) {
        p.push();
        p.stroke(0);
        p.strokeWeight(1);
        p.fill(color[0], color[1], color[2], 200);
        p.ellipse(pos.x, pos.y, locatorRadius * 2, locatorRadius * 2);
        p.line(pos.x - locatorRadius, pos.y, pos.x + locatorRadius, pos.y);
        p.line(pos.x, pos.y - locatorRadius, pos.x, pos.y + locatorRadius);
        p.pop();
    }

    function drawPoint(pos) {
        p.push();
        p.noStroke();
        p.fill('#CB3335');
        p.ellipse(pos.x, pos.y, 5, 5);
        p.pop();
    }

    function drawFox(pos, angle, scale) {
        p.push();
        p.translate(pos.x, pos.y);
        p.rotate(angle); // Rotate to align with tangent
        p.scale(scale);

        if (itemType === 'filled-fox') {
            p.fill('#D6292D');
            p.noStroke();
        } else { // 'fox'
            p.noFill();
            p.stroke('#D6292D');
            p.strokeWeight(1/scale); // Keep stroke weight constant
        }

        p.beginShape();
        for (const v of fox_vertices) {
            p.vertex(v.x, v.y);
        }
        p.endShape(p.CLOSE);
        p.pop();
    }


    // --- Math Helpers ---

    function complexMultiply(a, b) {
        return p.createVector(a.x * b.x - a.y * b.y, a.x * b.y + a.y * b.x);
    }

    function complexDivide(a, b) {
        const d = b.x * b.x + b.y * b.y;
        if (p.abs(d) < 1e-9) return null; // Avoid division by zero
        return p.createVector(
            (a.x * b.x + a.y * b.y) / d,
            (a.y * b.x - a.x * b.y) / d
        );
    }

    function mobiusTransform(t, k, p_vec, q_vec) {
        // w(t) = exp((k+i)*t)
        const exp_kt = p.exp(k * t);
        const w_t = p.createVector(exp_kt * p.cos(t), exp_kt * p.sin(t));

        // num = p*w(t) - q
        const p_w = complexMultiply(p_vec, w_t);
        const num = p5.Vector.sub(p_w, q_vec);

        // den = w(t) - 1
        const den = p5.Vector.sub(w_t, p.createVector(1, 0));

        return complexDivide(num, den);
    }

    function mobiusDerivative(t, k, p_vec, q_vec) {
        // z'(t) = (p - q) * (k + i) * w(t) / (w(t) - 1)^2
        const p_minus_q = p5.Vector.sub(p_vec, q_vec);
        const k_plus_i = p.createVector(k, 1);

        const exp_kt = p.exp(k * t);
        const w_t = p.createVector(exp_kt * p.cos(t), exp_kt * p.sin(t));

        let num = complexMultiply(p_minus_q, k_plus_i);
        num = complexMultiply(num, w_t);

        const w_minus_1 = p5.Vector.sub(w_t, p.createVector(1, 0));
        const den = complexMultiply(w_minus_1, w_minus_1);

        const derivative = complexDivide(num, den);
        // We negate the y-component because our canvas y-axis is inverted
        if (derivative) derivative.y *= -1; 
        return derivative;
    }


    // --- Mouse Interactions ---

    p.mousePressed = () => {
        // Transform mouse coords to world coords (centered)
        const mx = p.mouseX - p.width / 2;
        const my = p.mouseY - p.height / 2;

        if (spiralType === 'single') {
            if (p.dist(mx, my, z0_loc.x, z0_loc.y) < locatorRadius) {
                draggingZ0 = true;
            }
        } else {
            if (p.dist(mx, my, z_plus_loc.x, z_plus_loc.y) < locatorRadius) {
                draggingZPlus = true;
            } else if (p.dist(mx, my, z_minus_loc.x, z_minus_loc.y) < locatorRadius) {
                draggingZMinus = true;
            }
        }

        if (draggingZ0 || draggingZPlus || draggingZMinus) {
            p.loop(); // Start drawing loop for smooth dragging
        }
    };

    p.mouseDragged = () => {
        const mx = p.mouseX - p.width / 2;
        const my = p.mouseY - p.height / 2;

        if (draggingZ0) z0_loc.set(mx, my);
        if (draggingZPlus) z_plus_loc.set(mx, my);
        if (draggingZMinus) z_minus_loc.set(mx, my);

        // Return false to prevent default browser behavior like text selection
        if (draggingZ0 || draggingZPlus || draggingZMinus) {
            return false;
        }
    };

    p.mouseReleased = () => {
        draggingZ0 = false;
        draggingZPlus = false;
        draggingZMinus = false;
        p.noLoop(); // Stop drawing loop
        p.redraw(); // Draw one final frame
    };

    p.windowResized = () => {
        const canvasContainer = document.getElementById('canvas-container');
        p.resizeCanvas(canvasContainer.offsetWidth, 600);
        p.redraw();
    };
};

// Initialize p5.js in instance mode
new p5(sketch);
</script>

</body>
</html>
