            text-align: center;
        }
        .vertex-grid button.active {
            background-color: #4CAF50;
            color: white;
            border-color: #4CAF50;
        }
    </style>
</head>
<body>

    <div id="control-panel">
        <div class="button-group">
            <button id="start-over-btn">Start Over</button>
        </div>

        <div class="button-group">
            <label for="vertex-grid-container">Initial Vertex</label>
            <div id="vertex-grid-container" class="vertex-grid">
                <button id="btn-m">m</button><button id="btn-n">n</button><button id="btn-o">o</button><button id="btn-p">p</button>
                <button id="btn-i">i</button><button id="btn-j">j</button><button id="btn-k">k</button><button id="btn-l">l</button>
                <button id="btn-e">e</button><button id="btn-f">f</button><button id="btn-g">g</button><button id="btn-h">h</button>
                <button id="btn-a">a</button><button id="btn-b">b</button><button id="btn-c">c</button><button id="btn-d">d</button>
            </div>
        </div>

        <div class="button-group">
            <label>Example Type</label>
            <button id="fixed-example-btn">Fixed Example</button>
            <button id="random-example-btn">Random Example</button>
        </div>

        <div class="button-group">
            <label>Implement Algorithm</label>
            <button id="next-step-btn">Next Step</button>
        </div>
    </div>

    <div id="viz-container">
        <h1 id="title-display">Dijkstra's Algorithm</h1>
        <div id="canvas-container"></div>
    </div>

    <script>
        const sketch = (p) => {
            const CANVAS_SIZE = 600;
            const PADDING = 50;

            let vertices = {};
            let edges = [];
            const vertexNames = [
                'm', 'n', 'o', 'p', 'i', 'j', 'k', 'l', 
                'e', 'f', 'g', 'h', 'a', 'b', 'c', 'd'
            ];

            // Algorithm state
            let startVertexName = 'm';
            let distances = {};
            let predecessors = {};
            let visited = new Set();
            let unvisited = new Set();

            // UI Elements
            let startOverBtn, fixedExampleBtn, randomExampleBtn, nextStepBtn, vertexButtons = {};
            let titleDisplay;

            p.setup = () => {
                p.createCanvas(CANVAS_SIZE, CANVAS_SIZE).parent('canvas-container');
                p.noLoop();

                initializeUI();
                initializeGraphData();
                initializeAppState();
            };

            const initializeUI = () => {
                titleDisplay = document.getElementById('title-display');
                startOverBtn = document.getElementById('start-over-btn');
                fixedExampleBtn = document.getElementById('fixed-example-btn');
                randomExampleBtn = document.getElementById('random-example-btn');
                nextStepBtn = document.getElementById('next-step-btn');

                vertexNames.forEach(name => {
                    const btn = document.getElementById(`btn-${name}`);
                    vertexButtons[name] = btn;
                    btn.addEventListener('click', () => handleVertexSelect(name));
                });

                startOverBtn.addEventListener('click', handleStartOver);
                fixedExampleBtn.addEventListener('click', handleFixedExample);
                randomExampleBtn.addEventListener('click', handleRandomExample);
                nextStepBtn.addEventListener('click', handleNextStep);
            };

            const initializeGraphData = () => {
                // Setup vertex positions in a 4x4 grid
                const step = (CANVAS_SIZE - 2 * PADDING) / 3;
                for (let row = 0; row < 4; row++) {
                    for (let col = 0; col < 4; col++) {
                        const name = vertexNames[row * 4 + col];
                        vertices[name] = {
                            name,
                            x: PADDING + col * step,
                            y: PADDING + row * step
                        };
                    }
                }

                // Setup edges
                const connections = [
                    ['m','n'], ['n','o'], ['o','p'],
                    ['i','j'], ['j','k'], ['k','l'],
                    ['e','f'], ['f','g'], ['g','h'],
                    ['a','b'], ['b','c'], ['c','d'],
                    ['m','i'], ['i','e'], ['e','a'],
                    ['n','j'], ['j','f'], ['f','b'],
                    ['o','k'], ['k','g'], ['g','c'],
                    ['p','l'], ['l','h'], ['h','d']
                ];
                edges = connections.map(conn => ({ v1: conn[0], v2: conn[1], weight: 1 }));
            };

            const loadFixedWeights = () => {
                const fixedWeights = {
                    'm-n': 8, 'n-o': 7, 'o-p': 7, 'i-j': 9, 'j-k': 7, 'k-l': 1, 'e-f': 1, 'f-g': 7,
                    'g-h': 6, 'a-b': 10, 'b-c': 4, 'c-d': 8, 'm-i': 6, 'i-e': 9, 'e-a': 9, 'n-j': 7,
                    'j-f': 6, 'f-b': 5, 'o-k': 1, 'k-g': 8, 'g-c': 8, 'p-l': 2, 'l-h': 1, 'h-d': 7
                };

                edges.forEach(edge => {
                    const key1 = `${edge.v1}-${edge.v2}`;
                    const key2 = `${edge.v2}-${edge.v1}`;
                    edge.weight = fixedWeights[key1] || fixedWeights[key2] || 1;
                });
            };

            const generateRandomWeights = () => {
                edges.forEach(edge => {
                    edge.weight = Math.floor(Math.random() * 10) + 1;
                });
            };

            const initializeAppState = () => {
                startVertexName = 'm';
                loadFixedWeights();
                resetAlgorithmState();
                p.redraw();
            };

            const resetAlgorithmState = () => {
                visited.clear();
                unvisited.clear();
                predecessors = {};

                vertexNames.forEach(name => {
                    distances[name] = (name === startVertexName) ? 0 : Infinity;
                    unvisited.add(name);
                });

                updateUIState();
                p.redraw();
            };

            const updateUIState = () => {
                titleDisplay.textContent = `Dijkstra's algorithm starting at vertex ${startVertexName}`;
                nextStepBtn.disabled = false;

                vertexNames.forEach(name => {
                    vertexButtons[name].classList.toggle('active', name === startVertexName);
                });
            };

            // --- Event Handlers ---
            const handleVertexSelect = (name) => {
                startVertexName = name;
                resetAlgorithmState();
            };

            const handleStartOver = () => {
                initializeAppState();
            };

            const handleFixedExample = () => {
                loadFixedWeights();
                resetAlgorithmState();
            };

            const handleRandomExample = () => {
                generateRandomWeights();
                resetAlgorithmState();
            };

            const handleNextStep = () => {
                // 1. Find the unvisited vertex with the smallest distance
                let minDistance = Infinity;
                let u = null;

                unvisited.forEach(vertexName => {
                    if (distances[vertexName] < minDistance) {
                        minDistance = distances[vertexName];
                        u = vertexName;
                    }
                });

                // If no reachable vertex is found, or all are visited, stop.
                if (u === null) {
                    nextStepBtn.disabled = true;
                    p.redraw();
                    return;
                }

                // 2. Mark as visited
                unvisited.delete(u);
                visited.add(u);

                // 3. Update neighbors
                const neighbors = edges.filter(e => e.v1 === u || e.v2 === u);
                neighbors.forEach(edge => {
                    const v = (edge.v1 === u) ? edge.v2 : edge.v1;
                    if (unvisited.has(v)) {
                        const newDist = distances[u] + edge.weight;
                        if (newDist < distances[v]) {
                            distances[v] = newDist;
                            predecessors[v] = u;
                        }
                    }
                });

                if (unvisited.size === 0) {
                    nextStepBtn.disabled = true;
                }

                p.redraw();
            };


            p.draw = () => {
                p.background(255);
                drawGraph();
            };

            const drawGraph = () => {
                // Draw edges
                edges.forEach(edge => {
                    const v1 = vertices[edge.v1];
                    const v2 = vertices[edge.v2];

                    // Check if edge is in the shortest path tree
                    const isTreeEdge = (predecessors[edge.v1] === edge.v2) || (predecessors[edge.v2] === edge.v1);

                    p.strokeWeight(isTreeEdge ? 4 : 1);
                    p.stroke(isTreeEdge ? '#3498db' : '#333'); // Blue for tree, black for others
                    p.line(v1.x, v1.y, v2.x, v2.y);

                    // Draw edge weight
                    const midX = (v1.x + v2.x) / 2;
                    const midY = (v1.y + v2.y) / 2;
                    p.noStroke();
                    p.fill(255);
                    p.rectMode(p.CENTER);
                    p.rect(midX, midY, 20, 20); // Clear background for text
                    p.fill(0);
                    p.textAlign(p.CENTER, p.CENTER);
                    p.textSize(12);
                    p.text(edge.weight, midX, midY);
                });

                // Draw vertices and labels
                Object.values(vertices).forEach(v => {
                    const isVisited = visited.has(v.name);
                    const isStart = v.name === startVertexName;

                    // Draw vertex circle
                    p.noStroke();
                    p.fill(isVisited ? '#e74c3c' : '#000000'); // Red for visited, black for unvisited
                    p.ellipse(v.x, v.y, 16, 16);

                    // Draw vertex name label
                    p.fill(0);
                    p.textSize(16);
                    p.textAlign(p.LEFT, p.CENTER);
                    p.text(v.name, v.x + 15, v.y);

                    // Draw distance label
                    p.fill(isVisited ? '#e74c3c' : '#000000');
                    p.textSize(14);
                    p.textAlign(p.CENTER, p.BOTTOM);
                    const distText = distances[v.name] === Infinity ? '∞' : `(${distances[v.name]})`;
                    p.text(distText, v.x, v.y - 12);
                });
            };
        };

        new p5(sketch);
    </script>

</body>
</html>
