    <script type="module">
        import * as THREE from 'three';
        import { OrbitControls } from 'three/addons/controls/OrbitControls.js';

        // Scene variables
        let scene, camera, renderer, controls;
        let sphere, conesGroup;

        // Materials
        const sphereMaterial = new THREE.MeshPhongMaterial({ color: 0xdddddd, shininess: 50 });
        const coneMaterial = new THREE.MeshPhongMaterial({ color: 0xffa500, shininess: 30 });

        // DOM elements
        const sphereDiameterSlider = document.getElementById('slider-sphere-diameter');
        const coneHeightSlider = document.getElementById('slider-cone-height');
        const coneBaseSlider = document.getElementById('slider-cone-base');
        const polyhedronButtons = document.querySelectorAll('#polyhedron-buttons button');
        const canvasContainer = document.getElementById('canvas-container');

        // Store vertex data
        const POLYHEDRON_VERTICES = {};

        // Function to generate and store vertex data
        function generateVertexData() {
            // Helper function to extract unique, normalized vertices from a geometry
            const getNormalizedVertices = (geometry) => {
                const vertices = [];
                const positionAttribute = geometry.attributes.position;
                const tempVector = new THREE.Vector3();
                for (let i = 0; i < positionAttribute.count; i++) {
                    tempVector.fromBufferAttribute(positionAttribute, i).normalize();
                    if (!vertices.some(v => v.distanceTo(tempVector) < 0.0001)) {
                        vertices.push(tempVector.clone());
                    }
                }
                return vertices;
            };

            POLYHEDRON_VERTICES.tetrahedron = getNormalizedVertices(new THREE.TetrahedronGeometry(1));
            POLYHEDRON_VERTICES.cube = getNormalizedVertices(new THREE.BoxGeometry(1, 1, 1));
            POLYHEDRON_VERTICES.octahedron = getNormalizedVertices(new THREE.OctahedronGeometry(1));
            POLYHEDRON_VERTICES.dodecahedron = getNormalizedVertices(new THREE.DodecahedronGeometry(1));
            POLYHEDRON_VERTICES.icosahedron = getNormalizedVertices(new THREE.IcosahedronGeometry(1));
        }

        // Initialization function
        function init() {
            // Generate vertex data first
            generateVertexData();

            // Scene
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xf0f0f0);

            // Camera
            camera = new THREE.PerspectiveCamera(45, canvasContainer.clientWidth / canvasContainer.clientHeight, 0.1, 100);
            camera.position.set(0, 0, 8);

            // Renderer
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setPixelRatio(window.devicePixelRatio);
            renderer.setSize(canvasContainer.clientWidth, canvasContainer.clientHeight);
            canvasContainer.appendChild(renderer.domElement);

            // Lighting
            const ambientLight = new THREE.AmbientLight(0x404040); // Soft white light
            scene.add(ambientLight);

            const directionalLight1 = new THREE.DirectionalLight(0xffffff, 1.2);
            directionalLight1.position.set(1, 1, 1).normalize();
            scene.add(directionalLight1);

            const directionalLight2 = new THREE.DirectionalLight(0xffffff, 0.8);
            directionalLight2.position.set(-1, -1, -1).normalize();
            scene.add(directionalLight2);

            // Controls
            controls = new OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.05;
            controls.screenSpacePanning = false;
            controls.minDistance = 2;
            controls.maxDistance = 50;

            // Central Sphere
            const sphereGeometry = new THREE.SphereGeometry(1, 64, 64); // Base geometry with radius 1
            sphere = new THREE.Mesh(sphereGeometry, sphereMaterial);
            scene.add(sphere);

            // Cones Group
            conesGroup = new THREE.Group();
            scene.add(conesGroup);

            // Initial draw
            updateScene();

            // Event Listeners
            sphereDiameterSlider.addEventListener('input', updateScene);
            coneHeightSlider.addEventListener('input', updateScene);
            coneBaseSlider.addEventListener('input', updateScene);

            polyhedronButtons.forEach(button => {
                button.addEventListener('click', () => {
                    polyhedronButtons.forEach(btn => btn.classList.remove('active'));
                    button.classList.add('active');
                    updateScene();
                });
            });

            window.addEventListener('resize', onWindowResize, false);
        }

        // Update scene function
        function updateScene() {
            // Get current values from controls
            const sphereDiameter = parseFloat(sphereDiameterSlider.value);
            const sphereRadius = sphereDiameter / 2;
            const coneHeight = parseFloat(coneHeightSlider.value);
            const coneBaseRadius = parseFloat(coneBaseSlider.value);
            const activePolyhedronButton = document.querySelector('#polyhedron-buttons button.active');
            const selectedPolyhedron = activePolyhedronButton.id.replace('btn-', '');

            // Update sphere scale
            sphere.scale.set(sphereRadius, sphereRadius, sphereRadius);

            // Clear existing cones and dispose their geometries
            while (conesGroup.children.length > 0) {
                const child = conesGroup.children[0];
                conesGroup.remove(child);
                child.geometry.dispose();
            }

            // Get vertices for selected polyhedron
            const vertices = POLYHEDRON_VERTICES[selectedPolyhedron];

            if (!vertices) return;

            // Create new cone geometry (re-used for all cones in this update)
            const coneGeometry = new THREE.ConeGeometry(coneBaseRadius, coneHeight, 32);
            const yAxis = new THREE.Vector3(0, 1, 0);

            vertices.forEach(vertexDirection => {
                const cone = new THREE.Mesh(coneGeometry, coneMaterial);

                // Position cone so its base touches the sphere
                const position = vertexDirection.clone().multiplyScalar(sphereRadius + coneHeight / 2);
                cone.position.copy(position);

                // Orient cone to point radially outward
                cone.quaternion.setFromUnitVectors(yAxis, vertexDirection);

                conesGroup.add(cone);
            });
        }

        // Window resize handler
        function onWindowResize() {
            camera.aspect = canvasContainer.clientWidth / canvasContainer.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(canvasContainer.clientWidth, canvasContainer.clientHeight);
        }

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update(); // Required for damping
            renderer.render(scene, camera);
        }

        // Start the application
        init();
        animate();
    </script>
</body>
</html>
