            // --- Event Listeners ---
            selectPlanet.addEventListener('change', () => {
                updateControlsFromPlanet();
                needsRecalculation = true;
                p.redraw();
            });

            checkboxManual.addEventListener('change', () => {
                updateControlsFromCheckbox();
                needsRecalculation = true;
                p.redraw();
            });

            const sliders = [sliderTilt, sliderEquinox, sliderEccentricity];
            sliders.forEach(slider => {
                slider.addEventListener('input', () => {
                    needsRecalculation = true;
                    p.redraw();
                });
            });

            const displayCheckboxes = [checkboxSnapshots, checkboxScales];
            displayCheckboxes.forEach(checkbox => {
                checkbox.addEventListener('change', () => {
                    needsRecalculation = false; // No recalc needed for display changes
                    p.redraw();
                });
            });

            // Set initial state based on default HTML values and trigger first draw
            updateControlsFromCheckbox();
            p.redraw();
        };

        p.windowResized = () => {
            const visArea = document.getElementById('visualization-area');
            p.resizeCanvas(visArea.clientWidth, visArea.clientHeight);
            p.redraw();
        };

        // --- Interaction Logic Handlers ---
        function updateControlsFromPlanet() {
            const planet = selectPlanet.value;
            const sliders = [sliderTilt, sliderEquinox, sliderEccentricity];
            if (planet !== 'manual') {
                checkboxManual.checked = false;
                const data = planetData[planet];
                sliderTilt.value = data.tilt;
                sliderEccentricity.value = data.eccentricity;
                sliderEquinox.value = data.equinox;
                sliders.forEach(s => s.disabled = true);
            } else {
                checkboxManual.checked = true;
                sliders.forEach(s => s.disabled = false);
            }
        }

        function updateControlsFromCheckbox() {
            const isManual = checkboxManual.checked;
            const sliders = [sliderTilt, sliderEquinox, sliderEccentricity];
            sliders.forEach(s => s.disabled = !isManual);

            if (isManual) {
                selectPlanet.value = 'manual';
            } else {
                // If the user unchecks the box, it implies they no longer want manual control.
                // We default to a planet preset as the UI needs to reflect a non-manual state.
                if (selectPlanet.value === 'manual') {
                    selectPlanet.value = 'Earth';
                    updateControlsFromPlanet(); // Sync sliders to Earth's data
                }
            }
        }

        // --- Kepler's Equation Solver (Newton's method) ---
        const solveKepler = (M, ecc) => {
            let E = M; // Initial guess
            for (let i = 0; i < 7; i++) { // 7 iterations is sufficient for high accuracy
                E = E - (E - ecc * p.sin(E) - M) / (1 - ecc * p.cos(E));
            }
            return E;
        };

        // --- Analemma Point Calculation ---
        const calculateAnalemmaPoints = (tilt, ecc, equinoxAngle) => {
            let points = [];
            const daysInYear = 365.25;
            const tiltRad = p.radians(tilt);
            const equinoxRad = p.radians(equinoxAngle);

            for (let d = 0; d <= daysInYear; d++) {
                const M = (2 * p.PI * d) / daysInYear;
                const E = solveKepler(M, ecc);
                const v = 2 * p.atan2(p.sqrt(1 + ecc) * p.sin(E / 2), p.sqrt(1 - ecc) * p.cos(E / 2));
                const lambda = v + equinoxRad;
                const declination = p.asin(p.sin(tiltRad) * p.sin(lambda));
                const rightAscension = p.atan2(p.cos(tiltRad) * p.sin(lambda), p.cos(lambda));
                let eot = M - rightAscension;

                // Normalize Equation of Time to [-PI, PI]
                eot = (eot + p.PI) % (2 * p.PI) - p.PI;

                points.push({eot: eot, dec: declination});
            }
            return points;
        };

        // --- p5.js draw function ---
        p.draw = () => {
            const tilt = parseFloat(sliderTilt.value);
            const equinox = parseFloat(sliderEquinox.value);
            const eccentricity = parseFloat(sliderEccentricity.value);
            const showSnapshots = checkboxSnapshots.checked;
            const showScales = checkboxScales.checked;

            if (needsRecalculation) {
                analemmaPoints = calculateAnalemmaPoints(tilt, eccentricity, equinox);
                needsRecalculation = false;
            }

            p.background(255);
            p.translate(p.width / 2, p.height / 2 + 40); // Move origin, leave space at top

            const scaleFactor = p.min(p.width, p.height) * 2.3; // Convert radians to pixels

            // Draw Orbit Ellipse
            p.push();
            p.translate(0, -p.height / 2 + 50); // Move to top of canvas
            p.stroke(139, 69, 19); // Brown
            p.strokeWeight(1.5);
            p.noFill();
            const orbitWidth = 80;
            const orbitHeight = orbitWidth * p.sqrt(1 - eccentricity * eccentricity);
            p.ellipse(0, 0, orbitWidth, orbitHeight);
            p.fill(139, 69, 19);
            p.noStroke();
            p.circle((orbitWidth / 2) * eccentricity, 0, 6); // Sun at one focus
            p.pop();

            // Draw Scales
            if (showScales) {
                p.stroke(200);
                p.strokeWeight(1);
                p.fill(120);
                p.textSize(10);

                // Equation of Time to minutes conversion factor
                const radToMin = (180 / p.PI) * 4;

                // X-axis (Equation of Time)
                p.textAlign(p.CENTER, p.TOP);
                p.line(-p.width/2, 0, p.width/2, 0);
                for (let min = -20; min <= 20; min += 5) {
                    if (min === 0) continue;
                    let x = (min / radToMin) * scaleFactor;
                    p.line(x, -5, x, 5);
                    p.text(`${min}m`, x, 8);
                }

                // Y-axis (Declination)
                p.textAlign(p.RIGHT, p.CENTER);
                p.line(0, -p.height/2, 0, p.height/2);
                for (let deg = -40; deg <= 40; deg += 10) {
                    let y = -p.radians(deg) * scaleFactor;
                    p.line(-5, y, 5, y);
                    if (deg !== 0) p.text(`${deg}°`, -8, y);
                }
                p.text("0°", -8, 0);
            }

            if (analemmaPoints.length > 0) {
                // Draw Analemma Curve
                p.noFill();
                p.stroke('orange');
                p.strokeWeight(2.5);
                p.beginShape();
                for(const point of analemmaPoints) {
                    // Y is inverted in canvas coordinates
                    p.vertex(point.eot * scaleFactor, -point.dec * scaleFactor);
                }
                p.endShape();

                // Draw Sun Snapshots
                if (showSnapshots) {
                    p.fill('orange');
                    p.noStroke();
                    const snapshotStep = Math.round(analemmaPoints.length / 24); // About 24 snapshots
                    for (let i = 0; i < analemmaPoints.length; i += snapshotStep) {
                        const point = analemmaPoints[i];
                        p.circle(point.eot * scaleFactor, -point.dec * scaleFactor, 8);
                    }
                }
            }
        };
    };

    new p5(sketch);
});
</script>

