    // --- ALGORITHM & UTILITY FUNCTIONS ---

    // Union-Find for Cheapest Link cycle detection
    function createUnionFind(vertices) {
        const parent = {};
        vertices.forEach(v => parent[v.id] = v.id);
        const find = (i) => {
            if (parent[i] === i) return i;
            return parent[i] = find(parent[i]);
        };
        const union = (i, j) => {
            const rootI = find(i);
            const rootJ = find(j);
            if (rootI !== rootJ) {
                parent[rootI] = rootJ;
            }
        };
        return { find, union };
    }

    // Brute-force TSP solver for random graphs
    function solveTSPBruteForce(graph) {
        const vertices = graph.vertices.map(v => v.id);
        const vertexIndices = new Map(vertices.map((v, i) => [v, i]));
        const startVertex = vertices[0];
        const otherVertices = vertices.slice(1);
        let minWeight = Infinity;
        let optimalPath = [];

        function permute(arr, l, r) {
            if (l === r) {
                const currentPath = [startVertex, ...arr];
                let currentWeight = 0;
                for (let i = 0; i < currentPath.length - 1; i++) {
                    const u = vertexIndices.get(currentPath[i]);
                    const v = vertexIndices.get(currentPath[i+1]);
                    currentWeight += graph.weights[u][v];
                }
                const lastV = vertexIndices.get(currentPath[currentPath.length - 1]);
                const startV = vertexIndices.get(startVertex);
                currentWeight += graph.weights[lastV][startV];

                if (currentWeight < minWeight) {
                    minWeight = currentWeight;
                    optimalPath = [...currentPath];
                }
            } else {
                for (let i = l; i <= r; i++) {
                    [arr[l], arr[i]] = [arr[i], arr[l]]; // Swap
                    permute(arr, l + 1, r);
                    [arr[l], arr[i]] = [arr[i], arr[l]]; // Backtrack
                }
            }
        }

        permute(otherVertices, 0, otherVertices.length - 1);
        return { path: optimalPath, weight: minWeight };
    }

    function initializeState(graphData) {
        const vertexIndices = new Map(graphData.vertices.map((v, i) => [v.id, i]));
        const sortedEdges = [];
        if (dom.algoCheapestLink.checked) {
            for (let i = 0; i < graphData.vertices.length; i++) {
                for (let j = i + 1; j < graphData.vertices.length; j++) {
                    sortedEdges.push({
                        u: graphData.vertices[i].id,
                        v: graphData.vertices[j].id,
                        weight: graphData.weights[i][j]
                    });
                }
            }
            sortedEdges.sort((a, b) => a.weight - b.weight);
        }

        state = {
            graph: graphData,
            vertexIndices,
            algorithm: dom.algoNearestNeighbor.checked ? 'nearest-neighbor' : 'cheapest-link',

            // Algorithm-specific state
            startVertex: null,
            currentVertex: null,
            visited: new Set(),
            path: [], // For NN: list of vertex IDs. For CL: list of edge objects.
            pathEdges: [], // For consistent table display
            totalWeight: 0,
            step: 0,

            // CL specific
            sortedEdges: sortedEdges,
            edgePointer: 0,
            unionFind: createUnionFind(graphData.vertices),
            degrees: graphData.vertices.reduce((acc, v) => ({ ...acc, [v.id]: 0 }), {}),

            isComplete: false,
        };
    }

    function stepNearestNeighbor() {
        if (state.isComplete) return;

        if (state.step < state.graph.vertices.length - 1) {
            let nearestVertex = null;
            let minWeight = Infinity;

            const currentIndex = state.vertexIndices.get(state.currentVertex);
            for (let i = 0; i < state.graph.vertices.length; i++) {
                const neighborId = state.graph.vertices[i].id;
                if (!state.visited.has(neighborId)) {
                    const weight = state.graph.weights[currentIndex][i];
                    if (weight < minWeight) {
                        minWeight = weight;
                        nearestVertex = neighborId;
                    }
                }
            }
            state.pathEdges.push({ u: state.currentVertex, v: nearestVertex, weight: minWeight });
            state.totalWeight += minWeight;
            state.visited.add(nearestVertex);
            state.currentVertex = nearestVertex;
            state.path.push(nearestVertex);
        } else { // Final step: close the circuit
            const lastIndex = state.vertexIndices.get(state.currentVertex);
            const startIndex = state.vertexIndices.get(state.startVertex);
            const weight = state.graph.weights[lastIndex][startIndex];
            state.pathEdges.push({ u: state.currentVertex, v: state.startVertex, weight: weight });
            state.totalWeight += weight;
            state.path.push(state.startVertex);
            state.isComplete = true;
        }
        state.step++;
        updateUI();
    }

    function stepCheapestLink() {
        if (state.isComplete) return;

        let edgeAdded = false;
        while (state.edgePointer < state.sortedEdges.length && !edgeAdded) {
            const edge = state.sortedEdges[state.edgePointer];
            const { u, v, weight } = edge;

            const isClosingEdge = state.path.length === state.graph.vertices.length - 1;
            const createsCycle = state.unionFind.find(u) === state.unionFind.find(v);

            if (state.degrees[u] < 2 && state.degrees[v] < 2 && (!createsCycle || isClosingEdge)) {
                state.path.push(edge);
                state.pathEdges.push(edge);
                state.totalWeight += weight;
                state.degrees[u]++;
                state.degrees[v]++;
                state.unionFind.union(u, v);
                edgeAdded = true;
                state.step++;
            }
            state.edgePointer++;
        }

        if (state.path.length === state.graph.vertices.length) {
            state.isComplete = true;
        }
        updateUI();
    }


    // --- UI UPDATE FUNCTIONS ---
    function updateUI() {
        // Update Canvas
        const pathForDrawing = state.algorithm === 'nearest-neighbor' ? state.path : state.pathEdges.flatMap(e => [e.u, e.v]);
        const reconstructedPath = [];
        if (state.algorithm === 'cheapest-link' && state.pathEdges.length > 0) {
            const adj = {};
            state.pathEdges.forEach(({u, v}) => {
                if (!adj[u]) adj[u] = [];
                if (!adj[v]) adj[v] = [];
                adj[u].push(v);
                adj[v].push(u);
            });
            let curr = state.pathEdges[0].u;
            let prev = null;
            for (let i = 0; i < state.pathEdges.length; i++) {
                reconstructedPath.push(curr);
                const neighbors = adj[curr];
                const next = neighbors.find(n => n !== prev);
                prev = curr;
                curr = next;
            }
        }
        algoCanvas.updateAndDraw(state.graph, state.algorithm === 'nearest-neighbor' ? state.path : reconstructedPath, COLORS.ALGO_PATH);

        // Update Table
        dom.tableTitle.textContent = state.pathEdges.length > 0 ? "Edges in circuit:" : "";
        let tableHTML = '<thead><tr><th>Step</th><th>Edge</th><th>Weight</th></tr></thead><tbody>';
        state.pathEdges.forEach((edge, i) => {
            tableHTML += `<tr><td>${i+1}</td><td>${edge.u} - ${edge.v}</td><td>${edge.weight}</td></tr>`;
        });
        tableHTML += '</tbody>';
        dom.tableEdgeList.innerHTML = tableHTML;

        // Update Total Weight
        if (state.totalWeight > 0) {
            dom.textTotalWeight.textContent = `Total weight: ${state.totalWeight}`;
        } else {
            dom.textTotalWeight.textContent = "";
        }
        if (state.isComplete) {
            dom.textTotalWeight.textContent = `Circuit complete! Final weight: ${state.totalWeight}`;
            dom.btnNextStep.disabled = true;
        }
    }

    function resetUI() {
        dom.btnNextStep.disabled = true;
        if (state.algorithm === 'nearest-neighbor') {
            dom.startingVertexSelector.style.display = 'block';
            dom.vertexButtons.forEach(btn => {
                btn.disabled = false;
                btn.classList.remove('selected');
            });
            dom.titleAlgo.textContent = "The Nearest Neighbor Algorithm";
            dom.textTotalWeight.textContent = "Select a starting vertex.";
        } else { // Cheapest Link
            dom.startingVertexSelector.style.display = 'none';
            dom.btnNextStep.disabled = false;
            dom.titleAlgo.textContent = "The Cheapest Link Algorithm";
            dom.textTotalWeight.textContent = "Click 'Next Step' to begin.";
        }

        dom.textExampleTypeAlgo.textContent = state.graph.type;
        dom.textExampleTypeOptimal.textContent = state.graph.type;
        dom.tableTitle.textContent = "";
        dom.tableEdgeList.innerHTML = "";

        algoCanvas.updateAndDraw(state.graph, []);

        // Handle optimal view
        if(dom.checkboxOptimal.checked) {
            showOptimalSolution();
        }
    }

    // --- EVENT HANDLER FUNCTIONS ---
    function handleAlgorithmChange() {
        startNewSimulation(state.graph.type === 'random example' ? null : FIXED_GRAPH);
    }

    function startNewSimulation(graphData) {
        if (graphData) { // Fixed example
             initializeState(graphData);
        } else { // Random example
            const vertices = VERTEX_LABELS.map(id => ({
                id,
                x: Math.floor(Math.random() * (CANVAS_SIZE - 40)) + 20,
                y: Math.floor(Math.random() * (CANVASSIZE - 40)) + 20
            }));
            const weights = Array(7).fill(0).map(() => Array(7).fill(0));
            for(let i=0; i<7; i++){
                for(let j=i+1; j<7; j++){
                    weights[i][j] = weights[j][i] = Math.floor(Math.random() * 41) + 10;
                }
            }
            const randomGraph = { vertices, weights, type: 'random example' };
            const optimal = solveTSPBruteForce(randomGraph);
            randomGraph.optimalPath = optimal.path;
            randomGraph.optimalWeight = optimal.weight;
            initializeState(randomGraph);
        }
        resetUI();
    }

    function handleStartOver() {
        initializeState(state.graph);
        resetUI();
    }

    function handleVertexSelect(e) {
        const selectedVertex = e.target.dataset.vertex;
        state.startVertex = selectedVertex;
        state.currentVertex = selectedVertex;
        state.path = [selectedVertex];
        state.visited.add(selectedVertex);

        dom.vertexButtons.forEach(btn => {
            btn.disabled = true;
            if (btn.dataset.vertex === selectedVertex) {
                btn.classList.add('selected');
            }
        });
        dom.btnNextStep.disabled = false;
        dom.titleAlgo.textContent = `The Nearest Neighbor Algorithm starting at ${selectedVertex}`;
        dom.textTotalWeight.textContent = "Click 'Next Step' to begin.";
    }

    function handleNextStep() {
        if (state.algorithm === 'nearest-neighbor') {
            stepNearestNeighbor();
        } else {
            stepCheapestLink();
        }
    }

    function showOptimalSolution() {
        dom.optimalResultContainer.style.display = 'block';
        optimalCanvas.updateAndDraw(state.graph, state.graph.optimalPath.concat(state.graph.optimalPath[0]), COLORS.OPTIMAL_PATH);
        dom.textOptimalWeight.textContent = `Optimal weight: ${state.graph.optimalWeight}`;
    }

    function handleToggleOptimal(e) {
        if (e.target.checked) {
            showOptimalSolution();
        } else {
            dom.optimalResultContainer.style.display = 'none';
        }
    }


    // --- INITIALIZATION ---
    function init() {
        // Instantiate p5 canvases
        algoCanvas = new p5(sketch, dom.canvasAlgoWrapper);
        optimalCanvas = new p5(sketch, dom.canvasOptimalWrapper);

        // Add event listeners
        dom.algoCheapestLink.addEventListener('change', handleAlgorithmChange);
        dom.algoNearestNeighbor.addEventListener('change', handleAlgorithmChange);
        dom.btnStartOver.addEventListener('click', handleStartOver);
        dom.btnFixedExample.addEventListener('click', () => startNewSimulation(FIXED_GRAPH));
        dom.btnRandomExample.addEventListener('click', () => startNewSimulation(null));
        dom.vertexButtons.forEach(btn => btn.addEventListener('click', handleVertexSelect));
        dom.btnNextStep.addEventListener('click', handleNextStep);
        dom.checkboxOptimal.addEventListener('change', handleToggleOptimal);

        // Initial load
        startNewSimulation(FIXED_GRAPH);
    }

    init();
});
</script>

</body>
</html>
