    </div>

    <script>
        // Global variables for visualization data
        let pA, pB, vU, vV, vW;
        let calculationValid = true;

        // p5.js sketch setup
        function sketch(p) {
            // Slider DOM elements and values
            const sliders = {};
            const sliderIds = [
                'au', 'bu', 'av', 'bv', 'aw', 'bw',
                'x-scale', 'y-scale'
            ];

            p.setup = function() {
                const canvasContainer = document.getElementById('canvas-container');
                const canvas = p.createCanvas(canvasContainer.offsetWidth, canvasContainer.offsetHeight);
                canvas.parent('canvas-container');

                sliderIds.forEach(id => {
                    sliders[id] = {
                        input: document.getElementById(`slider-${id}`),
                        valueSpan: document.getElementById(`value-${id}`)
                    };
                    sliders[id].input.addEventListener('input', updateAndRedraw);
                });

                // Initialize vectors
                pA = p.createVector(0, 0);
                pB = p.createVector(0, 0);
                vU = p.createVector(0, 0);
                vV = p.createVector(0, 0);
                vW = p.createVector(0, 0);

                p.noLoop();
                updateAndRedraw();
            };

            function updateAndRedraw() {
                updateSliderDisplays();
                performCalculations();
                p.redraw();
            }

            function updateSliderDisplays() {
                sliderIds.forEach(id => {
                    const slider = sliders[id];
                    let value = parseFloat(slider.input.value);
                    // Format dot products to 2 decimal places
                    if (id.length <= 2) { 
                        slider.valueSpan.textContent = value.toFixed(2);
                    } else {
                        slider.valueSpan.textContent = value;
                    }
                });
            }

            function performCalculations() {
                const au = parseFloat(sliders.au.input.value);
                const bu = parseFloat(sliders.bu.input.value);
                const av = parseFloat(sliders.av.input.value);
                const bv = parseFloat(sliders.bv.input.value);
                const aw = parseFloat(sliders.aw.input.value);
                const bw = parseFloat(sliders.bw.input.value);

                const M = math.matrix([[au, bu], [av, bv], [aw, bw]]);

                try {
                    const svdResult = math.svd(M);
                    const U = svdResult.U;
                    const D = math.diag(svdResult.s);
                    const V = svdResult.V;

                    const S_hat = U;

                    const s_hat_u = S_hat.subset(math.index(0, [0, 1])).toArray()[0];
                    const s_hat_v = S_hat.subset(math.index(1, [0, 1])).toArray()[0];
                    const s_hat_w = S_hat.subset(math.index(2, [0, 1])).toArray()[0];

                    const K = math.matrix([
                        [s_hat_u[0]**2, 2 * s_hat_u[0] * s_hat_u[1], s_hat_u[1]**2],
                        [s_hat_v[0]**2, 2 * s_hat_v[0] * s_hat_v[1], s_hat_v[1]**2],
                        [s_hat_w[0]**2, 2 * s_hat_w[0] * s_hat_w[1], s_hat_w[1]**2]
                    ]);

                    const l_vec_matrix = math.lusolve(K, [1, 1, 1]);
                    const l_vec = l_vec_matrix.toArray().flat();

                    const l1 = l_vec[0];
                    const l2 = l_vec[1];
                    const l3 = l_vec[2];

                    // Check for positive definite L
                    if (l1 <= 0 || (l1 * l3 - l2 * l2) <= 0) {
                        calculationValid = false;
                        return;
                    }

                    const L = math.matrix([[l1, l2], [l2, l3]]);
                    const Q_lower = math.cholesky(L).L; // L = Q * Q^T, math.js returns Q in .L

                    if (!Q_lower) {
                         calculationValid = false;
                         return;
                    }

                    const Q = Q_lower; // Using the lower triangular matrix

                    const S = math.multiply(U, Q);
                    const P_intermediate = math.multiply(math.inv(Q), D);
                    const P = math.multiply(P_intermediate, math.transpose(V));

                    // Extract vectors
                    const s_data = S.valueOf();
                    vU.set(s_data[0][0], s_data[0][1]);
                    vV.set(s_data[1][0], s_data[1][1]);
                    vW.set(s_data[2][0], s_data[2][1]);

                    const p_data = P.valueOf();
                    pA.set(p_data[0][0], p_data[1][0]);
                    pB.set(p_data[0][1], p_data[1][1]);

                    calculationValid = true;

                } catch (error) {
                    console.error("Calculation failed:", error);
                    calculationValid = false;
                }
            }

            p.draw = function() {
                p.background('#F0F0F0');
                const xScale = parseFloat(sliders['x-scale'].input.value);
                const yScale = parseFloat(sliders['y-scale'].input.value);

                p.translate(p.width / 2, p.height / 2);
                p.scale(xScale, -yScale); // Flip Y-axis for standard Cartesian

                if (!calculationValid) {
                    p.push();
                    p.resetMatrix(); // Draw text in screen space
                    p.fill(255, 0, 0);
                    p.textAlign(p.CENTER, p.CENTER);
                    p.textSize(24);
                    p.text("Calculation Error: Invalid Input", p.width / 2, p.height / 2);
                    p.textSize(16);
                    p.text("(The matrix L may not be positive-definite)", p.width / 2, p.height / 2 + 30);
                    p.pop();
                    return;
                }

                // Draw Projections first (bottom layer)
                drawProjections();

                // Draw Camera Axes
                drawAxis(vU, '#0000CD', 'u'); // Dark Blue
                drawAxis(vV, '#6495ED', 'v'); // Light Blue
                drawAxis(vW, '#0000CD', 'w'); // Dark Blue

                // Draw Points
                drawPoint(pA, '#FF0000', 'A'); // Red
                drawPoint(pB, '#FFA500', 'B'); // Orange
                drawPoint(p.createVector(0,0), '#FF0000', 'O', 0.1); // Red Origin
            };

            function drawAxis(vec, color, label) {
                p.push();
                p.stroke(color);
                p.strokeWeight(3 / p.max(xScale, yScale)); // Keep stroke weight consistent on zoom

                const lineLength = p.max(p.width, p.height) * 2; // Make line very long
                const p1 = p5.Vector.mult(vec, lineLength);
                const p2 = p5.Vector.mult(vec, -lineLength);
                p.line(p1.x, p1.y, p2.x, p2.y);

                // Draw label
                p.noStroke();
                p.fill(color);
                p.textSize(24 / p.max(xScale, yScale));
                let labelPos = p5.Vector.mult(vec, 1.5);
                p.scale(1, -1); // Un-flip Y for text
                p.textAlign(p.CENTER, p.CENTER);
                p.text(label, labelPos.x, -labelPos.y);
                p.pop();
            }

            function drawPoint(pt, color, label, size = 0.2) {
                p.push();
                p.noStroke();
                p.fill(color);
                p.ellipse(pt.x, pt.y, size, size);

                // Draw label
                p.fill(color);
                p.textSize(20 / p.max(xScale, yScale));
                let labelOffset = p.createVector(0.1, 0.1);
                p.scale(1, -1); // Un-flip Y for text
                p.text(label, pt.x + labelOffset.x, -pt.y + labelOffset.y);
                p.pop();
            }

            function drawProjections() {
                const dotProducts = {
                    au: parseFloat(sliders.au.input.value), bu: parseFloat(sliders.bu.input.value),
                    av: parseFloat(sliders.av.input.value), bv: parseFloat(sliders.bv.input.value),
                    aw: parseFloat(sliders.aw.input.value), bw: parseFloat(sliders.bw.input.value)
                };

                const projA_u = p5.Vector.mult(vU, dotProducts.au);
                const projA_v = p5.Vector.mult(vV, dotProducts.av);
                const projA_w = p5.Vector.mult(vW, dotProducts.aw);

                const projB_u = p5.Vector.mult(vU, dotProducts.bu);
                const projB_v = p5.Vector.mult(vV, dotProducts.bv);
                const projB_w = p5.Vector.mult(vW, dotProducts.bw);

                // Draw connecting lines
                p.push();
                p.stroke('#808080');
                p.strokeWeight(1 / p.max(xScale, yScale));
                p.line(pA.x, pA.y, projA_u.x, projA_u.y);
                p.line(pA.x, pA.y, projA_v.x, projA_v.y);
                p.line(pA.x, pA.y, projA_w.x, projA_w.y);

                p.line(pB.x, pB.y, projB_u.x, projB_u.y);
                p.line(pB.x, pB.y, projB_v.x, projB_v.y);
                p.line(pB.x, pB.y, projB_w.x, projB_w.y);
                p.pop();

                // Draw projection points
                p.push();
                p.noStroke();
                p.fill('#00FF00'); // Green
                const projPointSize = 0.08;
                p.ellipse(projA_u.x, projA_u.y, projPointSize, projPointSize);
                p.ellipse(projA_v.x, projA_v.y, projPointSize, projPointSize);
                p.ellipse(projA_w.x, projA_w.y, projPointSize, projPointSize);

                p.ellipse(projB_u.x, projB_u.y, projPointSize, projPointSize);
                p.ellipse(projB_v.x, projB_v.y, projPointSize, projPointSize);
                p.ellipse(projB_w.x, projB_w.y, projPointSize, projPointSize);
                p.pop();
            }

            p.windowResized = function() {
                const canvasContainer = document.getElementById('canvas-container');
                p.resizeCanvas(canvasContainer.offsetWidth, canvasContainer.offsetHeight);
                p.redraw();
            }
        }

        // Start the p5 sketch
        new p5(sketch);

    </script>
</body>
