            position: relative;
        }

        canvas {
            display: block;
        }

    </style>
</head>
<body>

    <div id="main-container">
        <div id="control-panel">
            <div id="controls-2d">
                <h3>2D</h3>
                <div id="buttons-2d-container">
                    <button id="btn-2A1" data-type="2A1" data-dim="2D">²A₁</button>
                    <button id="btn-2A2" data-type="2A2" data-dim="2D">²A₂</button>
                    <button id="btn-2B2" data-type="2B2" data-dim="2D">²B₂</button>
                </div>
            </div>
            <div id="controls-3d">
                <h3>3D</h3>
                <div id="buttons-3d-container">
                    <button id="btn-3A1p" data-type="3A1p" data-dim="3D">²A₁(+)</button>
                    <button id="btn-3A1m" data-type="3A1m" data-dim="3D">²A₁(-)</button>
                    <button id="btn-3A2" data-type="3A2" data-dim="3D">²A₂</button>
                    <button id="btn-3A3" data-type="3A3" data-dim="3D">²A₃</button>
                    <button id="btn-3B2p" data-type="3B2p" data-dim="3D">²B₂(+)</button>
                    <button id="btn-3B2m" data-type="3B2m" data-dim="3D">²B₂(-)</button>
                    <button id="btn-3B3" data-type="3B3" data-dim="3D">²B₃</button>
                    <button id="btn-3C3p" data-type="3C3p" data-dim="3D">²C₃⁺</button>
                    <button id="btn-3C3m" data-type="3C3m" data-dim="3D">²C₃⁻</button>
                </div>
            </div>
            <div id="time-control-container">
                <label for="time-pad">time</label>
