    <script type="module">
        // --- STATE ---
        let currentType = '2A2';
        let currentDimension = '2D';
        let t1 = 0;
        let t2 = 0;

        // --- THREE.JS SETUP ---
        let scene, renderer, perspectiveCamera, orthoCamera, orbitControls, currentObject;
        const visContainer = document.getElementById('visualization-container');

        // --- PARAMETRIC FUNCTION DEFINITIONS ---
        const BIFURCATION_FNS = {
            // 2D Functions
            '2A1': (u, v, t1, t2) => {
                const U = -2 + 4 * u;
                const T1 = t1 * 1.5; const T2 = t2 * 1.5;
                return new THREE.Vector3(U + T1, U**2 + T2, 0);
            },
            '2A2': (u, v, t1, t2) => {
                const U = -1.5 + 3 * u;
                const T1 = t1 * 1.5; const T2 = t2 * 1.5;
                return new THREE.Vector3(2 * U**3 + T1, 3 * U**2 + T2, 0);
            },
            '2B2': (u, v, t1, t2) => {
                const U = -2 + 4 * u;
                const T1 = t1 * 1.5; const T2 = t2 * 1.5;
                return new THREE.Vector3(U**2 + T1, U**4 + T2, 0);
            },
            // 3D Functions
            '3A1p': (u, v, t1, t2, target) => {
                const U = -2 + 4 * u, V = -2 + 4 * v;
                const T1 = t1 * 1.5, T2 = t2 * 1.5;
                const x = U;
                const y = V;
                const z = 0.3 * (U**2 + V**2) + T1 * U + T2 * V;
                return target.set(x, y, z);
            },
            '3A1m': (u, v, t1, t2, target) => {
                const U = -2 + 4 * u, V = -2 + 4 * v;
                const T1 = t1 * 1.5, T2 = t2 * 1.5;
                const x = U;
                const y = V;
                const z = -0.3 * (U**2 + V**2) + T1 * U + T2 * V;
                return target.set(x, y, z);
            },
            '3A2': (u, v, t1, t2, target) => {
                const U = -1.5 + 3 * u, V = -2 + 4 * v;
                const T1 = t1 * 1.5, T2 = t2 * 1.5;
                const x = 2 * U**3 + T1;
                const y = 3 * U**2 + T2;
                const z = V;
                return target.set(x, y, z);
            },
            '3A3': (u, v, t1, t2, target) => { // Swallowtail
                const U = -1.5 + 3 * u, V = -4 + 8 * v;
                const T1 = t1 * 2, T2 = t2 * 2;
                const x = 3 * U**4 + U**2 * V;
                const y = -4 * U**3 - 2 * U * V + T1;
                const z = V + T2;
                return target.set(x, y, z).multiplyScalar(0.5);
            },
            '3B2p': (u, v, t1, t2, target) => {
                const U = -2 + 4 * u, V = -2 + 4 * v;
                const x = U;
                const y = V;
                const z = (U**2 + t1)**2 + V**2 + t2;
                return target.set(x, y, z);
            },
            '3B2m': (u, v, t1, t2, target) => {
                const U = -2 + 4 * u, V = -2 + 4 * v;
                const T1 = t1 * 2, T2 = t2;
                const x = U;
                const y = V;
                const z = -(U**4 + T1 * U**2 + V**2 - T2);
                return target.set(x, y, z);
            },
            '3B3': (u, v, t1, t2, target) => {
                const U = -2 + 4 * u, V = -2 + 4 * v;
                const x = U;
                const y = V;
                const z = U**3 + t1 * U + V**2 + t2;
                return target.set(x, y, z);
            },
            '3C3p': (u, v, t1, t2, target) => { // Elliptic Umbilic
                const U = -2 + 4 * u, V = -2 + 4 * v;
                const T1 = t1 * 2, T2 = t2 * 4;
                const x = U;
                const y = V;
                const z = U**3 - 3 * U * V**2 + T1 * (U**2 + V**2) + T2 * U;
                return target.set(x, y, z).multiplyScalar(0.2);
            },
            '3C3m': (u, v, t1, t2, target) => { // Hyperbolic Umbilic
                const U = -2 + 4 * u, V = -2 + 4 * v;
                const T1 = t1 * 4, T2 = t2 * 4;
                const x = U;
                const y = V;
                const z = U**3 + V**3 + T1 * U + T2 * V;
                return target.set(x, y, z).multiplyScalar(0.2);
            }
        };

        function init() {
            // --- Scene and Renderer ---
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xffffff);
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(visContainer.clientWidth, visContainer.clientHeight);
            visContainer.appendChild(renderer.domElement);

            // --- Lighting ---
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
            scene.add(ambientLight);
            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
            directionalLight.position.set(1, 1, 1);
            scene.add(directionalLight);

            // --- Cameras ---
            const aspect = visContainer.clientWidth / visContainer.clientHeight;
            perspectiveCamera = new THREE.PerspectiveCamera(75, aspect, 0.1, 1000);
            orthoCamera = new THREE.OrthographicCamera(-5 * aspect, 5 * aspect, 5, -5, 0.1, 100);

            // --- Controls ---
            orbitControls = new THREE.OrbitControls(perspectiveCamera, renderer.domElement);

            // --- Event Listeners ---
            window.addEventListener('resize', onWindowResize, false);
            document.getElementById('control-panel').addEventListener('click', onButtonClick);
            setupTimePad();

            // --- Initial State ---
            document.getElementById('btn-2A2').classList.add('active');
            updateVisualization();

            animate();
        }

        function animate() {
            requestAnimationFrame(animate);
            if (orbitControls.enabled) {
                orbitControls.update();
            }
            const camera = currentDimension === '3D' ? perspectiveCamera : orthoCamera;
            renderer.render(scene, camera);
        }

        function onWindowResize() {
            const width = visContainer.clientWidth;
            const height = visContainer.clientHeight;
            const aspect = width / height;

            renderer.setSize(width, height);

            perspectiveCamera.aspect = aspect;
            perspectiveCamera.updateProjectionMatrix();

            orthoCamera.left = -5 * aspect;
            orthoCamera.right = 5 * aspect;
            orthoCamera.updateProjectionMatrix();
        }

        function setupCamera(dimension) {
             if (dimension === '2D') {
                orbitControls.enabled = false;
                orthoCamera.position.set(0, 0, 10);
                orthoCamera.lookAt(0,0,0);
                orthoCamera.zoom = 1;
                orthoCamera.updateProjectionMatrix();

            } else { // 3D
                orbitControls.enabled = true;
                perspectiveCamera.position.set(4, 3, 5);
                perspectiveCamera.lookAt(0, 0, 0);
                orbitControls.reset();
            }
        }

        function updateVisualization() {
            if (currentObject) {
                scene.remove(currentObject);
                // Dispose geometries and materials if necessary for performance
                // For simplicity here, we're just removing the object
            }

            setupCamera(currentDimension);

            currentObject = new THREE.Group();

            if (currentDimension === '2D') {
                const points = [];
                const divisions = 200;
                for (let i = 0; i <= divisions; i++) {
                    const u = i / divisions;
                    points.push(BIFURCATION_FNS[currentType](u, 0, t1, t2));
                }
                const geometry = new THREE.BufferGeometry().setFromPoints(points);
                const material = new THREE.LineBasicMaterial({ color: 0x4a69bd, linewidth: 2 });
                const line = new THREE.Line(geometry, material);
                currentObject.add(line);

            } else { // 3D
                const divisions = 50;
                const func = BIFURCATION_FNS[currentType];
                const geometry = new THREE.ParametricGeometry(
                    (u, v, target) => func(u, v, t1, t2, target), 
                    divisions, 
                    divisions
                );

                const material = new THREE.MeshPhongMaterial({ 
                    color: 0xffc34d, 
                    side: THREE.DoubleSide,
                    shininess: 50
                });
                const mesh = new THREE.Mesh(geometry, material);
                currentObject.add(mesh);

                // Add wireframe for specific types
                if (currentType === '3A3' || currentType === '3C3m' || currentType === '3B2m') {
                    const wireframeMaterial = new THREE.LineBasicMaterial({ color: 0x000000, linewidth: 1, transparent: true, opacity: 0.5 });
                    const wireframe = new THREE.LineSegments(new THREE.WireframeGeometry(geometry), wireframeMaterial);
                    currentObject.add(wireframe);
                }
            }

            scene.add(currentObject);
        }

        // --- INTERACTION LOGIC ---

        function onButtonClick(event) {
            const button = event.target.closest('button');
            if (!button) return;

            // Update state
            currentType = button.dataset.type;
            currentDimension = button.dataset.dim;

            // Update UI
            document.querySelectorAll('#control-panel button').forEach(btn => btn.classList.remove('active'));
            button.classList.add('active');

            // Update visualization
            updateVisualization();
        }

        function setupTimePad() {
            const pad = document.getElementById('time-pad');
            const handle = document.getElementById('time-handle');
            let isDragging = false;

            function updateFromEvent(e) {
                const rect = pad.getBoundingClientRect();
                const x = e.clientX - rect.left;
                const y = e.clientY - rect.top;

                const clampedX = Math.max(0, Math.min(rect.width, x));
                const clampedY = Math.max(0, Math.min(rect.height, y));

                handle.style.left = `${clampedX}px`;
                handle.style.top = `${clampedY}px`;

                // Map to [-1, 1] range
                t1 = (clampedX / rect.width) * 2 - 1;
                t2 = -((clampedY / rect.height) * 2 - 1); // Y is inverted

                updateVisualization();
            }

            pad.addEventListener('mousedown', (e) => {
                isDragging = true;
                updateFromEvent(e);
            });

            pad.addEventListener('pointerdown', (e) => { // For touch devices
                isDragging = true;
                pad.setPointerCapture(e.pointerId);
                updateFromEvent(e);
            });

            document.addEventListener('mousemove', (e) => {
                if (isDragging) {
                    updateFromEvent(e);
                }
            });

            document.addEventListener('pointermove', (e) => { // For touch devices
                if (isDragging) {
                    updateFromEvent(e);
                }
            });

            document.addEventListener('mouseup', (e) => {
                isDragging = false;
            });

            document.addEventListener('pointerup', (e) => { // For touch devices
                isDragging = false;
                pad.releasePointerCapture(e.pointerId);
            });
        }

        // --- START ---
        init();
    </script>
</body>
</html>
