    <script>
        const sketch = (p) => {
            // --- DOM Elements ---
            let controls = {};
            let legendContainer;

            // --- Simulation State ---
            let state = {
                plotType: "semibatch",
                feedConfig: 0, // 0: A to B, 1: B to A
                feedRate: 10,
                UA: 15000,
                dH1: 1000,
                dH2: 3000,
            };
            const feedOptions = ["feed A to B", "feed B to A"];

            // --- Simulation Results ---
            let plotData = {
                t: [], Na: [], Nb: [], Nd: [], Nu: [], S_ratio: []
            };
            let yRange = [0, 100];
            let yLabel = "moles (mol)";

            // --- Physical Constants ---
            const V = 100; // L
            const T_initial = 330; // K
            const T_coolant = 330; // K
            const T_feed = 330; // K
            const R = 8.314; // J/mol/K
            const Cp_total = 150000; // J/K
            const k1_0 = 3e4; // L^2/(mol^2*h)
            const E1 = 30000; // J/mol
            const k2_0 = 1e5; // L/(mol*h)
            const E2 = 40000; // J/mol
            const initialMoles = 100;

            // --- p5.js Setup ---
            p.setup = () => {
                const plotArea = document.getElementById('plot-area');
                const canvas = p.createCanvas(plotArea.clientWidth, plotArea.clientHeight);
                canvas.parent('plot-canvas');
                p.noLoop();

                // Get control elements
                controls.selectPlot = document.getElementById('select-plot');
                controls.btnFeedDown = document.getElementById('btn-feed-down');
                controls.btnFeedUp = document.getElementById('btn-feed-up');
                controls.cyclerFeedLabel = document.getElementById('cycler-feed-label');
                controls.sliderFeedRate = document.getElementById('slider-feed-rate');
                controls.inputFeedRate = document.getElementById('input-feed-rate');
                controls.sliderUA = document.getElementById('slider-ua');
                controls.inputUA = document.getElementById('input-ua');
                controls.sliderdH1 = document.getElementById('slider-dh1');
                controls.inputdH1 = document.getElementById('input-dh1');
                controls.sliderdH2 = document.getElementById('slider-dh2');
                controls.inputdH2 = document.getElementById('input-dh2');
                legendContainer = document.getElementById('legend-container');

                // Attach event listeners
                controls.selectPlot.addEventListener('change', updateAndRedraw);
                controls.btnFeedDown.addEventListener('click', cycleFeedDown);
                controls.btnFeedUp.addEventListener('click', cycleFeedUp);

                // Sync sliders and number inputs
                setupSliderSync(controls.sliderFeedRate, controls.inputFeedRate);
                setupSliderSync(controls.sliderUA, controls.inputUA);
                setupSliderSync(controls.sliderdH1, controls.inputdH1);
                setupSliderSync(controls.sliderdH2, controls.inputdH2);

                // Initial run
                updateAndRedraw();
            };

            function setupSliderSync(slider, input) {
                slider.addEventListener('input', () => {
                    input.value = slider.value;
                    updateAndRedraw();
                });
                input.addEventListener('change', () => {
                    slider.value = input.value;
                    updateAndRedraw();
                });
            }

            function cycleFeedDown() {
                state.feedConfig = (state.feedConfig - 1 + feedOptions.length) % feedOptions.length;
                updateAndRedraw();
            }

            function cycleFeedUp() {
                state.feedConfig = (state.feedConfig + 1) % feedOptions.length;
                updateAndRedraw();
            }

            function updateStateFromControls() {
                state.plotType = controls.selectPlot.value;
                controls.cyclerFeedLabel.textContent = feedOptions[state.feedConfig];

                state.feedRate = parseFloat(controls.sliderFeedRate.value);
                state.UA = parseFloat(controls.sliderUA.value);
                state.dH1 = parseFloat(controls.sliderdH1.value);
                state.dH2 = parseFloat(controls.sliderdH2.value);

                // Disable feed rate slider for batch mode
                const isBatch = state.plotType === 'batch';
                controls.sliderFeedRate.disabled = isBatch;
                controls.inputFeedRate.disabled = isBatch;
            }

            function updateAndRedraw() {
                updateStateFromControls();
                runSimulation();
                updateLegend();
                p.redraw();
            }

            // --- Simulation Core ---
            function getDerivatives(t, Y, simParams) {
                const [Na, Nb, Nd, Nu, T] = Y;
                const { type, feedConfig, feedRate } = simParams;

                // Rate constants
                const k1 = k1_0 * Math.exp(-E1 / (R * T));
                const k2 = k2_0 * Math.exp(-E2 / (R * T));

                // Reaction rates (mol/L/h)
                const r1 = k1 * (Na / V) * (Nb / V) * (Nb / V);
                const r2 = k2 * (Na / V) * (Nb / V);

                // Derivative calculations
                let dNa_dt, dNb_dt;
                const dNd_dt = r1 * V;
                const dNu_dt = r2 * V;
                const dT_dt = (state.UA * (T_coolant - T) + (-state.dH1 * r1 - state.dH2 * r2) * V) / Cp_total;

                if (type === 'batch') {
                    dNa_dt = -(r1 + r2) * V;
                    dNb_dt = -(r1 + r2) * V;
                } else { // semibatch
                    const t_f = initialMoles / feedRate;
                    if (feedConfig === 0) { // feed A to B
                        const F_A0 = (t < t_f) ? feedRate : 0;
                        dNa_dt = F_A0 - (r1 + r2) * V;
                        dNb_dt = -(r1 + r2) * V;
                    } else { // feed B to A
                        const F_B0 = (t < t_f) ? feedRate : 0;
                        dNa_dt = -(r1 + r2) * V;
                        dNb_dt = F_B0 - (r1 + r2) * V;
                    }
                }

                return [dNa_dt, dNb_dt, dNd_dt, dNu_dt, dT_dt];
            }

            function rungeKutta4(odes, y, t, dt, simParams) {
                const k1 = odes(t, y, simParams);
                const y2 = y.map((yi, i) => yi + 0.5 * dt * k1[i]);
                const k2 = odes(t + 0.5 * dt, y2, simParams);
                const y3 = y.map((yi, i) => yi + 0.5 * dt * k2[i]);
                const k3 = odes(t + 0.5 * dt, y3, simParams);
                const y4 = y.map((yi, i) => yi + dt * k3[i]);
                const k4 = odes(t + dt, y4, simParams);

                return y.map((yi, i) => yi + (dt / 6) * (k1[i] + 2 * k2[i] + 2 * k3[i] + k4[i]));
            }

            function solveOdes(simParams) {
                let y;
                if (simParams.type === 'batch') {
                    y = [initialMoles, initialMoles, 0, 0, T_initial];
                } else { // semibatch
                    if (simParams.feedConfig === 0) { // A to B
                        y = [0, initialMoles, 0, 0, T_initial];
                    } else { // B to A
                        y = [initialMoles, 0, 0, 0, T_initial];
                    }
                }

                const t_max = 2.0;
                const dt = 0.01;
                const results = { t: [], Na: [], Nb: [], Nd: [], Nu: [] };

                for (let t = 0; t <= t_max; t += dt) {
                    results.t.push(t);
                    results.Na.push(y[0]);
                    results.Nb.push(y[1]);
                    results.Nd.push(y[2]);
                    results.Nu.push(y[3]);
                    y = rungeKutta4(getDerivatives, y, t, dt, simParams);
                }
                return results;
            }

            function runSimulation() {
                plotData = { t: [], Na: [], Nb: [], Nd: [], Nu: [], S_ratio: [] };

                if (state.plotType === "selectivity") {
                    const batchParams = { type: 'batch' };
                    const batchResults = solveOdes(batchParams);

                    const semiParams = { type: 'semibatch', feedConfig: state.feedConfig, feedRate: state.feedRate };
                    const semiResults = solveOdes(semiParams);

                    plotData.t = batchResults.t;
                    let minRatio = Infinity, maxRatio = -Infinity;

                    for (let i = 0; i < plotData.t.length; i++) {
                        // Start from t=dt to avoid division by zero
                        if (i === 0) {
                            plotData.S_ratio.push(NaN);
                            continue;
                        }
                        const S_batch = batchResults.Nu[i] > 1e-6 ? batchResults.Nd[i] / batchResults.Nu[i] : NaN;
                        const S_semi = semiResults.Nu[i] > 1e-6 ? semiResults.Nd[i] / semiResults.Nu[i] : NaN;

                        let ratio = (S_batch > 1e-6 && !isNaN(S_semi)) ? S_semi / S_batch : NaN;
                        if (!isNaN(ratio) && isFinite(ratio)) {
                           if(ratio < minRatio) minRatio = ratio;
                           if(ratio > maxRatio) maxRatio = ratio;
                        }
                        plotData.S_ratio.push(ratio);
                    }

                    yLabel = "selectivity ratio";
                    const padding = (maxRatio - minRatio) * 0.1 || 0.1;
                    yRange = [Math.max(0, minRatio - padding), maxRatio + padding];

                } else {
                    const simParams = { 
                        type: state.plotType, 
                        feedConfig: state.feedConfig, 
                        feedRate: state.feedRate 
                    };
                    const results = solveOdes(simParams);
                    plotData = results;
                    plotData.S_ratio = [];
                    yLabel = "moles (mol)";
                    yRange = [0, 100];
                }
            }

            // --- Drawing ---
            p.draw = () => {
                p.background('#F0F0F0');
                const margin = { top: 60, right: 30, bottom: 50, left: 60 };
                const plotW = p.width - margin.left - margin.right;
                const plotH = p.height - margin.top - margin.bottom;

                p.push();
                p.translate(margin.left, margin.top);

                // Draw Grid & Axes
                drawGridAndAxes(plotW, plotH);

                // Draw Curves
                if (state.plotType === 'selectivity') {
                    drawCurve(plotData.t, plotData.S_ratio, plotW, plotH, '#800080', 'solid');
                } else {
                    drawCurve(plotData.t, plotData.Na, plotW, plotH, '#2ca02c', 'dashed');
                    drawCurve(plotData.t, plotData.Nb, plotW, plotH, '#000000', 'dashed');
                    drawCurve(plotData.t, plotData.Nd, plotW, plotH, '#1f77b4', 'solid');
                    drawCurve(plotData.t, plotData.Nu, plotW, plotH, '#d62728', 'solid');
                }

                p.pop();
            };

            function drawGridAndAxes(w, h) {
                p.stroke(200);
                p.strokeWeight(1);
                p.fill(100);
                p.textAlign(p.CENTER, p.CENTER);
                p.textSize(12);

                // X-axis (time)
                const xTicks = [0, 0.5, 1.0, 1.5, 2.0];
                for (const tick of xTicks) {
                    const x = p.map(tick, 0, 2.0, 0, w);
                    p.line(x, 0, x, h); // Grid line
                    p.text(tick.toFixed(1), x, h + 20);
                }
                p.text("time (h)", w / 2, h + 40);

                // Y-axis
                const numYTicks = 5;
                for (let i = 0; i <= numYTicks; i++) {
                    const val = yRange[0] + i * (yRange[1] - yRange[0]) / numYTicks;
                    const y = p.map(val, yRange[0], yRange[1], h, 0);
                    p.line(0, y, w, y); // Grid line
                    p.textAlign(p.RIGHT, p.CENTER);
                    p.text(val.toPrecision(2), -10, y);
                }
                p.push();
                p.translate(-45, h/2);
                p.rotate(-p.HALF_PI);
                p.textAlign(p.CENTER, p.CENTER);
                p.text(yLabel, 0, 0);
                p.pop();

                // Axes lines
                p.stroke(0);
                p.line(0, 0, 0, h);
                p.line(0, h, w, h);
            }

            function drawCurve(xData, yData, w, h, color, style) {
                p.noFill();
                p.stroke(color);
                p.strokeWeight(2);

                let points = [];
                for(let i=0; i<xData.length; i++) {
                    if (isNaN(yData[i])) continue;
                    const x = p.map(xData[i], 0, 2.0, 0, w);
                    const y = p.map(yData[i], yRange[0], yRange[1], h, 0);
                    if (y >= 0 && y <= h) { // Simple clipping
                        points.push(p.createVector(x,y));
                    }
                }

                if (style === 'dashed') {
                    drawDashedLine(points, 5, 5);
                } else {
                    p.beginShape();
                    for(const pt of points) {
                        p.vertex(pt.x, pt.y);
                    }
                    p.endShape();
                }
            }

            function drawDashedLine(points, dashLen, gapLen) {
                for (let i = 0; i < points.length - 1; i++) {
                    let p1 = points[i];
                    let p2 = points[i + 1];
                    let d = p1.dist(p2);
                    let dashCount = Math.floor(d / (dashLen + gapLen));
                    let dir = p5.Vector.sub(p2, p1).normalize();

                    for (let j = 0; j < dashCount; j++) {
                        let start = p5.Vector.add(p1, p5.Vector.mult(dir, j * (dashLen + gapLen)));
                        let end = p5.Vector.add(start, p5.Vector.mult(dir, dashLen));
                        p.line(start.x, start.y, end.x, end.y);
                    }
                    // Draw remaining part
                    let lastDashEnd = p5.Vector.add(p1, p5.Vector.mult(dir, dashCount * (dashLen + gapLen)));
                    if (p5.Vector.dist(lastDashEnd, p1) < p5.Vector.dist(p2, p1)) {
                         let remainingDist = p5.Vector.dist(p2, lastDashEnd);
                         let end = p5.Vector.add(lastDashEnd, p5.Vector.mult(dir, Math.min(dashLen, remainingDist)));
                         p.line(lastDashEnd.x, lastDashEnd.y, end.x, end.y);
                    }
                }
            }

            function updateLegend() {
                let html = '';
                if (state.plotType === 'selectivity') {
                    html = `
                        <div class="legend-item">
                            <div class="legend-line" style="background-color: #800080;"></div>
                            <span>semibatch \\(S_{d/u}\\) / batch \\(S_{d/u}\\)</span>
                        </div>
                    `;
                } else {
                    html = `
                        <div class="legend-item">
                            <div class="legend-line" style="background-color: #2ca02c; border-top: 2px dashed #2ca02c; height:0px;"></div>
                            <span>\\(N_a\\)</span>
                        </div>
                        <div class="legend-item">
                            <div class="legend-line" style="background-color: #000000; border-top: 2px dashed #000000; height:0px;"></div>
                            <span>\\(N_b\\)</span>
                        </div>
                        <div class="legend-item">
                            <div class="legend-line" style="background-color: #1f77b4;"></div>
                            <span>\\(N_d\\)</span>
                        </div>
                        <div class="legend-item">
                            <div class="legend-line" style="background-color: #d62728;"></div>
                            <span>\\(N_u\\)</span>
                        </div>
                    `;
                }
                legendContainer.innerHTML = html;
                if (window.MathJax) {
                    MathJax.typesetPromise([legendContainer]).catch(function (err) {
                        console.log('MathJax typesetting failed: ' + err.message);
                    });
                }
            }
        };

        new p5(sketch);
    </script>
</body>
</html>
