        </div>

        <div id="canvas-container">
            <p id="status-text"></p>
            <button id="btn-reset">+</button>
        </div>
    </div>

    <script>
        const sketch = (p) => {
            // --- State Variables ---
            let tree = [];
            let startNode, goalNode;
            let obstacles = [];
            let path = [];
            let pathLength = 0;
            let goalReached = false;
            let isDraggingGoal = false;

            const CANVAS_SIZE = 500;
            const STEER_DISTANCE = 20;
            const RRT_STAR_NEIGHBOR_RADIUS = 50;

            // --- UI Elements ---
            const btnRt = document.getElementById('btn-rt');
            const btnRrt = document.getElementById('btn-rrt');
            const btnRrtStar = document.getElementById('btn-rrt-star');
            const selectObstacle = document.getElementById('select-obstacle');
            const btnAdd = {
                1: document.getElementById('btn-add-1'),
                10: document.getElementById('btn-add-10'),
                100: document.getElementById('btn-add-100'),
                200: document.getElementById('btn-add-200'),
                500: document.getElementById('btn-add-500'),
            };
            const sliderBias = document.getElementById('slider-bias');
            const labelBias = document.getElementById('bias-value-label');
            const sliderRadius = document.getElementById('slider-radius');
            const labelRadius = document.getElementById('radius-value-label');
            const btnReset = document.getElementById('btn-reset');
            const statusText = document.getElementById('status-text');

            // --- Control State ---
            let state = {
                treeType: 'rt',
                obstacleType: 'narrow-passage',
                explorationBias: 0,
                goalRadius: 10,
            };

            // --- p5.js Setup & Draw ---
            p.setup = function() {
                const canvas = p.createCanvas(CANVAS_SIZE, CANVAS_SIZE);
                canvas.parent('canvas-container');
                p.noLoop();
                setupControls();
                resetSimulation(true);
            };

            p.draw = function() {
                p.background(255);
                drawObstacles();
                drawTree();
                drawPath();
                drawStartAndGoal();
                updateStatusText();
            };

            // --- Simulation Logic ---
            function resetSimulation(fullReset = false) {
                if (fullReset) {
                    startNode = { pos: p.createVector(50, 250), parent: null, cost: 0 };
                    goalNode = { pos: p.createVector(450, 450) };
                }
                tree = [startNode];
                path = [];
                pathLength = 0;
                goalReached = false;
                generateObstacles();
                checkIfGoalReached();
                p.redraw();
            }

            function addNodes(count) {
                for (let i = 0; i < count; i++) {
                    if (goalReached && state.treeType !== 'rrt-star') break;

                    let sample;
                    if (p.random() < state.explorationBias) {
                        sample = goalNode.pos.copy();
                    } else {
                        sample = p.createVector(p.random(p.width), p.random(p.height));
                    }

                    if (state.treeType === 'rt') {
                        addRandomTree(sample);
                    } else if (state.treeType === 'rrt') {
                        addRRT(sample);
                    } else if (state.treeType === 'rrt-star') {
                        addRRTStar(sample);
                    }
                }
                p.redraw();
            }

            function addRandomTree(sample) {
                const nearest = findNearestNode(sample);
                if (isCollisionFree(nearest.pos, sample)) {
                    const newNode = { pos: sample, parent: nearest, cost: 0 }; // Cost not used in RT
                    tree.push(newNode);
                    checkIfGoalReached(newNode);
                }
            }

            function addRRT(sample) {
                const nearest = findNearestNode(sample);
                const newNodePos = steer(nearest.pos, sample);

                if (isCollisionFree(nearest.pos, newNodePos)) {
                    const newNode = { pos: newNodePos, parent: nearest, cost: 0 }; // Cost not used in RRT
                    tree.push(newNode);
                    checkIfGoalReached(newNode);
                }
            }

            function addRRTStar(sample) {
                const nearest = findNearestNode(sample);
                const steeredPos = steer(nearest.pos, sample);

                if (!isCollisionFree(nearest.pos, steeredPos)) return;

                const neighbors = findNeighbors(steeredPos, RRT_STAR_NEIGHBOR_RADIUS);
                let bestParent = nearest;
                let minCost = nearest.cost + p.dist(nearest.pos.x, nearest.pos.y, steeredPos.x, steeredPos.y);

                // Find best parent among neighbors
                for (const neighbor of neighbors) {
                    if (isCollisionFree(neighbor.pos, steeredPos)) {
                        const cost = neighbor.cost + p.dist(neighbor.pos.x, neighbor.pos.y, steeredPos.x, steeredPos.y);
                        if (cost < minCost) {
                            minCost = cost;
                            bestParent = neighbor;
                        }
                    }
                }

                const newNode = { pos: steeredPos, parent: bestParent, cost: minCost };
                tree.push(newNode);

                // Rewire neighbors
                for (const neighbor of neighbors) {
                    if (neighbor === bestParent) continue;
                    const costViaNew = newNode.cost + p.dist(newNode.pos.x, newNode.pos.y, neighbor.pos.x, neighbor.pos.y);
                    if (costViaNew < neighbor.cost && isCollisionFree(newNode.pos, neighbor.pos)) {
                        neighbor.parent = newNode;
                        neighbor.cost = costViaNew;
                        // Propagate cost update to children (optional, but correct)
                    }
                }

                checkIfGoalReached(newNode);
            }

            // --- Helper Functions ---
            function findNearestNode(point) {
                let nearest = null;
                let minDist = Infinity;
                for (const node of tree) {
                    const d = p.dist(node.pos.x, node.pos.y, point.x, point.y);
                    if (d < minDist) {
                        minDist = d;
                        nearest = node;
                    }
                }
                return nearest;
            }

            function findNeighbors(point, radius) {
                const neighbors = [];
                for (const node of tree) {
                    if (p.dist(node.pos.x, node.pos.y, point.x, point.y) < radius) {
                        neighbors.push(node);
                    }
                }
                return neighbors;
            }

            function steer(from, to) {
                const dir = p5.Vector.sub(to, from);
                const dist = dir.mag();
                if (dist < STEER_DISTANCE) {
                    return to.copy();
                } else {
                    dir.setMag(STEER_DISTANCE);
                    return p5.Vector.add(from, dir);
                }
            }

            function checkIfGoalReached(newNode = null) {
                let nodeToCheck = newNode || tree[tree.length - 1];
                if (!nodeToCheck) return;

                const distToGoal = p.dist(nodeToCheck.pos.x, nodeToCheck.pos.y, goalNode.pos.x, goalNode.pos.y);

                if (distToGoal < state.goalRadius) {
                    const newPath = tracePath(nodeToCheck);
                    const newLength = calculatePathLength(newPath);

                    if (!goalReached || newLength < pathLength) {
                        goalReached = true;
                        path = newPath;
                        pathLength = newLength;
                    }
                }
            }

            function tracePath(endNode) {
                const currentPath = [];
                let current = endNode;
                while (current !== null) {
                    currentPath.push(current);
                    current = current.parent;
                }
                return currentPath.reverse();
            }

            function calculatePathLength(pathArr) {
                let len = 0;
                for (let i = 0; i < pathArr.length - 1; i++) {
                    len += p.dist(pathArr[i].pos.x, pathArr[i].pos.y, pathArr[i+1].pos.x, pathArr[i+1].pos.y);
                }
                return len;
            }

            // --- Obstacle Generation and Collision ---
            function generateObstacles() {
                obstacles = [];
                if (state.obstacleType === 'narrow-passage') {
                    const w = p.width;
                    const h = p.height;
                    const thickness = 8;
                    const gap = 100;
                    obstacles.push({ x: 0, y: 0, w: w, h: thickness });
                    obstacles.push({ x: 0, y: h - thickness, w: w, h: thickness });
                    obstacles.push({ x: 0, y: 0, w: thickness, h: h });
                    obstacles.push({ x: w - thickness, y: 0, w: thickness, h: h });

                    obstacles.push({ x: w / 3, y: 0, w: thickness, h: h / 2 - gap / 2 });
                    obstacles.push({ x: w / 3, y: h / 2 + gap / 2, w: thickness, h: h / 2 - gap / 2 });

                    obstacles.push({ x: 2 * w / 3, y: 0, w: thickness, h: 2*h / 3 - gap / 2 });
                    obstacles.push({ x: 2 * w / 3, y: 2*h/3 + gap / 2, w: thickness, h: h/3 - gap/2 });
                } else if (state.obstacleType === 'random-rects') {
                    for (let i = 0; i < 15; i++) {
                        obstacles.push({
                            x: p.random(p.width),
                            y: p.random(p.height),
                            w: p.random(20, 100),
                            h: p.random(20, 100)
                        });
                    }
                }
            }

            function isCollisionFree(p1, p2) {
                for (const obs of obstacles) {
                    if (lineRect(p1.x, p1.y, p2.x, p2.y, obs.x, obs.y, obs.w, obs.h)) {
                        return false;
                    }
                }
                return true;
            }

            function lineRect(x1, y1, x2, y2, rx, ry, rw, rh) {
                const left =   lineLine(x1, y1, x2, y2, rx, ry, rx, ry + rh);
                const right =  lineLine(x1, y1, x2, y2, rx + rw, ry, rx + rw, ry + rh);
                const top =    lineLine(x1, y1, x2, y2, rx, ry, rx + rw, ry);
                const bottom = lineLine(x1, y1, x2, y2, rx, ry + rh, rx + rw, ry + rh);
                return left || right || top || bottom;
            }

            function lineLine(x1, y1, x2, y2, x3, y3, x4, y4) {
                const uA = ((x4 - x3) * (y1 - y3) - (y4 - y3) * (x1 - x3)) / ((y4 - y3) * (x2 - x1) - (x4 - x3) * (y2 - y1));
                const uB = ((x2 - x1) * (y1 - y3) - (y2 - y1) * (x1 - x3)) / ((y4 - y3) * (x2 - x1) - (x4 - x3) * (y2 - y1));
                return uA >= 0 && uA <= 1 && uB >= 0 && uB <= 1;
            }

            // --- Drawing Functions ---
            function drawObstacles() {
                p.stroke('blue');
                p.strokeWeight(8);
                p.noFill();
                for (const obs of obstacles) {
                    p.rect(obs.x, obs.y, obs.w, obs.h);
                }
            }

            function drawTree() {
                if (state.treeType === 'rrt-star') {
                    p.stroke(0);
                    p.strokeWeight(0.5);
                    for (const node of tree) {
                        if (node.parent) {
                            p.line(node.pos.x, node.pos.y, node.parent.pos.x, node.parent.pos.y);
                        }
                    }
                }

                p.noStroke();
                p.fill('red');
                for (let i = 1; i < tree.length; i++) {
                    p.circle(tree[i].pos.x, tree[i].pos.y, 5);
                }
            }

            function drawPath() {
                if (goalReached) {
                    p.stroke('green');
                    p.strokeWeight(3);
                    p.noFill();
                    p.beginShape();
                    for(const node of path) {
                        p.vertex(node.pos.x, node.pos.y);
                    }
                    p.endShape();
                }
            }

            function drawStartAndGoal() {
                // Start
                p.noStroke();
                p.fill(tree.length > 1 ? 'green' : 'red');
                p.circle(startNode.pos.x, startNode.pos.y, 10);

                // Goal
                p.fill(255, 255, 0, 100);
                p.noStroke();
                p.circle(goalNode.pos.x, goalNode.pos.y, state.goalRadius * 2);

                p.stroke(0);
                p.strokeWeight(1);
                p.line(goalNode.pos.x - 7, goalNode.pos.y, goalNode.pos.x + 7, goalNode.pos.y);
                p.line(goalNode.pos.x, goalNode.pos.y - 7, goalNode.pos.x, goalNode.pos.y + 7);
            }

            // --- UI Interaction ---
            function updateStatusText() {
                let text = `${tree.length} node${tree.length !== 1 ? 's' : ''}`;
                if (goalReached) {
                    text += `, path length ${pathLength.toFixed(2)}`;
                } else {
                    text += `, goal not yet reached`;
                }
                statusText.innerText = text;
            }

            function setupControls() {
                // Tree Type Buttons
                const treeButtons = { rt: btnRt, rrt: btnRrt, 'rrt-star': btnRrtStar };
                Object.entries(treeButtons).forEach(([type, button]) => {
                    button.addEventListener('click', () => {
                        Object.values(treeButtons).forEach(b => b.classList.remove('active'));
                        button.classList.add('active');
                        state.treeType = type;
                        sliderBias.disabled = (type === 'rt');
                        resetSimulation();
                    });
                });
                sliderBias.disabled = true; // Initial state for RT

                // Obstacle Select
                selectObstacle.addEventListener('change', (e) => {
                    state.obstacleType = e.target.value;
                    resetSimulation(true);
                });

                // Add Nodes Buttons
                Object.entries(btnAdd).forEach(([count, button]) => {
                    button.addEventListener('click', () => addNodes(parseInt(count, 10)));
                });

                // Sliders
                sliderBias.addEventListener('input', (e) => {
                    state.explorationBias = parseFloat(e.target.value);
                    labelBias.innerText = state.explorationBias.toFixed(2);
                });
                labelBias.innerText = parseFloat(sliderBias.value).toFixed(2);

                sliderRadius.addEventListener('input', (e) => {
                    state.goalRadius = parseFloat(e.target.value);
                    labelRadius.innerText = state.goalRadius.toFixed(1);
                    checkIfGoalReached(); // Re-check with new radius
                    p.redraw();
                });
                labelRadius.innerText = parseFloat(sliderRadius.value).toFixed(1);

                // Reset Button
                btnReset.addEventListener('click', () => resetSimulation(true));
            }

            // --- Mouse controls for goal dragging ---
            p.mousePressed = function() {
                if (p.mouseX < 0 || p.mouseX > p.width || p.mouseY < 0 || p.mouseY > p.height) return;
                const distToGoal = p.dist(p.mouseX, p.mouseY, goalNode.pos.x, goalNode.pos.y);
                if (distToGoal < state.goalRadius) {
                    isDraggingGoal = true;
                }
            };

            p.mouseDragged = function() {
                if (isDraggingGoal) {
                    goalNode.pos.x = p.constrain(p.mouseX, 0, p.width);
                    goalNode.pos.y = p.constrain(p.mouseY, 0, p.height);
                    resetSimulation(false); // Reset tree but keep new goal pos
                }
            };

            p.mouseReleased = function() {
                isDraggingGoal = false;
            };
        };

        new p5(sketch);
    </script>
</body>
</html>
