    <script>
        let scene, camera, renderer, controls;
        let ionPathMesh = null;
        const ION_PATH_NAME = 'ionPath';

        function init() {
            // Scene Setup
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xf0f0f0);

            // Camera
            const container = document.getElementById('visualization-container');
            camera = new THREE.PerspectiveCamera(60, container.clientWidth / container.clientHeight, 0.1, 100);
            camera.position.set(4, 4, 4);
            camera.lookAt(0, 1, 0);

            // Renderer
            const canvas = document.getElementById('three-canvas');
            renderer = new THREE.WebGLRenderer({ canvas: canvas, antialias: true });
            renderer.setPixelRatio(window.devicePixelRatio);
            renderer.setSize(container.clientWidth, container.clientHeight);

            // Controls
            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.target.set(0, 1, 0);
            controls.update();

            // Lighting
            scene.add(new THREE.AmbientLight(0x666666));
            const directionalLight = new THREE.DirectionalLight(0xffffff, 1.0);
            directionalLight.position.set(5, 5, 5);
            scene.add(directionalLight);

            // Axes Helper
            const axesHelper = new THREE.AxesHelper(4);
            scene.add(axesHelper);

            // Static Geometry
            createStaticGeometry();

            // Event Listeners
            setupEventListeners();

            // Initial Visualization
            updateVisualization();

            // Start Animation Loop
            animate();
        }

        function createStaticGeometry() {
            // Potential Surface
            const surfaceFunc = (u, v, target) => {
                const rho = 0.4 + u * 2.1;
                const theta = v * 2 * Math.PI;

                const x = rho * Math.cos(theta);
                const y_surf = rho * rho + 1 / (rho * rho);
                const z_surf = -rho * Math.sin(theta); // Map y from physics to -z in three.js

                target.set(x, y_surf, z_surf);
            };
            const surfaceGeometry = new THREE.ParametricGeometry(surfaceFunc, 64, 64);
            const surfaceMaterial = new THREE.MeshStandardMaterial({
                color: 0xE69943,
                metalness: 0.4,
                roughness: 0.5,
                side: THREE.DoubleSide
            });
            const potentialSurface = new THREE.Mesh(surfaceGeometry, surfaceMaterial);
            scene.add(potentialSurface);

            // Central Cylinder
            const cylinderGeometry = new THREE.CylinderGeometry(0.4, 0.4, 2.5, 32);
            const cylinderMaterial = new THREE.MeshStandardMaterial({
                color: 0xcccccc,
                metalness: 0.1,
                roughness: 0.9
            });
            const centralCylinder = new THREE.Mesh(cylinderGeometry, cylinderMaterial);
            centralCylinder.position.set(0, 1.25, 0);
            scene.add(centralCylinder);
        }

        function setupEventListeners() {
            const controlsMap = [
                { sliderId: 'slider-t', inputId: 'input-t' },
                { sliderId: 'slider-rho0', inputId: 'input-rho0' },
                { sliderId: 'slider-zeta0', inputId: 'input-zeta0' },
                { sliderId: 'slider-e0', inputId: 'input-e0' },
                { sliderId: 'slider-v', inputId: 'input-v' },
                { sliderId: 'slider-lambda', inputId: 'input-lambda' },
            ];

            controlsMap.forEach(({ sliderId, inputId }) => {
                const slider = document.getElementById(sliderId);
                const input = document.getElementById(inputId);

                slider.addEventListener('input', () => {
                    input.value = slider.value;
                    updateVisualization();
                });

                input.addEventListener('input', () => {
                    slider.value = input.value;
                    updateVisualization();
                });
            });

            window.addEventListener('resize', onWindowResize, false);
        }

        function onWindowResize() {
            const container = document.getElementById('visualization-container');
            camera.aspect = container.clientWidth / container.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(container.clientWidth, container.clientHeight);
        }

        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        function updateVisualization() {
            // Remove previous path
            const oldPath = scene.getObjectByName(ION_PATH_NAME);
            if (oldPath) {
                scene.remove(oldPath);
                oldPath.geometry.dispose();
                oldPath.material.dispose();
            }

            // Read parameters from controls
            const params = {
                t: parseFloat(document.getElementById('input-t').value),
                rho0: parseFloat(document.getElementById('input-rho0').value),
                zeta0: parseFloat(document.getElementById('input-zeta0').value),
                e0: parseFloat(document.getElementById('input-e0').value),
                nu: parseFloat(document.getElementById('input-v').value),
                lambda: parseFloat(document.getElementById('input-lambda').value)
            };

            // Calculate path points
            const points = calculatePath(params);

            // Create new path geometry if points are available
            if (points.length > 1) {
                const curve = new THREE.CatmullRomCurve3(points);
                const geometry = new THREE.TubeGeometry(curve, 512, 0.04, 8, false);
                const material = new THREE.MeshBasicMaterial({ color: 0x61AEEF });
                ionPathMesh = new THREE.Mesh(geometry, material);
                ionPathMesh.name = ION_PATH_NAME;
                scene.add(ionPathMesh);
            }
        }

        function calculatePath(params) {
            const { t, rho0, zeta0, e0, nu, lambda } = params;

            // ODE system: Y = [ρ, dρ/ds, φ]
            const derivatives = (s, Y) => {
                const rho = Y[0];
                const d_rho_ds = Y[1];

                // Avoid division by zero or large numbers if rho is close to zero
                if (Math.abs(rho) < 1e-6) return [0, 0, 0];

                const rho2 = rho * rho;
                const rho3 = rho2 * rho;

                // d(ρ)/ds
                const dy1 = d_rho_ds;

                // d^2(ρ)/ds^2 = -(2*ρ - 2/ρ³) - 4*λ²*ρ + 4*e₀/ρ³
                const dy2 = -(2 * rho - 2 / rho3) - 4 * lambda * lambda * rho + 4 * e0 / rho3;

                // d(φ)/ds = 2*(e₀/ρ² - λ)
                const dy3 = 2 * (e0 / rho2 - lambda);

                return [dy1, dy2, dy3];
            };

            const rk4Step = (s, Y, ds) => {
                const k1 = derivatives(s, Y);
                const k2 = derivatives(s + ds / 2, [Y[0] + (ds / 2) * k1[0], Y[1] + (ds / 2) * k1[1], Y[2] + (ds / 2) * k1[2]]);
                const k3 = derivatives(s + ds / 2, [Y[0] + (ds / 2) * k2[0], Y[1] + (ds / 2) * k2[1], Y[2] + (ds / 2) * k2[2]]);
                const k4 = derivatives(s + ds, [Y[0] + ds * k3[0], Y[1] + ds * k3[1], Y[2] + ds * k3[2]]);

                const newY = [
                    Y[0] + (ds / 6) * (k1[0] + 2 * k2[0] + 2 * k3[0] + k4[0]),
                    Y[1] + (ds / 6) * (k1[1] + 2 * k2[1] + 2 * k3[1] + k4[1]),
                    Y[2] + (ds / 6) * (k1[2] + 2 * k2[2] + 2 * k3[2] + k4[2])
                ];
                return newY;
            };

            const points = [];
            let Y = [rho0, 0, 0]; // Initial conditions: [ρ₀, dρ/ds=0, φ=0]
            const ds = 0.05;

            for (let s = 0; s <= t; s += ds) {
                const rho_i = Y[0];
                const phi_i = Y[2];

                // Calculate Cartesian coordinates from integrated state
                const u_rho = rho_i * rho_i + 1 / (rho_i * rho_i);
                const sqrt_arg = e0 - u_rho;

                // z_i = sqrt(max(0, e₀ - u_rho)) * sin(sqrt(2) * ν * s + ζ₀)
                const z_i = Math.sqrt(Math.max(0, sqrt_arg)) * Math.sin(Math.sqrt(2) * nu * s + zeta0);

                // x_i = ρ_i * cos(φ_i)
                const x_i = rho_i * Math.cos(phi_i);

                // y_i = ρ_i * sin(φ_i)
                const y_i = rho_i * Math.sin(phi_i);

                // Map physics coordinates (x_i, y_i, z_i) to three.js coordinates (x, y, z)
                // The physics 'z' coordinate is the axial motion, which is vertical (y-axis in three.js).
                // The physics '(x,y)' plane is the radial motion, which is horizontal (x-z plane in three.js).
                // (x_i, y_i, z_i) -> THREE.Vector3(x_i, z_i, -y_i)
                points.push(new THREE.Vector3(x_i, z_i, -y_i));

                // Perform next integration step
                Y = rk4Step(s, Y, ds);
            }

            return points;
        }

        // Initialize the application
        init();
    </script>
</body>
</html>
