<body>

    <div id="app-container">
        <div id="control-panel">
            <h4>Time</h4>
            <input type="range" id="slider-time" min="0" max="672" step="1" value="0">
            <div>
                <button id="btn-step-back">«</button>
                <button id="btn-play-pause">▶</button>
                <button id="btn-reset">⥀</button>
                <button id="btn-step-forward">»</button>
            </div>
            <p id="display-time">0 d 0 h</p>
            <hr>
            <h4>Moon's rotational period related to the current value</h4>
            <div id="rotation-selector">
                <button id="btn-rot-half">half</button>
                <button id="btn-rot-normal">normal</button>
                <button id="btn-rot-double">double</button>
                <button id="btn-rot-none">no rotation</button>
            </div>
            <hr>
            <h4>Display Options</h4>
            <div>
                <input type="checkbox" id="check-day-night">
                <label for="check-day-night">day/night</label>
            </div>
            <div>
                <input type="checkbox" id="check-near-side" checked>
                <label for="check-near-side">near side of the Moon</label>
            </div>
            <div>
                <input type="checkbox" id="check-earth-moon-line" checked>
                <label for="check-earth-moon-line">Earth-Moon line</label>
            </div>
            <div>
                <input type="checkbox" id="check-moon-only">
                <label for="check-moon-only">the Moon only</label>
            </div>
        </div>
        <div id="visualization-area">
            <!-- The canvas will be created here by p5.js -->
        </div>
    </div>

    <script>
        // === DOM ELEMENTS ===
        const sliderTime = document.getElementById('slider-time');
        const btnPlayPause = document.getElementById('btn-play-pause');
        const btnStepBack = document.getElementById('btn-step-back');
        const btnStepForward = document.getElementById('btn-step-forward');
        const btnReset = document.getElementById('btn-reset');
        const displayTime = document.getElementById('display-time');

        const rotationButtons = {
            half: document.getElementById('btn-rot-half'),
            normal: document.getElementById('btn-rot-normal'),
            double: document.getElementById('btn-rot-double'),
            none: document.getElementById('btn-rot-none'),
        };

        const checkDayNight = document.getElementById('check-day-night');
        const checkNearSide = document.getElementById('check-near-side');
        const checkEarthMoonLine = document.getElementById('check-earth-moon-line');
        const checkMoonOnly = document.getElementById('check-moon-only');

        // === SIMULATION STATE ===
        let time = 0; // in hours
        let isPlaying = false;
        let rotationMode = 'normal';
        let showDayNight = false;
        let showNearSide = true;
        let showEarthMoonLine = true;
        let showMoonOnly = false;

        const maxTime = 672; // 28 days in hours

        // === P5.JS SKETCH ===
        const sketch = (p) => {
            // --- Constants ---
            const EARTH_RADIUS = 80;
            const MOON_RADIUS = 25;
            const ORBIT_RADIUS = 250;
            const ORBITAL_PERIOD = 672; // hours
            const EARTH_ROTATION_PERIOD = 24; // hours

            p.setup = () => {
                const visArea = document.getElementById('visualization-area');
                const canvasSize = Math.min(visArea.offsetWidth, visArea.offsetHeight) - 40;
                const canvas = p.createCanvas(canvasSize, canvasSize);
                canvas.id('main-canvas');
                canvas.parent('visualization-area');
                p.noLoop(); // We will manually call redraw
                updateUI();
            };

            p.draw = () => {
                if (isPlaying) {
                    time = (time + 0.5) % maxTime; // Increment time
                    updateUI(false); // Update UI without triggering redraw
                }

                p.background(0, 0, 16);
                p.translate(p.width / 2, p.height / 2);

                // --- Calculations ---
                const earthRotation = (time / EARTH_ROTATION_PERIOD) * p.TWO_PI;
                const moonOrbit = (time / ORBITAL_PERIOD) * p.TWO_PI;
                let moonRotation;
                switch (rotationMode) {
                    case 'half': moonRotation = moonOrbit * 0.5; break;
                    case 'normal': moonRotation = moonOrbit; break;
                    case 'double': moonRotation = moonOrbit * 2.0; break;
                    case 'none': moonRotation = 0; break;
                }

                const moonX = ORBIT_RADIUS * p.cos(moonOrbit - p.PI / 2);
                const moonY = ORBIT_RADIUS * p.sin(moonOrbit - p.PI / 2);

                // --- Drawing Logic ---
                if (!showMoonOnly) {
                    drawEarth();
                    if (showEarthMoonLine) {
                        drawDashedLine(p, 0, 0, moonX, moonY, [10, 5]);
                    }
                }

                drawMoon(moonX, moonY);

                // --- Nested Draw Functions ---

                function drawEarth() {
                    p.push();
                    // Landmasses and Pole (rotate with the Earth)
                    p.push();
                    p.rotate(earthRotation);

                    // Blue planet
                    p.noStroke();
                    p.fill(59, 130, 246); // #3B82F6
                    p.ellipse(0, 0, EARTH_RADIUS * 2);

                    // Green landmasses
                    p.fill(34, 197, 94, 200); // #22C55E
                    p.beginShape();
                    p.vertex(-20, -50); p.vertex(10, -60); p.vertex(30, -40);
                    p.vertex(40, -10); p.vertex(20, 10); p.vertex(0, -5);
                    p.endShape(p.CLOSE);
                    p.rect(40, 20, 30, 20);

                    // North pole
                    p.fill(255);
                    p.ellipse(0, -EARTH_RADIUS * 0.95, 15, 15);

                    // Red stick figure on Earth
                    drawStickFigure(p, 0, -EARTH_RADIUS, 15, '#F87171');
                    p.pop();

                    // Day/Night terminator (does not rotate with Earth)
                    if (showDayNight) {
                        drawTerminator(0, 0, EARTH_RADIUS);
                    }
                    p.pop();
                }

                function drawMoon(mx, my) {
                    const finalMx = showMoonOnly ? 0 : mx;
                    const finalMy = showMoonOnly ? 0 : my;

                    p.push();
                    p.translate(finalMx, finalMy);

                    // Gray moon body
                    p.noStroke();
                    p.fill(128); // #808080
                    p.ellipse(0, 0, MOON_RADIUS * 2);

                    // Near side marker and stick figure
                    if (showNearSide) {
                        p.push();
                        // This rotation makes the marker always face Earth in 'normal' mode
                        // The marker points 'up' (towards -y), and the angle to earth is moonOrbit + PI/2
                        // So we rotate by moonRotation, then rotate back to face the Earth.
                        const markerAngle = moonRotation - moonOrbit;
                        p.rotate(markerAngle);

                        // Green arc for near side
                        p.noFill();
                        p.stroke(34, 197, 94); // #22C55E
                        p.strokeWeight(3);
                        p.arc(0, 0, MOON_RADIUS * 2, MOON_RADIUS * 2, -p.PI, 0);

                        // Magenta stick figure on Moon
                        drawStickFigure(p, 0, -MOON_RADIUS, 10, '#D946EF');
                        p.pop();
                    }

                    // Day/Night terminator
                    if (showDayNight) {
                        drawTerminator(0, 0, MOON_RADIUS);
                    }
                    p.pop();
                }

                function drawTerminator(x, y, radius) {
                    p.fill(0, 128);
                    p.noStroke();
                    p.arc(x, y, radius * 2, radius * 2, p.PI / 2, 3 * p.PI / 2);
                }
            };

            // --- Helper Functions ---
            function drawDashedLine(ctx, x1, y1, x2, y2, dashArray) {
                ctx.push();
                ctx.stroke(255, 100);
                ctx.strokeWeight(1);
                const d = ctx.dist(x1, y1, x2, y2);
                const dashLen = dashArray[0] + dashArray[1];
                const numDashes = Math.floor(d / dashLen);
                const angle = ctx.atan2(y2 - y1, x2 - x1);
                ctx.translate(x1, y1);
                ctx.rotate(angle);
                for (let i = 0; i < numDashes; i++) {
                    ctx.line(i * dashLen, 0, i * dashLen + dashArray[0], 0);
                }
                ctx.pop();
            }

            function drawStickFigure(ctx, x, y, size, color) {
                ctx.push();
                ctx.stroke(color);
                ctx.strokeWeight(2);
                // Head
                ctx.noFill();
                ctx.ellipse(x, y, size * 0.6, size * 0.6);
                // Body
                ctx.line(x, y + size * 0.3, x, y + size * 0.8);
                // Arms
                ctx.line(x - size * 0.4, y + size * 0.5, x + size * 0.4, y + size * 0.5);
                // Legs
                ctx.line(x, y + size * 0.8, x - size * 0.3, y + size * 1.2);
                ctx.line(x, y + size * 0.8, x + size * 0.3, y + size * 1.2);
                ctx.pop();
            }

            // Public method to be called from outside
            p.updateAndRedraw = () => {
                p.redraw();
            };

            p.windowResized = () => {
                const visArea = document.getElementById('visualization-area');
                const canvasSize = Math.min(visArea.offsetWidth, visArea.offsetHeight) - 40;
                p.resizeCanvas(canvasSize, canvasSize);
                p.redraw();
            };
        };

        // Initialize p5 sketch
        let myp5 = new p5(sketch);

        // === UI UPDATE & EVENT LISTENERS ===

        function updateTime(newTime) {
            time = parseFloat(newTime);
            if (time < 0) time = 0;
            if (time > maxTime) time = maxTime;
            updateUI();
        }

        function updateUI(triggerRedraw = true) {
            // Slider
            sliderTime.value = time;

            // Text Display
            const days = Math.floor(time / 24);
            const hours = Math.floor(time % 24);
            displayTime.textContent = `${days} d ${hours} h`;

            // Redraw canvas
            if (triggerRedraw && myp5) {
                myp5.updateAndRedraw();
            }
        }

        function togglePlayPause() {
            isPlaying = !isPlaying;
            if (isPlaying) {
                btnPlayPause.textContent = '❚❚';
                myp5.loop(); // Start p5's internal draw loop
            } else {
                btnPlayPause.textContent = '▶';
                myp5.noLoop(); // Stop p5's internal draw loop
            }
        }

        function updateRotationMode(newMode) {
            rotationMode = newMode;
            Object.values(rotationButtons).forEach(btn => btn.classList.remove('active'));
            rotationButtons[newMode].classList.add('active');
            myp5.updateAndRedraw();
        }

        // --- Attach Event Listeners ---
        window.addEventListener('load', () => {
            // Time controls
            sliderTime.addEventListener('input', (e) => updateTime(e.target.value));
            btnPlayPause.addEventListener('click', togglePlayPause);
            btnStepBack.addEventListener('click', () => updateTime(time - 1));
            btnStepForward.addEventListener('click', () => updateTime(time + 1));
            btnReset.addEventListener('click', () => {
                isPlaying = false;
                btnPlayPause.textContent = '▶';
                myp5.noLoop();
                updateTime(0);
            });

            // Rotation controls
            Object.entries(rotationButtons).forEach(([mode, button]) => {
                button.addEventListener('click', () => updateRotationMode(mode));
            });
            updateRotationMode('normal'); // Set initial active button

            // Checkbox controls
            checkDayNight.addEventListener('change', (e) => {
                showDayNight = e.target.checked;
                myp5.updateAndRedraw();
            });
            checkNearSide.addEventListener('change', (e) => {
                showNearSide = e.target.checked;
                myp5.updateAndRedraw();
            });
            checkEarthMoonLine.addEventListener('change', (e) => {
                showEarthMoonLine = e.target.checked;
                myp5.updateAndRedraw();
            });
            checkMoonOnly.addEventListener('change', (e) => {
                showMoonOnly = e.target.checked;
                myp5.updateAndRedraw();
            });

            // Initialize UI state from defaults
            checkDayNight.checked = showDayNight;
            checkNearSide.checked = showNearSide;
            checkEarthMoonLine.checked = showEarthMoonLine;
            checkMoonOnly.checked = showMoonOnly;
        });

    </script>

</body>
</html>
