<script>
document.addEventListener('DOMContentLoaded', function() {
    // --- Constants ---
    const constants = {
        nu0: 1000,      // cm⁻¹
        B_upper: 0.98,  // cm⁻¹
        A_upper: 1.95,  // cm⁻¹
        B_lower: 1.0,   // cm⁻¹
        A_lower: 2.0,   // cm⁻¹
        zeta: 0.2,
        T: 300,         // K
        k: 0.695,       // cm⁻¹/K
        J_max: 60,
        K_max: 5,
    };

    // --- DOM Element References ---
    const controls = {
        view: document.querySelectorAll('input[name="view-selector"]'),
        k: document.querySelectorAll('input[name="k-selector"]'),
        subbandType: document.querySelectorAll('input[name="subband-type"]'),
        branch: document.querySelectorAll('input[name="branch-selector"]'),

        kGroup: document.getElementById('k-selector-group'),
        subbandTypeGroup: document.getElementById('subband-type-group'),
        branchGroup: document.getElementById('branch-selector-group'),

        radioSubbandNegative: document.getElementById('radio-subband-negative'),
        labelSubbandNegative: document.querySelector('label[for="radio-subband-negative"]'),

        sliderLower: document.getElementById('slider-x-lower'),
        sliderUpper: document.getElementById('slider-x-upper'),
        spanLower: document.getElementById('span-x-lower-value'),
        spanUpper: document.getElementById('span-x-upper-value'),
    };

    // --- Data Caching ---
    let precalculatedData = null;

    // --- State Management ---
    function getControlState() {
        return {
            view: document.querySelector('input[name="view-selector"]:checked').value,
            k: parseInt(document.querySelector('input[name="k-selector"]:checked').value),
            subbandType: document.querySelector('input[name="subband-type"]:checked').value,
            branch: document.querySelector('input[name="branch-selector"]:checked').value,
            xRange: [parseFloat(controls.sliderLower.value), parseFloat(controls.sliderUpper.value)]
        };
    }

    // --- Calculation Functions ---
    function lowerStateEnergy(J, K) {
        const { B_lower, A_lower } = constants;
        return B_lower * J * (J + 1) + (A_lower - B_lower) * K * K;
    }

    function calculateAllData() {
        const data = {};
        const { nu0, B_upper, A_upper, B_lower, A_lower, zeta, T, k, J_max } = constants;

        for (let K_lower = 0; K_lower <= constants.K_max; K_lower++) {
            data[K_lower] = {
                positive: { p: { x: [], y: [] }, q: { x: [], y: [] }, r: { x: [], y: [] } },
                negative: { p: { x: [], y: [] }, q: { x: [], y: [] }, r: { x: [], y: [] } }
            };

            // Population pre-factor for this K
            const gK = (K_lower === 0) ? 1 : 2;

            for (let J_lower = K_lower; J_lower <= J_max; J_lower++) {
                const E_lower = lowerStateEnergy(J_lower, K_lower);
                const population = gK * (2 * J_lower + 1) * Math.exp(-E_lower / (k * T));

                // --- Positive Sub-band (ΔK = +1) ---
                const K_upper_pos = K_lower + 1;
                const E_upper_base_pos = (A_upper - B_upper) * K_upper_pos * K_upper_pos - 2 * A_upper * zeta * K_upper_pos;

                // R branch (ΔJ = +1)
                let J_upper = J_lower + 1;
                if (J_upper >= K_upper_pos) {
                    const E_upper = B_upper * J_upper * (J_upper + 1) + E_upper_base_pos;
                    data[K_lower].positive.r.x.push(nu0 + E_upper - E_lower);
                    data[K_lower].positive.r.y.push(population);
                }
                // Q branch (ΔJ = 0)
                J_upper = J_lower;
                if (J_upper >= K_upper_pos) {
                    const E_upper = B_upper * J_upper * (J_upper + 1) + E_upper_base_pos;
                    data[K_lower].positive.q.x.push(nu0 + E_upper - E_lower);
                    data[K_lower].positive.q.y.push(population);
                }
                // P branch (ΔJ = -1)
                J_upper = J_lower - 1;
                if (J_upper >= K_upper_pos) {
                    const E_upper = B_upper * J_upper * (J_upper + 1) + E_upper_base_pos;
                    data[K_lower].positive.p.x.push(nu0 + E_upper - E_lower);
                    data[K_lower].positive.p.y.push(population);
                }

                // --- Negative Sub-band (ΔK = -1) ---
                if (K_lower > 0) {
                    const K_upper_neg = K_lower - 1;
                    const E_upper_base_neg = (A_upper - B_upper) * K_upper_neg * K_upper_neg + 2 * A_upper * zeta * K_upper_neg;

                    // R branch (ΔJ = +1)
                    J_upper = J_lower + 1;
                    if (J_upper >= K_upper_neg) {
                       const E_upper = B_upper * J_upper * (J_upper + 1) + E_upper_base_neg;
                       data[K_lower].negative.r.x.push(nu0 + E_upper - E_lower);
                       data[K_lower].negative.r.y.push(population);
                    }
                    // Q branch (ΔJ = 0)
                    J_upper = J_lower;
                    if (J_upper >= K_upper_neg) {
                        const E_upper = B_upper * J_upper * (J_upper + 1) + E_upper_base_neg;
                        data[K_lower].negative.q.x.push(nu0 + E_upper - E_lower);
                        data[K_lower].negative.q.y.push(population);
                    }
                    // P branch (ΔJ = -1)
                    J_upper = J_lower - 1;
                    if (J_upper >= K_upper_neg) {
                        const E_upper = B_upper * J_upper * (J_upper + 1) + E_upper_base_neg;
                        data[K_lower].negative.p.x.push(nu0 + E_upper - E_lower);
                        data[K_lower].negative.p.y.push(population);
                    }
                }
            }
        }
        return data;
    }


    // --- Plotting Function ---
    function updatePlot() {
        const state = getControlState();
        const traces = [];
        let plotTitle = "";

        const createTrace = (x, y, color, name) => ({
            x, y, name,
            type: 'bar',
            width: 0.1,
            marker: { color }
        });

        let allYValues = [];

        if (state.view === 'full') {
            plotTitle = 'Rovibronic Spectrum of a Perpendicular Band';
            let allX = [], allY = [];
            for (let k = 0; k <= constants.K_max; k++) {
                Object.values(precalculatedData[k].positive).forEach(branch => {
                    allX.push(...branch.x);
                    allY.push(...branch.y);
                });
                Object.values(precalculatedData[k].negative).forEach(branch => {
                    allX.push(...branch.x);
                    allY.push(...branch.y);
                });
            }
            allYValues.push(...allY);
            traces.push(createTrace(allX, allY, 'black', 'Full Spectrum'));
        } else { // sub-band view
            plotTitle = `K = ${state.k} sub-band`;
            const kData = precalculatedData[state.k];

            const addBranchTrace = (branchData, color, name) => {
                if(branchData.x.length > 0) {
                    traces.push(createTrace(branchData.x, branchData.y, color, name));
                    allYValues.push(...branchData.y);
                }
            };

            const addSubBandTraces = (subBandData, color, typeName) => {
                if (state.branch === 'all' || state.branch === 'P') addBranchTrace(subBandData.p, color, `P (${typeName})`);
                if (state.branch === 'all' || state.branch === 'Q') addBranchTrace(subBandData.q, color, `Q (${typeName})`);
                if (state.branch === 'all' || state.branch === 'R') addBranchTrace(subBandData.r, color, `R (${typeName})`);
                if (state.branch !== 'all') plotTitle += `\n${state.branch} branch`;
            };

            if (state.subbandType === 'full') {
                addBranchTrace({x: [].concat(...Object.values(kData.positive).map(b=>b.x)), y: [].concat(...Object.values(kData.positive).map(b=>b.y))}, 'red', 'Positive');
                addBranchTrace({x: [].concat(...Object.values(kData.negative).map(b=>b.x)), y: [].concat(...Object.values(kData.negative).map(b=>b.y))}, 'blue', 'Negative');
                allYValues.push(...traces.flatMap(t => t.y));
            } else if (state.subbandType === 'positive') {
                plotTitle += `\n"positive" sub-band (ΔK=+1)`;
                addSubBandTraces(kData.positive, 'red', '+');
            } else if (state.subbandType === 'negative') {
                plotTitle += `\n"negative" sub-band (ΔK=-1)`;
                addSubBandTraces(kData.negative, 'blue', '-');
            }
        }

        // Normalize intensity
        const maxY = Math.max(...allYValues, 0);
        if (maxY > 0) {
            traces.forEach(trace => {
                trace.y = trace.y.map(val => val / maxY);
            });
        }

        const layout = {
            title: plotTitle,
            xaxis: {
                title: 'wavenumber (cm⁻¹)',
                range: state.xRange,
            },
            yaxis: {
                title: 'absorbance',
                range: [0, 1.2],
                fixedrange: true
            },
            showlegend: false,
            bargap: 0,
            barmode: 'stack', // Stack bars at same x to avoid overlap issues
        };

        Plotly.react('plot-div', traces, layout);
    }

    // --- UI Control Logic ---
    function updateControlStates() {
        const state = getControlState();

        if (state.view === 'full') {
            controls.kGroup.classList.add('disabled');
            controls.subbandTypeGroup.classList.add('disabled');
            controls.branchGroup.classList.add('hidden');
        } else { // sub-band view
            controls.kGroup.classList.remove('disabled');
            controls.subbandTypeGroup.classList.remove('disabled');

            // Handle K=0 case (no negative sub-band)
            if (state.k === 0) {
                controls.radioSubbandNegative.disabled = true;
                controls.labelSubbandNegative.classList.add('disabled');
                // If negative was selected, switch to full
                if(state.subbandType === 'negative') {
                    document.getElementById('radio-subband-full').checked = true;
                }
            } else {
                controls.radioSubbandNegative.disabled = false;
                controls.labelSubbandNegative.classList.remove('disabled');
            }

            // Handle branch selector visibility
            const subbandTypeNow = getControlState().subbandType; // Re-check in case K=0 changed it
            if (subbandTypeNow === 'full') {
                controls.branchGroup.classList.add('hidden');
            } else {
                controls.branchGroup.classList.remove('hidden');
            }
        }
    }

    // --- Event Handlers ---
    function handleControlsChange() {
        updateControlStates();
        updatePlot();
    }

    function handleZoomChange() {
        let lower = parseFloat(controls.sliderLower.value);
        let upper = parseFloat(controls.sliderUpper.value);

        // Prevent sliders from crossing
        if (this.id === 'slider-x-lower' && lower >= upper) {
            upper = lower + 1;
            controls.sliderUpper.value = upper;
        }
        if (this.id === 'slider-x-upper' && upper <= lower) {
            lower = upper - 1;
            controls.sliderLower.value = lower;
        }

        controls.spanLower.textContent = lower;
        controls.spanUpper.textContent = upper;

        Plotly.relayout('plot-div', { 'xaxis.range': [lower, upper] });
    }

    // --- Initialization ---
    function init() {
        // Pre-calculate all spectral data
        precalculatedData = calculateAllData();

        // Add event listeners
        controls.view.forEach(el => el.addEventListener('change', handleControlsChange));
        controls.k.forEach(el => el.addEventListener('change', handleControlsChange));
        controls.subbandType.forEach(el => el.addEventListener('change', handleControlsChange));
        controls.branch.forEach(el => el.addEventListener('change', handleControlsChange));

        controls.sliderLower.addEventListener('input', handleZoomChange);
        controls.sliderUpper.addEventListener('input', handleZoomChange);

        // Initial setup
        updateControlStates();
        updatePlot();
    }

    init();
});
</script>
</body>
