<script type="module">
    import * as THREE from 'three';
    import { OrbitControls } from 'three/addons/controls/OrbitControls.js';

    // --- STATE ---
    let currentWaveType = 'P';
    let radiationPatternGroup, radiationMesh, boxHelper;
    let scene, camera, renderer, controls;

    // --- DOM ELEMENTS ---
    const canvasContainer = document.getElementById('canvas-container');

    const strikeSlider = document.getElementById('slider-strike');
    const dipSlider = document.getElementById('slider-dip');
    const rakeSlider = document.getElementById('slider-rake');

    const strikeValueSpan = document.getElementById('strike-value');
    const dipValueSpan = document.getElementById('dip-value');
    const rakeValueSpan = document.getElementById('rake-value');

    const pButton = document.getElementById('btn-p');
    const svButton = document.getElementById('btn-sv');
    const shButton = document.getElementById('btn-sh');
    const waveButtons = [pButton, svButton, shButton];

    const boxCheckbox = document.getElementById('checkbox-box');

    // --- INITIALIZATION ---
    function init() {
        // Scene setup
        scene = new THREE.Scene();
        scene.background = new THREE.Color(0xf0f0f0);

        // Camera setup - Z is UP
        camera = new THREE.PerspectiveCamera(50, canvasContainer.clientWidth / canvasContainer.clientHeight, 0.1, 100);
        camera.position.set(3, 3, 3);
        camera.up.set(0, 0, 1); // Set Z as the up direction
        camera.lookAt(0, 0, 0);

        // Renderer setup
        renderer = new THREE.WebGLRenderer({ antialias: true });
        renderer.setSize(canvasContainer.clientWidth, canvasContainer.clientHeight);
        renderer.setPixelRatio(window.devicePixelRatio);
        canvasContainer.appendChild(renderer.domElement);

        // Lighting
        const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
        scene.add(ambientLight);
        const dirLight1 = new THREE.DirectionalLight(0xffffff, 1.5);
        dirLight1.position.set(5, 5, 5);
        scene.add(dirLight1);
        const dirLight2 = new THREE.DirectionalLight(0xffffff, 1.0);
        dirLight2.position.set(-5, -5, 2);
        scene.add(dirLight2);

        // Controls
        controls = new OrbitControls(camera, renderer.domElement);
        controls.target.set(0, 0, 0);
        controls.enableDamping = true;
        controls.dampingFactor = 0.05;

        // Parent group for fault-plane rotations
        radiationPatternGroup = new THREE.Object3D();
        scene.add(radiationPatternGroup);

        // Coordinate System Helpers
        createCompass();
        createUpArrow();

        // Initial radiation pattern
        updateWaveform();

        // Bounding box helper
        boxHelper = new THREE.BoxHelper(radiationPatternGroup, 0x000000);
        boxHelper.visible = false;
        scene.add(boxHelper);

        // Event Listeners
        setupEventListeners();

        // Initial render
        updateOrientation();
        animate();

        // Render LaTeX
        if (window.MathJax) {
            window.MathJax.typeset();
        }
    }

    // --- EVENT LISTENERS ---
    function setupEventListeners() {
        [strikeSlider, dipSlider, rakeSlider].forEach(slider => {
            slider.addEventListener('input', () => {
                strikeValueSpan.textContent = strikeSlider.value;
                dipValueSpan.textContent = dipSlider.value;
                rakeValueSpan.textContent = rakeSlider.value;
                updateOrientation();
            });
        });

        waveButtons.forEach(button => {
            button.addEventListener('click', (e) => {
                const newType = e.target.id.split('-')[1].toUpperCase();
                if (newType !== currentWaveType) {
                    currentWaveType = newType;
                    waveButtons.forEach(btn => btn.classList.remove('active'));
                    e.target.classList.add('active');
                    updateWaveform();
                }
            });
        });

        boxCheckbox.addEventListener('change', () => {
            boxHelper.visible = boxCheckbox.checked;
        });

        window.addEventListener('resize', onWindowResize);
    }

    // --- CORE LOGIC ---

    function updateWaveform() {
        if (radiationMesh) {
            radiationPatternGroup.remove(radiationMesh);
            radiationMesh.geometry.dispose();
            radiationMesh.material.dispose();
        }
        radiationMesh = createRadiationMesh(currentWaveType);
        radiationPatternGroup.add(radiationMesh);
        updateOrientation();
    }

    function updateOrientation() {
        const strike = THREE.MathUtils.degToRad(parseFloat(strikeSlider.value));
        const dip = THREE.MathUtils.degToRad(parseFloat(dipSlider.value));
        const rake = THREE.MathUtils.degToRad(parseFloat(rakeSlider.value));

        // Rotations to convert from geographic coordinates to fault-plane coordinates
        // 1. Strike: Clockwise rotation around Z (Up) axis.
        const qStrike = new THREE.Quaternion().setFromAxisAngle(new THREE.Vector3(0, 0, 1), -strike);
        // 2. Dip: Rotation around the new local X-axis (the strike line).
        const qDip = new THREE.Quaternion().setFromAxisAngle(new THREE.Vector3(1, 0, 0), dip);
        // 3. Rake: Rotation around the new local Y-axis.
        const qRake = new THREE.Quaternion().setFromAxisAngle(new THREE.Vector3(0, 1, 0), rake);

        // Combine rotations: strike, then dip, then rake.
        // three.js quaternion multiplication is right-to-left in effect.
        // C = A * B applies B then A. So we want C = strike * dip * rake.
        radiationPatternGroup.quaternion.copy(qStrike).multiply(qDip).multiply(qRake);

        boxHelper.update();
    }

    function createRadiationMesh(type) {
        const parametricFn = (u, v, target) => {
            const theta = u * Math.PI; // Polar angle (0 to PI)
            const phi = v * 2 * Math.PI; // Azimuthal angle (0 to 2*PI)
            let r = 0;

            // These are simplified far-field amplitude functions
            switch(type) {
                case 'P':
                    r = Math.abs(Math.sin(2 * theta) * Math.sin(phi));
                    break;
                case 'SV':
                    r = Math.abs(Math.cos(2 * theta) * Math.sin(phi));
                    break;
                case 'SH':
                    r = Math.abs(Math.cos(theta) * Math.cos(phi));
                    break;
            }

            // Spherical to Cartesian with Z-up
            const x = r * Math.sin(theta) * Math.cos(phi);
            const y = r * Math.sin(theta) * Math.sin(phi);
            const z = r * Math.cos(theta);

            target.set(x, y, z);
        };

        const geometry = new THREE.ParametricGeometry(parametricFn, 64, 64);
        const material = new THREE.MeshStandardMaterial({
            color: 0xD2B48C,
            side: THREE.DoubleSide,
            metalness: 0.3,
            roughness: 0.6,
        });

        return new THREE.Mesh(geometry, material);
    }

    // --- HELPERS AND SCENE ELEMENTS ---

    function createCompass() {
        const compassGroup = new THREE.Object3D();
        const radius = 2.5;

        // Dashed circle
        const circleGeom = new THREE.CircleGeometry(radius, 64);
        const edges = new THREE.EdgesGeometry(circleGeom);
        const line = new THREE.LineSegments(edges, new THREE.LineDashedMaterial({
            color: 0x888888,
            dashSize: 0.1,
            gapSize: 0.05
        }));
        line.computeLineDistances();
        compassGroup.add(line);

        // Cardinal directions
        const directions = { 'N': [0, radius, 0], 'E': [radius, 0, 0], 'S': [0, -radius, 0], 'W': [-radius, 0, 0] };
        for (const [label, pos] of Object.entries(directions)) {
            const sprite = makeTextSprite(label, { fontsize: 32, fontface: "Arial", textColor: { r: 50, g: 50, b: 50, a: 1.0 } });
            sprite.position.set(pos[0], pos[1], pos[2]);
            compassGroup.add(sprite);
        }

        // Degree markings
        for (let i = 0; i < 360; i += 30) {
            if (i % 90 !== 0) {
                const angle = THREE.MathUtils.degToRad(i);
                const label = `${i}°`;
                const sprite = makeTextSprite(label, { fontsize: 24, textColor: { r: 128, g: 128, b: 128, a: 1.0 } });
                const r_inner = radius * 1.15;
                sprite.position.set(r_inner * Math.sin(angle), r_inner * Math.cos(angle), 0);
                compassGroup.add(sprite);
            }
        }

        scene.add(compassGroup);
    }

    function createUpArrow() {
        const dir = new THREE.Vector3(0, 0, 1);
        const origin = new THREE.Vector3(0, 0, 0);
        const length = 2;
        const arrowHelper = new THREE.ArrowHelper(dir, origin, length, 0x000000, 0.25, 0.1);
        scene.add(arrowHelper);

        const sprite = makeTextSprite("U", { fontsize: 32, fontface: "Arial", textColor: { r: 0, g: 0, b: 0, a: 1.0 } });
        sprite.position.set(0, 0, length + 0.3);
        scene.add(sprite);
    }

    function makeTextSprite(message, parameters) {
        const fontface = parameters.hasOwnProperty("fontface") ? parameters.fontface : "Arial";
        const fontsize = parameters.hasOwnProperty("fontsize") ? parameters.fontsize : 18;
        const textColor = parameters.hasOwnProperty("textColor") ? parameters.textColor : { r: 0, g: 0, b: 0, a: 1.0 };

        const canvas = document.createElement('canvas');
        const context = canvas.getContext('2d');
        context.font = `Bold ${fontsize}px ${fontface}`;

        const metrics = context.measureText(message);
        const textWidth = metrics.width;

        canvas.width = textWidth;
        canvas.height = fontsize;
        context.font = `Bold ${fontsize}px ${fontface}`; // font is reset on canvas resize
        context.fillStyle = `rgba(${textColor.r}, ${textColor.g}, ${textColor.b}, ${textColor.a})`;
        context.fillText(message, 0, fontsize);

        const texture = new THREE.CanvasTexture(canvas);
        texture.minFilter = THREE.LinearFilter;

        const spriteMaterial = new THREE.SpriteMaterial({ map: texture });
        const sprite = new THREE.Sprite(spriteMaterial);
        sprite.scale.set(0.01 * textWidth, 0.01 * fontsize, 1.0);
        return sprite;
    }

    function onWindowResize() {
        const width = canvasContainer.clientWidth;
        const height = canvasContainer.clientHeight;

        camera.aspect = width / height;
        camera.updateProjectionMatrix();
        renderer.setSize(width, height);
    }

    // --- ANIMATION LOOP ---
    function animate() {
        requestAnimationFrame(animate);
        controls.update();
        renderer.render(scene, camera);
    }

    // --- RUN ---
    init();

</script>
</body>
</html>
