<body>

    <div id="main-container">
        <h1>Geometric Inversion</h1>
        <div id="control-panel">
            <div id="rings-control-group" class="control-group">
                <label for="rings-radios">rings</label>
                <div id="rings-radios">
                    <input type="radio" name="rings" value="1" id="rings-1" checked>
                    <label for="rings-1">1</label>
                    <input type="radio" name="rings" value="2" id="rings-2">
                    <label for="rings-2">2</label>
                    <input type="radio" name="rings" value="3" id="rings-3">
                    <label for="rings-3">3</label>
                    <input type="radio" name="rings" value="4" id="rings-4">
                    <label for="rings-4">4</label>
                    <input type="radio" name="rings" value="5" id="rings-5">
                    <label for="rings-5">5</label>
                    <input type="radio" name="rings" value="6" id="rings-6">
                    <label for="rings-6">6</label>
                    <input type="radio" name="rings" value="7" id="rings-7">
                    <label for="rings-7">7</label>
                </div>
            </div>

            <div id="circles-per-ring-control-group" class="control-group">
                <label for="cpr-radios">circles per ring</label>
                <div id="cpr-radios">
                    <input type="radio" name="cpr" value="4" id="cpr-4">
                    <label for="cpr-4">4</label>
                    <input type="radio" name="cpr" value="6" id="cpr-6">
                    <label for="cpr-6">6</label>
                    <input type="radio" name="cpr" value="8" id="cpr-8" checked>
                    <label for="cpr-8">8</label>
                    <input type="radio" name="cpr" value="10" id="cpr-10">
                    <label for="cpr-10">10</label>
                    <input type="radio" name="cpr" value="12" id="cpr-12">
                    <label for="cpr-12">12</label>
                    <input type="radio" name="cpr" value="14" id="cpr-14">
                    <label for="cpr-14">14</label>
                </div>
            </div>

            <div id="inverse-control-group" class="control-group">
                <label for="checkbox-inverse">inverse</label>
                <input type="checkbox" id="checkbox-inverse">
            </div>
        </div>

        <div id="canvas-container">
            <!-- p5.js canvas will be inserted here -->
        </div>
    </div>

    <script>
        // --- p5.js Sketch ---

        let locators;
        let draggedLocator = -1;
        const locatorSize = 8;

        let inverseCheckbox;
        let ringsRadios;
        let cprRadios;

        function setup() {
            const canvasSize = 550;
            const canvas = createCanvas(canvasSize, canvasSize);
            canvas.parent('canvas-container');
            canvas.id('p5-canvas');
            angleMode(RADIANS);

            // Initialize state variables
            locators = [createVector(-50, 0), createVector(50, 0)];

            // Get references to HTML controls
            inverseCheckbox = select('#checkbox-inverse');
            ringsRadios = selectAll('input[name="rings"]');
            cprRadios = selectAll('input[name="cpr"]');

            // No need for explicit event listeners as the draw loop will read values on each frame.
        }

        function draw() {
            // Read current values from controls
            const numRings = parseInt(getRadioValue('rings'));
            const circlesPerRing = parseInt(getRadioValue('cpr'));
            const isInverseMode = inverseCheckbox.checked();

            // Set background
            background('#C1D4E6');

            // Translate to center for easier calculations
            translate(width / 2, height / 2);

            // Calculate the geometry of the original circles
            const originalCircles = calculateOriginalCircles(numRings, circlesPerRing);

            if (isInverseMode) {
                drawInverseMode(originalCircles);
            } else {
                drawNormalMode(originalCircles, circlesPerRing);
            }
        }

        function getRadioValue(name) {
            const radios = document.querySelectorAll(`input[name="${name}"]`);
            for (const radio of radios) {
                if (radio.checked) {
                    return radio.value;
                }
            }
        }

        function calculateOriginalCircles(numRings, N) {
            const circles = [];
            const R_max = width / 2 * 0.9;

            if (N < 2) return [];

            const sin_pi_n = sin(PI / N);
            const k = (1 - sin_pi_n) / (1 + sin_pi_n); // Scaling factor
            const R_c = R_max / (1 + sin_pi_n); // Center radius of outermost ring
            const r = R_c * sin_pi_n; // Circle radius of outermost ring

            for (let i = 0; i < numRings; i++) {
                const k_i = pow(k, i);
                const current_R_c = R_c * k_i;
                const current_r = r * k_i;

                for (let j = 0; j < N; j++) {
                    const angle = j * TWO_PI / N;
                    const cx = current_R_c * cos(angle);
                    const cy = current_R_c * sin(angle);
                    circles.push({
                        center: createVector(cx, cy),
                        radius: current_r
                    });
                }
            }
            return circles;
        }

        function drawNormalMode(circles, circlesPerRing) {
            // Draw guide lines for the outermost ring
            stroke('#A0A0A0');
            strokeWeight(1);
            const outermostCircles = circles.slice(0, circlesPerRing);
            outermostCircles.forEach(c => {
                line(0, 0, c.center.x, c.center.y);
            });

            // Draw all circles
            noStroke();
            fill('#8C929D');
            stroke(0);
            strokeWeight(1);
            circles.forEach(c => {
                circle(c.center.x, c.center.y, c.radius * 2);
            });
        }

        function drawInverseMode(originalCircles) {
            // Define inversion circle from locators
            const C_inv = p5.Vector.add(locators[0], locators[1]).div(2);
            const R_inv_sq = pow(locators[0].dist(locators[1]) / 2, 2);

            // Invert and draw each original circle
            fill(220, 100, 220, 128); // rgba(220, 100, 220, 0.5)
            stroke(0);
            strokeWeight(1);

            originalCircles.forEach(c_orig => {
                const d_sq = C_inv.distSq(c_orig.center);
                const r_orig_sq = pow(c_orig.radius, 2);

                // If circle passes (almost) through inversion center, its inverse is a line
                if (abs(d_sq - r_orig_sq) < 1e-4) { 
                    // Vector from inversion center to original circle center
                    const V = p5.Vector.sub(c_orig.center, C_inv);
                    const d = sqrt(d_sq);

                    // The line is perpendicular to V.
                    // A point on the line is P = C_inv + V_norm * (R_inv^2 / (2*d))
                    const pointOnLine = p5.Vector.add(C_inv, V.copy().normalize().mult(R_inv_sq / (2 * d)));

                    // The direction of the line is perpendicular to V
                    const lineDir = createVector(-V.y, V.x);

                    // Draw a very long line that will be clipped by the canvas
                    const p1 = p5.Vector.add(pointOnLine, lineDir.copy().mult(width * 2));
                    const p2 = p5.Vector.sub(pointOnLine, lineDir.copy().mult(width * 2));
                    line(p1.x, p1.y, p2.x, p2.y);

                } else { // Otherwise, the inverse is a circle
                    const scalar = R_inv_sq / (d_sq - r_orig_sq);
                    const C_new = p5.Vector.add(C_inv, p5.Vector.sub(c_orig.center, C_inv).mult(scalar));
                    const r_new = abs(scalar) * c_orig.radius;
                    circle(C_new.x, C_new.y, r_new * 2);
                }
            });

            // Draw the inversion circle itself
            noFill();
            stroke(0);
            strokeWeight(1);
            fill(255, 0, 0, 77); // rgba(255, 0, 0, 0.3)
            circle(C_inv.x, C_inv.y, sqrt(R_inv_sq) * 2);

            // Draw locators and connecting line
            stroke('#FF0000');
            strokeWeight(1);
            line(locators[0].x, locators[0].y, locators[1].x, locators[1].y);
            noStroke();
            fill('#FF0000');
            locators.forEach(loc => {
                circle(loc.x, loc.y, locatorSize);
            });
        }

        // --- Mouse Interaction ---

        function mousePressed() {
            if (!inverseCheckbox.checked()) {
                draggedLocator = -1;
                return;
            }

            // Mouse coordinates are relative to canvas top-left.
            // We need them relative to the translated center.
            const mouseVec = createVector(mouseX - width / 2, mouseY - height / 2);

            for (let i = 0; i < locators.length; i++) {
                if (mouseVec.dist(locators[i]) < locatorSize) {
                    draggedLocator = i;
                    // Change cursor to indicate dragging
                    cursor('grabbing');
                    return; // Stop after finding one
                }
            }
        }

        function mouseDragged() {
            if (draggedLocator !== -1) {
                const mouseVec = createVector(mouseX - width / 2, mouseY - height / 2);
                locators[draggedLocator] = mouseVec;
            }
        }

        function mouseReleased() {
            draggedLocator = -1;
            cursor(ARROW);
        }

        // Change cursor on hover over locators
        function mouseMoved() {
            if (!inverseCheckbox.checked()) {
                cursor(ARROW);
                return;
            }
            const mouseVec = createVector(mouseX - width / 2, mouseY - height / 2);
            let onLocator = false;
            for (let i = 0; i < locators.length; i++) {
                if (mouseVec.dist(locators[i]) < locatorSize) {
                    onLocator = true;
                    break;
                }
            }
            if(onLocator) {
                cursor('grab');
            } else {
                cursor(ARROW);
            }
        }

    </script>
</body>
</html>
