<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="slider-group">
                <label for="slider-logCa">\( \log C_a \): <span id="logCa-value-display"></span> (\(C_a = \) <span id="Ca-value-display"></span> M)</label>
                <input type="range" id="slider-logCa" min="-4" max="0" value="-1" step="0.01">
            </div>
            <div class="slider-group">
                <label for="slider-pka">\(pK_a\): <span id="pKa-value-display"></span></label>
                <input type="range" id="slider-pka" min="-4" max="7" value="1.2" step="0.1">
            </div>
        </div>

        <div id="visualization-container">
            <!-- p5.js canvas is inserted here -->
            <div id="formula-strong">
                \( \color{maroon}{pH = - \log C_a} \)
            </div>
            <div id="formula-weak">
                \( \color{green}{pH = \frac{1}{2} (pK_a - \log C_a)} \)
            </div>
            <div id="label-ph-axis">\(pH\)</div>
            <div id="label-pka-axis">\(pK_a\)</div>
        </div>
    </div>

    <script>
        let logCaSlider, pkaSlider;
        let logCaValueSpan, caValueSpan, pKaValueSpan;

        // Plot dimensions and data ranges
        const canvasSize = 600;
        const margin = 60;
        const pKaMin = -4, pKaMax = 7;
        const pHMin = 0, pHMax = 7;

        function setup() {
            let canvas = createCanvas(canvasSize, canvasSize);
            canvas.parent('visualization-container');

            // Select HTML elements
            logCaSlider = select('#slider-logCa');
            pkaSlider = select('#slider-pka');
            logCaValueSpan = select('#logCa-value-display');
            caValueSpan = select('#Ca-value-display');
            pKaValueSpan = select('#pKa-value-display');

            // Attach input event listeners to sliders
            logCaSlider.input(updateAndRedraw);
            pkaSlider.input(updateAndRedraw);

            noLoop(); // Use redraw() on demand
            updateAndRedraw(); // Draw the initial state
        }

        function updateAndRedraw() {
            updateSliderDisplays();
            redraw();
        }

        function updateSliderDisplays() {
            const logCa = logCaSlider.value();
            const pKa = pkaSlider.value();
            logCaValueSpan.html(Number(logCa).toFixed(2));
            caValueSpan.html(pow(10, logCa).toExponential(2));
            pKaValueSpan.html(Number(pKa).toFixed(1));
        }

        // --- Coordinate Mapping ---
        function mapX(pKa) {
            return map(pKa, pKaMin, pKaMax, margin, width - margin);
        }

        function mapY(pH) {
            // Invert Y-axis for screen coordinates
            return map(pH, pHMin, pHMax, height - margin, margin);
        }

        // --- pH Calculation ---
        function calculateExactPH(pKa, logCa) {
            const Ca = pow(10, logCa);
            const Ka = pow(10, -pKa);
            // Solve the quadratic equation [H+]^2 + Ka[H+] - Ka*Ca = 0 for [H+]
            const h_plus = (-Ka + sqrt(Ka * Ka + 4 * Ka * Ca)) / 2;
            // pH = -log10([H+])
            // p5.js log() is natural log, so we convert using log(x)/log(10)
            return -log(h_plus) / log(10);
        }

        function draw() {
            background(255);
            const currentLogCa = logCaSlider.value();
            const currentPka = pkaSlider.value();

            // --- Draw Axes and Gridlines ---
            // Y-axis ticks and gridlines
            for (let pH = pHMin; pH <= pHMax; pH++) {
                let y = mapY(pH);
                stroke(220);
                strokeWeight(1);
                line(margin, y, width - margin, y);
                noStroke();
                fill(80);
                textAlign(RIGHT, CENTER);
                textSize(12);
                text(pH.toFixed(0), margin - 10, y);
            }

            // X-axis ticks and gridlines
            for (let pKa = pKaMin; pKa <= pKaMax; pKa++) {
                let x = mapX(pKa);
                stroke(220);
                strokeWeight(1);
                line(x, margin, x, height - margin);
                noStroke();
                fill(80);
                textAlign(CENTER, TOP);
                textSize(12);
                text(pKa.toFixed(0), x, height - margin + 10);
            }

            // Main axes lines (drawn on top of grid)
            stroke(50);
            strokeWeight(1.5);
            line(margin, margin, margin, height - margin); // Y-axis
            line(margin, height - margin, width - margin, height - margin); // X-axis

            // --- Draw pH Curves ---

            // 1. Strong Acid Approximation (Red/Maroon Line)
            const strongAcidPH = -currentLogCa;
            strokeWeight(2);
            stroke('maroon');
            let yStrong = mapY(strongAcidPH);
            // Ensure the line is drawn only within the plot bounds
            if (yStrong >= margin && yStrong <= height - margin) {
                line(margin, yStrong, width - margin, yStrong);
            }

            // 2. Weak Acid Approximation (Green Line)
            stroke('green');
            strokeWeight(2);
            const weak_ph_start = 0.5 * (pKaMin - currentLogCa);
            const weak_ph_end = 0.5 * (pKaMax - currentLogCa);
            line(mapX(pKaMin), mapY(weak_ph_start), mapX(pKaMax), mapY(weak_ph_end));

            // 3. Exact pH Curve (Blue Curve)
            stroke('blue');
            strokeWeight(3.5);
            noFill();
            beginShape();
            const steps = (width - 2 * margin); // One vertex per pixel for a smooth curve
            for (let i = 0; i <= steps; i++) {
                let pka_i = map(i, 0, steps, pKaMin, pKaMax);
                const pH = calculateExactPH(pka_i, currentLogCa);
                vertex(mapX(pka_i), mapY(pH));
            }
            endShape();

            // 4. Interactive Indicator Point (Orange)
            const indicatorPH = calculateExactPH(currentPka, currentLogCa);
            const indicatorX = mapX(currentPka);
            const indicatorY = mapY(indicatorPH);

            strokeWeight(2);
            stroke('#333');
            fill('orange');
            circle(indicatorX, indicatorY, 14);
        }
    </script>
</body>
</html>
