                    <div id="handle-start" class="joystick-handle"></div>
                </div>
            </div>

            <!-- End Point Control -->
            <div class="control-group">
                <label>end point</label>
                <div id="joystick-end" class="joystick-area">
                    <div id="handle-end" class="joystick-handle"></div>
                </div>
            </div>

            <!-- Obstacles Dropdown -->
            <div class="control-group">
                <label for="select-obstacles">obstacles</label>
                <select id="select-obstacles">
                    <option value="1-square">1 square</option>
                    <option value="2-squares">2 squares</option>
                    <option value="l-shaped-wall">L-shaped wall</option>
                    <option value="random-maze">Random Maze</option>
                    <option value="none">None</option>
                </select>
            </div>
        </div>

        <!-- Right column: Visualization Canvas -->
        <div id="canvas-container">
            <!-- p5.js canvas will be inserted here by the script -->
        </div>
    </div>

<script>
// --- p5.js Sketch ---
const sketch = (p) => {
    // --- State & Config ---
    const GRID_SIZE = 50;
    let canvasSize;
    let cellSize;

    let grid = [];
    let history = [];

    // Control elements
    let radioDijkstra, radioAStar, sliderStep, labelStep, selectObstacles;
    let joystickStart, handleStart, joystickEnd, handleEnd;

    // State variables
    let startCoords = { x: 0.15, y: 0.85 };
    let endCoords = { x: 0.85, y: 0.15 };
    let currentAlgorithm = 'dijkstra';
    let currentObstacles = '1-square';
    let currentStep = 1;

    // --- Node and Priority Queue for Algorithms ---
    class Node {
        constructor(i, j) {
            this.i = i;
            this.j = j;
            this.isObstacle = false;
            this.neighbors = [];
            this.previous = null;
            // Pathfinding costs
            this.g = Infinity; // cost from start
            this.h = 0;        // heuristic cost to end
            this.f = Infinity; // g + h
        }

        addNeighbors(grid) {
            this.neighbors = [];
            const i = this.i;
            const j = this.j;

            const directions = [
                [0, 1], [1, 0], [0, -1], [-1, 0], // Cardinal
                [-1, -1], [1, -1], [-1, 1], [1, 1]  // Diagonal
            ];

            for (const [di, dj] of directions) {
                const ni = i + di;
                const nj = j + dj;
                if (ni >= 0 && ni < GRID_SIZE && nj >= 0 && nj < GRID_SIZE) {
                    this.neighbors.push(grid[ni][nj]);
                }
            }
        }
    }

    // Simple Priority Queue using an array and sort
    class PriorityQueue {
        constructor() {
            this.elements = [];
        }
        enqueue(element, priority) {
            this.elements.push({ element, priority });
            this.elements.sort((a, b) => a.priority - b.priority);
        }
        dequeue() {
            return this.elements.shift().element;
        }
        isEmpty() {
            return this.elements.length === 0;
        }
    }

    // --- Setup ---
    p.setup = () => {
        const container = document.getElementById('canvas-container');
        const size = Math.min(container.clientWidth - 40, container.clientHeight - 40, 600);
        canvasSize = size;
        cellSize = canvasSize / GRID_SIZE;

        let canvas = p.createCanvas(canvasSize, canvasSize);
        canvas.parent('canvas-container');
        p.noLoop(); // We will manually redraw when needed

        // Get DOM elements
        radioDijkstra = document.getElementById('radio-dijkstra');
        radioAStar = document.getElementById('radio-a-star');
        sliderStep = document.getElementById('slider-step');
        labelStep = document.getElementById('label-step');
        selectObstacles = document.getElementById('select-obstacles');

        joystickStart = document.getElementById('joystick-start');
        handleStart = document.getElementById('handle-start');
        joystickEnd = document.getElementById('joystick-end');
        handleEnd = document.getElementById('handle-end');

        // Setup Event Listeners
        radioDijkstra.addEventListener('change', () => { currentAlgorithm = 'dijkstra'; runSimulation(); });
        radioAStar.addEventListener('change', () => { currentAlgorithm = 'a-star'; runSimulation(); });
        selectObstacles.addEventListener('change', (e) => { currentObstacles = e.target.value; runSimulation(); });
        sliderStep.addEventListener('input', updateVisualization);

        setupJoystick(joystickStart, handleStart, startCoords);
        setupJoystick(joystickEnd, handleEnd, endCoords);

        // Initial Run
        setJoystickPosition(handleStart, startCoords);
        setJoystickPosition(handleEnd, endCoords);
        runSimulation();
    };

    // --- Drawing ---
    p.draw = () => {
        p.background(255);
        if (history.length === 0) return;

        const frame = history[currentStep - 1];

        // Draw grid states
        p.stroke(230);
        p.strokeWeight(1);
        for (let i = 0; i < GRID_SIZE; i++) {
            for (let j = 0; j < GRID_SIZE; j++) {
                p.fill(255); // Default empty
                if (frame.closedSet.has(`${i},${j}`)) {
                    p.fill(p.color('#0000CD')); // Dark Blue (visited)
                } else if (frame.openSet.has(`${i},${j}`)) {
                    p.fill(p.color('#BDE0FE')); // Light Blue (frontier)
                }

                if (grid[i][j].isObstacle) {
                    p.fill(0); // Black (obstacle)
                }
                p.rect(i * cellSize, j * cellSize, cellSize, cellSize);
            }
        }

        // Draw Start and End nodes
        const startNode = getNodeFromCoords(startCoords);
        const endNode = getNodeFromCoords(endCoords);
        p.fill(p.color('#34A853')); // Green
        p.rect(startNode.i * cellSize, startNode.j * cellSize, cellSize, cellSize);
        p.rect(endNode.i * cellSize, endNode.j * cellSize, cellSize, cellSize);

        // Draw final path if on the last step
        if (currentStep === history.length && frame.path) {
            p.noFill();
            p.stroke(p.color('#FF0000')); // Red
            p.strokeWeight(cellSize / 4);
            p.beginShape();
            for (const node of frame.path) {
                p.vertex(node.i * cellSize + cellSize / 2, node.j * cellSize + cellSize / 2);
            }
            p.endShape();
        }
    };

    // --- Interaction Logic ---
    function runSimulation() {
        // 1. Initialize grid and nodes
        grid = Array(GRID_SIZE).fill(null).map(() => Array(GRID_SIZE).fill(null));
        for (let i = 0; i < GRID_SIZE; i++) {
            for (let j = 0; j < GRID_SIZE; j++) {
                grid[i][j] = new Node(i, j);
            }
        }

        // 2. Place obstacles
        setObstacles();

        // 3. Add neighbors
        for (let i = 0; i < GRID_SIZE; i++) {
            for (let j = 0; j < GRID_SIZE; j++) {
                grid[i][j].addNeighbors(grid);
            }
        }

        // 4. Define start and end
        const startNode = getNodeFromCoords(startCoords);
        const endNode = getNodeFromCoords(endCoords);
        // Ensure start/end are not obstacles
        if (startNode) startNode.isObstacle = false;
        if (endNode) endNode.isObstacle = false;

        if (!startNode || !endNode) return;

        // 5. Run algorithm
        history = [];
        if (currentAlgorithm === 'dijkstra') {
            dijkstra(startNode, endNode);
        } else {
            aStar(startNode, endNode);
        }

        // 6. Update slider
        sliderStep.max = history.length > 0 ? history.length : 1;
        sliderStep.value = 1;

        // 7. Trigger redraw
        updateVisualization();
    }

    function updateVisualization() {
        currentStep = parseInt(sliderStep.value);
        labelStep.textContent = `+ ${currentStep}`;
        p.redraw();
    }

    // --- Pathfinding Algorithms ---
    function heuristic(a, b) {
        // Euclidean distance
        return p.dist(a.i, a.j, b.i, b.j);
    }

    function reconstructPath(currentNode) {
        const path = [];
        let temp = currentNode;
        while(temp) {
            path.push(temp);
            temp = temp.previous;
        }
        return path.reverse();
    }

    function aStar(start, end) {
        const openSet = new PriorityQueue();
        const closedSet = new Set();
        const openSetForViz = new Set();

        start.g = 0;
        start.f = heuristic(start, end);
        openSet.enqueue(start, start.f);
        openSetForViz.add(`${start.i},${start.j}`);

        history.push({ openSet: new Set(openSetForViz), closedSet: new Set(closedSet), path: null });

        while (!openSet.isEmpty()) {
            const current = openSet.dequeue();
            openSetForViz.delete(`${current.i},${current.j}`);

            if (current === end) {
                // Found the path
                const path = reconstructPath(current);
                history.push({ openSet: new Set(openSetForViz), closedSet: new Set(closedSet).add(`${current.i},${current.j}`), path: path });
                return;
            }

            closedSet.add(`${current.i},${current.j}`);

            for (const neighbor of current.neighbors) {
                if (neighbor.isObstacle || closedSet.has(`${neighbor.i},${neighbor.j}`)) {
                    continue;
                }

                const dist = p.dist(current.i, current.j, neighbor.i, neighbor.j);
                const tentativeG = current.g + dist;

                if (tentativeG < neighbor.g) {
                    neighbor.previous = current;
                    neighbor.g = tentativeG;
                    neighbor.h = heuristic(neighbor, end);
                    neighbor.f = neighbor.g + neighbor.h;

                    if (!openSetForViz.has(`${neighbor.i},${neighbor.j}`)) {
                         openSet.enqueue(neighbor, neighbor.f);
                         openSetForViz.add(`${neighbor.i},${neighbor.j}`);
                    }
                }
            }
            history.push({ openSet: new Set(openSetForViz), closedSet: new Set(closedSet), path: null });
        }
        // No solution
    }

    function dijkstra(start, end) {
        const openSet = new PriorityQueue();
        const closedSet = new Set();
        const openSetForViz = new Set();

        for (let i = 0; i < GRID_SIZE; i++) {
            for (let j = 0; j < GRID_SIZE; j++) {
                grid[i][j].g = Infinity;
            }
        }
        start.g = 0;

        openSet.enqueue(start, 0);
        openSetForViz.add(`${start.i},${start.j}`);

        history.push({ openSet: new Set(openSetForViz), closedSet: new Set(closedSet), path: null });

        while (!openSet.isEmpty()) {
            const current = openSet.dequeue();
            openSetForViz.delete(`${current.i},${current.j}`);

            if (current === end) {
                const path = reconstructPath(current);
                history.push({ openSet: new Set(openSetForViz), closedSet: new Set(closedSet).add(`${current.i},${current.j}`), path: path });
                return;
            }

            if(closedSet.has(`${current.i},${current.j}`)) continue;
            closedSet.add(`${current.i},${current.j}`);

            for (const neighbor of current.neighbors) {
                if (neighbor.isObstacle || closedSet.has(`${neighbor.i},${neighbor.j}`)) {
                    continue;
                }
                const dist = p.dist(current.i, current.j, neighbor.i, neighbor.j);
                const newCost = current.g + dist;
                if (newCost < neighbor.g) {
                    neighbor.g = newCost;
                    neighbor.previous = current;
                    openSet.enqueue(neighbor, newCost);
                    openSetForViz.add(`${neighbor.i},${neighbor.j}`);
                }
            }
             history.push({ openSet: new Set(openSetForViz), closedSet: new Set(closedSet), path: null });
        }
        // No solution
    }

    // --- Helpers ---
    function getNodeFromCoords(coords) {
        const i = p.floor(coords.x * GRID_SIZE);
        const j = p.floor(coords.y * GRID_SIZE);
        if (i >= 0 && i < GRID_SIZE && j >= 0 && j < GRID_SIZE) {
            return grid[i][j];
        }
        return null;
    }

    function setObstacles() {
        const gs = GRID_SIZE;
        const place = (i, j) => {
            if (i >= 0 && i < gs && j >= 0 && j < gs) grid[i][j].isObstacle = true;
        };

        switch (currentObstacles) {
            case '1-square':
                for(let i = p.floor(gs*0.4); i < p.floor(gs*0.6); i++) {
                    for(let j = p.floor(gs*0.4); j < p.floor(gs*0.6); j++) {
                        place(i, j);
                    }
                }
                break;
            case '2-squares':
                for(let i = p.floor(gs*0.2); i < p.floor(gs*0.4); i++) {
                    for(let j = p.floor(gs*0.2); j < p.floor(gs*0.4); j++) place(i, j);
                    for(let j = p.floor(gs*0.6); j < p.floor(gs*0.8); j++) place(i, j);
                }
                 for(let i = p.floor(gs*0.6); i < p.floor(gs*0.8); i++) {
                    for(let j = p.floor(gs*0.2); j < p.floor(gs*0.4); j++) place(i, j);
                    for(let j = p.floor(gs*0.6); j < p.floor(gs*0.8); j++) place(i, j);
                }
                break;
            case 'l-shaped-wall':
                for(let i = p.floor(gs*0.2); i < p.floor(gs*0.8); i++) place(i, p.floor(gs*0.5));
                for(let j = p.floor(gs*0.2); j < p.floor(gs*0.5); j++) place(p.floor(gs*0.2), j);
                break;
            case 'random-maze':
                for (let i = 0; i < GRID_SIZE; i++) {
                    for (let j = 0; j < GRID_SIZE; j++) {
                        if (p.random(1) < 0.3) {
                            place(i, j);
                        }
                    }
                }
                break;
            case 'none':
            default:
                break;
        }
    }

    // --- Joystick Control ---
    function setupJoystick(area, handle, coords) {
        let isDragging = false;

        const startDrag = (e) => {
            isDragging = true;
            area.style.cursor = 'grabbing';
        };

        const endDrag = (e) => {
            if (!isDragging) return;
            isDragging = false;
            area.style.cursor = 'crosshair';
            runSimulation();
        };

        const onDrag = (e) => {
            if (!isDragging) return;
            e.preventDefault();
            const rect = area.getBoundingClientRect();
            let x = (e.clientX || e.touches[0].clientX) - rect.left;
            let y = (e.clientY || e.touches[0].clientY) - rect.top;

            x = p.constrain(x, 0, rect.width);
            y = p.constrain(y, 0, rect.height);

            coords.x = x / rect.width;
            coords.y = y / rect.height;

            setJoystickPosition(handle, coords);
        };

        area.addEventListener('mousedown', startDrag);
        document.addEventListener('mouseup', endDrag);
        document.addEventListener('mousemove', onDrag);

        area.addEventListener('touchstart', startDrag);
        document.addEventListener('touchend', endDrag);
        document.addEventListener('touchmove', onDrag);
    }

    function setJoystickPosition(handle, coords) {
        handle.style.left = `${coords.x * 100}%`;
        handle.style.top = `${coords.y * 100}%`;
    }
};

new p5(sketch);
</script>

</body>
</html>
