    <script>
        // DOM Elements
        let sliderSourceCharge, sliderTestCharge;
        let labelSourceCharge, labelTestCharge;
        let displayEMag, displayEx, displayEy, displayXPos, displayYPos, displayForce;

        // Simulation State
        let Q; // source charge
        let q; // test charge
        let testChargePos; // p5.Vector for (x, y)
        let isDragging = false;

        // Constants
        const k = 1; // Coulomb's constant
        const pixelsPerMeter = 40; // Scaling factor for the grid
        const vectorScaleFactor = 400; // Scaling factor for vector length visualization
        const canvasSize = { width: 600, height: 500 };

        function setup() {
            let canvas = createCanvas(canvasSize.width, canvasSize.height);
            canvas.parent('canvas-container');

            // --- Get DOM element references ---
            sliderSourceCharge = select('#slider-source-charge');
            sliderTestCharge = select('#slider-test-charge');
            labelSourceCharge = select('#label-source-charge');
            labelTestCharge = select('#label-test-charge');
            displayEMag = select('#display-E-mag');
            displayEx = select('#display-Ex');
            displayEy = select('#display-Ey');
            displayXPos = select('#display-x-pos');
            displayYPos = select('#display-y-pos');
            displayForce = select('#display-force');

            // --- Initialize state from plan ---
            testChargePos = createVector(2, 3);

            // --- Attach event listeners ---
            sliderSourceCharge.input(updateSimulation);
            sliderTestCharge.input(updateSimulation);

            // --- Initial render ---
            updateSimulation();
        }

        function draw() {
            background(255);

            // --- Handle Dragging Interaction ---
            if (isDragging) {
                // Convert mouse pixel coordinates to logical coordinates
                let logicalX = (mouseX - width / 2) / pixelsPerMeter;
                let logicalY = (height / 2 - mouseY) / pixelsPerMeter;
                testChargePos.set(logicalX, logicalY);
                updateSimulation();
            }

            // --- Setup Coordinate System ---
            translate(width / 2, height / 2);
            scale(1, -1); // Flip Y-axis to match standard math coordinates

            // --- Draw Visual Elements ---
            drawGrid();
            drawSourceCharge();

            // Calculations are done in updateSimulation, here we just draw
            let r = testChargePos.mag();
            if (r === 0) return; // Avoid division by zero at origin

            let E_mag = (k * Q) / (r * r);
            let E_vec = testChargePos.copy().setMag(E_mag);

            let F_vec = p5.Vector.mult(E_vec, q);

            drawTestCharge();
            drawVectors(E_vec, F_vec);
        }

        function updateSimulation() {
            // --- Read values from sliders ---
            Q = parseFloat(sliderSourceCharge.value());
            q = parseFloat(sliderTestCharge.value());

            // --- Update slider labels ---
            labelSourceCharge.html(`${Q.toFixed(1)} C`);
            labelTestCharge.html(`${q.toFixed(4)} C`);

            // --- Core Calculations (Coulomb's Law) ---
            let r = testChargePos.mag();
            let E_mag = 0, E_vec = createVector(0,0), F_mag = 0;

            if (r > 0.1) { // Add a small epsilon to avoid extreme values near origin
                E_mag = (k * Q) / (r * r);
                E_vec = testChargePos.copy().setMag(E_mag);
                F_mag = abs(q * E_mag);
            }

            // --- Update Info Panel ---
            displayEMag.html(`|E| = ${abs(E_mag).toFixed(3)} N/C`);
            displayEx.html(`E<sub>x</sub> component = ${E_vec.x.toFixed(3)} N/C`);
            displayEy.html(`E<sub>y</sub> component = ${E_vec.y.toFixed(3)} N/C`);
            displayXPos.html(`x position = ${testChargePos.x.toFixed(2)} m`);
            displayYPos.html(`y position = ${testChargePos.y.toFixed(2)} m`);
            displayForce.html(`Electric force = ${F_mag.toFixed(3)} N`);
        }

        // --- Drawing Helper Functions ---

        function drawGrid() {
            push();
            stroke(220);
            strokeWeight(1);

            const rangeX = width / 2 / pixelsPerMeter;
            const rangeY = height / 2 / pixelsPerMeter;

            for (let i = -rangeX; i <= rangeX; i++) {
                line(i * pixelsPerMeter, -height / 2, i * pixelsPerMeter, height / 2);
            }
            for (let i = -rangeY; i <= rangeY; i++) {
                line(-width / 2, i * pixelsPerMeter, width / 2, i * pixelsPerMeter);
            }

            // Axes
            stroke(150);
            strokeWeight(2);
            line(-width / 2, 0, width / 2, 0); // X-axis
            line(0, -height / 2, 0, height / 2); // Y-axis

            // Axis labels
            scale(1, -1); // Un-flip for text drawing
            fill(100);
            noStroke();
            textSize(12);
            textAlign(CENTER, CENTER);
            for (let i = -6; i <= 6; i+=2) {
                if (i !== 0) {
                    text(i, i * pixelsPerMeter, 15);
                    text(i, -15, -i * pixelsPerMeter);
                }
            }
            pop();
        }

        function drawSourceCharge() {
            push();
            noStroke();
            // Color based on charge sign
            if (Q > 0) fill(0, 0, 255); // Blue for positive
            else if (Q < 0) fill(255, 100, 100); // Lighter red for negative
            else fill(150); // Gray for neutral

            ellipse(0, 0, 20, 20); // At origin
            pop();
        }

        function drawTestCharge() {
            push();
            noStroke();
            fill(0); // Black
            let screenX = testChargePos.x * pixelsPerMeter;
            let screenY = testChargePos.y * pixelsPerMeter;
            ellipse(screenX, screenY, 15, 15);
            pop();
        }

        function drawVectors(E_vec, F_vec) {
            push();
            let base = testChargePos.copy().mult(pixelsPerMeter);

            // Draw Electric Field Vector
            let scaled_E_vec = E_vec.copy().mult(vectorScaleFactor);
            drawArrow(base, scaled_E_vec, color(255, 0, 0)); // Red

            // Draw Electric Force Vector
            if (q !== 0) {
                let scaled_F_vec = F_vec.copy().mult(vectorScaleFactor);
                drawArrow(base, scaled_F_vec, color(0, 0, 255)); // Blue
            }
            pop();
        }

        function drawArrow(base, vec, myColor) {
            push();
            stroke(myColor);
            strokeWeight(3);
            fill(myColor);
            translate(base.x, base.y);
            line(0, 0, vec.x, vec.y);
            rotate(vec.heading());
            let arrowSize = 7;
            translate(vec.mag() - arrowSize, 0);
            triangle(0, arrowSize / 2, 0, -arrowSize / 2, arrowSize, 0);
            pop();
        }

        // --- Mouse Interaction Functions ---

        function mousePressed() {
            // Convert test charge logical pos to screen pos
            let screenX = testChargePos.x * pixelsPerMeter + width / 2;
            let screenY = -testChargePos.y * pixelsPerMeter + height / 2;

            // Check distance from mouse to the center of the draggable circle
            let d = dist(mouseX, mouseY, screenX, screenY);
            if (d < 15) { // 15 is a bit larger than radius for easier grabbing
                isDragging = true;
            }
        }

        function mouseReleased() {
            isDragging = false;
        }

    </script>

</body>
</html>
