            <label for="checkbox-overlap">Overlap</label>
            <input type="checkbox" id="checkbox-gaussian" checked>
            <label for="checkbox-gaussian">Gaussian</label>
        </div>

        <div id="scale-selector">
            <label>Axes (k-plot):</label>
            <button id="btn-linear-linear">linear-linear</button>
            <button id="btn-log-linear">log-linear</button>
            <button id="btn-log-log">log-log</button>
        </div>

        <div id="sliders-container">
            <div id="slider-r-control" class="slider-control">
                <label for="slider-r">radius r</label>
                <input type="range" id="slider-r" min="0.1" max="5" step="0.1" value="2.5">
                <span id="span-r-value">2.5</span>
            </div>
            <div id="slider-k-control" class="slider-control">
                <label for="slider-k">wave number k</label>
                <input type="range" id="slider-k" min="1" max="100" step="0.01" value="50">
                <span id="span-k-value">50</span>
            </div>
        </div>
    </div>

    <p id="hover-info">(Hover over the curves to see math labels.)</p>

    <div id="plots-container">
        <div id="plot-left"></div>
        <div id="plot-right"></div>
    </div>

    <script>
        window.onload = function() {
            // --- DOM Element References ---
            const checkboxHertz = document.getElementById('checkbox-hertz');
            const checkboxOverlap = document.getElementById('checkbox-overlap');
            const checkboxGaussian = document.getElementById('checkbox-gaussian');

            const btnLinearLinear = document.getElementById('btn-linear-linear');
            const btnLogLinear = document.getElementById('btn-log-linear');
            const btnLogLog = document.getElementById('btn-log-log');
            const scaleButtons = [btnLinearLinear, btnLogLinear, btnLogLog];

            const sliderR = document.getElementById('slider-r');
            const spanRValue = document.getElementById('span-r-value');
            const sliderK = document.getElementById('slider-k');
            const spanKValue = document.getElementById('span-k-value');

            // --- Constants and Definitions ---
            const COLORS = {
                HERTZ: '#ff0000',   // red
                OVERLAP: '#000000', // black
                GAUSSIAN: '#0000ff' // blue
            };

            const HOVER_TEMPLATES = {
                HERTZ_R: `Hertz<br>f(r) = (1 - r^2)^{5/2}, r \\le 1<extra></extra>`,
                HERTZ_K: `Hertz<br>F(k) = 15\\pi \\frac{(3-k^2)\\sin(k) - 3k\\cos(k)}{k^5}<extra></extra>`,
                OVERLAP_R: `Overlap<br>f(r) = (1 - r)^2, r \\le 1<extra></extra>`,
                OVERLAP_K: `Overlap<br>F(k) = 8\\pi \\frac{2 - k^2 - 2\\cos(k) - 2k\\sin(k)}{k^4}<extra></extra>`,
                GAUSSIAN_R: `Gaussian<br>f(r) = e^{-\\pi r^2}<extra></extra>`,
                GAUSSIAN_K: `Gaussian<br>F(k) = \\frac{1}{\\sqrt{\\pi}} e^{-k^2 / (4\\pi)}<extra></extra>`
            };

            // --- Mathematical Functions ---
            const f_H = (r) => (r <= 1) ? Math.pow(1 - r * r, 2.5) : 0;
            const f_O = (r) => (r <= 1) ? Math.pow(1 - r, 2) : 0;
            const f_G = (r) => Math.exp(-Math.PI * r * r);

            const F_H = (k) => {
                if (k === 0) return Math.PI; // Limit as k -> 0
                const k2 = k * k;
                const k5 = k2 * k2 * k;
                return 15 * Math.PI * ((3 - k2) * Math.sin(k) - 3 * k * Math.cos(k)) / k5;
            };

            const F_O = (k) => {
                // This function as specified diverges at k=0. We return null to create a break in the plot.
                if (k < 1e-3) return null;
                const k2 = k * k;
                const k4 = k2 * k2;
                return 8 * Math.PI * (2 - k2 - 2 * Math.cos(k) - 2 * k * Math.sin(k)) / k4;
            };

            const F_G = (k) => (1 / Math.sqrt(Math.PI)) * Math.exp(-k * k / (4 * Math.PI));

            function linspace(start, stop, num) {
                const arr = [];
                const step = (stop - start) / (num - 1);
                for (let i = 0; i < num; i++) {
                    arr.push(start + (step * i));
                }
                return arr;
            }

            // --- Core Update Function ---
            function updatePlots() {
                // 1. Read control states
                const showHertz = checkboxHertz.checked;
                const showOverlap = checkboxOverlap.checked;
                const showGaussian = checkboxGaussian.checked;

                const rMax = parseFloat(sliderR.value);
                const kMax = parseFloat(sliderK.value);

                spanRValue.textContent = rMax.toFixed(1);
                spanKValue.textContent = kMax.toFixed(1);

                const activeScaleBtn = document.querySelector('#scale-selector button.active');
                const scaleMode = activeScaleBtn.id;

                // 2. Generate data points
                const NUM_POINTS = 201;
                const r_values = linspace(0, rMax, NUM_POINTS);

                // For log scale, start k slightly above 0
                const k_start = (scaleMode === 'btn-log-linear' || scaleMode === 'btn-log-log') ? 0.01 : 0;
                const k_values = linspace(k_start, kMax, NUM_POINTS);

                // 3. Prepare Plotly traces
                const traces_r = [];
                if (showHertz) traces_r.push({ x: r_values, y: r_values.map(f_H), mode: 'lines', name: 'Hertz', line: { color: COLORS.HERTZ }, hovertemplate: HOVER_TEMPLATES.HERTZ_R });
                if (showOverlap) traces_r.push({ x: r_values, y: r_values.map(f_O), mode: 'lines', name: 'Overlap', line: { color: COLORS.OVERLAP }, hovertemplate: HOVER_TEMPLATES.OVERLAP_R });
                if (showGaussian) traces_r.push({ x: r_values, y: r_values.map(f_G), mode: 'lines', name: 'Gaussian', line: { color: COLORS.GAUSSIAN }, hovertemplate: HOVER_TEMPLATES.GAUSSIAN_R });

                const traces_k = [];
                if (showHertz) traces_k.push({ x: k_values, y: k_values.map(F_H), mode: 'lines', name: 'Hertz', line: { color: COLORS.HERTZ }, hovertemplate: HOVER_TEMPLATES.HERTZ_K });
                if (showOverlap) traces_k.push({ x: k_values, y: k_values.map(F_O), mode: 'lines', name: 'Overlap', line: { color: COLORS.OVERLAP }, hovertemplate: HOVER_TEMPLATES.OVERLAP_K });
                if (showGaussian) traces_k.push({ x: k_values, y: k_values.map(F_G), mode: 'lines', name: 'Gaussian', line: { color: COLORS.GAUSSIAN }, hovertemplate: HOVER_TEMPLATES.GAUSSIAN_K });

                // 4. Define Plotly layouts
                const layout_left = {
                    title: { text: 'Function in Real Space', font: { color: '#6c757d' } },
                    xaxis: { title: 'r', range: [0, rMax], zeroline: false },
                    yaxis: { title: 'f(r)', zeroline: false },
                    margin: { l: 50, r: 30, b: 40, t: 40 },
                    hovermode: 'x unified',
                };

                let xaxis_type_k = 'linear';
                let yaxis_type_k = 'linear';
                if (scaleMode === 'btn-log-linear') {
                    xaxis_type_k = 'log';
                } else if (scaleMode === 'btn-log-log') {
                    xaxis_type_k = 'log';
                    yaxis_type_k = 'log';
                }

                const layout_right = {
                    title: { text: '3D Fourier Transform (Real Part)', font: { color: '#6c757d' } },
                    xaxis: { title: 'k', type: xaxis_type_k, range: [Math.log10(k_start > 0 ? k_start : 0.01), Math.log10(kMax)], autorange: xaxis_type_k === 'linear', zeroline: false},
                    yaxis: { title: 'F(k)', type: yaxis_type_k, zeroline: false },
                    margin: { l: 50, r: 30, b: 40, t: 40 },
                    hovermode: 'x unified'
                };
                 if (xaxis_type_k === 'linear') {
                    layout_right.xaxis.range = [0, kMax];
                }

                // 5. Render plots with Plotly.react for efficiency
                Plotly.react('plot-left', traces_r, layout_left);
                Plotly.react('plot-right', traces_k, layout_right);

                // 6. Ask MathJax to typeset any new math in tooltips
                if (window.MathJax && window.MathJax.typesetPromise) {
                    window.MathJax.typesetPromise();
                }
            }

            // --- Event Listeners Setup ---
            function init() {
                // Checkboxes
                [checkboxHertz, checkboxOverlap, checkboxGaussian].forEach(cb => {
                    cb.addEventListener('change', updatePlots);
                });

                // Sliders
                sliderR.addEventListener('input', updatePlots);
                sliderK.addEventListener('input', updatePlots);

                // Scale buttons
                scaleButtons.forEach(button => {
                    button.addEventListener('click', (e) => {
                        scaleButtons.forEach(btn => btn.classList.remove('active'));
                        e.target.classList.add('active');
                        updatePlots();
                    });
                });

                // Initial state
                btnLinearLinear.classList.add('active');

                // Initial plot render
                updatePlots();
            }

            init();
        };
    </script>
</body>
