                <button id="btn-neutral-2030" data-year="2030">2030</button>
                <button id="btn-neutral-2040" data-year="2040" class="active">2040</button>
                <button id="btn-neutral-2050" data-year="2050">2050</button>
                <button id="btn-neutral-never" data-year="Infinity">never</button>
            </div>
            <div id="main-plot-container"></div>
            <div id="budget-plot-container"></div>
        </div>
    </div>

    <script>
        document.addEventListener('DOMContentLoaded', () => {

            const controls = {
                scale: document.querySelector('input[name="scale"]:checked').value,
                population: document.getElementById('slider-population'),
                type: document.querySelector('input[name="type"]:checked').value,
                annualEmissions: document.getElementById('slider-annual-emissions'),
                reductionTime: document.getElementById('slider-reduction-time'),
                annualBudget: document.getElementById('slider-annual-budget'),
                startYear: document.getElementById('slider-start-year'),
                compensationCost: document.getElementById('slider-compensation-cost'),
                fractionalCompensation: document.getElementById('slider-fractional-compensation'),
                constantCompensation: document.getElementById('slider-constant-compensation'),
            };

            const valueDisplays = {
                population: document.getElementById('value-population'),
                annualEmissions: document.getElementById('value-annual-emissions'),
                reductionTime: document.getElementById('value-reduction-time'),
                annualBudget: document.getElementById('value-annual-budget'),
                startYear: document.getElementById('value-start-year'),
                compensationCost: document.getElementById('value-compensation-cost'),
                fractionalCompensation: document.getElementById('value-fractional-compensation'),
                constantCompensation: document.getElementById('value-constant-compensation'),
            };

            const climateNeutralButtons = document.querySelectorAll('#climate-neutral-buttons button');

            let p5sketch;
            let params = {};
            let neutralYear = 2040;

            function update() {
                // 1. Read all control values into params object
                params.scale = document.querySelector('input[name="scale"]:checked').value;
                params.type = document.querySelector('input[name="type"]:checked').value;

                params.population = parseFloat(controls.population.value);
                params.annualEmissions = parseFloat(controls.annualEmissions.value);
                params.reductionTime = parseFloat(controls.reductionTime.value);
                params.annualBudget = parseFloat(controls.annualBudget.value);
                params.startYear = parseInt(controls.startYear.value);
                params.compensationCost = parseFloat(controls.compensationCost.value);
                params.fractionalCompensation = parseFloat(controls.fractionalCompensation.value);
                params.constantCompensation = parseFloat(controls.constantCompensation.value);

                // 2. Update UI elements
                valueDisplays.population.textContent = params.population;
                valueDisplays.annualEmissions.textContent = params.annualEmissions.toFixed(1);
                valueDisplays.reductionTime.textContent = params.reductionTime;
                valueDisplays.annualBudget.textContent = params.annualBudget.toFixed(1);
                valueDisplays.startYear.textContent = params.startYear;
                valueDisplays.compensationCost.textContent = params.compensationCost;
                valueDisplays.fractionalCompensation.textContent = params.fractionalCompensation;
                valueDisplays.constantCompensation.textContent = params.constantCompensation;

                controls.population.disabled = (params.scale === 'individual');

                climateNeutralButtons.forEach(btn => {
                    btn.classList.toggle('active', parseInt(btn.dataset.year) === neutralYear);
                });

                // 3. Trigger p5.js redraw
                if (p5sketch) {
                    p5sketch.redraw();
                }
            }

            // Add event listeners to all controls
            document.querySelectorAll('#control-panel input').forEach(input => {
                input.addEventListener('input', update);
            });

            climateNeutralButtons.forEach(button => {
                button.addEventListener('click', () => {
                    neutralYear = parseInt(button.dataset.year);
                    update();
                });
            });


            // p5.js sketch definition
            const sketch = (s) => {
                const START_YEAR = 2020;
                const END_YEAR = 2050;
                const YEARS = Array.from({ length: END_YEAR - START_YEAR + 1 }, (_, i) => START_YEAR + i);
                let plotMargin, mainPlotHeight, budgetPlotHeight;

                s.setup = () => {
                    const container = document.getElementById('main-plot-container');
                    const canvas = s.createCanvas(container.offsetWidth, container.offsetHeight);
                    canvas.parent(container);
                    s.noLoop();

                    // Initial call to sync UI and draw
                    update();
                };

                s.windowResized = () => {
                    const container = document.getElementById('main-plot-container');
                    s.resizeCanvas(container.offsetWidth, container.offsetHeight);
                    s.redraw();
                };

                s.draw = () => {
                    s.background(getComputedStyle(document.documentElement).getPropertyValue('--bg-color'));
                    if (!params.annualEmissions) return; // Don't draw if params not ready

                    // Define plot areas
                    plotMargin = { top: 30, right: 30, bottom: 40, left: 60 };
                    budgetPlotHeight = 150;
                    mainPlotHeight = s.height - budgetPlotHeight;

                    // --- CALCULATIONS ---
                    const data = calculateModel();

                    // --- DRAWING ---
                    if (params.type === 'emissions') {
                        drawEmissionsPlot(data);
                    } else if (params.type === 'costs') {
                        drawCostsPlot(data);
                    }
                    drawBudgetPlot(data);
                };

                function calculateModel() {
                    let realEmissions = [], compensatedEmissions = [], effectiveEmissions = [], annualCosts = [];
                    let cumulativeEffective = 0;
                    let cumulativeData = {};

                    const initialEmissions = params.annualEmissions;

                    for (const t of YEARS) {
                        let real_emissions_person = initialEmissions;
                        if (t >= params.startYear) {
                            real_emissions_person = initialEmissions * Math.pow(0.5, (t - params.startYear) / params.reductionTime);
                        }

                        const fractional = real_emissions_person * (params.fractionalCompensation / 100);
                        const constant = initialEmissions * (params.constantCompensation / 100);
                        const compensated_emissions_person = fractional + constant;

                        const effective_emissions_person = real_emissions_person - compensated_emissions_person;

                        cumulativeEffective += effective_emissions_person;
                        cumulativeData[t] = cumulativeEffective;

                        const annual_costs = compensated_emissions_person * params.compensationCost;

                        realEmissions.push(real_emissions_person);
                        compensatedEmissions.push(compensated_emissions_person);
                        effectiveEmissions.push(effective_emissions_person);
                        annualCosts.push(annual_costs);
                    }

                    const totalCosts = annualCosts.reduce((a, b) => a + b, 0);

                    return { realEmissions, compensatedEmissions, effectiveEmissions, annualCosts, cumulativeData, totalCosts };
                }

                function drawEmissionsPlot(data) {
                    let { realEmissions, compensatedEmissions, effectiveEmissions, cumulativeData } = data;
                    let multiplier = 1;
                    let yLabel = "annual CO₂ emissions per person (tons)";

                    if (params.scale === 'population') {
                        multiplier = params.population * 1000 / 1000000; // to mil tons
                        yLabel = "annual city emission of CO₂ (mil tons)";
                    }

                    // Scale data
                    const scaledReal = realEmissions.map(v => v * multiplier);
                    const scaledEffective = effectiveEmissions.map(v => v * multiplier);

                    // Determine Y-axis range
                    const allValues = [...scaledReal, ...scaledEffective];
                    let yMax = Math.max(0, ...allValues) * 1.1;
                    let yMin = Math.min(0, ...allValues) * 1.1;
                    if (yMax === 0) yMax = 1; // Avoid flat axis

                    drawAxes(yMin, yMax, yLabel);

                    // --- DRAW AREAS ---
                    s.noStroke();
                    // Compensated (blue)
                    s.fill(hexToRgba(getComputedStyle(document.documentElement).getPropertyValue('--blue-color'), 0.3));
                    drawArea(scaledReal, scaledEffective);

                    // Over-budget (red)
                    s.fill(hexToRgba(getComputedStyle(document.documentElement).getPropertyValue('--red-color'), 0.5));
                    drawArea(scaledEffective, Array(YEARS.length).fill(0), (v) => v > 0);

                    // Under-budget (green)
                    s.fill(hexToRgba(getComputedStyle(document.documentElement).getPropertyValue('--green-color'), 0.5));
                    drawArea(scaledEffective, Array(YEARS.length).fill(0), (v) => v < 0);

                    // --- DRAW LINES ---
                    s.noFill();
                    // Real emissions (red line)
                    s.stroke(getComputedStyle(document.documentElement).getPropertyValue('--red-color'));
                    s.strokeWeight(2);
                    drawLine(scaledReal);

                    // Effective emissions (black line)
                    s.stroke(getComputedStyle(document.documentElement).getPropertyValue('--text-color'));
                    s.strokeWeight(2.5);
                    drawLine(scaledEffective);

                    // --- DRAW ANNOTATIONS ---
                    // Cumulative lines
                    const cumu2050 = cumulativeData[END_YEAR] * (params.scale === 'population' ? params.population * 1000 : 1);
                    drawHorizontalDashedLine(cumu2050 / (END_YEAR-START_YEAR+1), yMin, yMax, `CO₂ emissions until 2050 = ${cumu2050.toFixed(1)} tons CO₂`);

                    if (isFinite(neutralYear)) {
                        const cumuNeutral = cumulativeData[neutralYear] * (params.scale === 'population' ? params.population * 1000 : 1);
                        drawHorizontalDashedLine(cumuNeutral / (END_YEAR-START_YEAR+1), yMin, yMax, `CO₂ emissions until ${neutralYear} = ${cumuNeutral.toFixed(1)} tons CO₂`);
                        drawVerticalLine(neutralYear, `climate neutral ${neutralYear}`);
                    }
                }

                function drawCostsPlot(data) {
                    let { annualCosts, totalCosts } = data;
                    let multiplier = 1;
                    let yLabel = "annual personal compensation costs ($)";

                    if (params.scale === 'population') {
                        multiplier = params.population * 1000;
                        yLabel = "annual city compensation costs ($)";
                    }

                    const scaledCosts = annualCosts.map(v => v * multiplier);
                    const scaledTotalCosts = totalCosts * multiplier;

                    let yMax = Math.max(...scaledCosts) * 1.2;
                    if (yMax === 0) yMax = 100;

                    drawAxes(0, yMax, yLabel);

                    // Draw area and line
                    s.fill(hexToRgba(getComputedStyle(document.documentElement).getPropertyValue('--blue-color'), 0.3));
                    s.noStroke();
                    drawArea(scaledCosts, Array(YEARS.length).fill(0));

                    s.stroke(getComputedStyle(document.documentElement).getPropertyValue('--blue-color'));
                    s.strokeWeight(2);
                    s.noFill();
                    drawLine(scaledCosts);

                    // Draw annotations
                    const avgCost = scaledTotalCosts / (END_YEAR - START_YEAR + 1);
                    drawHorizontalDashedLine(avgCost, 0, yMax, `annual ${params.scale === 'individual' ? 'personal' : 'city'} compensation costs (average) = $${avgCost.toFixed(0)} per year`);

                    s.fill(getComputedStyle(document.documentElement).getPropertyValue('--text-color'));
                    s.noStroke();
                    s.textAlign(s.RIGHT, s.TOP);
                    s.text(`total ${params.scale === 'individual' ? 'personal' : 'city'} compensation costs (until 2050) = $${scaledTotalCosts.toFixed(0)}`, s.width - plotMargin.right, plotMargin.top + 20);
                }

                function drawBudgetPlot(data) {
                    let { effectiveEmissions, cumulativeData } = data;

                    s.fill(getComputedStyle(document.documentElement).getPropertyValue('--text-color'));
                    s.noStroke();
                    s.textAlign(s.CENTER, s.CENTER);
                    s.textSize(14);
                    const budgetTitle = params.scale === 'population' ? "population climate budget" : "individual climate budget";
                    s.text(budgetTitle, s.width / 2, mainPlotHeight + 25);

                    const budgetYears = [2020, 2030, 2040, 2050];
                    const totalBudget = (END_YEAR - START_YEAR) * params.annualBudget;
                    const circleArea = s.width / (budgetYears.length + 1);

                    let maxBudgetValue = 0;
                    budgetYears.forEach(year => {
                        const cumulative = cumulativeData[year] || 0;
                        const remainingBudget = totalBudget - cumulative;
                        if(Math.abs(remainingBudget) > maxBudgetValue) maxBudgetValue = Math.abs(remainingBudget);
                    });
                    if (maxBudgetValue === 0) maxBudgetValue = 1;


                    budgetYears.forEach((year, i) => {
                        const x = s.map(i, -0.5, budgetYears.length - 0.5, plotMargin.left, s.width - plotMargin.right);
                        const y = mainPlotHeight + budgetPlotHeight / 2 + 10;

                        const cumulative = cumulativeData[year] || 0;
                        let remainingBudget = totalBudget - cumulative;

                        let multiplier = 1;
                        if (params.scale === 'population') {
                            multiplier = params.population * 1000;
                        }
                        remainingBudget *= multiplier;

                        const radiusScale = 40 / Math.sqrt(maxBudgetValue * multiplier);
                        const radius = radiusScale * Math.sqrt(Math.abs(remainingBudget));

                        const yearIndex = year - START_YEAR;
                        const isNeutral = effectiveEmissions[yearIndex] <= 0;

                        if (isNeutral) {
                            s.strokeWeight(2);
                            s.stroke(getComputedStyle(document.documentElement).getPropertyValue('--green-color'));
                        } else {
                            s.drawingContext.setLineDash([5, 5]);
                            s.strokeWeight(2);
                            s.stroke(getComputedStyle(document.documentElement).getPropertyValue('--text-color-light'));
                        }

                        s.fill(hexToRgba(getComputedStyle(document.documentElement).getPropertyValue('--green-color'), remainingBudget > 0 ? 0.3 : 0));
                        if(remainingBudget < 0){
                           s.fill(hexToRgba(getComputedStyle(document.documentElement).getPropertyValue('--red-color'), 0.3));
                        }
                        s.circle(x, y, radius * 2);
                        s.drawingContext.setLineDash([]);

                        // Label
                        s.fill(getComputedStyle(document.documentElement).getPropertyValue('--text-color'));
                        s.noStroke();
                        s.textAlign(s.CENTER, s.CENTER);
                        s.textSize(12);
                        const unit = params.scale === 'population' ? 'k tons' : 'tons';
                        const valueText = params.scale === 'population' ? (remainingBudget/1000).toFixed(0) : remainingBudget.toFixed(1);
                        s.text(`${valueText} ${unit}\nCO₂`, x, y);
                        s.textSize(14);
                        s.text(year, x, y + radius + 20);
                    });
                }

                // --- DRAWING HELPER FUNCTIONS ---
                function drawAxes(yMin, yMax, yLabel) {
                    s.stroke(getComputedStyle(document.documentElement).getPropertyValue('--text-color-light'));
                    s.strokeWeight(1);
                    // X-Axis
                    s.line(plotMargin.left, mainPlotHeight - plotMargin.bottom, s.width - plotMargin.right, mainPlotHeight - plotMargin.bottom);
                    // Y-Axis
                    s.line(plotMargin.left, plotMargin.top, plotMargin.left, mainPlotHeight - plotMargin.bottom);

                    s.noStroke();
                    s.fill(getComputedStyle(document.documentElement).getPropertyValue('--text-color-light'));
                    s.textAlign(s.CENTER, s.TOP);
                    s.textSize(12);
                    for (let year = START_YEAR; year <= END_YEAR; year += 5) {
                        const x = s.map(year, START_YEAR, END_YEAR, plotMargin.left, s.width - plotMargin.right);
                        s.text(year, x, mainPlotHeight - plotMargin.bottom + 8);
                    }

                    s.textAlign(s.RIGHT, s.CENTER);
                    const numYTicks = 5;
                    for (let i = 0; i <= numYTicks; i++) {
                        const val = s.lerp(yMin, yMax, i / numYTicks);
                        const y = s.map(val, yMin, yMax, mainPlotHeight - plotMargin.bottom, plotMargin.top);
                        s.text(val.toFixed(yMax > 10 ? 0 : 1), plotMargin.left - 8, y);
                        s.stroke(50);
                        s.line(plotMargin.left - 4, y, plotMargin.left, y);
                        s.noStroke();
                    }

                    s.push();
                    s.translate(plotMargin.left - 45, (mainPlotHeight-plotMargin.bottom+plotMargin.top)/2);
                    s.rotate(-s.HALF_PI);
                    s.textAlign(s.CENTER, s.CENTER);
                    s.text(yLabel, 0, 0);
                    s.pop();
                }

                function drawLine(data) {
                    s.beginShape();
                    data.forEach((val, i) => {
                        const x = s.map(YEARS[i], START_YEAR, END_YEAR, plotMargin.left, s.width - plotMargin.right);
                        const y = s.map(val, s.yMin, s.yMax, mainPlotHeight - plotMargin.bottom, plotMargin.top);
                        s.vertex(x, y);
                    });
                    s.endShape();
                }

                function drawArea(data1, data2, condition = () => true) {
                    s.beginShape();
                    let lastValidY1, lastValidY2, lastValidX;

                    // Forward pass
                    data1.forEach((val1, i) => {
                        const val2 = data2[i];
                        if (condition(val1)) {
                            const x = s.map(YEARS[i], START_YEAR, END_YEAR, plotMargin.left, s.width - plotMargin.right);
                            const y1 = s.map(val1, s.yMin, s.yMax, mainPlotHeight - plotMargin.bottom, plotMargin.top);
                            if (i === 0 || !condition(data1[i-1])) {
                                s.vertex(x, s.map(val2, s.yMin, s.yMax, mainPlotHeight - plotMargin.bottom, plotMargin.top));
                            }
                            s.vertex(x, y1);
                            lastValidY1 = y1;
                            lastValidX = x;
                        }
                    });
                    if (lastValidX) s.vertex(lastValidX, s.map(data2[data2.length-1], s.yMin, s.yMax, mainPlotHeight - plotMargin.bottom, plotMargin.top));

                    // Backward pass
                    for (let i = data2.length - 1; i >= 0; i--) {
                        if (condition(data1[i])) {
                            const x = s.map(YEARS[i], START_YEAR, END_YEAR, plotMargin.left, s.width - plotMargin.right);
                            const y2 = s.map(data2[i], s.yMin, s.yMax, mainPlotHeight - plotMargin.bottom, plotMargin.top);
                            s.vertex(x, y2);
                        }
                    }
                    s.endShape(s.CLOSE);
                }

                function drawVerticalLine(year, label) {
                    const x = s.map(year, START_YEAR, END_YEAR, plotMargin.left, s.width - plotMargin.right);
                    s.stroke(getComputedStyle(document.documentElement).getPropertyValue('--accent-color'));
                    s.strokeWeight(1.5);
                    s.drawingContext.setLineDash([5, 5]);
                    s.line(x, plotMargin.top, x, mainPlotHeight - plotMargin.bottom);
                    s.drawingContext.setLineDash([]);

                    s.fill(getComputedStyle(document.documentElement).getPropertyValue('--accent-color'));
                    s.noStroke();
                    s.textAlign(s.CENTER, s.BOTTOM);
                    s.text(label, x, plotMargin.top - 5);
                }

                function drawHorizontalDashedLine(value, yMin, yMax, label) {
                    const y = s.map(value, yMin, yMax, mainPlotHeight - plotMargin.bottom, plotMargin.top);
                    s.stroke(getComputedStyle(document.documentElement).getPropertyValue('--text-color-light'));
                    s.strokeWeight(1);
                    s.drawingContext.setLineDash([3, 3]);
                    s.line(plotMargin.left, y, s.width - plotMargin.right, y);
                    s.drawingContext.setLineDash([]);

                    s.fill(getComputedStyle(document.documentElement).getPropertyValue('--text-color-light'));
                    s.noStroke();
                    s.textAlign(s.LEFT, s.BOTTOM);
                    s.text(label, plotMargin.left + 5, y - 2);
                }

                s.draw = (p) => { // Redefine draw with p5 and param arguments
                    s.background(getComputedStyle(document.documentElement).getPropertyValue('--bg-color'));
                    if (!params.annualEmissions) return;

                    plotMargin = { top: 30, right: 30, bottom: 40, left: 60 };
                    budgetPlotHeight = 160;
                    mainPlotHeight = s.height - budgetPlotHeight;

                    const data = calculateModel();

                    let yMin, yMax;
                    if(params.type === 'emissions'){
                        let multiplier = params.scale === 'population' ? params.population * 1000 / 1000000 : 1;
                        const allValues = [...data.realEmissions.map(v=>v*multiplier), ...data.effectiveEmissions.map(v=>v*multiplier)];
                        yMax = Math.max(0, ...allValues) * 1.1;
                        yMin = Math.min(0, ...allValues) * 1.1;
                    } else {
                        let multiplier = params.scale === 'population' ? params.population * 1000 : 1;
                        const allValues = data.annualCosts.map(v=>v*multiplier);
                        yMax = Math.max(...allValues) * 1.2;
                        yMin = 0;
                    }
                    if (yMax === yMin) yMax = yMin + 1;
                    s.yMin = yMin; // Store in sketch for access in helpers
                    s.yMax = yMax;

                    if (params.type === 'emissions') {
                        drawEmissionsPlot(data);
                    } else {
                        drawCostsPlot(data);
                    }
                    drawBudgetPlot(data);
                };

                // Helper to convert CSS hex color to p5.js rgba
                function hexToRgba(hex, alpha) {
                    let r = 0, g = 0, b = 0;
                    hex = hex.trim();
                    if (hex.length === 4) {
                        r = "0x" + hex[1] + hex[1];
                        g = "0x" + hex[2] + hex[2];
                        b = "0x" + hex[3] + hex[3];
                    } else if (hex.length === 7) {
                        r = "0x" + hex[1] + hex[2];
                        g = "0x" + hex[3] + hex[4];
                        b = "0x" + hex[5] + hex[6];
                    }
                    return s.color(parseInt(r), parseInt(g), parseInt(b), alpha * 255);
                }
            };

            p5sketch = new p5(sketch);
        });
    </script>
</body>
