    <script type="module">
        import * as THREE from 'three';
        import { OrbitControls } from 'three/addons/controls/OrbitControls.js';

        let scene, camera, renderer, controls;
        let solidGroup;
        let pivots = [];
        let faces = [];

        const UNFOLDED_COLOR = new THREE.Color("#F5E0E0");
        const solidsData = [];

        // --- Define Solid Geometries ---

        // Solid 1: Tetrahedron
        (() => {
            const s = Math.sqrt(3);
            const side = 2;
            const h = side * s / 2;
            const solidsDatum = {
                name: "Tetrahedron",
                vertices: [
                    { x: 0, y: h * 2 / 3 },     // 0 Top
                    { x: -side / 2, y: -h / 3 },  // 1 Bottom-left
                    { x: side / 2, y: -h / 3 },   // 2 Bottom-right
                    { x: 0, y: -h / 3 - h },   // 3 Bottom-of-net
                ],
                faces: [
                    { verts: [0, 1, 2], color: "#E47A55", parent: null, hinge: null, angle: 0 },
                    { verts: [1, 3, 2], color: "#A4C6E3", parent: 0, hinge: [1, 2], angle: Math.acos(1/3) },
                    { verts: [0, 4, 1], color: "#F3C76E", parent: 0, hinge: [0, 1], angle: Math.acos(1/3) },
                    { verts: [0, 2, 5], color: "#8B9E62", parent: 0, hinge: [0, 2], angle: Math.acos(1/3) },
                ],
                // Net vertices for drawing (some are duplicates in position)
                drawVertices: [
                    { x: 0, y: h * 2/3 },         // 0
                    { x: -side / 2, y: -h / 3 },     // 1
                    { x: side / 2, y: -h / 3 },      // 2
                    { x: 0, y: -h / 3 - h},      // 3 (for face 1)
                    { x: -side, y: h * 2/3 },        // 4 (for face 2)
                    { x: side, y: h * 2/3 },         // 5 (for face 3)
                ]
            };
            solidsDatum.faces[2].verts = [0, solidsDatum.drawVertices.length - 2, 1];
            solidsDatum.faces[3].verts = [0, 2, solidsDatum.drawVertices.length - 1];
            solidsData.push(solidsDatum);
        })();


        // Solid 2: Cube
        (() => {
            const s = 1.5;
            solidsData.push({
                name: "Cube",
                vertices: [
                    { x: 0, y: s }, { x: s, y: s }, { x: s, y: 0 }, { x: 0, y: 0 }, // Center face
                    { x: 0, y: 2*s }, { x: s, y: 2*s }, // Top face
                    { x: 0, y: -s }, { x: s, y: -s }, // Bottom face
                    { x: -s, y: s }, { x: -s, y: 0 }, // Left face
                    { x: 2*s, y: s }, { x: 2*s, y: 0 }, // Right face
                    { x: 0, y: 3*s }, { x: s, y: 3*s } // Top-most face
                ],
                faces: [
                    { verts: [0, 3, 2, 1], color: "#E47A55", parent: null, hinge: null, angle: 0 },
                    { verts: [4, 0, 1, 5], color: "#A4C6E3", parent: 0, hinge: [0, 1], angle: Math.PI / 2 },
                    { verts: [3, 6, 7, 2], color: "#F3C76E", parent: 0, hinge: [3, 2], angle: Math.PI / 2 },
                    { verts: [8, 9, 3, 0], color: "#8B9E62", parent: 0, hinge: [0, 3], angle: Math.PI / 2 },
                    { verts: [1, 2, 11, 10], color: "#C78283", parent: 0, hinge: [1, 2], angle: Math.PI / 2 },
                    { verts: [12, 4, 5, 13], color: "#B1A296", parent: 1, hinge: [4, 5], angle: Math.PI / 2 }
                ]
            });
        })();

        // Solid 3: Octahedron
        (() => {
            const s = 1.5;
            const h = s * Math.sqrt(3) / 2;
            solidsData.push({
                name: "Octahedron",
                vertices: [
                    { x: 0, y: h }, { x: s/2, y: 0 }, { x: -s/2, y: 0 }, // Top three
                    { x: s, y: h }, { x: -s, y: h }, // Middle side points
                    { x: s/2, y: -2*h }, { x: -s/2, y: -2*h }, // Bottom far points
                    { x: s, y: -h }, { x: -s, y: -h } // Bottom side points
                ],
                faces: [
                    { verts: [0, 2, 1], color: "#E47A55", parent: null, hinge: null, angle: 0 },
                    { verts: [2, 8, 1], color: "#A4C6E3", parent: 0, hinge: [2, 1], angle: Math.acos(-1/3) },
                    { verts: [0, 4, 2], color: "#F3C76E", parent: 0, hinge: [0, 2], angle: Math.acos(-1/3) },
                    { verts: [0, 1, 3], color: "#8B9E62", parent: 0, hinge: [0, 1], angle: Math.acos(-1/3) },
                    { verts: [2, 4, 8], color: "#C78283", parent: 2, hinge: [4, 2], angle: Math.acos(-1/3) },
                    { verts: [1, 8, 7], color: "#B1A296", parent: 1, hinge: [8, 1], angle: Math.acos(-1/3) },
                    { verts: [1, 7, 3], color: "#7395AE", parent: 3, hinge: [1, 3], angle: Math.acos(-1/3) },
                    { verts: [2, 8, 4], color: "#557A95", parent: 4, hinge: [2,4], angle: 0 } // This is complex, let's simplify the net
                ]
            });

            // Simplified Octahedron Net
            const s_octa = 1.5;
            const h_octa = s_octa * Math.sqrt(3) / 2;
            const octa_angle = Math.acos(-1/3);
            solidsData[2] = {
                name: "Octahedron",
                vertices: [
                    { x: 0, y: h_octa }, { x: -s_octa / 2, y: 0 }, { x: s_octa / 2, y: 0 }, // Central triangle
                    { x: s_octa * 1.5, y: 0 }, // Right point
                    { x: -s_octa * 1.5, y: 0 }, // Left point
                    { x: s_octa, y: -h_octa }, { x: -s_octa, y: -h_octa }, // Lower mid
                    { x: 0, y: -2*h_octa } // Bottom
                ],
                faces: [
                    { verts: [0, 1, 2], color: "#E47A55", parent: null, hinge: null, angle: 0 },
                    { verts: [1, 2, 6, 5], color: "#A4C6E3", parent: 0, hinge: [1, 2], angle: octa_angle, custom_verts: [{x:-s_octa/2, y:0},{x:s_octa/2, y:0},{x:s_octa,y:-h_octa},{x:-s_octa,y:-h_octa}] },
                    { verts: [2, 3, 5], color: "#F3C76E", parent: 1, hinge: [2, 5], angle: octa_angle },
                    { verts: [1, 6, 4], color: "#8B9E62", parent: 1, hinge: [1, 6], angle: octa_angle },
                    { verts: [5, 6, 7], color: "#C78283", parent: 1, hinge: [6, 5], angle: -octa_angle },
                    { verts: [0, 2, 3], color: "#B1A296", parent: 0, hinge: [0, 2], angle: octa_angle },
                    { verts: [0, 4, 1], color: "#7395AE", parent: 0, hinge: [0, 1], angle: octa_angle },
                    { verts: [4, 6, 7], color: "#557A95", parent: 3, hinge: [4, 6], angle: octa_angle },
                 ]
            };
        })();


        function init() {
            // --- Scene Setup ---
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xf0f0f0);

            // --- Camera ---
            const container = document.getElementById('canvas-container');
            camera = new THREE.PerspectiveCamera(50, container.clientWidth / container.clientHeight, 0.1, 100);
            camera.position.set(0, 0, 10);

            // --- Renderer ---
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(container.clientWidth, container.clientHeight);
            renderer.setPixelRatio(window.devicePixelRatio);
            container.appendChild(renderer.domElement);

            // --- Lighting ---
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.8);
            scene.add(ambientLight);
            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.7);
            directionalLight.position.set(5, 10, 7.5);
            scene.add(directionalLight);

            // --- Controls ---
            controls = new OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.05;

            // --- Event Listeners ---
            document.getElementById('slider-fold').addEventListener('input', onSliderChange);
            document.getElementById('btn-reset-view').addEventListener('click', resetView);

            document.querySelectorAll('.solid-btn').forEach(btn => {
                btn.addEventListener('click', (e) => selectSolid(e.target));
            });

            window.addEventListener('resize', onWindowResize);

            // --- Initial State ---
            createSolid(0);
            animate();
        }

        function createSolid(solidIndex) {
            if (solidGroup) {
                scene.remove(solidGroup);
            }
            solidGroup = new THREE.Group();
            scene.add(solidGroup);

            pivots = [];
            faces = [];
            const faceObjects = []; // To store { mesh, pivot } for parenting

            const data = solidsData[solidIndex];
            const vertices = (data.drawVertices || data.vertices).map(v => new THREE.Vector3(v.x, v.y, 0));

            data.faces.forEach((faceData, i) => {
                const shape = new THREE.Shape();
                const faceVerts = faceData.verts.map(vIdx => vertices[vIdx]);

                shape.moveTo(faceVerts[0].x, faceVerts[0].y);
                for(let j = 1; j < faceVerts.length; j++) {
                    shape.lineTo(faceVerts[j].x, faceVerts[j].y);
                }

                const geometry = new THREE.ShapeGeometry(shape);
                const material = new THREE.MeshStandardMaterial({
                    color: UNFOLDED_COLOR,
                    side: THREE.DoubleSide,
                    flatShading: true,
                    metalness: 0.1,
                    roughness: 0.8
                });
                material.userData = { foldedColor: new THREE.Color(faceData.color) };

                const mesh = new THREE.Mesh(geometry, material);
                mesh.userData.isFace = true;
                faces.push(mesh);

                if (faceData.parent === null) {
                    solidGroup.add(mesh);
                    faceObjects[i] = { mesh: mesh, pivot: null };
                } else {
                    const parentObj = faceObjects[faceData.parent];
                    const parentMesh = parentObj.mesh;

                    const hingeIndices = faceData.hinge;
                    const v1 = vertices[hingeIndices[0]];
                    const v2 = vertices[hingeIndices[1]];

                    const pivot = new THREE.Object3D();
                    pivots.push({ pivot: pivot, angle: faceData.angle });

                    // 1. Position pivot at hinge midpoint in parent's local space
                    const hingeMidpoint = new THREE.Vector3().addVectors(v1, v2).multiplyScalar(0.5);
                    pivot.position.copy(hingeMidpoint);

                    // 2. Align pivot's Z-axis with the hinge direction
                    const hingeDir = new THREE.Vector3().subVectors(v2, v1);
                    pivot.rotation.z = Math.atan2(hingeDir.y, hingeDir.x);

                    // 3. Translate the child mesh's geometry so its hinge is at its local origin
                    geometry.translate(-hingeMidpoint.x, -hingeMidpoint.y, 0);

                    // 4. Parent accordingly
                    pivot.add(mesh);
                    parentMesh.add(pivot);
                    faceObjects[i] = { mesh: mesh, pivot: pivot };
                }
            });

            // Center the final group
            const box = new THREE.Box3().setFromObject(solidGroup);
            const center = box.getCenter(new THREE.Vector3());
            solidGroup.position.sub(center);

            // Apply initial slider value
            updateFold(parseFloat(document.getElementById('slider-fold').value));
        }

        function updateFold(t) {
            pivots.forEach(p => {
                p.pivot.rotation.y = t * p.angle;
            });

            faces.forEach(face => {
                face.material.color.lerpColors(UNFOLDED_COLOR, face.material.userData.foldedColor, t);
            });
        }

        function selectSolid(clickedButton) {
            document.querySelectorAll('.solid-btn').forEach(btn => btn.classList.remove('active'));
            clickedButton.classList.add('active');

            let solidIndex = 0;
            if (clickedButton.id === 'btn-solid-2') solidIndex = 1;
            if (clickedButton.id === 'btn-solid-3') solidIndex = 2;

            createSolid(solidIndex);
            resetView();
        }

        function onSliderChange(e) {
            const t = parseFloat(e.target.value);
            updateFold(t);
        }

        function resetView() {
            controls.reset();

            // Optional: A more robust centering
            const box = new THREE.Box3().setFromObject(solidGroup);
            const size = box.getSize(new THREE.Vector3()).length();
            const center = box.getCenter(new THREE.Vector3());

            camera.position.copy(center);
            camera.position.x += size / 2;
            camera.position.y += size / 2;
            camera.position.z += size;
            camera.lookAt(center);

            controls.target.copy(center);
            controls.update();
        }

        function onWindowResize() {
            const container = document.getElementById('canvas-container');
            camera.aspect = container.clientWidth / container.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(container.clientWidth, container.clientHeight);
        }

        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        init();

    </script>
</body>
</html>
