        </div>
    </div>
    <div id="visualization-container">
        <canvas id="ternary-plot-canvas" width="600" height="550"></canvas>
    </div>
</div>

<script>
const sketch = (p) => {
    // DOM Elements
    let sliderAC, sliderBC;
    let valueAC, valueBC;
    let btnReset;

    // Plotting constants
    const N_DIVISIONS = 10;
    const MAX_F = 2.0; // Normalization factor for the visualized function

    // Main triangle vertex coordinates
    let vA, vB, vC;

    p.setup = () => {
        // Correctly associate p5 with the existing canvas element
        const canvasElement = document.getElementById('ternary-plot-canvas');
        p.createCanvas(canvasElement.width, canvasElement.height, p.P2D, canvasElement);

        // Get DOM elements
        sliderAC = p.select('#slider-ac');
        sliderBC = p.select('#slider-bc');
        valueAC = p.select('#value-ac');
        valueBC = p.select('#value-bc');
        btnReset = p.select('#btn-reset');

        // Setup event listeners
        sliderAC.input(handleSliderChange);
        sliderBC.input(handleSliderChange);
        btnReset.mousePressed(resetToDefaults);

        // Define main triangle vertices based on canvas dimensions
        const side = p.width - 100;
        const height = side * Math.sqrt(3) / 2;
        const topMargin = (p.height - height) / 2 - 10;
        const leftMargin = (p.width - side) / 2;

        vA = p.createVector(p.width / 2, topMargin); // Top vertex (Pure A)
        vB = p.createVector(leftMargin, topMargin + height); // Bottom-left vertex (Pure B)
        vC = p.createVector(leftMargin + side, topMargin + height); // Bottom-right vertex (Pure C)

        p.noLoop(); // Redraw only on user input
        handleSliderChange(); // Initial draw
    };

    function handleSliderChange() {
        valueAC.html(parseFloat(sliderAC.value()).toFixed(2));
        valueBC.html(parseFloat(sliderBC.value()).toFixed(2));
        p.redraw();
    }

    function resetToDefaults() {
        sliderAC.value(0.6);
        sliderBC.value(3.93);
        handleSliderChange();
    }

    p.draw = () => {
        p.background(255);

        const alpha_ac = parseFloat(sliderAC.value());
        const alpha_bc = parseFloat(sliderBC.value());

        drawTernaryGrid(alpha_ac, alpha_bc);
        drawAxesAndLabels();
    };

    // --- Helper Functions ---

    /**
     * Converts barycentric coordinates to Cartesian coordinates on the canvas.
     * @param {object} bary - An object with x1, x2, x3 properties (mole fractions for A, B, C).
     * @returns {p5.Vector} A vector with the (x, y) canvas coordinates.
     */
    function baryToCartesian(bary) {
        // P = x1*A + x2*B + x3*C
        const p1 = p5.Vector.mult(vA, bary.x1);
        const p2 = p5.Vector.mult(vB, bary.x2);
        const p3 = p5.Vector.mult(vC, bary.x3);
        return p5.Vector.add(p1, p5.Vector.add(p2, p3));
    }

    /**
     * Calculates the color based on the function value F.
     * @param {number} value - The normalized function value [0, 1].
     * @returns {p5.Color} The interpolated color.
     */
    function getColor(value) {
        const v = p.constrain(value, 0, 1);
        const c1 = p.color('#2c7bb6'); // 0.0: Dark Blue
        const c2 = p.color('#abd9e9'); // 0.25: Light Blue
        const c3 = p.color('#ffffbf'); // 0.5: Green-Yellow
        const c4 = p.color('#fdae61'); // 0.75: Orange
        const c5 = p.color('#d7191c'); // 1.0: Red

        if (v < 0.25) return p.lerpColor(c1, c2, v / 0.25);
        if (v < 0.5) return p.lerpColor(c2, c3, (v - 0.25) / 0.25);
        if (v < 0.75) return p.lerpColor(c3, c4, (v - 0.5) / 0.25);
        return p.lerpColor(c4, c5, (v - 0.75) / 0.25);
    }

    /**
     * Main function to draw the colored grid of triangles.
     */
    function drawTernaryGrid(alpha13, alpha23) {
        const alpha = { a1: alpha13, a2: alpha23, a3: 1.0 };
        p.strokeWeight(0.5);
        p.stroke(0, 0, 0, 50); // Thin, semi-transparent black lines

        for (let i = 0; i < N_DIVISIONS; i++) {
            for (let j = 0; j < N_DIVISIONS - i; j++) {
                // Barycentric coordinates of the vertices of the small grid cells
                const p1 = { x1: 1 - (i + j) / N_DIVISIONS, x2: i / N_DIVISIONS, x3: j / N_DIVISIONS };
                const p2 = { x1: 1 - (i + 1 + j) / N_DIVISIONS, x2: (i + 1) / N_DIVISIONS, x3: j / N_DIVISIONS };
                const p3 = { x1: 1 - (i + j + 1) / N_DIVISIONS, x2: i / N_DIVISIONS, x3: (j + 1) / N_DIVISIONS };
                const p4 = { x1: 1 - (i + 1 + j + 1) / N_DIVISIONS, x2: (i + 1) / N_DIVISIONS, x3: (j + 1) / N_DIVISIONS };

                // Upward-pointing triangle
                drawSmallTriangle([p1, p2, p3], alpha);

                // Downward-pointing triangle
                if (i + j < N_DIVISIONS - 1) {
                    drawSmallTriangle([p2, p4, p3], alpha);
                }
            }
        }
    }

    /**
     * Calculates color and draws a single small triangle.
     */
    function drawSmallTriangle(verticesBary, alpha) {
        // Calculate centroid in barycentric coordinates
        const centroid = {
            x1: (verticesBary[0].x1 + verticesBary[1].x1 + verticesBary[2].x1) / 3,
            x2: (verticesBary[0].x2 + verticesBary[1].x2 + verticesBary[2].x2) / 3,
            x3: (verticesBary[0].x3 + verticesBary[1].x3 + verticesBary[2].x3) / 3,
        };

        // Calculate vapor phase composition (y) from liquid phase (x=centroid)
        const D = alpha.a1 * centroid.x1 + alpha.a2 * centroid.x2 + alpha.a3 * centroid.x3;
        const vapor = {
            y1: (alpha.a1 * centroid.x1) / D,
            y2: (alpha.a2 * centroid.x2) / D,
            y3: (alpha.a3 * centroid.x3) / D,
        };

        // Calculate the function F to be visualized
        const F = p.pow(centroid.x1 - vapor.y1, 2) + p.pow(centroid.x2 - vapor.y2, 2) + p.pow(centroid.x3 - vapor.y3, 2);

        // Normalize and get color
        const normalizedF = F / MAX_F;
        const triColor = getColor(normalizedF);
        p.fill(triColor);

        // Convert vertices to Cartesian and draw
        const v_cart = verticesBary.map(b => baryToCartesian(b));
        p.triangle(v_cart[0].x, v_cart[0].y, v_cart[1].x, v_cart[1].y, v_cart[2].x, v_cart[2].y);
    }

    /**
     * Draws the main triangle border, labels, and tick marks.
     */
    function drawAxesAndLabels() {
        p.noFill();
        p.stroke(0);
        p.strokeWeight(1.5);
        p.triangle(vA.x, vA.y, vB.x, vB.y, vC.x, vC.y);

        p.fill(0);
        p.noStroke();
        p.textSize(16);
        p.textAlign(p.CENTER, p.BOTTOM);
        p.text('A', vA.x, vA.y - 10);
        p.textAlign(p.RIGHT, p.CENTER);
        p.text('B', vB.x - 10, vB.y);
        p.textAlign(p.LEFT, p.CENTER);
        p.text('C', vC.x + 10, vC.y);

        // Draw tick marks
        p.stroke(0);
        p.strokeWeight(1);
        p.textSize(10);
        for (let i = 1; i < 10; i++) {
            const t = i / 10;

            // Axis B (A-B side)
            let tickPosB = baryToCartesian({ x1: 1 - t, x2: t, x3: 0 });
            p.textAlign(p.RIGHT, p.CENTER);
            p.text((t).toFixed(1), tickPosB.x - 5, tickPosB.y);

            // Axis C (A-C side)
            let tickPosC = baryToCartesian({ x1: 1 - t, x2: 0, x3: t });
            p.textAlign(p.LEFT, p.CENTER);
            p.text((t).toFixed(1), tickPosC.x + 5, tickPosC.y);

            // Axis BC (B-C side)
            let tickPosBC = baryToCartesian({ x1: 0, x2: 1 - t, x3: t });
            p.textAlign(p.CENTER, p.TOP);
            p.text((t).toFixed(1), tickPosBC.x, tickPosBC.y + 5);
        }
    }
};

new p5(sketch);
</script>

