    <script>
        // Get references to all interactive DOM elements
        const sliderWso = document.getElementById('slider-wso');
        const valueWso = document.getElementById('value-wso');
        const sliderN = document.getElementById('slider-n');
        const valueN = document.getElementById('value-n');
        const sliderBx = document.getElementById('slider-bx');
        const valueBx = document.getElementById('value-bx');
        const sliderBy = document.getElementById('slider-by');
        const valueBy = document.getElementById('value-by');
        const sliderBz = document.getElementById('slider-bz');
        const valueBz = document.getElementById('value-bz');

        // Group controls for easier initialization
        const controls = [
            { slider: sliderWso, display: valueWso, precision: 1 },
            { slider: sliderN,   display: valueN,   precision: 0 },
            { slider: sliderBx,  display: valueBx,  precision: 1 },
            { slider: sliderBy,  display: valueBy,  precision: 1 },
            { slider: sliderBz,  display: valueBz,  precision: 1 },
        ];

        function updatePlot() {
            // 1. Read current values from sliders
            const wso = parseFloat(sliderWso.value);
            const n = parseInt(sliderN.value, 10);
            const BX = parseFloat(sliderBx.value);
            const BY = parseFloat(sliderBy.value);
            const BZ = parseFloat(sliderBz.value);

            // 2. Define a range for the k-axis
            const k_values = [];
            for (let k = -5; k <= 5.01; k += 0.1) {
                k_values.push(k);
            }

            // 3. Calculate the two energy bands
            const E_plus_data = [];
            const E_minus_data = [];

            k_values.forEach(k => {
                const E_common = (2 * n + 1) + k * k;
                const term_inside_sqrt = Math.pow(BZ + wso * k, 2) + Math.pow(BX, 2) + Math.pow(BY, 2);
                const split_term = Math.sqrt(term_inside_sqrt);

                E_plus_data.push(E_common + split_term);
                E_minus_data.push(E_common - split_term);
            });

            // 4. Determine plotting logic (one or two traces)
            const traces = [];
            const isDegenerate = wso === 0 && BX === 0 && BY === 0 && BZ === 0;

            if (isDegenerate) {
                traces.push({
                    x: k_values,
                    y: E_plus_data,
                    mode: 'lines',
                    name: 'Degenerate Band',
                    line: { color: 'red', dash: 'solid', width: 2 }
                });
            } else {
                traces.push({
                    x: k_values,
                    y: E_plus_data,
                    mode: 'lines',
                    name: 'E<sub>+</sub>',
                    line: { color: 'red', dash: 'solid', width: 2 }
                });
                traces.push({
                    x: k_values,
                    y: E_minus_data,
                    mode: 'lines',
                    name: 'E<sub>-</sub>',
                    line: { color: 'red', dash: 'dash', width: 2 }
                });
            }

            // 5. Create a dynamic title showing non-zero parameters
            let titleParts = [`n = ${n}`];
            if (wso !== 0) titleParts.push(`wso = ${wso.toFixed(1)}`);
            if (BX !== 0) titleParts.push(`BX = ${BX.toFixed(1)}`);
            if (BY !== 0) titleParts.push(`BY = ${BY.toFixed(1)}`);
            if (BZ !== 0) titleParts.push(`BZ = ${BZ.toFixed(1)}`);
            const plotTitle = titleParts.join(', ');

            // 6. Define the plot layout
            const layout = {
                title: {
                    text: plotTitle,
                    font: { size: 16 }
                },
                xaxis: {
                    title: 'normalized k length (lw<sub>ky</sub>)',
                    range: [-5, 5],
                    zeroline: true,
                    zerolinewidth: 1,
                    zerolinecolor: '#969696'
                },
                yaxis: {
                    title: '2 E / &#x210F;&omega;',
                    range: [0, 40],
                    zeroline: true,
                    zerolinewidth: 1,
                    zerolinecolor: '#969696'
                },
                margin: { t: 50, b: 60, l: 70, r: 30 },
                legend: { x: 0.05, y: 0.95 }
            };

            // 7. Update the plot using Plotly.react for efficiency
            Plotly.react('plot-container', traces, layout);
        }

        // Set up initial state and event listeners on page load
        function initialize() {
            controls.forEach(control => {
                // Update the display span with the initial value
                control.display.textContent = parseFloat(control.slider.value).toFixed(control.precision);

                // Add an 'input' event listener to each slider
                control.slider.addEventListener('input', (e) => {
                    control.display.textContent = parseFloat(e.target.value).toFixed(control.precision);
                    updatePlot();
                });
            });
            // Draw the initial plot
            updatePlot();
        }

        // Run the initialization function when the window loads
        window.onload = initialize;
    </script>

</body>
</html>
