                </div>
                <div class="slider-group">
                    <h4>UH Streamflow Rates</h4>
                    <div class="slider-row">
                        <label for="slider-uh-1">hour 1</label>
                        <input type="range" id="slider-uh-1" min="0" max="10" step="1" value="1">
                        <span id="value-uh-1">1</span>
                    </div>
                    <div class="slider-row">
                        <label for="slider-uh-2">hour 2</label>
                        <input type="range" id="slider-uh-2" min="0" max="10" step="1" value="5">
                        <span id="value-uh-2">5</span>
                    </div>
                    <div class="slider-row">
                        <label for="slider-uh-3">hour 3</label>
                        <input type="range" id="slider-uh-3" min="0" max="10" step="1" value="3">
                        <span id="value-uh-3">3</span>
                    </div>
                    <div class="slider-row">
                        <label for="slider-uh-4">hour 4</label>
                        <input type="range" id="slider-uh-4" min="0" max="10" step="1" value="1">
                        <span id="value-uh-4">1</span>
                    </div>
                </div>
            </div>
        </div>

        <div id="plots-container">
            <div id="plot-canvas-1" class="plot-wrapper"></div>
            <div id="plot-canvas-2" class="plot-wrapper"></div>
            <div id="plot-canvas-3" class="plot-wrapper"></div>
            <div id="plot-canvas-4" class="plot-wrapper"></div>
            <div id="plot-canvas-5" class="plot-wrapper"></div>
            <div id="plot-canvas-6" class="plot-wrapper"></div>
        </div>
    </div>

    <script>
        document.addEventListener('DOMContentLoaded', () => {
            // --- STATE & DOM ELEMENTS ---
            const sliders = {
                precip: [
                    document.getElementById('slider-precip-1'),
                    document.getElementById('slider-precip-2'),
                    document.getElementById('slider-precip-3')
                ],
                uh: [
                    document.getElementById('slider-uh-1'),
                    document.getElementById('slider-uh-2'),
                    document.getElementById('slider-uh-3'),
                    document.getElementById('slider-uh-4')
                ]
            };

            const values = {
                precip: [
                    document.getElementById('value-precip-1'),
                    document.getElementById('value-precip-2'),
                    document.getElementById('value-precip-3')
                ],
                uh: [
                    document.getElementById('value-uh-1'),
                    document.getElementById('value-uh-2'),
                    document.getElementById('value-uh-3'),
                    document.getElementById('value-uh-4')
                ]
            };

            const toggleBtn = document.getElementById('toggle-controls');
            const slidersArea = document.getElementById('sliders-area');

            let P = [1, 0, 0]; // Precipitation data
            let U = [1, 5, 3, 1]; // Unit Hydrograph data
            let C1 = [], C2 = [], C3 = []; // Component hydrographs
            let Q = []; // Composite hydrograph

            let p5Instances = [];

            // --- INTERACTION LOGIC ---

            // Toggle controls visibility
            toggleBtn.addEventListener('click', () => {
                const isVisible = slidersArea.style.display !== 'none';
                slidersArea.style.display = isVisible ? 'none' : 'grid';
                toggleBtn.textContent = isVisible ? '+' : '−';
            });
            // Set initial state for toggle button
            slidersArea.style.display = 'grid';
            toggleBtn.textContent = '−';


            // Attach listeners to all sliders
            [...sliders.precip, ...sliders.uh].forEach(slider => {
                slider.addEventListener('input', update);
            });

            // --- CALCULATION & UPDATE ---
            function update() {
                // 1. Read slider values into P and U arrays
                P = sliders.precip.map((s, i) => {
                    const val = parseInt(s.value);
                    values.precip[i].textContent = val;
                    return val;
                });
                U = sliders.uh.map((s, i) => {
                    const val = parseInt(s.value);
                    values.uh[i].textContent = val;
                    return val;
                });

                // 2. Perform convolution calculation
                // a. Component hydrographs
                C1 = U.map(u_val => P[0] * u_val);
                C2 = U.map(u_val => P[1] * u_val);
                C3 = U.map(u_val => P[2] * u_val);

                // b. Composite hydrograph (using convolution sum)
                const Q_len = P.length + U.length - 1;
                Q = Array(Q_len).fill(0);
                for (let n = 0; n < Q_len; n++) {
                    for (let k = 0; k < P.length; k++) {
                        if (n - k >= 0 && n - k < U.length) {
                            Q[n] += P[k] * U[n - k];
                        }
                    }
                }

                // 3. Redraw all plots
                p5Instances.forEach(p => p.redraw());
            }

            // --- VISUALIZATION ---

            /**
             * Generic function to draw a step chart on a p5 canvas.
             * @param {object} p - The p5 instance.
             * @param {number[]} data - The data array to plot.
             * @param {string} title - The plot title.
             * @param {string} xLabel - The x-axis label.
             * @param {string} yLabel - The y-axis label.
             * @param {number} xMax - The maximum value for the x-axis.
             * @param {number} yMax - The maximum value for the y-axis.
             * @param {number} xTickStep - The interval for x-axis ticks.
             * @param {number} yTickStep - The interval for y-axis ticks.
             * @param {number} timeOffset - The horizontal shift for the data.
             */
            const drawPlot = (p, data, title, xLabel, yLabel, xMax, yMax, xTickStep, yTickStep, timeOffset = 0) => {
                const margin = { top: 30, right: 20, bottom: 40, left: 50 };
                const plotWidth = p.width - margin.left - margin.right;
                const plotHeight = p.height - margin.top - margin.bottom;

                const mapX = (val) => p.map(val, 0, xMax, 0, plotWidth);
                const mapY = (val) => p.map(val, 0, yMax, plotHeight, 0);

                p.background(255);
                p.translate(margin.left, margin.top);

                // Draw Plot Title
                p.fill(0);
                p.noStroke();
                p.textAlign(p.CENTER, p.CENTER);
                p.textSize(14);
                p.text(title, plotWidth / 2, -margin.top / 2 - 5);


                // Draw Axes
                p.stroke(0);
                p.strokeWeight(1);
                p.line(0, 0, 0, plotHeight); // Y-axis
                p.line(0, plotHeight, plotWidth, plotHeight); // X-axis

                // Draw Ticks and Labels
                p.fill(0);
                p.noStroke();
                p.textAlign(p.CENTER, p.TOP);
                p.textSize(10);
                // X-axis ticks
                for (let i = 0; i <= xMax; i += xTickStep) {
                    const x = mapX(i);
                    p.stroke(0);
                    p.line(x, plotHeight, x, plotHeight + 5);
                    p.noStroke();
                    p.text(i, x, plotHeight + 10);
                }
                p.text(xLabel, plotWidth / 2, plotHeight + 30);

                // Y-axis ticks
                p.textAlign(p.RIGHT, p.CENTER);
                for (let i = 0; i <= yMax; i += yTickStep) {
                    const y = mapY(i);
                    p.stroke(0);
                    p.line(-5, y, 0, y);
                    p.noStroke();
                    p.text(i, -10, y);
                }
                p.push();
                p.translate(-margin.left + 15, plotHeight / 2);
                p.rotate(-p.HALF_PI);
                p.textAlign(p.CENTER, p.CENTER);
                p.text(yLabel, 0, 0);
                p.pop();

                // Draw Step Chart
                p.fill(212, 230, 241, 150); // light blue fill with some transparency
                p.stroke(46, 134, 193); // darker blue stroke
                p.strokeWeight(2);

                p.beginShape();
                p.vertex(mapX(timeOffset), mapY(0));
                for (let i = 0; i < data.length; i++) {
                    const x1 = i + timeOffset;
                    const x2 = i + 1 + timeOffset;
                    const y = data[i];
                    p.vertex(mapX(x1), mapY(y));
                    p.vertex(mapX(x2), mapY(y));
                }
                p.vertex(mapX(data.length + timeOffset), mapY(0));
                p.endShape(p.CLOSE);
            };

            // --- p5.js INSTANCES ---

            const sketchDefinitions = [
                { id: 'plot-canvas-1', getData: () => P, title: 'Precipitation Hyetograph', xLabel: 't (hr)', yLabel: 'P (in)', xMax: 5, yMax: 10, xTick: 1, yTick: 2, offset: 0 },
                { id: 'plot-canvas-2', getData: () => U, title: 'Unit Hydrograph', xLabel: 't (hr)', yLabel: 'Q (cfs/in)', xMax: 5, yMax: 10, xTick: 1, yTick: 2, offset: 0 },
                { id: 'plot-canvas-3', getData: () => C1, title: 'Component Hydrograph 1', xLabel: 't (hr)', yLabel: 'Q (cfs)', xMax: 8, yMax: 100, xTick: 2, yTick: 20, offset: 0 },
                { id: 'plot-canvas-4', getData: () => C2, title: 'Component Hydrograph 2', xLabel: 't (hr)', yLabel: 'Q (cfs)', xMax: 8, yMax: 100, xTick: 2, yTick: 20, offset: 1 },
                { id: 'plot-canvas-5', getData: () => C3, title: 'Component Hydrograph 3', xLabel: 't (hr)', yLabel: 'Q (cfs)', xMax: 8, yMax: 100, xTick: 2, yTick: 20, offset: 2 },
                { id: 'plot-canvas-6', getData: () => Q, title: 'Composite Hydrograph', xLabel: 't (hr)', yLabel: 'Q (cfs)', xMax: 8, yMax: 100, xTick: 2, yTick: 20, offset: 0 },
            ];

            sketchDefinitions.forEach(def => {
                const sketch = (p) => {
                    p.setup = () => {
                        const parent = document.getElementById(def.id);
                        const canvas = p.createCanvas(parent.clientWidth, 300);
                        canvas.parent(def.id);
                        p.noLoop();
                        window.addEventListener('resize', () => {
                           p.resizeCanvas(parent.clientWidth, 300);
                           p.redraw();
                        });
                    };

                    p.draw = () => {
                        drawPlot(p, def.getData(), def.title, def.xLabel, def.yLabel, def.xMax, def.yMax, def.xTick, def.yTick, def.offset);
                    };
                };
                p5Instances.push(new p5(sketch));
            });

            // Initial calculation and draw
            update();
        });
    </script>

</body>
</html>
