<script>
document.addEventListener('DOMContentLoaded', () => {

    const state = {
        a11: -5, a12: -3, a21: -1, a22: -4,
        b11: -5, b12: -3, b21: -1, b22: -4
    };

    // --- HELPER FUNCTIONS ---

    function createSlider(containerId, id, label, value) {
        const container = document.getElementById(containerId);
        const controlDiv = document.createElement('div');
        controlDiv.className = 'slider-control';

        controlDiv.innerHTML = `
            <label for="slider-${id}">\\(${label}\\)</label>
            <button id="btn-minus-${id}">-</button>
            <input type="range" id="slider-${id}" min="-10" max="10" step="1" value="${value}">
            <button id="btn-plus-${id}">+</button>
            <span id="value-${id}">${value}</span>
        `;
        container.appendChild(controlDiv);
    }

    function gcd(a, b) {
        return b ? gcd(b, a % b) : a;
    }

    function toFraction(decimal, tolerance = 1.0E-6) {
        if (Math.abs(decimal - Math.round(decimal)) < tolerance) {
            return `${Math.round(decimal)}`;
        }
        let h1 = 1, h2 = 0, k1 = 0, k2 = 1;
        let b = decimal;
        do {
            let a = Math.floor(b);
            let aux = h1; h1 = a * h1 + h2; h2 = aux;
            aux = k1; k1 = a * k1 + k2; k2 = aux;
            b = 1 / (b - a);
        } while (Math.abs(decimal - h1 / k1) > decimal * tolerance);

        if (k1 > 1000) return decimal.toFixed(3); // Avoid huge denominators

        return `\\frac{${h1}}{${k1}}`;
    }

    // --- UI UPDATE FUNCTIONS ---

    function updatePayoffTable() {
        for (const key in state) {
            const cell = document.getElementById(`cell-${key}`);
            if (cell) cell.textContent = state[key];
        }
    }

    function updateAll() {
        updatePayoffTable();
        if (window.p5Sketch) {
            window.p5Sketch.redraw();
        }
    }

    // --- SETUP UI CONTROLS ---

    const slidersConfig = [
        { container: 'matrix-a-controls', id: 'a11', label: 'a_{11}', value: state.a11 },
        { container: 'matrix-a-controls', id: 'a12', label: 'a_{12}', value: state.a12 },
        { container: 'matrix-a-controls', id: 'a21', label: 'a_{21}', value: state.a21 },
        { container: 'matrix-a-controls', id: 'a22', label: 'a_{22}', value: state.a22 },
        { container: 'matrix-b-controls', id: 'b11', label: 'b_{11}', value: state.b11 },
        { container: 'matrix-b-controls', id: 'b12', label: 'b_{12}', value: state.b12 },
        { container: 'matrix-b-controls', id: 'b21', label: 'b_{21}', value: state.b21 },
        { container: 'matrix-b-controls', id: 'b22', label: 'b_{22}', value: state.b22 },
    ];

    slidersConfig.forEach(config => {
        createSlider(config.container, config.id, config.label, config.value);
    });

    // --- SETUP EVENT LISTENERS ---

    slidersConfig.forEach(({ id }) => {
        const slider = document.getElementById(`slider-${id}`);
        const valueSpan = document.getElementById(`value-${id}`);
        const btnMinus = document.getElementById(`btn-minus-${id}`);
        const btnPlus = document.getElementById(`btn-plus-${id}`);

        const updateFromSlider = () => {
            const val = parseInt(slider.value);
            state[id] = val;
            valueSpan.textContent = val;
            updateAll();
        };

        slider.addEventListener('input', updateFromSlider);

        btnMinus.addEventListener('click', () => {
            slider.stepDown();
            updateFromSlider();
        });

        btnPlus.addEventListener('click', () => {
            slider.stepUp();
            updateFromSlider();
        });
    });


    // --- P5.JS SKETCH ---

    const sketch = (p) => {
        const margin = 40;
        let canvasSize;
        const epsilon = 1e-9;

        p.setup = () => {
            const container = document.getElementById('canvas-container');
            const containerSize = Math.min(container.clientWidth, container.clientHeight);
            canvasSize = containerSize > 0 ? containerSize - 20 : 400;
            let canvas = p.createCanvas(canvasSize, canvasSize);
            canvas.parent('canvas-container');
            p.noLoop(); // We will redraw manually
            updateAll(); // Initial draw
        };

        p.draw = () => {
            p.background(255);
            p.push();

            // Map logical coords [0,1] to canvas pixels with margin
            p.translate(margin, canvasSize - margin);
            p.scale(1, -1);
            const plotSize = canvasSize - 2 * margin;
            p.scale(plotSize, plotSize);

            drawAxesAndGrid(p);

            const br1 = getPlayer1BestResponse();
            const br2 = getPlayer2BestResponse();

            drawBestResponse(p, br1, p.color(0, 0, 255)); // Blue for Player 1
            drawBestResponse(p, br2, p.color(0, 128, 0)); // Green for Player 2

            const { segments, vertices } = findIntersections(br1, br2);
            drawIntersections(p, segments, vertices);

            p.pop(); // Restore original transformation matrix

            updateEquilibriaDisplay(vertices);
        };

        function drawAxesAndGrid(p) {
            p.stroke(200);
            p.strokeWeight(1 / (canvasSize - 2 * margin));

            // Grid lines
            for (let i = 0.2; i < 1.0; i += 0.2) {
                p.line(0, i, 1, i);
                p.line(i, 0, i, 1);
            }

            p.stroke(0);
            p.strokeWeight(2 / (canvasSize - 2 * margin));
            // Axes
            p.line(0, 0, 1, 0); // x-axis
            p.line(0, 0, 0, 1); // y-axis

            // Ticks and labels
            p.push();
            p.scale(1, -1); // Flip text back
            p.textAlign(p.CENTER, p.TOP);
            p.noStroke();
            p.fill(0);
            const fontSize = 12 / (canvasSize - 2 * margin);
            p.textSize(fontSize);

            for (let i = 0; i <= 1; i += 0.2) {
                p.text(i.toFixed(1), i, 5 / (canvasSize - 2 * margin));
                if (i > 0) p.text(i.toFixed(1), -20 / (canvasSize - 2 * margin), -i);
            }
            p.textAlign(p.CENTER, p.CENTER);
            p.text("x₁", 1.05, 0);
            p.text("y₁", 0, -1.05);
            p.pop();
        }

        function getPlayer1BestResponse() {
            const { a11, a12, a21, a22 } = state;
            const segments = [];
            const den = (a11 - a12) - (a21 - a22);

            if (Math.abs(den) < epsilon) { // Dominant strategy
                if (a11 > a21) segments.push({p1: {x:1, y:0}, p2: {x:1, y:1}}); // Strat 1 dominant
                else if (a11 < a21) segments.push({p1: {x:0, y:0}, p2: {x:0, y:1}}); // Strat 2 dominant
                else segments.push({p1: {x:0, y:0}, p2: {x:1, y:1}}); // Indifferent
            } else {
                const y_indiff = (a22 - a12) / den;
                if (y_indiff > 0 && y_indiff < 1) {
                    segments.push({p1: {x:0, y:y_indiff}, p2: {x:1, y:y_indiff}});
                    if (den > 0) { // C-shape
                        segments.push({p1: {x:0, y:0}, p2: {x:0, y:y_indiff}});
                        segments.push({p1: {x:1, y:y_indiff}, p2: {x:1, y:1}});
                    } else { // Z-shape
                        segments.push({p1: {x:1, y:0}, p2: {x:1, y:y_indiff}});
                        segments.push({p1: {x:0, y:y_indiff}, p2: {x:0, y:1}});
                    }
                } else { // Dominant strategy in mixed extension
                    if ((den > 0 && y_indiff <= 0) || (den < 0 && y_indiff >= 1)) {
                         segments.push({p1: {x:1, y:0}, p2: {x:1, y:1}}); // Strat 1 is best response
                    } else {
                         segments.push({p1: {x:0, y:0}, p2: {x:0, y:1}}); // Strat 2 is best response
                    }
                }
            }
            return segments;
        }

        function getPlayer2BestResponse() {
            const { b11, b12, b21, b22 } = state;
            const segments = [];
            const den = (b11 - b21) - (b12 - b22);

            if (Math.abs(den) < epsilon) { // Dominant strategy
                if (b11 > b12) segments.push({p1: {x:0, y:1}, p2: {x:1, y:1}}); // Strat 1 dominant
                else if (b11 < b12) segments.push({p1: {x:0, y:0}, p2: {x:1, y:0}}); // Strat 2 dominant
                else segments.push({p1: {x:0, y:0}, p2: {x:1, y:1}}); // Indifferent
            } else {
                const x_indiff = (b22 - b21) / den;
                if (x_indiff > 0 && x_indiff < 1) {
                    segments.push({p1: {x:x_indiff, y:0}, p2: {x:x_indiff, y:1}});
                    if (den > 0) {
                        segments.push({p1: {x:0, y:1}, p2: {x:x_indiff, y:1}});
                        segments.push({p1: {x:x_indiff, y:0}, p2: {x:1, y:0}});
                    } else {
                        segments.push({p1: {x:0, y:0}, p2: {x:x_indiff, y:0}});
                        segments.push({p1: {x:x_indiff, y:1}, p2: {x:1, y:1}});
                    }
                } else { // Dominant strategy in mixed extension
                    if ((den > 0 && x_indiff <= 0) || (den < 0 && x_indiff >= 1)) {
                        segments.push({p1: {x:0, y:1}, p2: {x:1, y:1}}); // Strat 1 is best response
                    } else {
                        segments.push({p1: {x:0, y:0}, p2: {x:1, y:0}}); // Strat 2 is best response
                    }
                }
            }
            return segments;
        }

        function drawBestResponse(p, segments, color) {
            p.stroke(color);
            p.strokeWeight(2 / (canvasSize - 2 * margin));
            segments.forEach(seg => {
                p.line(seg.p1.x, seg.p1.y, seg.p2.x, seg.p2.y);
            });
        }

        function findIntersections(br1, br2) {
            const segments = [];
            const vertices = [];

            for (const s1 of br1) {
                for (const s2 of br2) {
                    const s1_is_vert = Math.abs(s1.p1.x - s1.p2.x) < epsilon;
                    const s2_is_vert = Math.abs(s2.p1.x - s2.p2.x) < epsilon;

                    if (s1_is_vert && s2_is_vert) { // Both vertical
                        if (Math.abs(s1.p1.x - s2.p1.x) < epsilon) { // Collinear
                            const y_min = Math.max(Math.min(s1.p1.y, s1.p2.y), Math.min(s2.p1.y, s2.p2.y));
                            const y_max = Math.min(Math.max(s1.p1.y, s1.p2.y), Math.max(s2.p1.y, s2.p2.y));
                            if (y_min <= y_max) segments.push({ p1: {x:s1.p1.x, y:y_min}, p2: {x:s1.p1.x, y:y_max} });
                        }
                    } else if (!s1_is_vert && !s2_is_vert) { // Both horizontal
                        if (Math.abs(s1.p1.y - s2.p1.y) < epsilon) { // Collinear
                            const x_min = Math.max(Math.min(s1.p1.x, s1.p2.x), Math.min(s2.p1.x, s2.p2.x));
                            const x_max = Math.min(Math.max(s1.p1.x, s1.p2.x), Math.max(s2.p1.x, s2.p2.x));
                            if (x_min <= x_max) segments.push({ p1: {x:x_min, y:s1.p1.y}, p2: {x:x_max, y:s1.p1.y} });
                        }
                    } else { // One vertical, one horizontal
                        const v_seg = s1_is_vert ? s1 : s2;
                        const h_seg = s1_is_vert ? s2 : s1;

                        const ix = v_seg.p1.x;
                        const iy = h_seg.p1.y;

                        if (ix >= Math.min(h_seg.p1.x, h_seg.p2.x) - epsilon && ix <= Math.max(h_seg.p1.x, h_seg.p2.x) + epsilon &&
                            iy >= Math.min(v_seg.p1.y, v_seg.p2.y) - epsilon && iy <= Math.max(v_seg.p1.y, v_seg.p2.y) + epsilon) {
                                segments.push({ p1: {x:ix, y:iy}, p2: {x:ix, y:iy} });
                        }
                    }
                }
            }

            // Collect unique vertices
            const vertexSet = new Set();
            segments.forEach(seg => {
                vertexSet.add(JSON.stringify({x: seg.p1.x, y: seg.p1.y}));
                vertexSet.add(JSON.stringify({x: seg.p2.x, y: seg.p2.y}));
            });
            Array.from(vertexSet).forEach(v => vertices.push(JSON.parse(v)));

            return { segments, vertices };
        }

        function drawIntersections(p, segments, vertices) {
            p.stroke(255, 0, 0); // Red
            p.strokeWeight(4 / (canvasSize - 2 * margin));
            segments.forEach(seg => {
                p.line(seg.p1.x, seg.p1.y, seg.p2.x, seg.p2.y);
            });

            p.noStroke();
            p.fill(255, 0, 0);
            vertices.forEach(v => {
                p.ellipse(v.x, v.y, 8 / plotSize, 8 / plotSize);
            });
        }

        function updateEquilibriaDisplay(vertices) {
            const display = document.getElementById('equilibria-display');
            if (vertices.length === 0) {
                display.innerHTML = "\\( \\emptyset \\)";
            } else {
                const uniquePoints = vertices.map(v => `(${toFraction(v.x)}, ${toFraction(v.y)})`).filter((v, i, a) => a.indexOf(v) === i);
                display.innerHTML = `\\( \\{ ${uniquePoints.join(', ')} \\} \\)`;
            }
            if (window.MathJax) {
                MathJax.typesetPromise([display]);
            }
        }

        // Expose p5 instance to window for global access
        window.p5Sketch = p;
    };

    // Initialize p5.js
    new p5(sketch);
});
</script>

</body>
</html>
