                    showFamily = checkboxFamily.checked();
                });
                btnReset.mousePressed(resetDemo);
            }

            function setupCoordinateSystem() {
                scaleX = p.width / (mathRangeX[1] - mathRangeX[0]);
                scaleY = p.height / (mathRangeY[1] - mathRangeY[0]);
                originX = p.width / 2;
                originY = p.height / 2;
            }

            function resetDemo() {
                sliderT.value(initialSliderT);
                inputT.value(initialSliderT);
                checkboxFamily.checked(initialCheckboxFamily);
                showFamily = initialCheckboxFamily;

                locators = initialLocators.map(pt => ({ x: pt.x, y: pt.y }));

                updateCalculations();
            }

            // --- COORDINATE MAPPING ---
            const mathToPixelX = (x) => x * scaleX;
            const mathToPixelY = (y) => -y * scaleY;
            const pixelToMathX = (px) => (px - originX) / scaleX;
            const pixelToMathY = (py) => -(py - originY) / scaleY;

            // --- CORE CALCULATION LOGIC ---
            function updateCalculations() {
                cubicCoeffs = solveCubicCoefficients(locators);
                const t = parseFloat(sliderT.value());
                quadCoeffs = getOsculatingQuadraticCoeffs(t, cubicCoeffs);
            }

            function solveCubicCoefficients(points) {
                // Solves the system Ax=b for the coefficients [a,b,c,d]
                // A is the Vandermonde matrix, b is the vector of y-values.
                let M = [];
                let y = [];
                for(let i=0; i<4; i++){
                    const pt = points[i];
                    M[i] = [p.pow(pt.x, 3), p.pow(pt.x, 2), pt.x, 1];
                    y[i] = pt.y;
                }

                // Using a simple Gaussian elimination for 4x4
                // Augmented matrix [M|y]
                for (let i = 0; i < 4; i++) {
                    M[i].push(y[i]);
                }

                // Forward elimination
                for (let i = 0; i < 4; i++) {
                    let maxRow = i;
                    for (let k = i + 1; k < 4; k++) {
                        if (p.abs(M[k][i]) > p.abs(M[maxRow][i])) {
                            maxRow = k;
                        }
                    }
                    [M[i], M[maxRow]] = [M[maxRow], M[i]]; // Swap rows

                    for (let k = i + 1; k < 4; k++) {
                        let factor = M[k][i] / M[i][i];
                        for (let j = i; j < 5; j++) {
                            M[k][j] -= factor * M[i][j];
                        }
                    }
                }

                // Back substitution
                let coeffs = new Array(4);
                for (let i = 3; i >= 0; i--) {
                    coeffs[i] = M[i][4];
                    for (let j = i + 1; j < 4; j++) {
                        coeffs[i] -= M[i][j] * coeffs[j];
                    }
                    coeffs[i] /= M[i][i];
                }

                return { a: coeffs[0], b: coeffs[1], c: coeffs[2], d: coeffs[3] };
            }

            function getOsculatingQuadraticCoeffs(t, cubic) {
                // f(x) = ax^3 + bx^2 + cx + d
                // g(x) = px^2 + qx + r
                // Matching value, first, and second derivatives at x=t gives:
                // g(t) = f(t)
                // g'(t) = f'(t)
                // g''(t) = f''(t)
                // Which simplifies to the coefficients below.
                const { a, b, c, d } = cubic;
                return {
                    p: 3 * a * t + b,
                    q: -3 * a * t * t + c,
                    r: a * t * t * t + d
                };
            }

            // --- DRAWING FUNCTIONS ---
            const f_cubic = (x, {a,b,c,d}) => a * x*x*x + b * x*x + c * x + d;
            const g_quad = (x, {p,q,r}) => p * x*x + q * x + r;

            function drawAxes() {
                p.stroke(200);
                p.strokeWeight(1);

                // X-Axis
                p.line(mathToPixelX(mathRangeX[0]), 0, mathToPixelX(mathRangeX[1]), 0);
                // Y-Axis
                p.line(0, mathToPixelY(mathRangeY[0]), 0, mathToPixelY(mathRangeY[1]));

                p.fill(150);
                p.noStroke();
                p.textAlign(p.CENTER, p.TOP);
                for (let i = Math.ceil(mathRangeX[0]); i <= Math.floor(mathRangeX[1]); i++) {
                    if (i === 0) continue;
                    p.line(mathToPixelX(i), -5, mathToPixelX(i), 5);
                    p.text(i, mathToPixelX(i), 8);
                }
                p.textAlign(p.RIGHT, p.CENTER);
                 for (let i = Math.ceil(mathRangeY[0]); i <= Math.floor(mathRangeY[1]); i++) {
                    if (i === 0) continue;
                    p.line(-5, mathToPixelY(i), 5, mathToPixelY(i));
                    p.text(i, -8, mathToPixelY(i));
                }
            }

            function drawCubic() {
                p.noFill();
                p.stroke(colorCubic);
                p.strokeWeight(3);
                p.beginShape();
                for (let px = 0; px <= p.width; px++) {
                    const x = pixelToMathX(px);
                    const y = f_cubic(x, cubicCoeffs);
                    p.vertex(mathToPixelX(x), mathToPixelY(y));
                }
                p.endShape();
            }

            function drawQuadratic(isHighlighted) {
                p.noFill();
                p.stroke(colorQuadratic);
                p.strokeWeight(isHighlighted ? 4 : 3);
                p.beginShape();
                for (let px = 0; px <= p.width; px++) {
                    const x = pixelToMathX(px);
                    const y = g_quad(x, quadCoeffs);
                    p.vertex(mathToPixelX(x), mathToPixelY(y));
                }
                p.endShape();
            }

            function drawFamilyOfQuadratics() {
                const numCurves = 20;
                const tStep = (sliderT.elt.max - sliderT.elt.min) / numCurves;

                for (let i = 0; i <= numCurves; i++) {
                    const t = parseFloat(sliderT.elt.min) + i * tStep;
                    const coeffs = getOsculatingQuadraticCoeffs(t, cubicCoeffs);
                    const color = familyColors[i % familyColors.length];

                    p.fill(color);
                    p.noStroke();
                    p.beginShape();

                    const opensUp = coeffs.p > 0;
                    const yBoundary = opensUp ? mathRangeY[1] * 1.1 : mathRangeY[0] * 1.1;

                    p.vertex(mathToPixelX(mathRangeX[0]), mathToPixelY(yBoundary));
                    for (let px = 0; px <= p.width; px++) {
                        const x = pixelToMathX(px);
                        const y = g_quad(x, coeffs);
                        p.vertex(mathToPixelX(x), mathToPixelY(y));
                    }
                    p.vertex(mathToPixelX(mathRangeX[1]), mathToPixelY(yBoundary));

                    p.endShape(p.CLOSE);
                }
            }

            function drawLocators() {
                for (const l of locators) {
                    const px = mathToPixelX(l.x);
                    const py = mathToPixelY(l.y);

                    const d = p.dist(p.mouseX, p.mouseY, px + originX, py + originY);
                    if (d < locatorSize || draggedLocator === locators.indexOf(l)) {
                        p.cursor('grab');
                    }

                    p.strokeWeight(2);
                    p.stroke(0);
                    p.fill(255);
                    p.ellipse(px, py, locatorSize * 1.5, locatorSize * 1.5);
                    p.line(px - locatorSize/2, py, px + locatorSize/2, py);
                    p.line(px, py - locatorSize/2, px, py + locatorSize/2);
                }
                 p.cursor(p.ARROW); // Reset cursor if not over a locator
            }
        };

        new p5(sketch);
    </script>
</body>
</html>
