<body>

<div id="demo-container">
    <div id="control-panel">
        <div class="control-group">
            <label for="slider-v0">initial volume v₀ (at 0°C)</label>
            <div id="v0-value" class="value-display"></div>
            <input type="range" id="slider-v0" min="100" max="500" step="1" value="211">
        </div>
        <div class="control-group">
            <label for="slider-T">temperature T</label>
            <div id="t-value" class="value-display"></div>
            <input type="range" id="slider-T">
        </div>
        <div class="control-group">
            <label>temperature units</label>
            <div class="value-display"></div> <!-- Spacer -->
            <div class="units-container">
                <input type="radio" id="radio-kelvin" name="temp-units" value="K">
                <label for="radio-kelvin">Kelvin</label>
                <input type="radio" id="radio-celsius" name="temp-units" value="C" checked>
                <label for="radio-celsius">Celsius</label>
            </div>
        </div>
    </div>
    <div id="visualization-area">
        <div id="plot-container"></div>
        <div id="piston-container"></div>
    </div>
</div>

<script>
document.addEventListener('DOMContentLoaded', () => {

    // --- DOM Element References ---
    const sliderV0 = document.getElementById('slider-v0');
    const sliderT = document.getElementById('slider-T');
    const radioKelvin = document.getElementById('radio-kelvin');
    const radioCelsius = document.getElementById('radio-celsius');
    const plotContainer = document.getElementById('plot-container');
    const pistonContainer = document.getElementById('piston-container');
    const v0ValueSpan = document.getElementById('v0-value');
    const tValueSpan = document.getElementById('t-value');

    // --- Constants and State ---
    const T0_KELVIN = 273.15; // 0°C in Kelvin
    const Y_AXIS_MAX = 2000;  // Max volume for visualization
    const PLOT_TEMP_POINTS = 500; // Number of points for the line trace

    let state = {
        v0: Number(sliderV0.value),
        temperatureK: 270 + T0_KELVIN, // Initial temp is 270°C
        tempUnit: 'C',
        get k() { return this.v0 / T0_KELVIN; } // Proportionality constant V/T
    };

    let p5Sketch;

    // --- P5.js Sketch for Piston Animation ---
    const sketch = (p) => {
        let currentVolume = 0;
        let currentTempK = 0;

        const colorLow = p.color(128, 0, 128); // Purple at 0K
        const colorMid = p.color(255, 255, 0); // Yellow at 500K
        const colorHigh = p.color(255, 0, 0);   // Red at 1000K

        p.setup = () => {
            const containerSize = pistonContainer.getBoundingClientRect();
            p.createCanvas(containerSize.width, containerSize.height);
            p.noLoop(); // We will redraw manually
        };

        p.windowResized = () => {
             const containerSize = pistonContainer.getBoundingClientRect();
             p.resizeCanvas(containerSize.width, containerSize.height);
             p.redraw();
        };

        p.update = (volume, tempK) => {
            currentVolume = volume;
            currentTempK = tempK;
            p.redraw();
        };

        p.draw = () => {
            p.background(250);

            const cylinderWidth = p.width * 0.4;
            const cylinderX = (p.width - cylinderWidth) / 2;
            const wallThickness = 10;
            const pistonPlateHeight = 20;

            // Calculate gas color by interpolating
            let gasColor;
            if (currentTempK <= 500) {
                gasColor = p.lerpColor(colorLow, colorMid, p.map(currentTempK, 0, 500, 0, 1));
            } else {
                gasColor = p.lerpColor(colorMid, colorHigh, p.map(currentTempK, 500, 1000, 0, 1));
            }

            // Gas visual
            const gasHeight = p.map(currentVolume, 0, Y_AXIS_MAX, 0, p.height);
            p.fill(gasColor);
            p.noStroke();
            p.rect(cylinderX, p.height - gasHeight, cylinderWidth, gasHeight);

            // Piston visual
            const pistonY = p.map(currentVolume, 0, Y_AXIS_MAX, p.height - pistonPlateHeight, -10);
            p.fill(120);
            p.stroke(80);
            p.strokeWeight(2);
            // Piston plate
            p.rect(cylinderX - wallThickness, pistonY, cylinderWidth + 2 * wallThickness, pistonPlateHeight);
            // Piston handle
            p.rect(p.width/2 - 15, pistonY - 30, 30, 30);

            // Cylinder walls (drawn on top for semi-transparent effect)
            p.fill(180, 200, 220, 150);
            p.noStroke();
            p.rect(cylinderX - wallThickness, 0, wallThickness, p.height);
            p.rect(cylinderX + cylinderWidth, 0, wallThickness, p.height);

            // Cylinder base
            p.stroke(50);
            p.strokeWeight(4);
            p.line(cylinderX - wallThickness, p.height, cylinderX + cylinderWidth + wallThickness, p.height);

            // Pressure arrows
            p.fill(255, 165, 0); // Orange
            p.stroke(200, 100, 0);
            p.strokeWeight(1);
            for(let i=0; i<4; i++){
                let arrowX = cylinderX + (i + 0.5) * (cylinderWidth / 4);
                let arrowY = pistonY - 60;
                if(arrowY < 10) arrowY = 10;
                p.line(arrowX, arrowY, arrowX, arrowY + 20);
                p.triangle(arrowX-5, arrowY+20, arrowX+5, arrowY+20, arrowX, arrowY+30);
            }
        };
    };

    // --- Plotly Chart Functions ---
    function getPlotlyColor(tempK) {
        const p5c = p5.instance;
        let c;
        if (tempK <= 500) {
            c = p5c.lerpColor(p5c.color(128, 0, 128), p5c.color(255, 255, 0), p5c.map(tempK, 0, 500, 0, 1));
        } else {
            c = p5c.lerpColor(p5c.color(255, 255, 0), p5c.color(255, 0, 0), p5c.map(tempK, 500, 1000, 0, 1));
        }
        return `rgb(${p5c.red(c)},${p5c.green(c)},${p5c.blue(c)})`;
    }

    function initPlotly() {
        const traceLine = {
            x: [], y: [],
            mode: 'lines', name: "Charles's Law",
            line: { color: [], colorscale: [[0, 'purple'], [0.5, 'yellow'], [1, 'red']], width: 4 },
            hoverinfo: 'none'
        };

        const traceMarker = {
            x: [], y: [],
            mode: 'markers', name: 'Current State',
            marker: { size: 14, color: 'black', line: { color: 'white', width: 2 } },
        };

        const layout = {
            xaxis: { title: 'temperature °C', range: [-273, 727], zeroline: true },
            yaxis: { title: 'volume', range: [0, Y_AXIS_MAX], zeroline: true },
            showlegend: false,
            margin: { t: 30, r: 20, b: 50, l: 60 },
            paper_bgcolor: 'rgba(0,0,0,0)',
            plot_bgcolor: 'rgba(0,0,0,0)',
        };

        Plotly.newPlot(plotContainer, [traceLine, traceMarker], layout, {responsive: true});
    }

    function updatePlot() {
        const isCelsius = state.tempUnit === 'C';
        const tempRangeK = Array.from({length: PLOT_TEMP_POINTS + 1}, (_, i) => i * (1000 / PLOT_TEMP_POINTS));

        const lineY = tempRangeK.map(T => state.k * T);
        const lineX = isCelsius ? tempRangeK.map(T => T - T0_KELVIN) : tempRangeK;

        const currentT = isCelsius ? state.temperatureK - T0_KELVIN : state.temperatureK;
        const currentV = state.k * state.temperatureK;

        Plotly.react(plotContainer, 
            [
                { line: { color: tempRangeK } }, // Keep gradient source as Kelvin
                { marker: { color: getPlotlyColor(state.temperatureK) } }
            ],
            { xaxis: { title: isCelsius ? 'temperature °C' : 'temperature K', range: isCelsius ? [-273, 727] : [0, 1000] }}
        ).then(() => {
            // Use restyle for data after layout change to ensure smooth updates
            Plotly.restyle(plotContainer, {
                x: [lineX, [currentT]],
                y: [lineY, [currentV]]
            });
        });
    }

    // --- Main Logic & Event Handlers ---
    function updateAllVisuals() {
        const currentV = state.k * state.temperatureK;

        // Update value displays
        v0ValueSpan.textContent = `${state.v0.toFixed(0)} units`;
        if (state.tempUnit === 'C') {
            tValueSpan.textContent = `${(state.temperatureK - T0_KELVIN).toFixed(1)} °C`;
        } else {
            tValueSpan.textContent = `${state.temperatureK.toFixed(1)} K`;
        }

        updatePlot();
        if (p5Sketch) p5Sketch.update(currentV, state.temperatureK);
    }

    function handleV0Change(e) {
        state.v0 = Number(e.target.value);
        updateAllVisuals();
    }

    function handleTempChange(e) {
        const sliderValue = Number(e.target.value);
        state.temperatureK = (state.tempUnit === 'C') ? sliderValue + T0_KELVIN : sliderValue;
        updateAllVisuals();
    }

    function handleUnitChange(e) {
        const newUnit = e.target.value;
        if (newUnit === state.tempUnit) return;

        state.tempUnit = newUnit;

        if (newUnit === 'K') {
            sliderT.min = 0;
            sliderT.max = 1000;
            sliderT.step = 1;
            sliderT.value = state.temperatureK;
        } else { // 'C'
            sliderT.min = -273;
            sliderT.max = 727;
            sliderT.step = 1;
            sliderT.value = state.temperatureK - T0_KELVIN;
        }
        updateAllVisuals();
    }

    function init() {
        // Set initial slider values from state
        radioCelsius.checked = true;
        sliderT.min = -273;
        sliderT.max = 727;
        sliderT.step = 1;
        sliderT.value = state.temperatureK - T0_KELVIN;

        // Create p5 sketch
        p5Sketch = new p5(sketch, pistonContainer);

        // Initialize Plotly chart
        initPlotly();

        // Add event listeners
        sliderV0.addEventListener('input', handleV0Change);
        sliderT.addEventListener('input', handleTempChange);
        radioKelvin.addEventListener('change', handleUnitChange);
        radioCelsius.addEventListener('change', handleUnitChange);
        window.addEventListener('resize', () => {
            if(p5Sketch) p5Sketch.windowResized();
        });

        // Initial render of all components
        updateAllVisuals();
    }

    init();
});
</script>

</body>
</html>
