<body>

    <h1>Circles & Triangles</h1>
    <p>
        Circles have 360 degrees, and a triangle's angles add up to 180 degrees. This visualization explores the relationship between them. The angle of a vertex in an inscribed triangle is always half the measure of its opposite arc. Drag the vertices A, B, or C around the circle, or use the sliders, to see this relationship in action.
    </p>

    <div id="main-container">
        <div id="canvas-container"></div>
        <div id="info-panel">
            <span class="info-header">Arc</span>
            <span class="info-header"></span>
            <span class="info-header">Vertex</span>
            <span class="info-header"></span>

            <span id="arc-label-1"></span>
            <span id="arc-value-1"></span>
            <span id="vertex-label-1"></span>
            <span id="vertex-value-1"></span>

            <span id="arc-label-2"></span>
            <span id="arc-value-2"></span>
            <span id="vertex-label-2"></span>
            <span id="vertex-value-2"></span>

            <span id="arc-label-3"></span>
            <span id="arc-value-3"></span>
            <span id="vertex-label-3"></span>
            <span id="vertex-value-3"></span>

            <hr>

            <span class="info-total-label">Total</span>
            <span id="arc-total"></span>
            <span class="info-total-label"></span>
            <span id="vertex-total"></span>
        </div>
    </div>

    <div id="controls-container">
        <div>
            <label>Vertex</label>
            <div>
                <input type="radio" id="radio-a" name="vertex-select" value="A"> <label for="radio-a">A</label>
                <input type="radio" id="radio-b" name="vertex-select" value="B"> <label for="radio-b">B</label>
                <input type="radio" id="radio-c" name="vertex-select" value="C" checked> <label for="radio-c">C</label>
                <input type="radio" id="radio-none" name="vertex-select" value="None"> <label for="radio-none">None</label>
            </div>
        </div>
        <div>
            <label for="slider-a">A</label>
            <input type="range" id="slider-a" min="0" max="360" step="0.5" value="306">
        </div>
        <div>
            <label for="slider-b">B</label>
            <input type="range" id="slider-b" min="0" max="360" step="0.5" value="54">
        </div>
        <div>
            <label for="slider-c">C</label>
            <input type="range" id="slider-c" min="0" max="360" step="0.5" value="180">
        </div>
    </div>

    <script>
        // --- State Variables ---
        let angles = { A: 306, B: 54, C: 180 };
        let selectedVertex = 'C';
        let draggedVertex = null;

        // --- DOM Elements ---
        let dom = {};

        // --- p5.js Sketch ---
        const sketch = (p) => {
            const canvasSize = 400;
            const center = { x: canvasSize / 2, y: canvasSize / 2 };
            const radius = 150;
            const vertexHitRadius = 20;

            let vertexPositions = {};

            p.setup = () => {
                const canvas = p.createCanvas(canvasSize, canvasSize);
                canvas.parent('canvas-container');
                p.angleMode(p.DEGREES);
                p.noLoop(); // We will manually redraw when state changes

                // Get all DOM elements
                dom.sliders = {
                    A: document.getElementById('slider-a'),
                    B: document.getElementById('slider-b'),
                    C: document.getElementById('slider-c'),
                };
                dom.radios = document.querySelectorAll('input[name="vertex-select"]');

                dom.info = {
                    labels: [
                        { arc: document.getElementById('arc-label-1'), vertex: document.getElementById('vertex-label-1') },
                        { arc: document.getElementById('arc-label-2'), vertex: document.getElementById('vertex-label-2') },
                        { arc: document.getElementById('arc-label-3'), vertex: document.getElementById('vertex-label-3') },
                    ],
                    values: [
                        { arc: document.getElementById('arc-value-1'), vertex: document.getElementById('vertex-value-1') },
                        { arc: document.getElementById('arc-value-2'), vertex: document.getElementById('vertex-value-2') },
                        { arc: document.getElementById('arc-value-3'), vertex: document.getElementById('vertex-value-3') },
                    ],
                    totals: {
                        arc: document.getElementById('arc-total'),
                        vertex: document.getElementById('vertex-total'),
                    }
                };

                // Add event listeners
                for (const key in dom.sliders) {
                    dom.sliders[key].addEventListener('input', handleSliderInput);
                }
                dom.radios.forEach(radio => radio.addEventListener('change', handleRadioChange));

                // Initial update
                updateStateAndRedraw();
            };

            p.draw = () => {
                p.background(255);

                // Calculate positions
                for (const key in angles) {
                    vertexPositions[key] = {
                        x: center.x + radius * p.cos(angles[key]),
                        y: center.y + radius * p.sin(angles[key]),
                    };
                }

                drawBackgroundSectors();
                drawMainCircle();
                drawTriangle();
                drawVerticesAndLabels();

                if (selectedVertex !== 'None') {
                    drawHighlights();
                }
            };

            // --- Drawing Functions ---
            const drawBackgroundSectors = () => {
                const sortedVertices = Object.entries(angles)
                    .map(([label, angle]) => ({ label, angle }))
                    .sort((a, b) => a.angle - b.angle);

                const colorMap = { 'AB': '#e0f0e0', 'BC': '#f5f5d5', 'CA': '#e0e8f8' };
                const sectorColors = {
                    [sortedVertices[0].label]: colorMap[[sortedVertices[0].label, sortedVertices[1].label].sort().join('')],
                    [sortedVertices[1].label]: colorMap[[sortedVertices[1].label, sortedVertices[2].label].sort().join('')],
                    [sortedVertices[2].label]: colorMap[[sortedVertices[2].label, sortedVertices[0].label].sort().join('')]
                };

                p.noStroke();

                p.fill(sectorColors[sortedVertices[0].label]);
                p.arc(center.x, center.y, radius * 2, radius * 2, sortedVertices[0].angle, sortedVertices[1].angle, p.PIE);

                p.fill(sectorColors[sortedVertices[1].label]);
                p.arc(center.x, center.y, radius * 2, radius * 2, sortedVertices[1].angle, sortedVertices[2].angle, p.PIE);

                p.fill(sectorColors[sortedVertices[2].label]);
                p.arc(center.x, center.y, radius * 2, radius * 2, sortedVertices[2].angle, sortedVertices[0].angle, p.PIE);
            };

            const drawMainCircle = () => {
                p.noStroke();
                p.fill('#aaccde');
                p.circle(center.x, center.y, radius * 2);
                p.stroke('#666666');
                p.strokeWeight(2);
                p.noFill();
                p.circle(center.x, center.y, radius * 2);
                p.fill('#666666');
                p.noStroke();
                p.circle(center.x, center.y, 8);
            };

            const drawTriangle = () => {
                p.fill(173, 216, 230, 128); // rgba(173, 216, 230, 0.5)
                p.stroke('#666666');
                p.strokeWeight(1.5);
                p.beginShape();
                p.vertex(vertexPositions.A.x, vertexPositions.A.y);
                p.vertex(vertexPositions.B.x, vertexPositions.B.y);
                p.vertex(vertexPositions.C.x, vertexPositions.C.y);
                p.endShape(p.CLOSE);
            };

            const drawVerticesAndLabels = () => {
                for (const key in vertexPositions) {
                    const pos = vertexPositions[key];
                    p.fill(255);
                    p.stroke('#00008B');
                    p.strokeWeight(2);
                    p.circle(pos.x, pos.y, 10);

                    const labelOffset = 25;
                    const labelX = center.x + (radius + labelOffset) * p.cos(angles[key]);
                    const labelY = center.y + (radius + labelOffset) * p.sin(angles[key]);
                    p.fill('#00008B');
                    p.noStroke();
                    p.textSize(24);
                    p.textAlign(p.CENTER, p.CENTER);
                    p.textStyle(p.BOLD);
                    p.text(key, labelX, labelY);
                }
            };

            const drawHighlights = () => {
                const oppositeArc = { A: ['B', 'C'], B: ['C', 'A'], C: ['A', 'B'] };
                const [v1, v2] = oppositeArc[selectedVertex];

                const arcAngle = (angles[v2] - angles[v1] + 360) % 360;
                const vertexAngle = arcAngle / 2;

                // Central Angle (θ)
                p.noFill();
                p.stroke('#008000'); // Green
                p.strokeWeight(4);
                p.arc(center.x, center.y, radius * 2 - 20, radius * 2 - 20, angles[v1], angles[v2]);
                p.arc(center.x, center.y, 20, 20, angles[v1], angles[v2]);

                // Inscribed Angle (θ/2)
                p.stroke('#FF0000'); // Red
                p.strokeWeight(4);
                p.arc(vertexPositions[selectedVertex].x, vertexPositions[selectedVertex].y, 80, 80, angles[v1], angles[v2]);

                // Angle Text
                p.noStroke();
                p.textAlign(p.LEFT, p.CENTER);
                p.textSize(16);
                p.textStyle(p.NORMAL);

                // θ label
                const midArcAngle = angles[v1] + arcAngle / 2;
                p.fill('#008000');
                const textX_theta = center.x + (radius-20) * p.cos(midArcAngle);
                const textY_theta = center.y + (radius-20) * p.sin(midArcAngle);
                p.text(`θ = ${formatAngle(arcAngle)}`, textX_theta, textY_theta);

                // θ/2 label
                p.fill('#FF0000');
                const textX_vertex = vertexPositions[selectedVertex].x + 50 * p.cos(midArcAngle);
                const textY_vertex = vertexPositions[selectedVertex].y + 50 * p.sin(midArcAngle);
                p.text(`θ/2 = ${formatAngle(vertexAngle)}`, textX_vertex, textY_vertex);
            };

            // --- Mouse Interaction ---
            p.mousePressed = () => {
                for (const key in vertexPositions) {
                    const d = p.dist(p.mouseX, p.mouseY, vertexPositions[key].x, vertexPositions[key].y);
                    if (d < vertexHitRadius) {
                        draggedVertex = key;
                        p.loop(); // Allow dragging
                        return;
                    }
                }
            };

            p.mouseDragged = () => {
                if (draggedVertex) {
                    const newAngle = p.atan2(p.mouseY - center.y, p.mouseX - center.x);
                    angles[draggedVertex] = (newAngle + 360) % 360;
                    dom.sliders[draggedVertex].value = angles[draggedVertex];
                    updateStateAndRedraw();
                }
            };

            p.mouseReleased = () => {
                if (draggedVertex) {
                    draggedVertex = null;
                    p.noLoop(); // Stop continuous redrawing
                }
            };

            // Public function to be called from outside event handlers
            window.updateStateAndRedraw = () => {
                updateInfoPanel();
                p.redraw();
            };
        };

        // --- Global Control Functions ---

        function handleSliderInput(e) {
            const vertex = e.target.id.split('-')[1].toUpperCase();
            angles[vertex] = parseFloat(e.target.value);
            updateStateAndRedraw();
        }

        function handleRadioChange(e) {
            selectedVertex = e.target.value;
            updateStateAndRedraw();
        }

        function formatAngle(num) {
            return `${num.toFixed(1)}°`;
        }

        function calculateValues() {
            const arcBC = (angles.C - angles.B + 360) % 360;
            const arcCA = (angles.A - angles.C + 360) % 360;
            const arcAB = (angles.B - angles.A + 360) % 360;

            const vertexA = arcBC / 2;
            const vertexB = arcCA / 2;
            const vertexC = arcAB / 2;

            return {
                A: { arcLabel: 'Arc BC', arcValue: arcBC, vertexLabel: 'Vertex A', vertexValue: vertexA },
                B: { arcLabel: 'Arc CA', arcValue: arcCA, vertexLabel: 'Vertex B', vertexValue: vertexB },
                C: { arcLabel: 'Arc AB', arcValue: arcAB, vertexLabel: 'Vertex C', vertexValue: vertexC },
            };
        }

        function updateInfoPanel() {
            const values = calculateValues();

            // Clear all fields first
            for (let i=0; i<3; i++) {
                dom.info.labels[i].arc.innerText = '';
                dom.info.values[i].arc.innerText = '';
                dom.info.labels[i].vertex.innerText = '';
                dom.info.values[i].vertex.innerText = '';
            }

            if (selectedVertex === 'None') {
                const allVertices = ['A', 'B', 'C'];
                allVertices.forEach((key, index) => {
                    dom.info.labels[index].arc.innerText = values[key].arcLabel;
                    dom.info.values[index].arc.innerText = formatAngle(values[key].arcValue);
                    dom.info.labels[index].vertex.innerText = values[key].vertexLabel;
                    dom.info.values[index].vertex.innerText = formatAngle(values[key].vertexValue);
                });

                dom.info.totals.arc.innerText = '360.0°';
                dom.info.totals.vertex.innerText = '180.0°';
            } else {
                const data = values[selectedVertex];
                dom.info.labels[0].arc.innerText = data.arcLabel;
                dom.info.values[0].arc.innerText = formatAngle(data.arcValue);
                dom.info.labels[0].vertex.innerText = data.vertexLabel;
                dom.info.values[0].vertex.innerText = formatAngle(data.vertexValue);

                dom.info.totals.arc.innerText = '?';
                dom.info.totals.vertex.innerText = '?';
            }
        }

        // Initialize the p5 sketch
        new p5(sketch);

    </script>
</body>
</html>
