<body>

    <div id="main-container">
        <div id="control-panel">
            <h2>Parameters</h2>

            <div class="control-group">
                <div class="control-row">
                    <label for="slider-tau">&tau;</label>
                    <button id="btn-dec-tau">-</button>
                    <input type="range" id="slider-tau" min="0.01" max="5" step="0.01" value="1.0">
                    <button id="btn-inc-tau">+</button>
                </div>
