            <div class="control-group">
                <div class="control-row">
                    <label for="slider-phi">&phi;</label>
                    <button id="btn-dec-phi">-</button>
                    <input type="range" id="slider-phi" min="0.1" max="10" step="0.1" value="2.0">
                    <button id="btn-inc-phi">+</button>
                </div>
                <div class="value-display">Thiele Modulus: <span id="value-phi">2.0</span></div>
            </div>

            <div class="control-group">
                <div class="control-row">
                    <label for="slider-beta">&beta;</label>
                    <button id="btn-dec-beta">-</button>
                    <input type="range" id="slider-beta" min="-0.5" max="0.5" step="0.01" value="0.2">
                    <button id="btn-inc-beta">+</button>
                </div>
                <div class="value-display">Prater Number: <span id="value-beta">0.20</span></div>
            </div>

            <div class="control-group">
                <div class="control-row">
                    <label for="slider-gamma">&gamma;</label>
                    <button id="btn-dec-gamma">-</button>
                    <input type="range" id="slider-gamma" min="0" max="40" step="1" value="20">
                    <button id="btn-inc-gamma">+</button>
                </div>
                <div class="value-display">Arrhenius Number: <span id="value-gamma">20</span></div>
            </div>

            <div class="control-group">
                <div class="control-row">
                    <label for="slider-xi">&xi;</label>
                    <button id="btn-dec-xi">-</button>
                    <input type="range" id="slider-xi" min="0.001" max="1.0" step="0.001" value="0.4">
                    <button id="btn-inc-xi">+</button>
                </div>
                <div class="value-display">Position: <span id="value-xi">0.400</span></div>
            </div>
        </div>
        <div id="visualization-panel">
            <div id="plot-concentration"></div>
            <div id="plot-temperature"></div>
        </div>
    </div>

    <script>
        document.addEventListener('DOMContentLoaded', () => {
            // --- DOM Element References ---
            const sliders = {
                tau: document.getElementById('slider-tau'),
                phi: document.getElementById('slider-phi'),
                beta: document.getElementById('slider-beta'),
                gamma: document.getElementById('slider-gamma'),
                xi: document.getElementById('slider-xi'),
            };
            const values = {
                tau: document.getElementById('value-tau'),
                phi: document.getElementById('value-phi'),
                beta: document.getElementById('value-beta'),
                gamma: document.getElementById('value-gamma'),
                xi: document.getElementById('value-xi'),
            };
            const plotDivs = {
                concentration: document.getElementById('plot-concentration'),
                temperature: document.getElementById('plot-temperature'),
            };

            // --- Simulation Constants ---
            const N = 101; // Number of grid points (0 to 100)
            const d_xi = 1.0 / (N - 1);
            const d_xi2 = d_xi * d_xi;
            const xi_grid = Array.from({ length: N }, (_, i) => i * d_xi);

            // --- State Variables ---
            let simResults = { c: [], t: [] };

            // --- Vector Math Helpers ---
            const vec_add = (a, b) => a.map((val, i) => val + b[i]);
            const vec_mul_scalar = (a, s) => a.map(val => val * s);

            // --- Numerical Solver ---
            function getDerivatives(y, params) {
                const { phi, beta, gamma } = params;
                const M = N - 1; // Number of internal points
                const c_internal = y.slice(0, M);
                const theta_internal = y.slice(M);

                const c = [...c_internal, 1.0]; // Add boundary condition c(1)=1
                const theta = [...theta_internal, 1.0]; // Add boundary condition θ(1)=1

                const dcdt = new Array(M);
                const dthetadt = new Array(M);

                for (let i = 0; i < M; i++) {
                    // Reaction Term
                    const R = phi * phi * c[i] * Math.exp(gamma * (1 - 1 / theta[i]));

                    // Second Derivatives (Diffusion/Conduction)
                    let d2c_dxi2, d2theta_dxi2;
                    if (i === 0) { // Center point (ξ=0), using symmetry condition
                        d2c_dxi2 = 2 * (c[1] - c[0]) / d_xi2;
                        d2theta_dxi2 = 2 * (theta[1] - theta[0]) / d_xi2;
                    } else { // Internal points
                        d2c_dxi2 = (c[i + 1] - 2 * c[i] + c[i - 1]) / d_xi2;
                        d2theta_dxi2 = (theta[i + 1] - 2 * theta[i] + theta[i - 1]) / d_xi2;
                    }

                    // System of ODEs
                    dcdt[i] = d2c_dxi2 - R;
                    dthetadt[i] = d2theta_dxi2 + beta * R;
                }

                return [...dcdt, ...dthetadt];
            }

            function runSimulation() {
                const params = {
                    phi: parseFloat(sliders.phi.value),
                    beta: parseFloat(sliders.beta.value),
                    gamma: parseFloat(sliders.gamma.value),
                };
                const tau_final = parseFloat(sliders.tau.value);

                // Initial Conditions
                const M = N - 1;
                let y = new Array(2 * M).fill(0);
                for (let i = 0; i < M; i++) {
                    y[i] = 0.0;     // c(ξ, 0) = 0 for ξ < 1
                    y[i + M] = 1.0; // θ(ξ, 0) = 1
                }

                // RK4 Time Integration
                // Stability criteria: dt < d_xi^2 / 2. We use a smaller value for safety.
                const dt = 0.2 * d_xi2; 
                let t = 0;
                while (t < tau_final) {
                    const current_dt = Math.min(dt, tau_final - t);

                    const k1 = vec_mul_scalar(getDerivatives(y, params), current_dt);
                    const k2 = vec_mul_scalar(getDerivatives(vec_add(y, vec_mul_scalar(k1, 0.5)), params), current_dt);
                    const k3 = vec_mul_scalar(getDerivatives(vec_add(y, vec_mul_scalar(k2, 0.5)), params), current_dt);
                    const k4 = vec_mul_scalar(getDerivatives(vec_add(y, k3), params), current_dt);

                    const dy = vec_mul_scalar(vec_add(k1, vec_add(vec_mul_scalar(k2, 2), vec_add(vec_mul_scalar(k3, 2), k4))), 1 / 6);

                    y = vec_add(y, dy);
                    t += current_dt;
                }

                simResults.c = [...y.slice(0, M), 1.0];
                simResults.t = [...y.slice(M), 1.0];
            }

            // --- Plotting ---
            function interpolate(x_arr, y_arr, x_target) {
                if (x_target <= x_arr[0]) return y_arr[0];
                if (x_target >= x_arr[x_arr.length-1]) return y_arr[x_arr.length-1];

                const index = Math.floor(x_target / d_xi);
                const x1 = x_arr[index];
                const x2 = x_arr[index + 1];
                const y1 = y_arr[index];
                const y2 = y_arr[index + 1];

                return y1 + (y2 - y1) * (x_target - x1) / (x2 - x1);
            }

            function updatePlots() {
                runSimulation();

                const current_tau = parseFloat(sliders.tau.value);
                const current_xi = parseFloat(sliders.xi.value);
                const c_at_xi = interpolate(xi_grid, simResults.c, current_xi);
                const t_at_xi = interpolate(xi_grid, simResults.t, current_xi);

                // Concentration Plot
                const c_trace = {
                    x: xi_grid,
                    y: simResults.c,
                    type: 'scatter',
                    mode: 'lines',
                    name: 'Concentration',
                    line: { color: 'royalblue', width: 3 }
                };
                const c_marker_trace = {
                    x: [current_xi, current_xi, 0],
                    y: [0, c_at_xi, c_at_xi],
                    type: 'scatter',
                    mode: 'lines',
                    name: 'Marker',
                    line: { color: '#333333', dash: 'dot', width: 1.5 }
                };
                const c_layout = {
                    xaxis: { title: 'ξ (Dimensionless Position)', range: [0, 1] },
                    yaxis: { title: 'c (Dimensionless Concentration)', range: [0, 1.1] },
                    margin: { t: 20, r: 20, b: 40, l: 50 },
                    showlegend: false,
                    annotations: [{
                        xref: 'paper', yref: 'paper',
                        x: 0.95, y: 0.95,
                        xanchor: 'right', yanchor: 'top',
                        text: `τ = ${current_tau.toFixed(2)}<br>ξ = ${current_xi.toFixed(3)}<br>c = ${c_at_xi.toFixed(5)}`,
                        showarrow: false,
                        align: 'left',
                        bgcolor: 'rgba(255,255,255,0.7)',
                        borderpad: 4
                    }]
                };
                Plotly.react(plotDivs.concentration, [c_trace, c_marker_trace], c_layout);

                // Temperature Plot
                const t_min_y = Math.min(...simResults.t);
                const t_trace = {
                    x: xi_grid,
                    y: simResults.t,
                    type: 'scatter',
                    mode: 'lines',
                    name: 'Temperature',
                    line: { color: 'firebrick', width: 3 }
                };
                const t_marker_trace = {
                    x: [current_xi, current_xi, 0],
                    y: [t_min_y, t_at_xi, t_at_xi],
                    type: 'scatter',
                    mode: 'lines',
                    name: 'Marker',
                    line: { color: '#333333', dash: 'dot', width: 1.5 }
                };
                const t_layout = {
                    xaxis: { title: 'ξ (Dimensionless Position)', range: [0, 1] },
                    yaxis: { title: 'θ (Dimensionless Temperature)', autorange: true },
                    margin: { t: 20, r: 20, b: 40, l: 50 },
                    showlegend: false,
                    annotations: [{
                        xref: 'paper', yref: 'paper',
                        x: 0.95, y: 0.95,
                        xanchor: 'right', yanchor: 'top',
                        text: `τ = ${current_tau.toFixed(2)}<br>ξ = ${current_xi.toFixed(3)}<br>θ = ${t_at_xi.toFixed(5)}`,
                        showarrow: false,
                        align: 'left',
                        bgcolor: 'rgba(255,255,255,0.7)',
                        borderpad: 4
                    }]
                };
                Plotly.react(plotDivs.temperature, [t_trace, t_marker_trace], t_layout);
            }

            function updateMarkersOnly() {
                const current_tau = parseFloat(sliders.tau.value);
                const current_xi = parseFloat(sliders.xi.value);
                const c_at_xi = interpolate(xi_grid, simResults.c, current_xi);
                const t_at_xi = interpolate(xi_grid, simResults.t, current_xi);

                // Update concentration marker
                const c_marker_update = {
                    x: [[current_xi, current_xi, 0]],
                    y: [[0, c_at_xi, c_at_xi]]
                };
                const c_annot_update = {
                    'annotations[0].text': `τ = ${current_tau.toFixed(2)}<br>ξ = ${current_xi.toFixed(3)}<br>c = ${c_at_xi.toFixed(5)}`
                };
                Plotly.restyle(plotDivs.concentration, c_marker_update, [1]);
                Plotly.relayout(plotDivs.concentration, c_annot_update);

                // Update temperature marker
                const t_plot_layout = plotDivs.temperature.layout;
                const t_min_y = t_plot_layout.yaxis.range[0];
                const t_marker_update = {
                    x: [[current_xi, current_xi, 0]],
                    y: [[t_min_y, t_at_xi, t_at_xi]]
                };
                const t_annot_update = {
                    'annotations[0].text': `τ = ${current_tau.toFixed(2)}<br>ξ = ${current_xi.toFixed(3)}<br>θ = ${t_at_xi.toFixed(5)}`
                };
                Plotly.restyle(plotDivs.temperature, t_marker_update, [1]);
                Plotly.relayout(plotDivs.temperature, t_annot_update);
            }

            // --- Event Handlers Setup ---
            function setupEventListeners() {
                // Slider input listeners
                for (const key in sliders) {
                    sliders[key].addEventListener('input', (e) => {
                        const slider = e.target;
                        const valueSpan = values[key];
                        const step = parseFloat(slider.step);
                        let numDecimals = 0;
                        if (Math.floor(step) !== step) {
                            numDecimals = step.toString().split(".")[1].length || 0;
                        }
                        valueSpan.textContent = parseFloat(slider.value).toFixed(numDecimals || (key === 'xi' ? 3 : (key === 'tau' || key === 'beta' ? 2 : 1)));

                        if (key === 'xi') {
                            updateMarkersOnly();
                        } else {
                            updatePlots();
                        }
                    });
                }

                // Increment/Decrement button listeners
                const setupButtons = (param) => {
                    const slider = sliders[param];
                    const decBtn = document.getElementById(`btn-dec-${param}`);
                    const incBtn = document.getElementById(`btn-inc-${param}`);

                    decBtn.addEventListener('click', () => {
                        slider.stepDown();
                        slider.dispatchEvent(new Event('input'));
                    });

                    incBtn.addEventListener('click', () => {
                        slider.stepUp();
                        slider.dispatchEvent(new Event('input'));
                    });
                };

                ['tau', 'phi', 'beta', 'gamma', 'xi'].forEach(setupButtons);
            }

            // --- Initial Execution ---
            setupEventListeners();
            // Trigger initial plot rendering by simulating an input event on a main parameter
            sliders.tau.dispatchEvent(new Event('input'));
        });
    </script>
</body>
</html>
