        document.addEventListener('DOMContentLoaded', function() {
            // Component references
            const sliderBmax = document.getElementById('slider-bmax');
            const sliderB0 = document.getElementById('slider-b0');
            const sliderR = document.getElementById('slider-r');
            const sliderBeta = document.getElementById('slider-beta');

            const valueBmax = document.getElementById('value-bmax');
            const valueB0 = document.getElementById('value-b0');
            const valueR = document.getElementById('value-r');
            const valueBeta = document.getElementById('value-beta');

            const resultU = document.getElementById('result-u');
            const resultP = document.getElementById('result-p');

            const plotContainer = document.getElementById('plot-container');

            // Static empirical data
            const empiricalAges = [30, 35, 40, 45, 50, 55];
            const empiricalVolumes = [289, 445, 565, 704, 833, 924];

            // Initial Plotly setup
            function initializePlot() {
                const empiricalDataTrace = {
                    x: empiricalAges,
                    y: empiricalVolumes,
                    mode: 'markers',
                    type: 'scatter',
                    name: 'Empirical Data',
                    marker: { color: 'blue', size: 8 }
                };

                const richardsCurveTrace = {
                    x: [], y: [], mode: 'lines', type: 'scatter', name: 'Richards Curve',
                    line: { color: 'orange', width: 3 }
                };

                const fourFifthsLawTrace = {
                    x: [], y: [], mode: 'lines', type: 'scatter', name: '4/5 Law',
                    line: { color: 'blue', width: 3 }
                };

                const layout = {
                    title: 'forest growth curve',
                    xaxis: {
                        title: 'stand age (years)',
                        range: [15, 80],
                        gridcolor: 'lightgrey',
                        zeroline: false,
                        griddash: 'dash'
                    },
                    yaxis: {
                        title: 'wood volume, m³ ha⁻¹<br>Richards curve vs 4/5 law',
                        range: [0, 1600],
                        gridcolor: 'lightgrey',
                        zeroline: false,
                        griddash: 'dash'
                    },
                    plot_bgcolor: '#FFFFF0',
                    showlegend: false,
                    margin: { t: 40, b: 60, l: 100, r: 20 },
                    autosize: true
                };

                Plotly.newPlot(plotContainer, [empiricalDataTrace, richardsCurveTrace, fourFifthsLawTrace], layout, {responsive: true});
            }

            // Main update function
            function update() {
                // 1. Read input values from sliders
                const b_max = parseFloat(sliderBmax.value);
                const b_0 = parseFloat(sliderB0.value);
                const r = parseFloat(sliderR.value);
                const beta = parseFloat(sliderBeta.value);

                // 2. Update value displays
                valueBmax.textContent = b_max.toFixed(0);
                valueB0.textContent = b_0.toFixed(1);
                valueR.textContent = r.toFixed(3);
                valueBeta.textContent = beta.toFixed(3);

                // Time points for curve plotting
                const t_values = [];
                for (let t = 15; t <= 80; t += 0.5) {
                    t_values.push(t);
                }

                // 3. Calculate Richards Curve
                const richards_y = [];
                const M = Math.pow(b_max / b_0, beta) - 1;

                if (M > 0) {
                    t_values.forEach(t => {
                        const val = b_max / Math.pow(1 + M * Math.exp(-r * t), 1 / beta);
                        richards_y.push(val);
                    });
                } else {
                     t_values.forEach(() => richards_y.push(null));
                }

                // 4. Calculate 4/5 Law Parameters
                let u = NaN;
                let p = NaN;
                let four_fifths_y = t_values.map(() => null);

                // Calculate inflection point properties only if M is valid
                if (M > 0 && r > 0 && beta > 0) {
                    const t_i_num = Math.log(M * beta);
                    const t_i = t_i_num / r;
                    const V_i = b_max * Math.pow((beta + 1) / (2 * beta), 1 / beta);
                    const V_prime_i = (r / beta) * V_i * (1 - Math.pow(V_i / b_max, beta));

                    if (isFinite(t_i) && isFinite(V_i) && V_prime_i > 0) {
                        u = t_i - (4 / 5) * (V_i / V_prime_i);

                        const p_base = t_i - u;
                        if (p_base > 0) {
                            p = V_i / Math.pow(p_base, 4 / 5);

                            // 5. Calculate 4/5 Law Curve
                            four_fifths_y = t_values.map(t => {
                                const base = t - u;
                                return base > 0 ? p * Math.pow(base, 4 / 5) : null;
                            });
                        }
                    }
                }

                // 6. Update Results Display
                resultU.textContent = isFinite(u) ? u.toFixed(4) : 'N/A';
                resultP.textContent = isFinite(p) ? p.toFixed(4) : 'N/A';

                // 7. Update Plot
                const updateData = {
                    y: [empiricalVolumes, richards_y, four_fifths_y]
                };
                const updateLayout = {
                    'yaxis.range': [0, b_max * 1.05]
                };

                Plotly.update(plotContainer, updateData, updateLayout);
            }

            // Attach event listeners
            [sliderBmax, sliderB0, sliderR, sliderBeta].forEach(slider => {
                slider.addEventListener('input', update);
            });

            // Initial setup
            initializePlot();
            update();
        });
    </script>
