            const fov = 75;
            const aspect = canvasContainer.clientWidth / canvasContainer.clientHeight;
            const near = 0.1;
            const far = 1000;
            camera = new THREE.PerspectiveCamera(fov, aspect, near, far);
            camera.position.z = 150;

            // Renderer
            renderer = new THREE.WebGLRenderer({ canvas: canvas, antialias: true });
            renderer.setSize(canvasContainer.clientWidth, canvasContainer.clientHeight);
            renderer.setPixelRatio(window.devicePixelRatio);

            // Lights
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
            scene.add(ambientLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
            directionalLight.position.set(50, 50, 100);
            scene.add(directionalLight);

            // Main group for all segments
            group = new THREE.Group();
            scene.add(group);

            // Event Listeners
            setupEventListeners();

            // Initial Draw and Animation
            redraw();
            animate();
        }

        function setupEventListeners() {
            // Radios
            document.querySelectorAll('input[name="base-polygon"]').forEach(radio => {
                radio.addEventListener('change', redraw);
            });

            // Sliders
            Object.values(controls).forEach(element => {
                if (element.type === 'range') {
                    element.addEventListener('input', redraw);
                }
            });

            // Plus Buttons
            const setupPlusButton = (btnId, sliderId) => {
                const button = document.getElementById(btnId);
                const slider = document.getElementById(sliderId);
                button.addEventListener('click', () => {
                    const step = parseFloat(slider.step);
                    slider.value = parseFloat(slider.value) + step;
                    // Dispatch an input event to trigger redraw via the slider's listener
                    slider.dispatchEvent(new Event('input'));
                });
            };
            setupPlusButton('btn-units-plus', 'slider-units');
            setupPlusButton('btn-angle-plus', 'slider-angle');
            setupPlusButton('btn-conical-plus', 'slider-conical');
            setupPlusButton('btn-rotate-x-plus', 'slider-rotate-x');
            setupPlusButton('btn-rotate-z-plus', 'slider-rotate-z');

            // Window Resize
            window.addEventListener('resize', onWindowResize);
        }

        function onWindowResize() {
            const width = canvasContainer.clientWidth;
            const height = canvasContainer.clientHeight;

            camera.aspect = width / height;
            camera.updateProjectionMatrix();
            renderer.setSize(width, height);
        }

        function redraw() {
            // 1. Read current values from controls
            const numSides = parseInt(controls.basePolygon(), 10);
            const numUnits = parseInt(controls.units.value, 10);
            const angularAdvanceDeg = parseFloat(controls.angle.value);
            const conicalFactor = parseFloat(controls.conical.value);
            const rotateXDeg = parseFloat(controls.rotateX.value);
            const rotateZDeg = parseFloat(controls.rotateZ.value);

            // 2. Clear previous meshes from the group
            while(group.children.length > 0){ 
                const object = group.children[0];
                if (object.geometry) object.geometry.dispose();
                if (object.material) object.material.dispose();
                group.remove(object);
            }

            // 3. Create a base geometry for a single segment
            const polygonRadius = 10; // Fixed radius for the base shape
            const shape = new THREE.Shape();
            for (let i = 0; i <= numSides; i++) {
                const angle = (i / numSides) * Math.PI * 2;
                const x = polygonRadius * Math.cos(angle);
                const y = polygonRadius * Math.sin(angle);
                if (i === 0) {
                    shape.moveTo(x, y);
                } else {
                    shape.lineTo(x, y);
                }
            }
            baseGeometry = new THREE.ShapeGeometry(shape);

            // 4. Loop to generate and place segments
            const angularAdvanceRad = angularAdvanceDeg * (Math.PI / 180);

            for (let i = 0; i < numUnits; i++) {
                // a. Calculate transformations
                const angle = i * angularAdvanceRad;
                const radius = 4 * Math.sqrt(i);
                const scale = 0.5 * Math.log(i + 2);
                const x = radius * Math.cos(angle);
                const y = radius * Math.sin(angle);
                const z = conicalFactor * radius;

                // b. Create mesh with unique color
                const material = new THREE.MeshStandardMaterial({
                    roughness: 0.7,
                    metalness: 0.1,
                });
                material.color.setHSL((angle / (2 * Math.PI)) % 1.0, 0.8, 0.6);

                const mesh = new THREE.Mesh(baseGeometry, material);

                // c. Apply transformations
                mesh.position.set(x, y, z);
                mesh.scale.set(scale, scale, scale);
                mesh.rotation.z = angle; // Align with spiral tangent

                // d. Add mesh to the main group
                group.add(mesh);
            }

            // 5. Apply master rotations to the group
            group.rotation.x = rotateXDeg * (Math.PI / 180);
            group.rotation.z = rotateZDeg * (Math.PI / 180);
        }

        function animate() {
            requestAnimationFrame(animate);
            // 6. Render the updated scene
            renderer.render(scene, camera);
        }

        // --- Start the application ---
        init();

    </script>
</body>
</html>
