    <script src="https://cdn.jsdelivr.net/npm/three@0.164.1/examples/js/controls/OrbitControls.js"></script>

    <script>
        // --- Global State & THREE.js objects ---
        let scene, camera, renderer, controls;
        let atomGroup, planeGroup;

        // --- DOM Elements ---
        const btnSC = document.getElementById('btn-sc');
        const btnBCC = document.getElementById('btn-bcc');
        const btnFCC = document.getElementById('btn-fcc');
        const inputH = document.getElementById('input-h');
        const inputK = document.getElementById('input-k');
        const inputL = document.getElementById('input-l');
        const sliderOpacity = document.getElementById('slider-opacity');
        const canvasContainer = document.getElementById('canvas-container');

        // --- Initialization ---
        function init() {
            // Scene
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xeeeeee);

            // Camera
            camera = new THREE.PerspectiveCamera(50, canvasContainer.clientWidth / canvasContainer.clientHeight, 0.1, 100);
            camera.position.set(1.5, 1.5, 2.5);
            camera.lookAt(0, 0, 0);

            // Renderer
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(canvasContainer.clientWidth, canvasContainer.clientHeight);
            renderer.setPixelRatio(window.devicePixelRatio);
            canvasContainer.appendChild(renderer.domElement);

            // Controls
            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.1;

            // Lighting
            scene.add(new THREE.AmbientLight(0xaaaaaa));
            const light1 = new THREE.DirectionalLight(0xffffff, 1.5);
            light1.position.set(1, 1, 1);
            scene.add(light1);
            const light2 = new THREE.DirectionalLight(0xffffff, 0.8);
            light2.position.set(-1, -1, -1);
            scene.add(light2);

            // Static Scene Elements: Unit Cell and Axes
            const cubeGeom = new THREE.BoxGeometry(1, 1, 1);
            const edgesGeom = new THREE.EdgesGeometry(cubeGeom);
            const wireframe = new THREE.LineSegments(edgesGeom, new THREE.LineBasicMaterial({ color: 0x333333 }));
            scene.add(wireframe);

            scene.add(new THREE.ArrowHelper(new THREE.Vector3(1, 0, 0), new THREE.Vector3(0, 0, 0), 0.8, 0xff0000));
            scene.add(new THREE.ArrowHelper(new THREE.Vector3(0, 1, 0), new THREE.Vector3(0, 0, 0), 0.8, 0x00ff00));
            scene.add(new THREE.ArrowHelper(new THREE.Vector3(0, 0, 1), new THREE.Vector3(0, 0, 0), 0.8, 0x0000ff));

            // Groups for dynamic objects
            atomGroup = new THREE.Group();
            planeGroup = new THREE.Group();
            scene.add(atomGroup);
            scene.add(planeGroup);

            // Event Listeners
            btnSC.addEventListener('click', () => { setActiveLattice('sc'); updateScene(); });
            btnBCC.addEventListener('click', () => { setActiveLattice('bcc'); updateScene(); });
            btnFCC.addEventListener('click', () => { setActiveLattice('fcc'); updateScene(); });

            [inputH, inputK, inputL].forEach(input => input.addEventListener('change', updateScene));

            sliderOpacity.addEventListener('input', () => {
                const opacity = parseFloat(sliderOpacity.value);
                planeGroup.children.forEach(plane => {
                    plane.material.opacity = opacity;
                });
            });

            window.addEventListener('resize', onWindowResize);

            // Initial call
            updateScene();
            animate();
        }

        // --- Animation Loop ---
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        // --- Core Update Function ---
        function updateScene() {
            // Clear previous dynamic objects
            atomGroup.clear();
            planeGroup.clear();

            // Draw new objects based on current state
            drawAtoms();
            drawPlanes();
        }

        // --- Drawing Logic ---
        function drawAtoms() {
            const latticeType = getActiveLattice();
            const atomRadius = 0.08;
            const atomGeometry = new THREE.SphereGeometry(atomRadius, 32, 16);
            const atomMaterial = new THREE.MeshPhongMaterial({ 
                color: 0x225577, 
                specular: 0x88ffff, 
                shininess: 50 
            });

            const positions = [];
            const corners = [
                [0.5, 0.5, 0.5], [-0.5, 0.5, 0.5], [0.5, -0.5, 0.5], [0.5, 0.5, -0.5],
                [-0.5, -0.5, 0.5], [-0.5, 0.5, -0.5], [0.5, -0.5, -0.5], [-0.5, -0.5, -0.5]
            ];

            positions.push(...corners); // SC has only corner atoms

            if (latticeType === 'bcc') {
                positions.push([0, 0, 0]); // Body-centered atom
            } else if (latticeType === 'fcc') {
                positions.push( // Face-centered atoms
                    [0.5, 0, 0], [-0.5, 0, 0], [0, 0.5, 0],
                    [0, -0.5, 0], [0, 0, 0.5], [0, 0, -0.5]
                );
            }

            positions.forEach(pos => {
                const atom = new THREE.Mesh(atomGeometry, atomMaterial);
                atom.position.set(pos[0], pos[1], pos[2]);
                atomGroup.add(atom);
            });
        }

        function drawPlanes() {
            let h = parseInt(inputH.value);
            let k = parseInt(inputK.value);
            let l = parseInt(inputL.value);
            const opacity = parseFloat(sliderOpacity.value);

            if (h === 0 && k === 0 && l === 0) return;

            // Simplify Miller indices
            const commonDivisor = gcd(gcd(h, k), l);
            h /= commonDivisor;
            k /= commonDivisor;
            l /= commonDivisor;

            const normal = new THREE.Vector3(h, k, l);

            // Define cube geometry for intersection
            const cubeVertices = [
                new THREE.Vector3(-0.5, -0.5, -0.5), new THREE.Vector3( 0.5, -0.5, -0.5),
                new THREE.Vector3( 0.5,  0.5, -0.5), new THREE.Vector3(-0.5,  0.5, -0.5),
                new THREE.Vector3(-0.5, -0.5,  0.5), new THREE.Vector3( 0.5, -0.5,  0.5),
                new THREE.Vector3( 0.5,  0.5,  0.5), new THREE.Vector3(-0.5,  0.5,  0.5)
            ];
            const cubeEdges = [
                [0, 1], [1, 2], [2, 3], [3, 0], // bottom face
                [4, 5], [5, 6], [6, 7], [7, 4], // top face
                [0, 4], [1, 5], [2, 6], [3, 7]  // side edges
            ];

            // Determine range of n for hx + ky + lz = n
            const values = cubeVertices.map(v => v.dot(normal));
            const minVal = Math.min(...values);
            const maxVal = Math.max(...values);

            for (let n = Math.ceil(minVal - 1e-6); n <= Math.floor(maxVal + 1e-6); n++) {
                const intersectionPoints = [];
                cubeEdges.forEach(([i, j]) => {
                    const p1 = cubeVertices[i];
                    const p2 = cubeVertices[j];
                    const val1 = p1.dot(normal) - n;
                    const val2 = p2.dot(normal) - n;

                    if (val1 * val2 <= 0) { // Intersects edge (or touches endpoint)
                        const t = Math.abs(val1) / (Math.abs(val1) + Math.abs(val2));
                        if(Math.abs(val1) + Math.abs(val2) > 1e-9) { // Avoid division by zero if plane contains edge
                           const intersect = new THREE.Vector3().lerpVectors(p1, p2, t);
                           // Avoid duplicate points
                           if (!intersectionPoints.some(p => p.distanceTo(intersect) < 1e-6)) {
                               intersectionPoints.push(intersect);
                           }
                        }
                    }
                });

                if (intersectionPoints.length >= 3) {
                    // Sort points to form a polygon
                    const center = intersectionPoints.reduce((acc, p) => acc.add(p), new THREE.Vector3()).divideScalar(intersectionPoints.length);
                    const sortedPoints = sortVertices(intersectionPoints, normal, center);

                    // Create polygon mesh from sorted points via triangulation
                    const geometry = new THREE.BufferGeometry();
                    const vertices = [];
                    for (let i = 1; i < sortedPoints.length - 1; i++) {
                        vertices.push(sortedPoints[0].x, sortedPoints[0].y, sortedPoints[0].z);
                        vertices.push(sortedPoints[i].x, sortedPoints[i].y, sortedPoints[i].z);
                        vertices.push(sortedPoints[i+1].x, sortedPoints[i+1].y, sortedPoints[i+1].z);
                    }
                    geometry.setAttribute('position', new THREE.Float32BufferAttribute(vertices, 3));
                    geometry.computeVertexNormals();

                    const material = new THREE.MeshBasicMaterial({
                        color: 0x007bff,
                        opacity: opacity,
                        transparent: true,
                        side: THREE.DoubleSide
                    });
                    const planeMesh = new THREE.Mesh(geometry, material);
                    planeGroup.add(planeMesh);
                }
            }
        }

        // --- Helper Functions ---
        function onWindowResize() {
            camera.aspect = canvasContainer.clientWidth / canvasContainer.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(canvasContainer.clientWidth, canvasContainer.clientHeight);
        }

        function setActiveLattice(type) {
            btnSC.classList.toggle('active', type === 'sc');
            btnBCC.classList.toggle('active', type === 'bcc');
            btnFCC.classList.toggle('active', type === 'fcc');
        }

        function getActiveLattice() {
            if (btnSC.classList.contains('active')) return 'sc';
            if (btnBCC.classList.contains('active')) return 'bcc';
            if (btnFCC.classList.contains('active')) return 'fcc';
        }

        function gcd(a, b) {
            a = Math.abs(a);
            b = Math.abs(b);
            while (b) {
                [a, b] = [b, a % b];
            }
            return a;
        }

        function sortVertices(points, normal, center) {
            // Choose two orthogonal vectors on the plane
            let u = new THREE.Vector3().subVectors(points[0], center).normalize();
            let v = new THREE.Vector3().crossVectors(normal, u).normalize();

            return points.sort((a, b) => {
                const vecA = new THREE.Vector3().subVectors(a, center);
                const vecB = new THREE.Vector3().subVectors(b, center);
                const angleA = Math.atan2(vecA.dot(v), vecA.dot(u));
                const angleB = Math.atan2(vecB.dot(v), vecB.dot(u));
                return angleA - angleB;
            });
        }

        // --- Run ---
        init();

    </script>
