            "12": [[0,0.447214,0.894427],[0,-0.447214,0.894427],[0.894427,0,0.447214],[-0.894427,0,0.447214],[0.587785,0.723607,-0.356822],[-0.587785,0.723607,-0.356822],[0.587785,-0.723607,-0.356822],[-0.587785,-0.723607,-0.356822],[0.356822,0.894427,0],[ -0.356822,0.894427,0],[0.951057,-0.309017,-0.223607],[-0.951057,-0.309017,-0.223607]], // Icosahedron (normalized)
            "20": [[0,0,1],[0.2831,0.8519,0.4472],[0.8718,0.309,0.3869],[-0.1557,0.5257,0.8354],[-0.7236,0.5878,0.366],[0.5393,0,0.8421],[-0.9254,0.1876,0.3228],[-0.4472,-0.1625,0.8789],[0.4472,0.1625,-0.8789],[-0.5393,0,-0.8421],[0.1557,-0.5257,-0.8354],[0.7236,-0.5878,-0.366],[-0.2831,-0.8519,-0.4472],[0.9254,-0.1876,-0.3228],[-0.8718,-0.309,-0.3869],[0,0,-1],[-0.2831,0.8519,-0.4472],[0.2831,-0.8519,0.4472],[-0.8718,0.309,-0.3869],[0.8718,-0.309,-0.3869]] // Dodecahedron (normalized)
        };
        Object.keys(THOMSON_POINTS).forEach(key => { // Normalize all pre-calculated points
             THOMSON_POINTS[key] = THOMSON_POINTS[key].map(p => new THREE.Vector3(...p).normalize().toArray());
        });

        // --- DOM ELEMENTS ---
        const sliderPoints = document.getElementById('slider-points');
        const labelPoints = document.getElementById('label-points');
        const btn3D = document.getElementById('btn-3d');
        const btn2D = document.getElementById('btn-2d');
        const canvas3D = document.getElementById('canvas-3d');
        const div2DPlot = document.getElementById('div-2d-plot');
        const canvas2D = document.getElementById('canvas-2d');
        const div2DText = document.getElementById('div-2d-text');

        // --- STATE ---
        let state = {
            numPoints: 20,
            currentView: '3D',
            pointsData: []
        };

        // --- 3D SCENE VARIABLES ---
        let scene, camera, renderer, controls, mainSphere, pointsGroup;
        let pointObjects = []; // To hold { solid, ring } pairs

        // --- 2D CANVAS VARIABLES ---
        let ctx2d = canvas2D.getContext('2d');

        // --- INITIALIZATION ---
        function init() {
            setupEventListeners();
            init3D();
            updateVisualization();
            animate();
        }

        // --- EVENT LISTENERS ---
        function setupEventListeners() {
            sliderPoints.addEventListener('input', () => {
                state.numPoints = parseInt(sliderPoints.value, 10);
                labelPoints.textContent = `+ ${state.numPoints}`;
                updateVisualization();
            });

            btn3D.addEventListener('click', () => {
                if (state.currentView === '3D') return;
                state.currentView = '3D';

                btn3D.classList.add('active');
                btn2D.classList.remove('active');

                canvas3D.style.display = 'block';
                div2DPlot.style.display = 'none';

                updateVisualization(); // Ensure 3D view is up-to-date
            });

            btn2D.addEventListener('click', () => {
                if (state.currentView === '2D') return;
                state.currentView = '2D';

                btn2D.classList.add('active');
                btn3D.classList.remove('active');

                canvas3D.style.display = 'none';
                div2DPlot.style.display = 'flex';

                updateVisualization(); // Draw 2D plot
            });

            window.addEventListener('resize', onWindowResize, false);
        }

        // --- CORE LOGIC ---
        function updateVisualization() {
            // Step 1: Get or generate point data
            const key = state.numPoints.toString();
            state.pointsData = THOMSON_POINTS[key] ? THOMSON_POINTS[key] : generateRandomPoints(state.numPoints);

            // Step 2: Render based on current view
            if (state.currentView === '3D') {
                update3DScene();
            } else { // '2D'
                draw2DPlot();
                calculateAndDisplayDistances();
            }
        }

        // --- 3D LOGIC ---
        function init3D() {
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xf0f0f0);

            const visSection = document.querySelector('.visualization-section');
            const width = visSection.clientWidth;
            const height = visSection.clientHeight;

            camera = new THREE.PerspectiveCamera(50, width / height, 0.1, 100);
            camera.position.z = 3.5;

            renderer = new THREE.WebGLRenderer({ canvas: canvas3D, antialias: true });
            renderer.setSize(width, height);

            // Lighting
            const ambientLight = new THREE.AmbientLight(0xcccccc, 0.5);
            scene.add(ambientLight);
            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
            directionalLight.position.set(1, 1, 1).normalize();
            scene.add(directionalLight);

            // Main Sphere
            const sphereGeometry = new THREE.SphereGeometry(1, 64, 64);
            const sphereMaterial = new THREE.MeshLambertMaterial({
                color: 0xFFFFE0,
                transparent: true,
                opacity: 0.6
            });
            mainSphere = new THREE.Mesh(sphereGeometry, sphereMaterial);
            scene.add(mainSphere);

            // Points Group
            pointsGroup = new THREE.Group();
            scene.add(pointsGroup);

            // Controls
            controls = new OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.05;
            controls.minDistance = 1.5;
            controls.maxDistance = 10;
        }

        function update3DScene() {
            // Clear previous points
            pointsGroup.clear();
            pointObjects = [];

            const solidGeom = new THREE.SphereGeometry(0.025, 16, 16);
            const ringGeom = new THREE.RingGeometry(0.02, 0.025, 32);
            const material = new THREE.MeshBasicMaterial({ color: 0x0000ff, side: THREE.DoubleSide });

            state.pointsData.forEach(posArray => {
                const pos = new THREE.Vector3(...posArray);

                const solid = new THREE.Mesh(solidGeom, material);
                solid.position.copy(pos);

                const ring = new THREE.Mesh(ringGeom, material);
                ring.position.copy(pos);

                pointsGroup.add(solid);
                pointsGroup.add(ring);
                pointObjects.push({ solid, ring });
            });
        }

        function animate() {
            requestAnimationFrame(animate);
            controls.update();

            // Front/Back hemisphere logic
            if (state.currentView === '3D' && pointObjects.length > 0) {
                const camDir = new THREE.Vector3();
                camera.getWorldDirection(camDir);

                pointObjects.forEach(p => {
                    const pointNormal = p.solid.position.clone().normalize();
                    const dot = camDir.dot(pointNormal);

                    if (dot < 0) { // Point is on the front hemisphere
                        p.solid.visible = true;
                        p.ring.visible = false;
                    } else { // Point is on the back hemisphere
                        p.solid.visible = false;
                        p.ring.visible = true;
                        p.ring.lookAt(camera.position); // Make ring face camera
                    }
                });
            }

            renderer.render(scene, camera);
        }

        function onWindowResize() {
            const visSection = document.querySelector('.visualization-section');
            const width = visSection.clientWidth;
            const height = visSection.clientHeight;

            camera.aspect = width / height;
            camera.updateProjectionMatrix();
            renderer.setSize(width, height);

            // Adjust 2D canvas size too if needed
            const canvas2DSize = Math.min(width - 40, height - 80);
            canvas2D.width = canvas2DSize;
            canvas2D.height = canvas2DSize / 2;
            if (state.currentView === '2D') {
                draw2DPlot();
            }
        }

        // --- 2D LOGIC ---
        function draw2DPlot() {
            const w = canvas2D.width;
            const h = canvas2D.height;
            ctx2d.clearRect(0, 0, w, h);
            ctx2d.fillStyle = '#0000ff';

            state.pointsData.forEach(posArray => {
                // Cartesian (x, y, z) to Spherical (theta, phi)
                const [x, y, z] = posArray;
                const r = Math.sqrt(x*x + y*y + z*z); // radius should be 1
                const theta = Math.atan2(y, x); // Equator angle [-PI, PI]
                const phi = Math.acos(z / r);   // Polar angle [0, PI]

                // Map to canvas coordinates
                const px = ((theta + Math.PI) / (2 * Math.PI)) * w;
                const py = (phi / Math.PI) * h;

                ctx2d.beginPath();
                ctx2d.arc(px, py, 3, 0, 2 * Math.PI);
                ctx2d.fill();
            });
        }

        function calculateAndDisplayDistances() {
            const points = state.pointsData;
            if (points.length < 2) {
                div2DText.innerHTML = "Not enough points to calculate distance.";
                return;
            }

            let minDistanceSq = Infinity;

            for (let i = 0; i < points.length; i++) {
                for (let j = i + 1; j < points.length; j++) {
                    const p1 = points[i];
                    const p2 = points[j];
                    const dx = p1[0] - p2[0];
                    const dy = p1[1] - p2[1];
                    const dz = p1[2] - p2[2];
                    const distSq = dx * dx + dy * dy + dz * dz;
                    if (distSq < minDistanceSq) {
                        minDistanceSq = distSq;
                    }
                }
            }

            const minDistance = Math.sqrt(minDistanceSq);
            div2DText.innerHTML = `
                minimum distance between points = ${minDistance.toFixed(5)}<br>
                maximum distance between points = 2.00000
            `;
        }

        // --- UTILITIES ---
        function generateRandomPoints(n) {
            const points = [];
            for (let i = 0; i < n; i++) {
                // Marsaglia's method
                let u, v, s;
                do {
                    u = Math.random() * 2 - 1;
                    v = Math.random() * 2 - 1;
                    s = u * u + v * v;
                } while (s >= 1);

                const mult = 2 * Math.sqrt(1 - s);
                const x = mult * u;
                const y = mult * v;
                const z = 1 - 2 * s;
                points.push([x, y, z]);
            }
            return points;
        }

        // --- RUN ---
        init();
        onWindowResize(); // Set initial canvas sizes correctly

    </script>
</body>
</html>
