    <script>
        // --- GLOBAL STATE AND CONTROLS ---

        const params = {
            epsilon: 23.45,   // angle
            omega: 102.95,    // longitude
            e: 0.016722       // eccentricity
        };

        let orbitSketchInstance, plotSketchInstance;

        document.addEventListener('DOMContentLoaded', () => {
            const sliderAngle = document.getElementById('slider-angle');
            const valueAngle = document.getElementById('value-angle');
            const sliderLongitude = document.getElementById('slider-longitude');
            const valueLongitude = document.getElementById('value-longitude');
            const sliderEccentricity = document.getElementById('slider-eccentricity');
            const valueEccentricity = document.getElementById('value-eccentricity');

            function updateValues() {
                params.epsilon = parseFloat(sliderAngle.value);
                params.omega = parseFloat(sliderLongitude.value);
                params.e = parseFloat(sliderEccentricity.value);

                valueAngle.textContent = params.epsilon.toFixed(2);
                valueLongitude.textContent = params.omega.toFixed(2);
                valueEccentricity.textContent = params.e.toFixed(6);
            }

            function onSliderInput() {
                updateValues();
                if (orbitSketchInstance) orbitSketchInstance.redraw();
                if (plotSketchInstance) plotSketchInstance.redraw();
            }

            // Attach listeners
            [sliderAngle, sliderLongitude, sliderEccentricity].forEach(slider => {
                slider.addEventListener('input', onSliderInput);
            });

            // Initialize
            updateValues();

            // Create p5.js sketches
            orbitSketchInstance = new p5(orbitSketch, 'orbit-canvas');
            plotSketchInstance = new p5(plotSketch, 'plot-canvas');
        });


        // --- ORBIT SKETCH (p5.js instance) ---

        const orbitSketch = (p) => {
            let canvasWidth, canvasHeight;

            p.setup = () => {
                const container = p.select('#orbit-canvas');
                canvasWidth = container.width;
                canvasHeight = canvasWidth * 0.5; // Maintain a 2:1 aspect ratio
                p.createCanvas(canvasWidth, canvasHeight);
                p.noLoop();
            };

            p.draw = () => {
                p.background('#f0f0f0');

                // Center the coordinate system
                p.translate(p.width / 2, p.height / 2);

                const e = params.e;
                const omega_rad = p.radians(params.omega);

                // Semi-major axis is fixed relative to canvas size
                const a = p.width * 0.4;
                // Calculate semi-minor axis based on eccentricity
                const b = a * p.sqrt(1 - e * e);

                p.rotate(omega_rad);

                p.stroke('#a0522d');
                p.strokeWeight(2.5);
                p.noFill();
                p.ellipse(0, 0, a * 2, b * 2);
            };
        };


        // --- EQUATION OF TIME PLOT SKETCH (p5.js instance) ---

        const plotSketch = (p) => {
            let canvasWidth, canvasHeight;
            const margin = { top: 30, right: 30, bottom: 50, left: 60 };
            let plotWidth, plotHeight;

            p.setup = () => {
                const container = p.select('#plot-canvas');
                canvasWidth = container.width;
                canvasHeight = canvasWidth * 0.5; // Maintain a 2:1 aspect ratio
                p.createCanvas(canvasWidth, canvasHeight);

                plotWidth = canvasWidth - margin.left - margin.right;
                plotHeight = canvasHeight - margin.top - margin.bottom;

                p.noLoop();
            };

            p.draw = () => {
                p.background(255);

                const e = params.e;
                const epsilon_rad = p.radians(params.epsilon);
                const omega_rad = p.radians(params.omega);

                // --- Calculation Step ---
                const points = [];
                const numPoints = 400;
                let last_eot_rad;

                for (let i = 0; i <= numPoints; i++) {
                    const M = (i / numPoints) * 2 * p.PI;

                    // 1. Solve Kepler's Equation for E using Newton's method
                    let E = M;
                    for (let j = 0; j < 8; j++) {
                        const numerator = E - e * p.sin(E) - M;
                        const denominator = 1 - e * p.cos(E);
                        if (p.abs(denominator) < 1e-6) break; // Avoid division by zero
                        E = E - numerator / denominator;
                    }

                    // 2. Calculate True Anomaly (ν)
                    const nu = 2 * p.atan2(p.sqrt(1 + e) * p.sin(E / 2), p.sqrt(1 - e) * p.cos(E / 2));

                    // 3. Calculate True Longitude (λ)
                    const lambda = nu + omega_rad;

                    // 4. Calculate Right Ascension (α)
                    let alpha = p.atan2(p.cos(epsilon_rad) * p.sin(lambda), p.cos(lambda));

                    // 5. Calculate Equation of Time in radians
                    let eot_rad = M - alpha;

                    // 6. Phase Unwrapping for a continuous curve
                    if (i > 0) {
                        while (eot_rad - last_eot_rad > p.PI) eot_rad -= 2 * p.PI;
                        while (eot_rad - last_eot_rad < -p.PI) eot_rad += 2 * p.PI;
                    }
                    last_eot_rad = eot_rad;

                    // 7. Convert to Minutes
                    const eot_minutes = eot_rad * (720 / p.PI);

                    points.push({ x: M, y: eot_minutes });
                }

                // --- Plotting Step ---

                // Determine dynamic Y-axis range
                const yValues = points.map(pt => pt.y);
                let minY = p.min(yValues);
                let maxY = p.max(yValues);
                const yRange = maxY - minY;

                // Add padding and handle flat line case
                minY -= yRange * 0.1 || 1;
                maxY += yRange * 0.1 || 1;

                p.translate(margin.left, margin.top);

                drawAxes(p, minY, maxY);

                // Draw the EOT curve
                p.noFill();
                p.stroke('#ff8c00');
                p.strokeWeight(2);
                p.beginShape();
                for (const pt of points) {
                    const screenX = p.map(pt.x, 0, 2 * p.PI, 0, plotWidth);
                    const screenY = p.map(pt.y, minY, maxY, plotHeight, 0); // Y is inverted in screen space
                    p.vertex(screenX, screenY);
                }
                p.endShape();
            };

            const drawAxes = (p, minY, maxY) => {
                p.push(); // Isolate drawing state for axes

                // Axes lines
                p.stroke(0);
                p.strokeWeight(1);
                p.line(0, 0, 0, plotHeight); // Y-axis
                p.line(0, plotHeight, plotWidth, plotHeight); // X-axis

                // Text properties
                p.fill(80);
                p.noStroke();
                p.textSize(12);

                // Y-axis label
                p.textAlign(p.CENTER, p.CENTER);
                p.push();
                p.translate(-margin.left + 20, plotHeight / 2);
                p.rotate(-p.HALF_PI);
                p.text("minutes", 0, 0);
                p.pop();

                // X-axis label
                p.text("Mean Anomaly (radians)", plotWidth / 2, plotHeight + margin.bottom / 2 + 10);

                // Y-axis ticks and gridlines
                p.textAlign(p.RIGHT, p.CENTER);
                const yTickCount = 5;
                for (let i = 0; i <= yTickCount; i++) {
                    const yVal = p.lerp(minY, maxY, i / yTickCount);
                    const y = p.map(yVal, minY, maxY, plotHeight, 0);

                    p.stroke(220); p.strokeWeight(1); // Gridline
                    p.line(0, y, plotWidth, y);

                    p.stroke(0); p.strokeWeight(1); // Tick
                    p.line(-5, y, 0, y);

                    p.noStroke(); // Label
                    p.text(yVal.toFixed(1), -8, y);
                }

                // X-axis ticks and gridlines
                p.textAlign(p.CENTER, p.TOP);
                for (let i = 0; i <= 6; i++) {
                    const x = p.map(i, 0, 2 * p.PI, 0, plotWidth);

                    p.stroke(220); p.strokeWeight(1); // Gridline
                    if (i > 0) p.line(x, 0, x, plotHeight);

                    p.stroke(0); p.strokeWeight(1); // Tick
                    p.line(x, plotHeight, x, plotHeight + 5);

                    p.noStroke(); // Label
                    p.text(i, x, plotHeight + 8);
                }
                p.pop(); // Restore drawing state
            };
        };

    </script>
</body>
