                <input type="range" id="slider-h_C" min="0.1" max="5" value="1.9" step="0.1">
            </div>

            <div class="control-group">
                <label for="slider-delta">&delta; = &alpha; - &beta; = <span id="delta-value" class="value-display">0.5</span></label>
                <input type="range" id="slider-delta" min="-1.5" max="1.5" value="0.5" step="0.01">
            </div>

