                <label>Steps</label>
                <div class="segmented-control">
                    <input type="radio" id="step1" name="steps" value="1">
                    <label for="step1">1</label>

                    <input type="radio" id="step2" name="steps" value="2">
                    <label for="step2">2</label>

                    <input type="radio" id="step3" name="steps" value="3">
                    <label for="step3">3</label>

                    <input type="radio" id="step4" name="steps" value="4">
                    <label for="step4">4</label>

                    <input type="radio" id="step5" name="steps" value="5" checked>
                    <label for="step5">5</label>
                </div>
            </div>

