            <div class="control-group">
                <label for="slider-plot-range">Plot Range = <span id="plot-range-value" class="value-display">1</span></label>
                <input type="range" id="slider-plot-range" min="0.5" max="2" value="1" step="0.01">
            </div>
        </div>
