<script>
let sketch = function(p) {
    // Control elements
    let sliderC, sliderHC, sliderDelta, sliderPlotRange;
    let cValueSpan, hCValueSpan, deltaValueSpan, plotRangeValueSpan;
    let stepRadios;
    let verificationCheck;

    // State variables
    let c, h_C, delta, plotRange, currentStep, showVerification;

    const baseScale = 50;

    p.setup = function() {
        const container = document.getElementById('canvas-container');
        const canvas = p.createCanvas(container.clientWidth, 500);
        canvas.id('visualization-canvas');
        canvas.parent(container);

        // Get references to all control elements
        sliderC = p.select('#slider-c');
        cValueSpan = p.select('#c-value');
        sliderHC = p.select('#slider-h_C');
        hCValueSpan = p.select('#h_C-value');
        sliderDelta = p.select('#slider-delta');
        deltaValueSpan = p.select('#delta-value');
        sliderPlotRange = p.select('#slider-plot-range');
        plotRangeValueSpan = p.select('#plot-range-value');

        stepRadios = document.querySelectorAll('input[name="steps"]');
        verificationCheck = p.select('#check-verification');

        // Attach event listeners
        sliderC.input(updateStateAndRedraw);
        sliderHC.input(updateStateAndRedraw);
        sliderDelta.input(updateStateAndRedraw);
        sliderPlotRange.input(updateStateAndRedraw);
        verificationCheck.changed(updateStateAndRedraw);
        stepRadios.forEach(radio => {
            radio.addEventListener('change', updateStateAndRedraw);
        });

        p.noLoop();
        updateStateAndRedraw(); // Initial draw
    };

    function updateStateAndRedraw() {
        // Read values from controls
        c = parseFloat(sliderC.value());
        h_C = parseFloat(sliderHC.value());
        delta = parseFloat(sliderDelta.value());
        plotRange = parseFloat(sliderPlotRange.value());
        showVerification = verificationCheck.checked();

        document.querySelectorAll('input[name="steps"]').forEach(radio => {
            if (radio.checked) {
                currentStep = parseInt(radio.value);
            }
        });

        // Update value displays
        cValueSpan.html(c.toFixed(1));
        hCValueSpan.html(h_C.toFixed(1));
        deltaValueSpan.html(delta.toFixed(2));
        plotRangeValueSpan.html(plotRange.toFixed(2));

        redrawScene();
    }

    function redrawScene() {
        p.background(224, 224, 224); // #E0E0E0

        // Set up coordinate system
        p.push();
        p.translate(p.width / 2, p.height * 0.8);
        p.scale(1, -1);
        const scaleFactor = baseScale * plotRange;
        p.scale(scaleFactor);

        // --- GEOMETRIC CALCULATIONS ---
        // 1. Initial points
        const F = p.createVector(0, 0);
        const C = p.createVector(0, h_C);
        const D = p.createVector(c / 2, 0);
        const K = p.createVector(c, 0);

        // 2. Solve for M
        const tanDelta = p.tan(delta);
        let M;

        // Equation for perpendicular bisector of CD: c*x - 2*h_C*y + h_C^2 - c^2/4 = 0
        // From this, x_M = (2*h_C/c)*y_M - h_C^2/c + c/4
        const k1 = (2 * h_C) / c;
        const k2 = (-h_C * h_C) / c + c / 4;

        // Substitute into locus equation for angle DMK = delta
        // t * (y^2 + x^2 - (3c/2)x + c^2/2) = (c/2)y
        // which becomes a quadratic A*y^2 + B*y + C_quad = 0
        const A_quad = tanDelta * (1 + k1 * k1);
        const B_quad = tanDelta * (2 * k1 * k2 - (3 * c / 2) * k1) - c / 2;
        const C_quad = tanDelta * (k2 * k2 - (3 * c / 2) * k2 + c * c / 2);

        const discriminant = B_quad * B_quad - 4 * A_quad * C_quad;
        let y_M, x_M;

        if (discriminant >= 0 && A_quad !== 0) {
            // As per geometric considerations, the y-coordinate of M should be negative.
            const y_M1 = (-B_quad + p.sqrt(discriminant)) / (2 * A_quad);
            const y_M2 = (-B_quad - p.sqrt(discriminant)) / (2 * A_quad);
            y_M = (p.abs(y_M1) > p.abs(y_M2)) ? y_M1 : y_M2; // Heuristic, often the one further from 0
            if (p.abs(tanDelta) < 1e-3) { // When delta is near 0, M is on y-axis
                y_M = (k2 * k2 - (3*c/2)*k2 + c*c/2) / (c / (2*tanDelta)); // Avoid division by zero
                y_M = y_M2; // The negative root is generally the correct one
            }
            x_M = k1 * y_M + k2;
            M = p.createVector(x_M, y_M);
        } else {
             M = p.createVector(0, -100); // Invalid solution, place M off-screen
        }

        // 3. Radius of circle kappa
        const R = C.dist(M);

        // 4. Point B
        const x_B = 2 * M.x - c/2;
        const B = p.createVector(x_B, 0);

        // 5. Point A
        const A = p.createVector(x_B - c, 0);

        // 6. Point E (Verification)
        const E = p.createVector(c - x_B, 0);

        // --- RENDERING ---
        p.stroke(0);
        p.strokeWeight(1 / scaleFactor); // Keep stroke weight constant

        // Step 1: Base geometry
        if (currentStep >= 1) {
            // Line lambda
            p.strokeWeight(1 / scaleFactor);
            p.line(-p.width, 0, p.width, 0);

            // Points F, C, D
            drawPoint(F, 'F');
            drawPoint(C, 'C');
            drawPoint(D, 'D');

            // Line segment FC (altitude)
            p.line(F.x, F.y, C.x, C.y);
        }

        // Step 2: Construction of M and kappa
        if (currentStep >= 2) {
            drawPoint(K, 'K');
            drawDashedLine(C, D);
            drawDashedLine(D, K);
            if (M) {
                drawPoint(M, 'M');
                drawDashedLine(M, C);
                drawDashedLine(M, D);
                drawDashedLine(M, K);

                // Arc kappa
                p.noFill();
                p.stroke(0, 0, 255, 150); // Blue for emphasis
                p.strokeWeight(2 / scaleFactor);
                let startAngle = p.atan2(C.y - M.y, C.x - M.x);
                p.arc(M.x, M.y, 2 * R, 2 * R, startAngle - p.PI * 1.5, startAngle + p.PI * 0.5);
                p.stroke(0);
            }
        }

        // Step 3: Find point B
        if (currentStep >= 3) {
            drawPoint(B, 'B');
        }

        // Step 4: Find point A
        if (currentStep >= 4) {
            drawPoint(A, 'A');
        }

        // Step 5: Draw the final triangle
        if (currentStep >= 5) {
            p.strokeWeight(3 / scaleFactor);
            p.line(A.x, A.y, B.x, B.y);
            p.line(B.x, B.y, C.x, C.y);
            p.line(C.x, C.y, A.x, A.y);
        }

        // Verification geometry
        if (showVerification) {
            drawPoint(E, 'E');
            drawDashedLine(C, E);
            drawDashedLine(K, B);
        }

        p.pop(); // Restore original drawing state
    }

    // --- HELPER DRAWING FUNCTIONS ---

    function drawPoint(pt, label) {
        const pointSize = 8 / (baseScale * plotRange);
        p.fill(0);
        p.noStroke();
        p.ellipse(pt.x, pt.y, pointSize, pointSize);

        // Draw label without transformations
        p.push();
        p.resetMatrix();
        p.translate(p.width / 2, p.height * 0.8);
        const scaleFactor = baseScale * plotRange;
        p.fill(0);
        p.noStroke();
        p.textSize(14);
        p.textAlign(p.CENTER, p.CENTER);
        // Offset label slightly from point
        p.text(label, pt.x * scaleFactor, -pt.y * scaleFactor - 12);
        p.pop();
    }

    function drawDashedLine(pt1, pt2) {
        p.drawingContext.setLineDash([5 / (baseScale * plotRange), 5 / (baseScale * plotRange)]);
        p.strokeWeight(1 / (baseScale * plotRange));
        p.line(pt1.x, pt1.y, pt2.x, pt2.y);
        p.drawingContext.setLineDash([]); // Reset to solid
    }

    p.windowResized = function() {
        const container = document.getElementById('canvas-container');
        p.resizeCanvas(container.clientWidth, 500);
        redrawScene();
    };

};

new p5(sketch);
</script>

</body>
</html>
