</head>
<body>

    <div id="main-container">
        <!-- 2. Control Panel -->
        <div id="control-panel">
            <label for="slider-z">z (m)</label>
            <input type="range" id="slider-z" min="0" max="32" value="0" step="0.01">
            <span id="label-z">0.</span>
        </div>

        <!-- 3. Plots Area -->
        <div id="plots-container">
            <div id="plot-intensity" class="plot-container"></div>
            <div id="plot-population" class="plot-container"></div>
            <div id="plot-coherence" class="plot-container"></div>
        </div>
    </div>

    <script>
    document.addEventListener('DOMContentLoaded', function() {
        // --- Get DOM Elements ---
        const sliderZ = document.getElementById('slider-z');
        const labelZ = document.getElementById('label-z');
        const PLOT_IDS = {
            intensity: 'plot-intensity',
            population: 'plot-population',
            coherence: 'plot-coherence'
        };

        // --- Constants and Data Generation ---
        const t_values = Array.from({length: 301}, (_, i) => i - 50);
        const C_LIGHT_M_PER_NS = 0.3;

        // Data generation functions for z=0 (Red Curves)
        const I_red = t => (t < -15 || t > 170) ? 0 : (t > 15 ? 1 : 0.5 * (1 + Math.tanh(t/4)));
        const P_red = t => (t < -15) ? 0 : 4.5 * (1 - Math.exp(-(t+15)/50));
        const C_red = t => (t < -15) ? 0 : 2.2 * (1 - Math.exp(-(t+15)/50));

        // Data generation for dynamic z (Blue Curves)
        const calculateBlueData = (z) => {
            const t_delay = z / C_LIGHT_M_PER_NS;
            const scaling_factor_p = 1 - 0.45 * (z / 32);
            const scaling_factor_c = 1 - 0.45 * (z / 32);

            const y_intensity_blue = t_values.map(t => {
                if (t > 170 + t_delay) return null; // Clip data
                const t_prime = t - t_delay;
                const I_base = (t_prime < -15) ? 0 : (t_prime > 15 ? 1 : 0.5 * (1 + Math.tanh(t_prime/4)));
                const envelope = (t_prime > 0) ? Math.exp(-0.035 * z * t_prime / 32) : 1;
                return I_base * envelope;
            });

            const y_population_blue = t_values.map(t => {
                if (t > 170 + t_delay) return null; // Clip data
                const t_prime = t - t_delay;
                return scaling_factor_p * ((t_prime < -15) ? 0 : 4.5 * (1 - Math.exp(-(t_prime+15)/50)));
            });

            const y_coherence_blue = t_values.map(t => {
                if (t > 170 + t_delay) return null; // Clip data
                const t_prime = t - t_delay;
                return scaling_factor_c * ((t_prime < -15) ? 0 : 2.2 * (1 - Math.exp(-(t_prime+15)/50)));
            });

            return { y_intensity_blue, y_population_blue, y_coherence_blue };
        };

        // --- Initial Data Calculation ---
        const y_intensity_red = t_values.map(I_red);
        const y_population_red = t_values.map(P_red);
        const y_coherence_red = t_values.map(C_red);
        const initialBlueData = calculateBlueData(0);

        // --- Plotly Configuration ---
        const deepCopy = obj => JSON.parse(JSON.stringify(obj));

        const commonLayout = {
            xaxis: { title: "time (ns)", range: [-50, 250], zeroline: true },
            margin: { l: 80, r: 20, t: 40, b: 40 },
            showlegend: true,
            legend: { x: 0.7, y: 0.9 },
            shapes: [
                { type: 'line', x0: 0, y0: 0, x1: 0, y1: 1, yref: 'paper', line: { color: 'black', width: 2, dash: 'dash' } },
                { type: 'line', x0: 0, y0: 0, x1: 0, y1: 1, yref: 'paper', line: { color: 'black', width: 2, dash: 'dash' } }
            ],
            annotations: [
                 { x: 0, y: 0.7, xref: 'x', yref: 'y', text: 'pulse<br>midpoint', showarrow: false, xanchor: 'left', yanchor: 'bottom' }
            ]
        };

        const plotLayouts = {
            intensity: deepCopy(commonLayout),
            population: deepCopy(commonLayout),
            coherence: deepCopy(commonLayout),
        };

        // Specific layout properties
        plotLayouts.intensity.yaxis = { title: 'intensity (arb)', range: [-0.1, 1.1], zeroline: true };
        plotLayouts.population.yaxis = { title: 'excited-state population × 10<sup>4</sup>', range: [-0.5, 5.5], zeroline: true };
        plotLayouts.coherence.yaxis = { title: 'coherence × 10<sup>2</sup>', range: [-0.2, 2.4], zeroline: true };

        // Add plot labels (a, b, c)
        plotLayouts.intensity.annotations.unshift({ x: -45, y: 0.9, xref: 'x', yref: 'y', text: '(a)', showarrow: false });
        plotLayouts.population.annotations.unshift({ x: -45, y: 5.0, xref: 'x', yref: 'y', text: '(b)', showarrow: false });
        plotLayouts.coherence.annotations.unshift({ x: -45, y: 2.2, xref: 'x', yref: 'y', text: '(c)', showarrow: false });

        // Adjust y-position of "pulse midpoint" label for each plot
        plotLayouts.population.annotations[1].y = 3.5;
        plotLayouts.coherence.annotations[1].y = 1.6;

        const traceRed = {
            x: t_values,
            mode: 'lines',
            name: 'z = 0 meters',
            line: { color: 'red', width: 2 }
        };
        const traceBlue = {
            x: t_values,
            mode: 'lines',
            name: 'z = 0. meters',
            line: { color: 'blue', width: 2, dash: 'dash' }
        };

        // --- Initial Plot Rendering ---
        Plotly.newPlot(PLOT_IDS.intensity, [
            { ...traceRed, y: y_intensity_red },
            { ...traceBlue, y: initialBlueData.y_intensity_blue }
        ], plotLayouts.intensity, {responsive: true});

        Plotly.newPlot(PLOT_IDS.population, [
            { ...traceRed, y: y_population_red },
            { ...traceBlue, y: initialBlueData.y_population_blue }
        ], plotLayouts.population, {responsive: true});

        Plotly.newPlot(PLOT_IDS.coherence, [
            { ...traceRed, y: y_coherence_red },
            { ...traceBlue, y: initialBlueData.y_coherence_blue }
        ], plotLayouts.coherence, {responsive: true});


        // --- Interaction Logic ---
        sliderZ.addEventListener('input', function(event) {
            const z = parseFloat(event.target.value);

            // Update Z label with specified formatting
            if (z === Math.floor(z)) {
                labelZ.textContent = z + '.';
            } else {
                labelZ.textContent = z.toFixed(5);
            }

            // Recalculate blue curve data
            const { y_intensity_blue, y_population_blue, y_coherence_blue } = calculateBlueData(z);

            // Update plot traces using Plotly.restyle
            const legendName = `z = ${z.toFixed(2)} meters`;
            Plotly.restyle(PLOT_IDS.intensity, { y: [y_intensity_blue], name: [legendName] }, [1]);
            Plotly.restyle(PLOT_IDS.population, { y: [y_population_blue], name: [legendName] }, [1]);
            Plotly.restyle(PLOT_IDS.coherence, { y: [y_coherence_blue], name: [legendName] }, [1]);

            // Update moving vertical line position using Plotly.relayout
            const t_delay = z / C_LIGHT_M_PER_NS;
            const layoutUpdate = {
                'shapes[1].x0': t_delay,
                'shapes[1].x1': t_delay
            };
            Plotly.relayout(PLOT_IDS.intensity, layoutUpdate);
            Plotly.relayout(PLOT_IDS.population, layoutUpdate);
            Plotly.relayout(PLOT_IDS.coherence, layoutUpdate);
        });
    });
    </script>
</body>
