        document.addEventListener('DOMContentLoaded', function() {
            // --- DOM Element References ---
            const sliderUA = document.getElementById('slider-ua');
            const sliderKr0 = document.getElementById('slider-kr0');
            const sliderTf = document.getElementById('slider-tf');
            const sliderTau = document.getElementById('slider-tau');

            const uaValueSpan = document.getElementById('ua-value');
            const kr0ValueSpan = document.getElementById('kr0-value');
            const tfValueSpan = document.getElementById('tf-value');
            const tauValueSpan = document.getElementById('tau-value');

            // --- Mathematical Constants ---
            const C_A0 = 10;        // mmol/dm³, initial concentration of A
            const R = 1.987;        // cal/mol K, ideal gas constant
            const E_f = 25000;      // cal/mol, forward activation energy
            const delta_H_rxn = -200000; // cal/mol, enthalpy of reaction
            const E_r = E_f - delta_H_rxn; // cal/mol, reverse activation energy
            const k_f0 = 1e15;      // 1/s, forward pre-exponential factor
            const rhoCp = 20000;    // cal/(dm³ K), product of density and heat capacity

            // --- Main Update Function ---
            function updatePlot() {
                // 1. Read current slider values
                const UA = parseFloat(sliderUA.value);
                const kr0_slider_val = parseFloat(sliderKr0.value);
                const k_r0 = kr0_slider_val * 1e12;
                const T_f = parseFloat(sliderTf.value);
                const tau = parseFloat(sliderTau.value);

                // 2. Update value displays
                uaValueSpan.textContent = UA.toFixed(1);
                kr0ValueSpan.innerHTML = kr0_slider_val.toFixed(1) + ' &times; 10<sup>12</sup>';
                tfValueSpan.textContent = T_f.toFixed(0);
                tauValueSpan.textContent = tau.toFixed(0);

                // 3. Recalculate curve data
                const T_values = [];
                const C_B_mass = [];
                const C_B_energy = [];

                for (let T = 250; T <= 400; T += 0.5) {
                    T_values.push(T);

                    // Mass Balance Calculation
                    const k_f = k_f0 * Math.exp(-E_f / (R * T));
                    const k_r = k_r0 * Math.exp(-E_r / (R * T));
                    const cb_mass = (tau * k_f * C_A0) / (1 + tau * (k_f + k_r));
                    C_B_mass.push(cb_mass);

                    // Energy Balance Calculation
                    const cb_energy = ( (rhoCp * (T - T_f)) + (UA * tau * (T - T_f)) ) / (-delta_H_rxn);
                    C_B_energy.push(cb_energy);
                }

                // 4. Update Plotly plot
                const massBalanceTrace = {
                    x: T_values,
                    y: C_B_mass,
                    mode: 'lines',
                    name: 'Mass Balance',
                    line: {
                        color: '#008000',
                        width: 3
                    }
                };

                const energyBalanceTrace = {
                    x: T_values,
                    y: C_B_energy,
                    mode: 'lines',
                    name: 'Energy Balance',
                    line: {
                        color: '#0000FF',
                        width: 3
                    }
                };

                // Position annotations dynamically
                const T_annot_mass = 360;
                const T_annot_energy = 280;
                const index_mass = T_values.indexOf(T_annot_mass);
                const index_energy = T_values.indexOf(T_annot_energy);

                const y_annot_mass = (index_mass !== -1) ? C_B_mass[index_mass] : C_B_mass[C_B_mass.length - 20];
                const y_annot_energy = (index_energy !== -1) ? C_B_energy[index_energy] : C_B_energy[20];

                const layout = {
                    xaxis: {
                        title: 'temperature (K)',
                        range: [250, 400],
                        gridcolor: '#eeeeee'
                    },
                    yaxis: {
                        title: 'product concentration (mmol/dm³)',
                        range: [0, 12],
                        gridcolor: '#eeeeee'
                    },
                    font: {
                        family: 'sans-serif'
                    },
                    margin: { l: 80, r: 20, t: 20, b: 50 },
                    showlegend: false,
                    annotations: [
                        {
                            x: T_annot_mass,
                            y: y_annot_mass,
                            text: 'mass balance',
                            showarrow: false,
                            font: { color: '#008000', size: 14 },
                            xanchor: 'right',
                            yanchor: 'bottom',
                            ay: -15
                        },
                        {
                            x: T_annot_energy,
                            y: y_annot_energy,
                            text: 'energy balance',
                            showarrow: false,
                            font: { color: '#0000FF', size: 14 },
                            xanchor: 'left',
                            yanchor: 'bottom',
                            ay: -15
                        }
                    ]
                };

                Plotly.react('plot-container', [massBalanceTrace, energyBalanceTrace], layout, {responsive: true});
            }

            // --- Event Listeners ---
            sliderUA.addEventListener('input', updatePlot);
            sliderKr0.addEventListener('input', updatePlot);
            sliderTf.addEventListener('input', updatePlot);
            sliderTau.addEventListener('input', updatePlot);

            // --- Initial Plot ---
            updatePlot();
        });
    </script>
</body>
</html>
