<body>

<div id="demo-container">

    <!-- Title and Description -->
    <h1>Wankel Rotary Engine: Epitrochoidal Envelopes</h1>
    <p>This Demonstration gives an animation of an epitrochoid and associated planetary-motion envelope curve. The configuration shown has applications in the internal combustion engines invented by Felix Wankel and popularized by Mazda in RX-7 and RX-8 cars. The "eccentricity ratio" changes the shapes of the curves. The "reference frame" determines what is held stationary in the animation: either the epitrochoid (blue), the envelope curve (purple), or the centers of rotation of both curves. The "inner" envelope is the triangular rotor shape used in place of a piston in a Wankel rotary engine, whereas the "outer" envelope is the continuation of the envelope curve along the opposite extreme of motion.</p>

    <!-- Control Panel -->
    <div id="control-panel">
        <div>
            <label for="slider-eccentricity">eccentricity ratio</label>
            <input type="range" id="slider-eccentricity" min="0.0" max="0.3" step="0.0001" value="0.14">
            <span id="label-eccentricity">0.1400</span>
        </div>
        <div>
            <label for="slider-rotation">rotation angle</label>
            <input type="range" id="slider-rotation" min="0" max="6.28318" step="0.0001" value="0">
            <span id="label-rotation">0.0000</span>
        </div>
        <div>
            <span>reference frame</span>
            <div>
                <input type="radio" name="ref-frame" id="radio-epitrochoid" value="epitrochoid" checked>
                <label for="radio-epitrochoid">epitrochoid</label>
                <input type="radio" name="ref-frame" id="radio-envelope" value="envelope">
                <label for="radio-envelope">envelope</label>
                <input type="radio" name="ref-frame" id="radio-fixed-centers" value="fixed centers">
                <label for="radio-fixed-centers">fixed centers</label>
            </div>
        </div>
        <div>
            <span>envelope to show</span>
            <div>
                <input type="radio" name="envelope-type" id="radio-inner" value="inner" checked>
                <label for="radio-inner">inner</label>
                <input type="radio" name="envelope-type" id="radio-outer" value="outer">
                <label for="radio-outer">outer</label>
                <input type="radio" name="envelope-type" id="radio-both" value="both">
                <label for="radio-both">both</label>
            </div>
        </div>
    </div>

    <!-- Visualization Canvas -->
    <div id="canvas-container">
        <!-- p5.js canvas will be inserted here -->
    </div>

</div>

<script>
let state = {
    k: 0.14,
    phi: 0,
    refFrame: 'epitrochoid',
    envelopeType: 'inner'
};

let epitrochoidPoints = [];
let innerEnvelopePoints = [];
let outerEnvelopePoints = [];

const RESOLUTION = 200;

function setup() {
    const canvasContainer = document.getElementById('canvas-container');
    const canvas = createCanvas(600, 600);
    canvas.parent(canvasContainer);

    // Link controls to state
    const sliderEccentricity = document.getElementById('slider-eccentricity');
    const labelEccentricity = document.getElementById('label-eccentricity');
    sliderEccentricity.addEventListener('input', (e) => {
        state.k = parseFloat(e.target.value);
        labelEccentricity.textContent = state.k.toFixed(4);
        recalculateCurves();
    });

    const sliderRotation = document.getElementById('slider-rotation');
    const labelRotation = document.getElementById('label-rotation');
    sliderRotation.addEventListener('input', (e) => {
        state.phi = parseFloat(e.target.value);
        labelRotation.textContent = state.phi.toFixed(4);
    });

    const refFrameRadios = document.querySelectorAll('input[name="ref-frame"]');
    refFrameRadios.forEach(radio => {
        radio.addEventListener('change', (e) => {
            state.refFrame = e.target.value;
        });
    });

    const envelopeTypeRadios = document.querySelectorAll('input[name="envelope-type"]');
    envelopeTypeRadios.forEach(radio => {
        radio.addEventListener('change', (e) => {
            state.envelopeType = e.target.value;
            recalculateCurves();
        });
    });

    // Initial setup from HTML values
    state.k = parseFloat(sliderEccentricity.value);
    labelEccentricity.textContent = state.k.toFixed(4);
    state.phi = parseFloat(sliderRotation.value);
    labelRotation.textContent = state.phi.toFixed(4);
    state.refFrame = document.querySelector('input[name="ref-frame"]:checked').value;
    state.envelopeType = document.querySelector('input[name="envelope-type"]:checked').value;

    recalculateCurves();
}

function calculateEpitrochoid(k) {
    const points = [];
    for (let i = 0; i <= RESOLUTION; i++) {
        const t = map(i, 0, RESOLUTION, 0, TWO_PI);
        const x = cos(t) - k * cos(3 * t);
        const y = sin(t) - k * sin(3 * t);
        points.push(createVector(x, y));
    }
    return points;
}

function calculateEnvelope(k, R = 1) {
    const innerPoints = [];
    const outerPoints = [];
    const e = k;
    for (let i = 0; i <= RESOLUTION; i++) {
        const t = map(i, 0, RESOLUTION, 0, TWO_PI);
        // Inner envelope
        const x_inner = (2 / 3) * R * cos(t) + e * cos(2 * t);
        const y_inner = (2 / 3) * R * sin(t) - e * sin(2 * t);
        innerPoints.push(createVector(x_inner, y_inner));
        // Outer envelope
        const x_outer = (2 / 3) * R * cos(t) - e * cos(2 * t);
        const y_outer = (2 / 3) * R * sin(t) + e * sin(2 * t);
        outerPoints.push(createVector(x_outer, y_outer));
    }
    return { inner: innerPoints, outer: outerPoints };
}

function recalculateCurves() {
    epitrochoidPoints = calculateEpitrochoid(state.k);
    const envelopes = calculateEnvelope(state.k);
    innerEnvelopePoints = envelopes.inner;
    outerEnvelopePoints = envelopes.outer;
}

function drawCurve(points, strokeColor, weight = 2) {
    stroke(strokeColor);
    strokeWeight(weight);
    noFill();
    beginShape();
    for (const p of points) {
        vertex(p.x, p.y);
    }
    endShape(CLOSE);
}

function drawAxes(scaleFactor, maxVal) {
    stroke(150);
    strokeWeight(1);

    // Axes
    line(-width / 2, 0, width / 2, 0);
    line(0, -height / 2, 0, height / 2);

    // Ticks
    textAlign(CENTER, CENTER);
    noStroke();
    fill(150);
    textSize(10);

    const step = maxVal > 1.2 ? 0.5 : 0.25;
    for (let i = -maxVal; i <= maxVal; i += step) {
        if (abs(i) < 1e-9) continue;
        const xPos = i * scaleFactor;
        const yPos = i * scaleFactor;

        // X-axis ticks
        stroke(150);
        line(xPos, -5, xPos, 5);
        noStroke();
        text(i.toFixed(2), xPos, 15);

        // Y-axis ticks
        stroke(150);
        line(-5, -yPos, 5, -yPos);
        noStroke();
        text(i.toFixed(2), -20, -yPos);
    }
    text("0.00", -15, 15);
}

function draw() {
    background(255);
    translate(width / 2, height / 2);

    // Dynamic scaling
    const maxRadius = (1.0 + state.k) * 1.1; // Add 10% padding
    const scaleFactor = (min(width, height) / 2) / maxRadius;

    push();
    scale(1, -1); // Flip Y-axis for standard Cartesian coordinates
    drawAxes(scaleFactor, maxRadius);
    pop();


    // Motion parameters
    const phi_shaft = 2 * state.phi;
    const theta_rotor = -phi_shaft / 3.0;
    const Cx = state.k * cos(phi_shaft);
    const Cy = state.k * sin(phi_shaft);

    push();
    scale(scaleFactor, -scaleFactor); // Apply scaling and flip Y

    const epitrochoidColor = color(50, 100, 200);
    const envelopeColor = color(230, 150, 0);

    const drawEnvelopes = () => {
        if (state.envelopeType === 'inner' || state.envelopeType === 'both') {
            drawCurve(innerEnvelopePoints, envelopeColor);
        }
        if (state.envelopeType === 'outer' || state.envelopeType === 'both') {
            drawCurve(outerEnvelopePoints, envelopeColor);
        }
    };

    switch (state.refFrame) {
        case 'epitrochoid':
        case 'fixed centers':
            // Draw epitrochoid stationary at origin
            drawCurve(epitrochoidPoints, epitrochoidColor);

            // Transform and draw envelope
            push();
            translate(Cx, Cy);
            rotate(theta_rotor);
            drawEnvelopes();
            pop();
            break;

        case 'envelope':
            // Draw envelope stationary at origin
            drawEnvelopes();

            // Apply inverse transform to epitrochoid
            push();
            // The inverse transformation is R_inv * T_inv
            rotate(-theta_rotor);
            translate(-Cx, -Cy);
            drawCurve(epitrochoidPoints, epitrochoidColor);
            pop();
            break;
    }

    pop();
}
</script>

</body>
</html>
