</head>
<body>
    <div id="container">
        <div id="control-panel">
            <h4>Stability of Multilayered Emulsions</h4>

            <div class="control-group">
                <label for="slider-phi">\(\phi\) <span id="phi-value-display" class="value-display"></span></label>
                <input type="range" id="slider-phi" min="0.0001" max="0.05" step="0.0001" value="0.005">
            </div>

            <hr>

            <div class="control-group">
                <label for="slider-gamma-sat">\(\Gamma_{sat}\) (mg/m²) <span id="gamma-sat-value-display" class="value-display"></span></label>
                <input type="range" id="slider-gamma-sat" min="0.1" max="10" step="0.1" value="1.5">
            </div>
            <div class="control-group">
                <label for="slider-r">`r` (nm) <span id="r-value-display" class="value-display"></span></label>
                <input type="range" id="slider-r" min="50" max="1000" step="10" value="300">
            </div>
            <div class="control-group">
                <label for="slider-rpe">`r_{pe}` (nm) <span id="rpe-value-display" class="value-display"></span></label>
                <input type="range" id="slider-rpe" min="5" max="50" step="1" value="20">
            </div>
            <div class="control-group">
                <label for="slider-mw">`M_W` (kDa) <span id="mw-value-display" class="value-display"></span></label>
                <input type="range" id="slider-mw" min="10" max="1000" step="1" value="166">
            </div>

            <hr>

            <div class="control-group">
                <label for="slider-phi-axis">\(\phi\) axis max. <span id="phi-axis-value-display" class="value-display"></span></label>
                <input type="range" id="slider-phi-axis" min="0.01" max="0.5" step="0.01" value="0.05">
            </div>
            <div class="control-group">
                <label for="slider-c-axis">C axis max. <span id="c-axis-value-display" class="value-display"></span></label>
                <input type="range" id="slider-c-axis" min="1.0" max="5.0" step="0.1" value="2.0">
            </div>
        </div>

        <div id="vis-area">
            <div id="info-display">
                <p id="formula-display"></p>
                <p id="phimax-display"></p>
                <p id="range-display"></p>
            </div>
            <div id="plot-container"></div>
        </div>
    </div>

    <script>
        // --- Global State and DOM References ---
        const state = {};

        const sliders = {
            phi: document.getElementById('slider-phi'),
            gamma_sat: document.getElementById('slider-gamma-sat'),
            r: document.getElementById('slider-r'),
            r_pe: document.getElementById('slider-rpe'),
            mw: document.getElementById('slider-mw'),
            phi_axis: document.getElementById('slider-phi-axis'),
            c_axis: document.getElementById('slider-c-axis'),
        };

        const displays = {
            phi: document.getElementById('phi-value-display'),
            gamma_sat: document.getElementById('gamma-sat-value-display'),
            r: document.getElementById('r-value-display'),
            r_pe: document.getElementById('rpe-value-display'),
            mw: document.getElementById('mw-value-display'),
            phi_axis: document.getElementById('phi-axis-value-display'),
            c_axis: document.getElementById('c-axis-value-display'),
        };

        const infoDisplays = {
            formula: document.getElementById('formula-display'),
            phimax: document.getElementById('phimax-display'),
            range: document.getElementById('range-display'),
        };

        const plotContainer = document.getElementById('plot-container');

        // --- Core Formulas & Constants ---
        const C_UNIT_CONVERSION = 1000;
        const K_DEPLETION = 0.137;

        const y1 = (phi, gamma_sat, r) => (3 * gamma_sat / r) * C_UNIT_CONVERSION * phi;
        const y2 = (phi, gamma_sat, r) => y1(phi, gamma_sat, r) / (1 - phi);
        const y3 = (phi, gamma_sat, r, mw, r_pe) => {
            const A = (3 * gamma_sat / r) * C_UNIT_CONVERSION;
            const B = K_DEPLETION * mw / (r_pe ** 3);
            const safe_phi = phi === 0 ? 1e-9 : phi;
            return A * safe_phi + B / safe_phi;
        };

        const calculatePhiMax = (gamma_sat, r, mw, r_pe) => {
            const A = (3 * gamma_sat / r) * C_UNIT_CONVERSION;
            const B = K_DEPLETION * mw / (r_pe ** 3);
            return Math.sqrt(B / A);
        };

        // --- Update Functions ---

        function updateStateAndDisplays() {
            for (const key in sliders) {
                state[key] = parseFloat(sliders[key].value);
            }
            displays.phi.textContent = state.phi.toFixed(4);
            displays.gamma_sat.textContent = state.gamma_sat.toFixed(1);
            displays.r.textContent = state.r.toFixed(0);
            displays.r_pe.textContent = state.r_pe.toFixed(0);
            displays.mw.textContent = state.mw.toFixed(0);
            displays.phi_axis.textContent = state.phi_axis.toFixed(2);
            displays.c_axis.textContent = state.c_axis.toFixed(1);

            sliders.phi.max = state.phi_axis;
             if (state.phi > state.phi_axis) {
                sliders.phi.value = state.phi_axis;
                state.phi = state.phi_axis;
                displays.phi.textContent = state.phi.toFixed(4);
            }
        }

        function updateInfoDisplays() {
            const phi_max = calculatePhiMax(state.gamma_sat, state.r, state.mw, state.r_pe);
            infoDisplays.phimax.innerHTML = `Minimum of <span style="color:green;">\\(y_3\\)</span> occurs at \\(\\phi_{max} \\approx ${phi_max.toFixed(4)}\\)`;

            const lower_bound = y2(state.phi, state.gamma_sat, state.r);
            const upper_bound = y3(state.phi, state.gamma_sat, state.r, state.mw, state.r_pe);
            infoDisplays.range.innerHTML = `At current \\(\\phi=${state.phi.toFixed(4)}\\), stability range is <b>[${lower_bound.toFixed(3)}, ${upper_bound.toFixed(3)}]</b> kg/m³`;

            if (window.MathJax && MathJax.typesetPromise) {
                MathJax.typesetPromise([infoDisplays.phimax, infoDisplays.range]);
            }
        }

        function updatePlot() {
            const phi_max = calculatePhiMax(state.gamma_sat, state.r, state.mw, state.r_pe);
            const n_points = 200;
            const x_values = Array.from({ length: n_points }, (_, i) => (i / (n_points - 1)) * state.phi_axis);
            if (x_values[0] === 0) x_values[0] = 1e-9;

            const y1_values = x_values.map(phi => y1(phi, state.gamma_sat, state.r));
            const y2_values = x_values.map(phi => y2(phi, state.gamma_sat, state.r));
            const y3_values = x_values.map(phi => y3(phi, state.gamma_sat, state.r, state.mw, state.r_pe));

            const fill_x = x_values.filter(phi => phi <= phi_max && phi <= state.phi_axis);
            if (fill_x.length > 0 && fill_x[fill_x.length - 1] < phi_max && phi_max <= state.phi_axis) {
                fill_x.push(phi_max);
            }
            const fill_y2 = fill_x.map(phi => y2(phi, state.gamma_sat, state.r));
            const fill_y3 = fill_x.map(phi => y3(phi, state.gamma_sat, state.r, state.mw, state.r_pe));

            const traces = [
                { x: x_values, y: y1_values, type: 'scatter', mode: 'lines', name: 'y1', line: { color: 'blue' } },
                { x: x_values, y: y2_values, type: 'scatter', mode: 'lines', name: 'y2', line: { color: 'red' } },
                { x: x_values, y: y3_values, type: 'scatter', mode: 'lines', name: 'y3', line: { color: 'green' } },
                { x: fill_x, y: fill_y2, type: 'scatter', mode: 'lines', line: { width: 0 }, hoverinfo: 'none', showlegend: false },
                { x: fill_x, y: fill_y3, type: 'scatter', mode: 'lines', fill: 'tonexty', fillcolor: 'rgba(173, 216, 230, 0.5)', line: { width: 0 }, name: 'Stability Region', hoverinfo: 'none', showlegend: false },
                {
                    x: [state.phi],
                    y: [y1(state.phi, state.gamma_sat, state.r), y2(state.phi, state.gamma_sat, state.r), y3(state.phi, state.gamma_sat, state.r, state.mw, state.r_pe)],
                    type: 'scatter', mode: 'markers', marker: { color: ['blue', 'red', 'green'], size: 10, line: { color: 'white', width: 1 } }, showlegend: false, hoverinfo: 'none'
                }
            ];

            const layout = {
                xaxis: { title: '\\(\\phi\\) (volume fraction)', range: [0, state.phi_axis], zeroline: false },
                yaxis: { title: 'C (kg/m³)', range: [0, state.c_axis], zeroline: false },
                shapes: [{
                    type: 'line', x0: state.phi, x1: state.phi, y0: 0, y1: state.c_axis,
                    line: { color: 'grey', width: 1.5, dash: 'dash' }
                }],
                showlegend: false,
                margin: { t: 20, r: 20, b: 60, l: 70 },
                hovermode: 'x unified'
            };

            Plotly.react(plotContainer, traces, layout, {responsive: true});
            updateInfoDisplays();
        }

        function updateInteractiveElements() {
            const new_marker_y = [
                y1(state.phi, state.gamma_sat, state.r),
                y2(state.phi, state.gamma_sat, state.r),
                y3(state.phi, state.gamma_sat, state.r, state.mw, state.r_pe)
            ];
            Plotly.restyle(plotContainer, { x: [[state.phi]], y: [new_marker_y] }, [5]);
            Plotly.relayout(plotContainer, { 'shapes[0].x0': state.phi, 'shapes[0].x1': state.phi, 'shapes[0].y1': state.c_axis });
            updateInfoDisplays();
        }

        // --- Initialization and Event Listeners ---
        function init() {
            infoDisplays.formula.innerHTML = `Functions: <span style="color:blue;">\\(y_1(\\phi)\\)</span>, <span style="color:red;">\\(y_2(\\phi)\\)</span>, <span style="color:green;">\\(y_3(\\phi)\\)</span>`;

            updateStateAndDisplays();
            updatePlot();

            Object.values(sliders).forEach(slider => {
                slider.addEventListener('input', (e) => {
                    const id = e.target.id;
                    updateStateAndDisplays();

                    if (id === 'slider-phi') {
                        updateInteractiveElements();
                    } else if (id === 'slider-c-axis') {
                        // Only requires a relayout for y-axis change
                        Plotly.relayout(plotContainer, { 'yaxis.range': [0, state.c_axis], 'shapes[0].y1': state.c_axis });
                    }
                    else {
                        // Parameter or phi-axis change requires a full plot redraw
                        updatePlot();
                    }
                });
            });

             window.addEventListener('resize', () => {
                Plotly.Plots.resize(plotContainer);
            });
        }

        document.addEventListener('DOMContentLoaded', init);
    </script>
</body>
</html>
