<body>

    <div id="main-container">
        <div id="control-panel">
            <h3>Controls</h3>
            <div class="control-group">
                <label for="slider-time">time relative to collision (s)</label>
                <div class="slider-container">
                    <input type="range" id="slider-time" min="-30" max="30" value="-10" step="0.1">
                    <span id="label-time" class="value-label"></span>
                </div>
            </div>

            <h4>object 1 (red)</h4>
            <div class="control-group">
                <label for="slider-m1">mass (kg)</label>
                <div class="slider-container">
                    <input type="range" id="slider-m1" min="1" max="10" value="5" step="0.01">
                    <span id="label-m1" class="value-label"></span>
                </div>
            </div>
            <div class="control-group">
                <label for="slider-v1">initial speed in CM frame (m/s)</label>
                <div class="slider-container">
                    <input type="range" id="slider-v1" min="1" max="20" value="10" step="0.1">
                    <span id="label-v1" class="value-label"></span>
                </div>
            </div>

            <h4>object 2 (blue)</h4>
            <div class="control-group">
                <label for="slider-m2">mass (kg)</label>
                <div class="slider-container">
                    <input type="range" id="slider-m2" min="1" max="10" value="3" step="0.01">
                    <span id="label-m2" class="value-label"></span>
                </div>
            </div>
            <div class="control-group">
                <label for="slider-v2">initial speed in CM frame (m/s)</label>
                <div class="slider-container">
                    <input type="range" id="slider-v2" min="1" max="20" value="5" step="0.1" disabled>
                    <span id="label-v2" class="value-label"></span>
                </div>
            </div>

            <h4>Observer</h4>
            <label>observer velocity (m/s)</label>
            <div id="joystick-container">
                <div id="joystick-knob"></div>
            </div>
            <div class="joystick-labels">
                <span id="label-v0x"></span>
                <span id="label-v0y"></span>
            </div>

        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        // --- P5.JS SKETCH ---

        // State variables
        let m1, v1_cm_scalar, m2, v2_cm_scalar, time;
        let v0x, v0y;

        // Physics vectors (p5.Vector)
        let u1_cm, u2_cm; // pre-collision velocities in CM frame
        let v1_cm, v2_cm; // post-collision velocities in CM frame

        // Calculated display metrics
        let obs_speed_val, p1_speed_val, p2_speed_val, total_energy_val;

        // DOM Elements
        let sliderTime, labelTime;
        let sliderM1, labelM1;
        let sliderV1, labelV1;
        let sliderM2, labelM2;
        let sliderV2, labelV2;
        let joystickContainer, joystickKnob;
        let labelV0x, labelV0y;

        // Canvas constants
        const scaleFactor = 15; // pixels per meter
        const radiusFactor = 10; // for particle size based on mass

        const sketch = (p) => {
            p.setup = () => {
                const canvasContainer = document.getElementById('canvas-container');
                const canvas = p.createCanvas(canvasContainer.clientWidth, canvasContainer.clientHeight);
                canvas.parent('canvas-container');
                p.angleMode(p.DEGREES);

                // --- Get DOM Elements ---
                sliderTime = document.getElementById('slider-time');
                labelTime = document.getElementById('label-time');
                sliderM1 = document.getElementById('slider-m1');
                labelM1 = document.getElementById('label-m1');
                sliderV1 = document.getElementById('slider-v1');
                labelV1 = document.getElementById('label-v1');
                sliderM2 = document.getElementById('slider-m2');
                labelM2 = document.getElementById('label-m2');
                sliderV2 = document.getElementById('slider-v2');
                labelV2 = document.getElementById('label-v2');
                joystickContainer = document.getElementById('joystick-container');
                joystickKnob = document.getElementById('joystick-knob');
                labelV0x = document.getElementById('label-v0x');
                labelV0y = document.getElementById('label-v0y');

                // --- Initial State ---
                v0x = 1.1;
                v0y = 1.1;

                // --- Event Listeners ---
                sliderTime.addEventListener('input', updateSimulation);
                sliderM1.addEventListener('input', updateSimulation);
                sliderV1.addEventListener('input', updateSimulation);
                sliderM2.addEventListener('input', updateSimulation);

                setupJoystick();

                // --- Initial Calculation ---
                updateSimulation();
            };

            p.draw = () => {
                p.background(245);
                p.translate(p.width / 2, p.height / 2);

                const t = parseFloat(sliderTime.value);
                const v0 = p.createVector(v0x, v0y);

                // Velocities in observer frame
                const u1_obs = p5.Vector.add(u1_cm, v0);
                const u2_obs = p5.Vector.add(u2_cm, v0);
                const v1_obs = p5.Vector.add(v1_cm, v0);
                const v2_obs = p5.Vector.add(v2_cm, v0);

                // --- Draw Trajectories ---
                drawTrajectory(u1_obs, v1_obs, p.color(255, 0, 0, 100));
                drawTrajectory(u2_obs, v2_obs, p.color(0, 0, 255, 100));

                // --- Draw Particles ---
                let pos1, pos2;
                if (t < 0) {
                    pos1 = p5.Vector.mult(u1_obs, t);
                    pos2 = p5.Vector.mult(u2_obs, t);
                } else {
                    pos1 = p5.Vector.mult(v1_obs, t);
                    pos2 = p5.Vector.mult(v2_obs, t);
                }

                drawParticle(pos1, m1, p.color('#FF0000'));
                drawParticle(pos2, m2, p.color('#0000FF'));

                // --- Draw UI elements on canvas ---
                p.resetMatrix();
                drawObserverVector(v0);
                drawMetrics();
            };

            function drawTrajectory(u_obs, v_obs, col) {
                const trajectoryRange = 40;
                const startPoint = p5.Vector.mult(u_obs, -trajectoryRange);
                const endPoint = p5.Vector.mult(v_obs, trajectoryRange);
                const collisionPoint = p.createVector(0,0); // Collision is at origin in observer frame at t=0

                p.push();
                p.stroke(col);
                p.strokeWeight(1.5);
                p.drawingContext.setLineDash([5, 5]);

                // Scale to fit canvas
                p.line(startPoint.x * scaleFactor, startPoint.y * scaleFactor, collisionPoint.x, collisionPoint.y);
                p.line(collisionPoint.x, collisionPoint.y, endPoint.x * scaleFactor, endPoint.y * scaleFactor);
                p.pop();
            }

            function drawParticle(pos, mass, col) {
                const radius = radiusFactor * p.sqrt(mass);
                p.push();
                p.noStroke();
                p.fill(col);
                p.circle(pos.x * scaleFactor, pos.y * scaleFactor, radius * 2);
                p.pop();
            }

            function drawObserverVector(v0) {
                p.push();
                const start = p.createVector(50, p.height - 50);
                // Draw velocity of CM relative to observer (-v0)
                const vec = p5.Vector.mult(v0, -1).mult(10); 
                const end = p5.Vector.add(start, vec);

                p.stroke(0);
                p.strokeWeight(2);
                p.fill(0);

                p.line(start.x, start.y, end.x, end.y);

                // Arrowhead
                p.translate(end.x, end.y);
                p.rotate(vec.heading() + 90);
                p.triangle(0, 0, -5, 10, 5, 10);
                p.pop();

                p.noStroke();
                p.fill(50);
                p.textAlign(p.LEFT, p.TOP);
                p.text("CM vel rel. to obs", 20, p.height - 75);
            }

            function drawMetrics() {
                p.push();
                p.fill(0);
                p.noStroke();
                p.textAlign(p.LEFT, p.TOP);
                p.textSize(14);
                p.textFont('monospace');

                const x = 20;
                let y = 20;
                const lineHeight = 18;

                p.text("Observer Frame (pre-collision):", x, y);
                y += lineHeight * 1.5;
                p.text(`v₀    = ${obs_speed_val.toFixed(2)} m/s`, x, y);
                y += lineHeight;
                p.text(`v₁    = ${p1_speed_val.toFixed(2)} m/s`, x, y);
                y += lineHeight;
                p.text(`v₂    = ${p2_speed_val.toFixed(2)} m/s`, x, y);
                y += lineHeight * 1.5;
                p.text(`Energy = ${total_energy_val.toFixed(2)} J`, x, y);

                p.pop();
            }

            function updateSimulation() {
                // --- Read Inputs from Controls ---
                m1 = parseFloat(sliderM1.value);
                v1_cm_scalar = parseFloat(sliderV1.value);
                m2 = parseFloat(sliderM2.value);
                time = parseFloat(sliderTime.value);

                // --- Update Labels ---
                labelM1.textContent = m1.toFixed(2);
                labelV1.textContent = v1_cm_scalar.toFixed(1);
                labelM2.textContent = m2.toFixed(2);
                labelTime.textContent = time.toFixed(1);

                // --- Physics Calculation (CM Frame) ---
                // Enforce momentum conservation: m1*u1 + m2*u2 = 0
                v2_cm_scalar = (m1 / m2) * v1_cm_scalar;

                // Update the disabled v2 slider for display
                if (v2_cm_scalar <= parseFloat(sliderV2.max)) {
                    sliderV2.value = v2_cm_scalar;
                } else {
                    sliderV2.value = sliderV2.max;
                }
                labelV2.textContent = v2_cm_scalar.toFixed(1);

                // Initial velocities (t < 0), along x-axis
                u1_cm = p.createVector(v1_cm_scalar, 0);
                u2_cm = p.createVector(-v2_cm_scalar, 0);

                // Final velocities (t > 0), elastic collision with 90-degree scatter
                // We rotate both velocity vectors by the same amount. Here, -45 degrees.
                v1_cm = u1_cm.copy().rotate(-45);
                v2_cm = u2_cm.copy().rotate(-45);

                // --- Observer Frame Transformation & Metrics (Pre-collision) ---
                const v0 = p.createVector(v0x, v0y);
                const u1_obs = p5.Vector.add(u1_cm, v0);
                const u2_obs = p5.Vector.add(u2_cm, v0);

                obs_speed_val = v0.mag();
                p1_speed_val = u1_obs.mag();
                p2_speed_val = u2_obs.mag();

                total_energy_val = 0.5 * m1 * p1_speed_val**2 + 0.5 * m2 * p2_speed_val**2;
            }

            function setupJoystick() {
                const joystickSize = joystickContainer.getBoundingClientRect().width;
                const knobSize = joystickKnob.getBoundingClientRect().width;
                const radius = (joystickSize - knobSize) / 2;
                const vMax = 5.0; // max velocity for joystick axis

                function updateJoystick(e) {
                    const rect = joystickContainer.getBoundingClientRect();
                    const x = e.clientX - rect.left - joystickSize / 2;
                    const y = e.clientY - rect.top - joystickSize / 2;
                    const dist = Math.min(radius, Math.sqrt(x*x + y*y));
                    const angle = Math.atan2(y, x);

                    const constrainedX = dist * Math.cos(angle);
                    const constrainedY = dist * Math.sin(angle);

                    joystickKnob.style.left = `${constrainedX + joystickSize / 2}px`;
                    joystickKnob.style.top = `${constrainedY + joystickSize / 2}px`;

                    v0x = (constrainedX / radius) * vMax;
                    v0y = (constrainedY / radius) * vMax;

                    labelV0x.textContent = `vx: ${v0x.toFixed(2)}`;
                    labelV0y.textContent = `vy: ${v0y.toFixed(2)}`;

                    updateSimulation();
                }

                let isDragging = false;
                joystickContainer.addEventListener('mousedown', (e) => {
                    isDragging = true;
                    updateJoystick(e);
                });
                document.addEventListener('mousemove', (e) => {
                    if (isDragging) {
                        updateJoystick(e);
                    }
                });
                document.addEventListener('mouseup', () => {
                    isDragging = false;
                });

                // Initial joystick position based on default v0x, v0y
                const initialX = (v0x / vMax) * radius;
                const initialY = (v0y / vMax) * radius;
                joystickKnob.style.left = `${initialX + joystickSize / 2}px`;
                joystickKnob.style.top = `${initialY + joystickSize / 2}px`;
                labelV0x.textContent = `vx: ${v0x.toFixed(2)}`;
                labelV0y.textContent = `vy: ${v0y.toFixed(2)}`;
            }

            // Make the updateSimulation function globally accessible for event listeners
            window.updateSimulation = updateSimulation;
        };

        new p5(sketch);

    </script>
</body>
</html>
