<body>
    <div class="main-container">
        <!-- Control Panel Section -->
        <div class="control-panel">
            <h2>Caesar Cipher Controls</h2>

            <div class="control-group">
                <label>shift</label>
                <div class="slider-container">
                    <input type="range" id="shift-slider" min="0" max="25" value="12" step="1">
                    <span class="shift-value">12</span>
                </div>
            </div>

            <div class="control-group">
                <label for="text-input">message</label>
                <input type="text" id="text-input" value="abcd" list="phrase-list">
                <datalist id="phrase-list">
                    <option value="abcd">
                    <option value="test phrase">
                    <option value="to be or not to be">
                    <option value="the quick brown fox jumped over the lazy dog">
                </datalist>
            </div>
        </div>

        <!-- Display Area Section -->
        <div class="display-area">
            <!-- Alphabet Mapping Visualization -->
            <div class="alphabet-mapping">
                <div id="plaintext-alphabet" class="alphabet-row"></div>
                <div id="ciphertext-alphabet" class="alphabet-row"></div>
            </div>

            <!-- Key Information Section -->
            <div class="key-section">
                <p id="encryption-key-display">encryption key: 12</p>
                <p id="decryption-key-display">decryption key: 14</p>
            </div>

            <!-- Text Output Section -->
            <div class="text-output">
                <p id="plaintext-output"><span class="blue">plaintext : </span>abcd</p>
                <p id="ciphertext-output"><span class="green">ciphertext:</span> MNOP</p>
            </div>
        </div>
    </div>

    <script>
        // Get DOM elements
        const shiftSlider = document.getElementById('shift-slider');
        const shiftValue = document.querySelector('.shift-value');
        const textInput = document.getElementById('text-input');
        const plaintextAlphabet = document.getElementById('plaintext-alphabet');
        const ciphertextAlphabet = document.getElementById('ciphertext-alphabet');
        const encryptionKeyDisplay = document.getElementById('encryption-key-display');
        const decryptionKeyDisplay = document.getElementById('decryption-key-display');
        const plaintextOutput = document.getElementById('plaintext-output');
        const ciphertextOutput = document.getElementById('ciphertext-output');

        // Standard alphabet
        const alphabet = 'abcdefghijklmnopqrstuvwxyz';
        const upperAlphabet = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ';

        // Initialize alphabet displays
        function initializeAlphabets() {
            // Clear existing content
            plaintextAlphabet.innerHTML = '';
            ciphertextAlphabet.innerHTML = '';

            // Create plaintext alphabet
            for (let i = 0; i < alphabet.length; i++) {
                const span = document.createElement('span');
                span.textContent = alphabet[i];
                plaintextAlphabet.appendChild(span);
            }
        }

        // Encryption function
        function encrypt(text, shift) {
            let result = '';
            for (let i = 0; i < text.length; i++) {
                const char = text[i].toLowerCase();
                if (char >= 'a' && char <= 'z') {
                    const index = char.charCodeAt(0) - 'a'.charCodeAt(0);
                    const shiftedIndex = (index + shift) % 26;
                    result += upperAlphabet[shiftedIndex];
                } else {
                    result += char;
                }
            }
            return result;
        }

        // Update cipher display
        function updateCipher() {
            const shift = parseInt(shiftSlider.value);

            // Update shift value display
            shiftValue.textContent = shift;

            // Update ciphertext alphabet
            ciphertextAlphabet.innerHTML = '';
            for (let i = 0; i < alphabet.length; i++) {
                const span = document.createElement('span');
                const shiftedIndex = (i + shift) % 26;
                span.textContent = upperAlphabet[shiftedIndex];
                ciphertextAlphabet.appendChild(span);
            }

            // Update key displays
            encryptionKeyDisplay.textContent = `encryption key: ${shift}`;
            const decryptionKey = (26 - shift) % 26;
            decryptionKeyDisplay.textContent = `decryption key: ${decryptionKey}`;

            // Update text outputs
            const plaintext = textInput.value;
            plaintextOutput.innerHTML = '<span class="blue">plaintext : </span>' + plaintext;

            const ciphertext = encrypt(plaintext, shift);
            ciphertextOutput.innerHTML = '<span class="green">ciphertext:</span> ' + ciphertext;
        }

        // Event listeners
        shiftSlider.addEventListener('input', updateCipher);
        textInput.addEventListener('input', updateCipher);

        // Initialize on page load
        window.addEventListener('load', () => {
            initializeAlphabets();
            updateCipher();
        });
    </script>
</body>
