        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="control-group">
                <label>coordination</label>
                <div class="button-group">
                    <button id="btn-threefold" class="active">threefold</button>
                    <button id="btn-fourfold">fourfold</button>
                    <button id="btn-sixfold">sixfold</button>
                    <button id="btn-eightfold">eightfold</button>
                </div>
            </div>

            <div class="control-group">
                <label>relative anion radius</label>
                <div class="slider-container">
                    <input type="range" id="slider-anion-radius" min="0.1" max="1.0" step="0.001" value="0.1">
                    <span id="value-anion-radius">0.100</span>
                </div>
            </div>

            <div class="control-group">
                <label>relative cation radius</label>
                <div class="slider-container">
                    <input type="range" id="slider-cation-radius" min="0.1" max="1.0" step="0.001" value="0.1">
                    <span id="value-cation-radius">0.100</span>
                </div>
            </div>

            <div class="control-group">
                <label>opacity of anions</label>
                <div class="slider-container">
                    <input type="range" id="slider-opacity" min="0.0" max="1.0" step="0.01" value="0.8">
                    <span id="value-opacity">0.80</span>
                </div>
            </div>
        </div>

        <div id="main-display-container">
            <div id="canvas-container"></div>
            <p id="ratio-text">The ratio of the cation radius to the anion radius is 1.00.</p>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>
    <script>
        // Global variables
        let scene, camera, renderer, controls;
        let cation, anions = [], boundingBox;
        let currentCoordination = 'threefold';
        let anionRadius = 0.1, cationRadius = 0.1, anionOpacity = 0.8;

        // Initialize Three.js scene
        function initScene() {
            const container = document.getElementById('canvas-container');
            const width = container.clientWidth;
            const height = container.clientHeight;

            // Scene
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xf0f0f0);

            // Camera
            camera = new THREE.PerspectiveCamera(75, width / height, 0.1, 1000);
            camera.position.set(3, 3, 3);

            // Renderer
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(width, height);
            renderer.shadowMap.enabled = true;
            container.appendChild(renderer.domElement);

            // Controls
            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.05;

            // Lighting
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
            scene.add(ambientLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
            directionalLight.position.set(5, 5, 5);
            directionalLight.castShadow = true;
            scene.add(directionalLight);

            // Create initial scene
            createCoordination(currentCoordination);

            // Handle window resize
            window.addEventListener('resize', onWindowResize);
        }

        // Create matcap texture for cation
        function createMatcapTexture() {
            const canvas = document.createElement('canvas');
            canvas.width = 256;
            canvas.height = 256;
            const ctx = canvas.getContext('2d');

            const centerX = canvas.width / 2;
            const centerY = canvas.height / 2;
            const radius = canvas.width / 2;

            // Create radial gradient
            const gradient = ctx.createRadialGradient(centerX, centerY, 0, centerX, centerY, radius);
            gradient.addColorStop(0, '#0066ff');
            gradient.addColorStop(0.7, '#ff6600');
            gradient.addColorStop(1, '#ff0000');

            ctx.fillStyle = gradient;
            ctx.beginPath();
            ctx.arc(centerX, centerY, radius, 0, 2 * Math.PI);
            ctx.fill();

            return new THREE.CanvasTexture(canvas);
        }

        // Create cation
        function createCation() {
            if (cation) scene.remove(cation);

            const geometry = new THREE.SphereGeometry(cationRadius, 32, 32);
            const matcapTexture = createMatcapTexture();
            const material = new THREE.MeshPhongMaterial({
                map: matcapTexture,
                shininess: 100
            });

            cation = new THREE.Mesh(geometry, material);
            cation.castShadow = true;
            scene.add(cation);
        }

        // Create anions
        function createAnions(positions) {
            // Remove existing anions
            anions.forEach(anion => scene.remove(anion));
            anions = [];

            const geometry = new THREE.SphereGeometry(anionRadius, 32, 32);
            const material = new THREE.MeshPhongMaterial({
                color: 0xe0d8e0,
                transparent: true,
                opacity: anionOpacity
            });

            positions.forEach(pos => {
                const anion = new THREE.Mesh(geometry, material.clone());
                anion.position.set(pos.x, pos.y, pos.z);
                anion.castShadow = true;
                scene.add(anion);
                anions.push(anion);
            });
        }

        // Create bounding box
        function createBoundingBox(size) {
            if (boundingBox) scene.remove(boundingBox);

            const geometry = new THREE.BoxGeometry(size, size, size);
            const edges = new THREE.EdgesGeometry(geometry);
            const material = new THREE.LineBasicMaterial({ color: 0x888888 });
            boundingBox = new THREE.LineSegments(edges, material);
            scene.add(boundingBox);
        }

        // Create coordination based on type
        function createCoordination(type) {
            currentCoordination = type;

            // Remove existing bounding box
            if (boundingBox) {
                scene.remove(boundingBox);
                boundingBox = null;
            }

            let positions = [];
            const distance = 1.5;

            switch(type) {
                case 'threefold':
                    // Equilateral triangle on XY plane
                    for (let i = 0; i < 3; i++) {
                        const angle = (i * 2 * Math.PI) / 3;
                        positions.push({
                            x: distance * Math.cos(angle),
                            y: distance * Math.sin(angle),
                            z: 0
                        });
                    }
                    break;

                case 'fourfold':
                    // Tetrahedron vertices
                    positions = [
                        { x: distance, y: distance, z: distance },
                        { x: -distance, y: -distance, z: distance },
                        { x: -distance, y: distance, z: -distance },
                        { x: distance, y: -distance, z: -distance }
                    ];
                    createBoundingBox(distance * 2.5);
                    break;

                case 'sixfold':
                    // Octahedron vertices
                    positions = [
                        { x: distance, y: 0, z: 0 },
                        { x: -distance, y: 0, z: 0 },
                        { x: 0, y: distance, z: 0 },
                        { x: 0, y: -distance, z: 0 },
                        { x: 0, y: 0, z: distance },
                        { x: 0, y: 0, z: -distance }
                    ];
                    createBoundingBox(distance * 2.5);
                    break;

                case 'eightfold':
                    // Cube vertices
                    const d = distance * 0.866; // Adjust for cube geometry
                    for (let x = -1; x <= 1; x += 2) {
                        for (let y = -1; y <= 1; y += 2) {
                            for (let z = -1; z <= 1; z += 2) {
                                positions.push({
                                    x: x * d,
                                    y: y * d,
                                    z: z * d
                                });
                            }
                        }
                    }
                    createBoundingBox(distance * 2);
                    break;
            }

            createCation();
            createAnions(positions);

            // Reset camera
            camera.position.set(3, 3, 3);
            controls.reset();
        }

        // Update ratio text
        function updateRatioText() {
            const ratio = cationRadius / anionRadius;
            document.getElementById('ratio-text').textContent = 
                `The ratio of the cation radius to the anion radius is ${ratio.toFixed(2)}.`;
        }

        // Handle window resize
        function onWindowResize() {
            const container = document.getElementById('canvas-container');
            const width = container.clientWidth;
            const height = container.clientHeight;

            camera.aspect = width / height;
            camera.updateProjectionMatrix();
            renderer.setSize(width, height);
        }

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        // Initialize event listeners
        function initEventListeners() {
            // Coordination buttons
            const buttons = ['threefold', 'fourfold', 'sixfold', 'eightfold'];
            buttons.forEach(type => {
                const button = document.getElementById(`btn-${type}`);
                button.addEventListener('click', () => {
                    // Update active state
                    buttons.forEach(b => {
                        document.getElementById(`btn-${b}`).classList.remove('active');
                    });
                    button.classList.add('active');

                    // Create new coordination
                    createCoordination(type);
                });
            });

            // Anion radius slider
            const anionSlider = document.getElementById('slider-anion-radius');
            anionSlider.addEventListener('input', (e) => {
                anionRadius = parseFloat(e.target.value);
                document.getElementById('value-anion-radius').textContent = anionRadius.toFixed(3);

                // Update anion geometries
                anions.forEach(anion => {
                    anion.geometry.dispose();
                    anion.geometry = new THREE.SphereGeometry(anionRadius, 32, 32);
                });

                updateRatioText();
            });

            // Cation radius slider
            const cationSlider = document.getElementById('slider-cation-radius');
            cationSlider.addEventListener('input', (e) => {
                cationRadius = parseFloat(e.target.value);
                document.getElementById('value-cation-radius').textContent = cationRadius.toFixed(3);

                // Update cation geometry
                if (cation) {
                    cation.geometry.dispose();
                    cation.geometry = new THREE.SphereGeometry(cationRadius, 32, 32);
                }

                updateRatioText();
            });

            // Opacity slider
            const opacitySlider = document.getElementById('slider-opacity');
            opacitySlider.addEventListener('input', (e) => {
                anionOpacity = parseFloat(e.target.value);
                document.getElementById('value-opacity').textContent = anionOpacity.toFixed(2);

                // Update anion materials
                anions.forEach(anion => {
                    anion.material.opacity = anionOpacity;
                });
            });
        }

        // Initialize everything
        window.addEventListener('load', () => {
            initScene();
            initEventListeners();
            animate();
        });
    </script>
</body>
</html>
