<body>
    <div id="control-panel">
        <div id="slider-container">
            <label for="slider-angle">slope θ</label>
            <input type="range" id="slider-angle" min="10" max="80" value="30" step="1">
        </div>

        <div id="animation-controls">
            <p>release block</p>
            <div class="button-group">
                <button id="btn-play">▶</button>
                <button id="btn-pause">||</button>
                <button id="btn-reset">|<</button>
            </div>
        </div>
    </div>

    <div id="visualization-area">
        <p id="info-display">slope = \\( \\theta \\) = 30°; acceleration = \\( g \\sin(\\theta) \\) = 4.905 m/s²</p>
        <div id="p5-canvas"></div>
    </div>

    <script>
        // MathJax configuration
        window.MathJax = {
            tex: {
                inlineMath: [['\\(', '\\)']],
                displayMath: [['$$', '$$'], ['\\[', '\\]']]
            }
        };

        // Global variables
        let theta = 30; // Initial angle in degrees
        let g = 9.81; // Gravity constant
        let isPlaying = false;
        let startTime = 0;
        let elapsedTime = 0;
        let blockPosition = 0; // Position along the slope
        let blockVelocity = 0;

        // Canvas dimensions
        let canvasWidth = 800;
        let canvasHeight = 600;

        // Physics parameters
        let planeLength = 400;
        let blockWidth = 40;
        let blockHeight = 30;
        let originX = 100;
        let originY = canvasHeight - 100;

        // DOM elements
        const sliderAngle = document.getElementById('slider-angle');
        const btnPlay = document.getElementById('btn-play');
        const btnPause = document.getElementById('btn-pause');
        const btnReset = document.getElementById('btn-reset');
        const infoDisplay = document.getElementById('info-display');

        // Event listeners
        sliderAngle.addEventListener('input', handleSliderChange);
        btnPlay.addEventListener('click', () => { isPlaying = true; if (elapsedTime === 0) startTime = millis(); });
        btnPause.addEventListener('click', () => { isPlaying = false; });
        btnReset.addEventListener('click', handleReset);

        function handleSliderChange() {
            theta = parseInt(sliderAngle.value);
            isPlaying = false;
            resetAnimation();
            updateInfoDisplay();
        }

        function handleReset() {
            isPlaying = false;
            resetAnimation();
        }

        function resetAnimation() {
            blockPosition = 0;
            blockVelocity = 0;
            elapsedTime = 0;
            startTime = millis();
        }

        function updateInfoDisplay() {
            const acceleration = g * Math.sin(theta * Math.PI / 180);
            infoDisplay.innerHTML = `slope = \\( \\theta \\) = ${theta}°; acceleration = \\( g \\sin(\\theta) \\) = ${acceleration.toFixed(3)} m/s²`;
            MathJax.typesetPromise([infoDisplay]).catch((err) => console.log(err.message));
        }

        // p5.js sketch
        function setup() {
            let canvas = createCanvas(canvasWidth, canvasHeight);
            canvas.parent('p5-canvas');
            updateInfoDisplay();
        }

        function draw() {
            background(255);

            // Update physics if playing
            if (isPlaying) {
                let currentTime = millis();
                elapsedTime = (currentTime - startTime) / 1000; // Convert to seconds
                let acceleration = g * Math.sin(theta * Math.PI / 180);
                blockPosition = 0.5 * acceleration * elapsedTime * elapsedTime * 50; // Scale for visualization

                // Check if block reached bottom
                if (blockPosition > planeLength - blockWidth) {
                    blockPosition = planeLength - blockWidth;
                    isPlaying = false;
                }
            }

            push();
            translate(originX, originY);

            // Draw inclined plane
            drawInclinedPlane();

            // Draw coordinate axes
            drawCoordinateAxes();

            // Draw block
            drawBlock();

            // Draw force vectors
            drawForceVectors();

            // Draw angle marker
            drawAngleMarker();

            pop();
        }

        function drawInclinedPlane() {
            push();
            fill('lightblue');
            stroke('yellow');
            strokeWeight(2);

            let thetaRad = theta * Math.PI / 180;
            let planeRight = planeLength * Math.cos(thetaRad);
            let planeTop = planeLength * Math.sin(thetaRad);

            triangle(0, 0, planeRight, 0, 0, -planeTop);
            pop();
        }

        function drawCoordinateAxes() {
            push();
            stroke(100);
            strokeWeight(2);

            let thetaRad = theta * Math.PI / 180;

            // X-axis (along slope)
            push();
            translate(50, 0);
            rotate(-thetaRad);
            line(0, 0, 80, 0);
            drawArrowHead(80, 0, 0);
            translate(90, 0);
            rotate(thetaRad);
            text('x', 0, -5);
            pop();

            // Y-axis (perpendicular to slope)
            push();
            translate(50, 0);
            rotate(-thetaRad + Math.PI/2);
            line(0, 0, 60, 0);
            drawArrowHead(60, 0, 0);
            translate(70, 0);
            rotate(-thetaRad - Math.PI/2);
            text('y', 0, -5);
            pop();

            pop();
        }

        function drawBlock() {
            push();
            fill('darkgrey');
            stroke('black');

            let thetaRad = theta * Math.PI / 180;
            let blockX = blockPosition * Math.cos(thetaRad);
            let blockY = -blockPosition * Math.sin(thetaRad);

            push();
            translate(blockX + blockWidth/2 * Math.sin(thetaRad), blockY - blockHeight/2 * Math.cos(thetaRad));
            rotate(-thetaRad);
            rect(-blockWidth/2, -blockHeight/2, blockWidth, blockHeight);
            pop();

            pop();
        }

        function drawForceVectors() {
            push();

            let thetaRad = theta * Math.PI / 180;
            let blockX = blockPosition * Math.cos(thetaRad) + blockWidth/2 * Math.sin(thetaRad);
            let blockY = -blockPosition * Math.sin(thetaRad) - blockHeight/2 * Math.cos(thetaRad);
            let scale = 0.5;

            // Gravity vector (always down)
            let gravEndX = blockX;
            let gravEndY = blockY + 50 * scale;
            drawArrow(blockX, blockY, gravEndX, gravEndY, 'red', 'F_grav=mg', -10, -5);

            // Normal force (perpendicular to slope)
            let normalForce = 50 * scale * Math.cos(thetaRad);
            let normalEndX = blockX - normalForce * Math.sin(thetaRad);
            let normalEndY = blockY - normalForce * Math.cos(thetaRad);
            drawArrow(blockX, blockY, normalEndX, normalEndY, 'deepskyblue', '"normal" force', 10, -10);

            // Net force (along slope)
            let netForce = 50 * scale * Math.sin(thetaRad);
            let netEndX = blockX + netForce * Math.cos(thetaRad);
            let netEndY = blockY + netForce * Math.sin(thetaRad);
            drawArrow(blockX, blockY, netEndX, netEndY, 'purple', 'F_net = m g sin(θ)', 20, 20);

            // Vector decomposition (dashed lines)
            push();
            stroke('black');
            strokeWeight(1);
            setLineDash([5, 5]);

            // Perpendicular component
            line(blockX, blockY, blockX, gravEndY);
            line(blockX, gravEndY, gravEndX, gravEndY);

            pop();

            // Label for mg cos(θ)
            push();
            fill('black');
            noStroke();
            textAlign(CENTER, CENTER);
            text('mg cos(θ)', blockX - 15, blockY + 25);
            pop();

            // Small arc for theta in decomposition
            push();
            noFill();
            stroke('black');
            strokeWeight(1);
            let arcStart = atan2(gravEndY - blockY, gravEndX - blockX);
            let arcRadius = 20;
            arc(blockX, blockY, arcRadius * 2, arcRadius * 2, arcStart - thetaRad * Math.PI/180, arcStart);
            push();
            textAlign(CENTER, CENTER);
            fill('black');
            text('θ', blockX - 12, blockY + 10);
            pop();
            pop();

            pop();
        }

        function drawArrow(x1, y1, x2, y2, color, label, labelOffsetX, labelOffsetY) {
            push();
            stroke(color);
            strokeWeight(2);
            line(x1, y1, x2, y2);

            // Arrowhead
            push();
            translate(x2, y2);
            rotate(atan2(y2 - y1, x2 - x1));
            drawArrowHead(0, 0, 0);
            pop();

            // Label
            push();
            fill(color);
            noStroke();
            textAlign(CENTER, CENTER);
            textSize(12);
            text(label, x2 + labelOffsetX, y2 + labelOffsetY);
            pop();

            pop();
        }

        function drawArrowHead(x, y, angle) {
            push();
            translate(x, y);
            rotate(angle);
            line(0, 0, -8, -4);
            line(0, 0, -8, 4);
            pop();
        }

        function drawAngleMarker() {
            push();
            noFill();
            stroke('black');
            strokeWeight(1);

            let thetaRad = theta * Math.PI / 180;
            arc(0, 0, 60, 60, -thetaRad, 0);

            push();
            textAlign(CENTER, CENTER);
            fill('black');
            noStroke();
            translate(-25, -15);
            rotate(-thetaRad/2);
            text('θ', 0, 0);
            pop();

            pop();
        }

        function setLineDash(list) {
            drawingContext.setLineDash(list);
        }
    </script>
</body>
</html>
