                [10, 0, 9, 14, 6, 3, 15, 5, 1, 13, 12, 7, 11, 4, 2, 8],
                [13, 7, 0, 9, 3, 4, 6, 10, 2, 8, 5, 14, 12, 11, 15, 1],
                [13, 6, 4, 9, 8, 15, 3, 0, 11, 1, 2, 12, 5, 10, 14, 7],
                [1, 10, 13, 0, 6, 9, 8, 7, 4, 15, 14, 3, 11, 5, 2, 12]
            ],
            [ // S4
                [7, 13, 14, 3, 0, 6, 9, 10, 1, 2, 8, 5, 11, 12, 4, 15],
                [13, 8, 11, 5, 6, 15, 0, 3, 4, 7, 2, 12, 1, 10, 14, 9],
                [10, 6, 9, 0, 12, 11, 7, 13, 15, 1, 3, 14, 5, 2, 8, 4],
                [3, 15, 0, 6, 10, 1, 13, 8, 9, 4, 5, 11, 12, 7, 2, 14]
            ],
            [ // S5
                [2, 12, 4, 1, 7, 10, 11, 6, 8, 5, 3, 15, 13, 0, 14, 9],
                [14, 11, 2, 12, 4, 7, 13, 1, 5, 0, 15, 10, 3, 9, 8, 6],
                [4, 2, 1, 11, 10, 13, 7, 8, 15, 9, 12, 5, 6, 3, 0, 14],
                [11, 8, 12, 7, 1, 14, 2, 13, 6, 15, 0, 9, 10, 4, 5, 3]
            ],
            [ // S6
                [12, 1, 10, 15, 9, 2, 6, 8, 0, 13, 3, 4, 14, 7, 5, 11],
                [10, 15, 4, 2, 7, 12, 9, 5, 6, 1, 13, 14, 0, 11, 3, 8],
                [9, 14, 15, 5, 2, 8, 12, 3, 7, 0, 4, 10, 1, 13, 11, 6],
                [4, 3, 2, 12, 9, 5, 15, 10, 11, 14, 1, 7, 6, 0, 8, 13]
            ],
            [ // S7
                [4, 11, 2, 14, 15, 0, 8, 13, 3, 12, 9, 7, 5, 10, 6, 1],
                [13, 0, 11, 7, 4, 9, 1, 10, 14, 3, 5, 12, 2, 15, 8, 6],
                [1, 4, 11, 13, 12, 3, 7, 14, 10, 15, 6, 8, 0, 5, 9, 2],
                [6, 11, 13, 8, 1, 4, 10, 7, 9, 5, 0, 15, 14, 2, 3, 12]
            ],
            [ // S8
                [13, 2, 8, 4, 6, 15, 11, 1, 10, 9, 3, 14, 5, 0, 12, 7],
                [1, 15, 13, 8, 10, 3, 7, 4, 12, 5, 6, 11, 0, 14, 9, 2],
                [7, 11, 4, 1, 9, 12, 14, 2, 0, 6, 10, 13, 15, 3, 5, 8],
                [2, 1, 14, 7, 4, 10, 8, 13, 15, 12, 9, 0, 3, 5, 6, 11]
            ]
        ];

        const SHIFTS = [1, 1, 2, 2, 2, 2, 2, 2, 1, 2, 2, 2, 2, 2, 2, 1];

        // Utility functions
        function permute(block, table) {
            let result = '';
            for (let i = 0; i < table.length; i++) {
                result += block[table[i] - 1];
            }
            return result;
        }

        function leftShift(key, shifts) {
            let left = key.substring(0, 28);
            let right = key.substring(28, 56);

            left = left.substring(shifts) + left.substring(0, shifts);
            right = right.substring(shifts) + right.substring(0, shifts);

            return left + right;
        }

        function generateSubkeys(key) {
            let left = key.substring(0, 28);
            let right = key.substring(28, 56);
            let subkeys = [];

            for (let i = 0; i < 16; i++) {
                left = left.substring(SHIFTS[i]) + left.substring(0, SHIFTS[i]);
                right = right.substring(SHIFTS[i]) + right.substring(0, SHIFTS[i]);
                let combined = left + right;
                subkeys.push(permute(combined, PC2));
            }

            return subkeys;
        }

        function feistel(rightBlock, subkey) {
            let expanded = permute(rightBlock, E);
            let xorResult = '';

            for (let i = 0; i < 48; i++) {
                xorResult += (expanded[i] === subkey[i] ? '0' : '1');
            }

            let sboxOutput = '';
            for (let i = 0; i < 8; i++) {
                let sixBits = xorResult.substring(i * 6, (i + 1) * 6);
                let row = parseInt(sixBits[0] + sixBits[5], 2);
                let col = parseInt(sixBits.substring(1, 5), 2);
                let sboxValue = S_BOXES[i][row][col].toString(2).padStart(4, '0');
                sboxOutput += sboxValue;
            }

            return permute(sboxOutput, P);
        }

        function desEncrypt(messageBlock, key) {
            // Initial permutation
            let permuted = permute(messageBlock, IP);
            let left = permuted.substring(0, 32);
            let right = permuted.substring(32, 64);

            // Generate subkeys
            let keyPermuted = permute(key, PC1);
            let subkeys = generateSubkeys(keyPermuted);

            // 16 rounds
            for (let i = 0; i < 16; i++) {
                let tempLeft = left;
                left = right;
                right = xor(tempLeft, feistel(right, subkeys[i]));
            }

            // Final permutation
            let finalBlock = right + left;
            return permute(finalBlock, FP);
        }

        function xor(a, b) {
            let result = '';
            for (let i = 0; i < a.length; i++) {
                result += (a[i] === b[i] ? '0' : '1');
            }
            return result;
        }

        function stringToBinary(str) {
            let result = '';
            for (let i = 0; i < str.length; i++) {
                result += str.charCodeAt(i).toString(2).padStart(8, '0');
            }
            return result;
        }

        function binaryToHex(binary) {
            let result = '';
            for (let i = 0; i < binary.length; i += 4) {
                let nibble = binary.substring(i, i + 4);
                result += parseInt(nibble, 2).toString(16).toUpperCase();
            }
            return result;
        }

        function desEncryptWithSteps(message, key, steps) {
            // Convert message to binary and pad to 64-bit blocks
            let messageBinary = stringToBinary(message);
            while (messageBinary.length % 64 !== 0) {
                messageBinary += '0';
            }
            let messageBlocks = [];
            for (let i = 0; i < messageBinary.length; i += 64) {
                messageBlocks.push(messageBinary.substring(i, i + 64));
            }

            // Convert key to binary and pad to 64 bits
            let keyBinary = stringToBinary(key);
            while (keyBinary.length < 64) {
                keyBinary += '0';
            }
            keyBinary = keyBinary.substring(0, 64);

            let output = '';

            for (let s = 2; s <= steps; s += 2) {
                let currentStepCiphertext = '';
                for (let block of messageBlocks) {
                    // Perform s rounds of DES
                    let permuted = permute(block, IP);
                    let left = permuted.substring(0, 32);
                    let right = permuted.substring(32, 64);

                    // Generate subkeys
                    let keyPermuted = permute(keyBinary, PC1);
                    let subkeys = generateSubkeys(keyPermuted);

                    // s rounds
                    for (let i = 0; i < s; i++) {
                        let tempLeft = left;
                        left = right;
                        right = xor(tempLeft, feistel(right, subkeys[i]));
                    }

                    // Only do final permutation if we've done 16 rounds
                    elseBlock = s === 16 ? permute(right + left, FP) : right + left;

                    currentStepCiphertext += binaryToHex(elseBlock);
                }

                // Format output with aligned columns
                output += s.toString().padEnd(8) + ' ' + currentStepCiphertext + '\n';
            }

            return output;
        }

        function updateDisplay() {
            const message = document.getElementById('input-message').value;
            const key = document.getElementById('input-key').value;
            const steps = parseInt(document.querySelector('input[name="steps"]:checked').value);

            const output = desEncryptWithSteps(message, key, steps);
            document.getElementById('output-results').textContent = output;
        }

        // Event listeners
        document.getElementById('input-message').addEventListener('input', updateDisplay);
        document.getElementById('input-key').addEventListener('input', updateDisplay);
        document.querySelectorAll('input[name="steps"]').forEach(radio => {
            radio.addEventListener('change', updateDisplay);
        });

        // Initialize on page load
        window.addEventListener('load', updateDisplay);
    </script>
</body>
