<body>
    <div id="demo-container">
        <!-- Control Panel Section -->
        <div id="control-panel">
            <button id="btn-reset">⟲ Reset</button>
            <div class="slider-row">
                <label for="slider-x1">x₁</label>
                <input type="range" id="slider-x1" min="-1000" max="1000" step="1" value="500">
                <span id="value-x1">500</span>
            </div>
            <div class="slider-row">
                <label for="slider-v1">v₁</label>
                <input type="range" id="slider-v1" min="-15" max="15" step="0.01" value="-10">
                <span id="value-v1">-10.00</span>
            </div>
            <div class="slider-row">
                <label for="slider-x2">x₂</label>
                <input type="range" id="slider-x2" min="-1000" max="1000" step="1" value="100">
                <span id="value-x2">100</span>
            </div>
            <div class="slider-row">
                <label for="slider-v2">v₂</label>
                <input type="range" id="slider-v2" min="-15" max="15" step="0.01" value="5">
                <span id="value-v2">5.00</span>
            </div>
        </div>

        <!-- Visualization Section -->
        <div id="visualization-area">
            <h2 id="result-text">T = 40.00 s | X = 100.00 m</h2>
            <div id="plot-container"></div>
        </div>
    </div>

    <script>
        // Get DOM elements
        const sliderX1 = document.getElementById('slider-x1');
        const sliderV1 = document.getElementById('slider-v1');
        const sliderX2 = document.getElementById('slider-x2');
        const sliderV2 = document.getElementById('slider-v2');

        const valueX1 = document.getElementById('value-x1');
        const valueV1 = document.getElementById('value-v1');
        const valueX2 = document.getElementById('value-x2');
        const valueV2 = document.getElementById('value-v2');

        const btnReset = document.getElementById('btn-reset');
        const resultText = document.getElementById('result-text');
        const plotContainer = document.getElementById('plot-container');

        // Function to update the visualization
        function updateVisualization() {
            // Get current slider values
            const x1 = parseFloat(sliderX1.value);
            const v1 = parseFloat(sliderV1.value);
            const x2 = parseFloat(sliderX2.value);
            const v2 = parseFloat(sliderV2.value);

            // Update value displays
            valueX1.textContent = x1;
            valueV1.textContent = v1.toFixed(2);
            valueX2.textContent = x2;
            valueV2.textContent = v2.toFixed(2);

            // Calculate intersection
            let T, X;
            let hasIntersection = false;

            if (v1 === v2) {
                resultText.textContent = "The runners' paths never intersect";
            } else {
                T = (x1 - x2) / (v2 - v1);
                X = x1 + v1 * T;

                if (T >= 0 && T <= 1000) {
                    resultText.textContent = `T = ${T.toFixed(2)} s | X = ${X.toFixed(2)} m`;
                    hasIntersection = true;
                } else {
                    resultText.textContent = "The runners' paths never intersect";
                }
            }

            // Create traces for Plotly
            const traceRunner1 = {
                x: [0, 1000],
                y: [x1, x1 + v1 * 1000],
                type: 'scatter',
                mode: 'lines',
                line: {
                    color: 'rgb(70, 130, 180)',
                    width: 3
                },
                name: 'Runner 1',
                hoverinfo: 'none'
            };

            const traceRunner2 = {
                x: [0, 1000],
                y: [x2, x2 + v2 * 1000],
                type: 'scatter',
                mode: 'lines',
                line: {
                    color: 'rgb(255, 165, 0)',
                    width: 3
                },
                name: 'Runner 2',
                hoverinfo: 'none'
            };

            let traceIntersection = {
                x: [],
                y: [],
                type: 'scatter',
                mode: 'markers',
                marker: {
                    color: 'rgb(34, 139, 34)',
                    size: 10,
                    line: {
                        color: 'rgb(0, 100, 0)',
                        width: 2
                    }
                },
                name: 'Intersection',
                hoverinfo: 'none'
            };

            if (hasIntersection) {
                traceIntersection.x = [T];
                traceIntersection.y = [X];
            }

            const data = [traceRunner1, traceRunner2, traceIntersection];

            const layout = {
                xaxis: {
                    title: 't',
                    range: [0, 1000],
                    gridcolor: '#e0e0e0',
                    zerolinecolor: '#999999'
                },
                yaxis: {
                    title: 'distance',
                    autorange: true,
                    gridcolor: '#e0e0e0',
                    zerolinecolor: '#999999'
                },
                showlegend: false,
                margin: {
                    t: 20,
                    r: 20,
                    b: 40,
                    l: 60
                },
                plot_bgcolor: '#ffffff',
                paper_bgcolor: '#ffffff'
            };

            const config = {
                responsive: true,
                displayModeBar: false
            };

            // Update or create the plot
            if (plotContainer.data) {
                Plotly.react(plotContainer, data, layout, config);
            } else {
                Plotly.newPlot(plotContainer, data, layout, config);
            }
        }

        // Event listeners for sliders
        sliderX1.addEventListener('input', updateVisualization);
        sliderV1.addEventListener('input', updateVisualization);
        sliderX2.addEventListener('input', updateVisualization);
        sliderV2.addEventListener('input', updateVisualization);

        // Event listener for reset button
        btnReset.addEventListener('click', function() {
            sliderX1.value = 500;
            sliderV1.value = -10;
            sliderX2.value = 100;
            sliderV2.value = 5;
            updateVisualization();
        });

        // Initialize the visualization
        updateVisualization();
    </script>
</body>
</html>
