        const resetBtn = document.getElementById('btn-reset');

        // Slider event listener
        slider.addEventListener('input', function() {
            sliderValue.textContent = this.value;
        });

        // Reset button event listener
        resetBtn.addEventListener('click', function() {
            slider.value = 0;
            sliderValue.textContent = '0';
        });

        // p5.js sketch
        let canvas;
        let hoveredLabel = null;

        // Label positions and sizes for hover detection
        const labels = [];

        // Tooltip content
        const tooltipContent = {
            'Clouds': "Water vapor condensed in the atmosphere.",
            'Rocks': "A primary reservoir of phosphorus in the form of phosphate.",
            'Animals': "Obtain phosphorus by eating plants or other animals.",
            'Plants': "Absorb phosphate from the soil to build organic molecules.",
            'Water': "Phosphorus is carried into water bodies by runoff and leaching.",
            'Erosion': "Weathering of rocks by wind and water releases phosphate.",
            'Runoff': "Phosphate from fertilizers and rocks is washed into rivers and oceans.",
            'Soil': "Phosphorus in the soil is available for plants.",
            'Leaching': "Phosphates in the soil are dissolved in water and carried away.",
            'Plant uptake': "Plants absorb inorganic phosphate from the soil through their roots.",
            'Animals eat plants': "Transfers phosphorus from plants to animals along the food chain.",
            'Decomposition': "Bacteria break down dead organic matter, returning phosphate to the soil and water.",
            'Geologic uplift': "Over millions of years, ocean sediments containing phosphate are lifted up to form new land."
        };

        function setup() {
            canvas = createCanvas(600, 500);
            canvas.parent('canvas-container');

            // Initialize static labels
            labels.push({text: 'Clouds', x: 120, y: 100, w: 60, h: 20});
            labels.push({text: 'Rocks', x: 160, y: 280, w: 50, h: 20});
            labels.push({text: 'Animals', x: 290, y: 320, w: 70, h: 20});
            labels.push({text: 'Plants', x: 380, y: 300, w: 50, h: 20});
            labels.push({text: 'Water', x: 100, y: 420, w: 60, h: 20});
        }

        function draw() {
            // Read slider value
            const phase = parseInt(slider.value);

            //Clear canvas
            background(255);

            // Draw background
            drawBackground();

            // Draw static elements
            drawSun();
            drawClouds();
            drawRain();
            drawRocks();
            drawAnimal();
            drawPlants();
            drawWater();

            // Draw dynamic elements based on phase
            if (phase >= 1) {
                drawErosion();
            }
            if (phase >= 2) {
                drawRunoff();
            }
            if (phase >= 3) {
                drawSoil();
            }
            if (phase >= 4) {
                drawLeaching();
            }
            if (phase >= 5) {
                drawPlantUptake();
            }
            if (phase >= 6) {
                drawAnimalsEatPlants();
            }
            if (phase >= 7) {
                drawDecomposition();
            }
            if (phase >= 8) {
                drawGeologicUplift();
            }

            // Check for hover
            hoveredLabel = null;
            for (let label of labels) {
                if (mouseX > label.x - label.w/2 && mouseX < label.x + label.w/2 &&
                    mouseY > label.y - label.h/2 && mouseY < label.y + label.h/2) {
                    hoveredLabel = label;
                    break;
                }
            }

            // Draw dynamic labels for processes
            if (phase >= 1) {
                labels.push({text: 'Erosion', x: 120, y: 180, w: 60, h: 20});
            }
            if (phase >= 2) {
                labels.push({text: 'Runoff', x: 180, y: 350, w: 60, h: 20});
            }
            if (phase >= 3) {
                labels.push({text: 'Soil', x: 250, y: 400, w: 40, h: 20});
            }
            if (phase >= 4) {
                labels.push({text: 'Leaching', x: 250, y: 370, w: 60, h: 20});
            }
            if (phase >= 5) {
                labels.push({text: 'Plant uptake', x: 340, y: 350, w: 90, h: 20});
            }
            if (phase >= 6) {
                labels.push({text: 'Animals eat plants', x: 350, y: 280, w: 120, h: 20});
            }
            if (phase >= 7) {
                labels.push({text: 'Decomposition', x: 320, y: 370, w: 90, h: 20});
            }
            if (phase >= 8) {
                labels.push({text: 'Geologic uplift', x: 300, y: 150, w: 110, h: 20});
            }

            // Draw tooltip if hovering
            if (hoveredLabel) {
                push();
                fill(0, 0, 0, 200);
                rect(mouseX + 10, mouseY - 30, textWidth(tooltipContent[hoveredLabel.text]) + 20, 25);
                fill(255);
                textAlign(LEFT);
                text(tooltipContent[hoveredLabel.text], mouseX + 20, mouseY - 10);
                pop();
            }

            // Reset dynamic labels for next frame
            labels.length = 5;
        }

        function drawBackground() {
            // Sky
            fill('#CAE9FF');
            rect(0, 0, width, height/2);

            // Ground
            fill('#9B7653');
            rect(0, height/2, width, height/2);
        }

        function drawSun() {
            push();
            fill('#FFDD00');
            // Sun rays
            for (let i = 0; i < 12; i++) {
                push();
                translate(500, 100);
                rotate(TWO_PI / 12 * i);
                triangle(40, -5, 40, 5, 65, 0);
                pop();
            }
            // Sun body
            circle(500, 100, 80);
            pop();
        }

        function drawClouds() {
            push();
            fill('#E0E0E0');
            // Cloud shape
            ellipse(80, 80, 60, 40);
            ellipse(120, 80, 80, 50);
            ellipse(160, 80, 60, 40);
            ellipse(120, 60, 70, 45);

            // Label
            fill('#505050');
            textAlign(CENTER);
            text('Clouds', 120, 100);
            pop();
        }

        function drawRain() {
            push();
            stroke('#0000FF');
            strokeWeight(2);
            for (let i = 0; i < 8; i++) {
                let x = 90 + i * 20;
                let y = 120 + sin(frameCount * 0.05 + i) * 10;
                line(x, y, x - 5, y + 15);
            }
            pop();
        }

        function drawRocks() {
            push();
            fill('#5A5A5A');
            // Rock shape
            ellipse(160, 260, 80, 60);
            ellipse(190, 270, 70, 50);
            ellipse(170, 285, 90, 55);

            // Label
            fill('#505050');
            textAlign(CENTER);
            text('Rocks', 160, 280);
            pop();
        }

        function drawAnimal() {
            push();
            // Body
            fill(255);
            stroke(0);
            strokeWeight(2);
            ellipse(300, 300, 80, 60);

            // Head
            ellipse(340, 290, 50, 50);

            // Legs
            rect(275, 320, 10, 30);
            rect(290, 320, 10, 30);
            rect(300, 320, 10, 30);
            rect(315, 320, 10, 30);

            // Spots
            noStroke();
            fill(0);
            ellipse(290, 290, 15, 15);
            ellipse(310, 295, 12, 12);
            ellipse(295, 310, 18, 18);

            // Eye
            fill('#0000FF');
            ellipse(350, 285, 8, 8);

            // Label
            fill('#505050');
            textAlign(CENTER);
            text('Animals', 290, 320);
            pop();
        }

        function drawPlants() {
            push();
            fill('#90EE90');
            noStroke();
            // Three plants
            triangle(380, 320, 390, 290, 400, 320);
            triangle(400, 310, 410, 280, 420, 310);
            triangle(420, 315, 430, 285, 440, 315);

            // Label
            fill('#006400');
            textAlign(CENTER);
            text('Plants', 380, 300);
            pop();
        }

        function drawWater() {
            push();
            fill('#0000FF', 150);
            ellipse(80, 450, 120, 70);
            ellipse(130, 460, 100, 60);

            // Label
            fill('#00008B');
            textAlign(CENTER);
            text('Water', 100, 420);
            pop();
        }

        function drawArrow(fromX, fromY, toX, toY) {
            push();
            stroke(0);
            strokeWeight(2);
            line(fromX, fromY, toX, toY);

            // Arrowhead
            let angle = atan2(toY - fromY, toX - fromX);
            push();
            translate(toX, toY);
            rotate(angle);
            triangle(-10, -5, -10, 5, 0, 0);
            pop();
            pop();
        }

        function drawProcessLabel(x, y, text) {
            push();
            fill(0);
            textAlign(CENTER);
            text(text, x, y);
            pop();
        }

        function drawErosion() {
            drawArrow(120, 140, 170, 260);
            drawProcessLabel(120, 180, 'Erosion');
        }

        function drawRunoff() {
            drawArrow(180, 320, 120, 420);
            drawProcessLabel(180, 350, 'Runoff');
        }

        function drawSoil() {
            drawArrow(130, 440, 220, 400);
            drawProcessLabel(250, 400, 'Soil');
        }

        function drawLeaching() {
            drawArrow(220, 350, 150, 440);
            drawProcessLabel(250, 370, 'Leaching');
        }

        function drawPlantUptake() {
            drawArrow(340, 370, 390, 330);
            drawProcessLabel(340, 350, 'Plant uptake');
        }

        function drawAnimalsEatPlants() {
            drawArrow(380, 290, 340, 290);
            drawProcessLabel(350, 280, 'Animals eat plants');
        }

        function drawDecomposition() {
            drawArrow(310, 330, 340, 370);
            drawProcessLabel(320, 370, 'Decomposition');
        }

        function drawGeologicUplift() {
            push();
            noFill();
            stroke(0);
            strokeWeight(2);
            beginShape();
            vertex(100, 480);
            bezierVertex(200, 400, 300, 300, 170, 280);
            endShape();

            // Arrowhead
            let angle = PI/4;
            push();
            translate(170, 280);
            rotate(angle);
            triangle(-10, -5, -10, 5, 0, 0);
            pop();

            drawProcessLabel(300, 150, 'Geologic uplift');
            pop();
        }
    </script>
</body>
</html>
